  INSERT INTO words (word, word_type, description) VALUES  (    'Scarves', 'pl. ', 'of Scarf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'n.', 'An article of dress of a light and decorative character,\n   worn loosely over the shoulders or about the neck or the waist; a light\n   shawl or handkerchief for the neck; also, a cravat; a neckcloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarfed', 'imp. & p. p.', 'of Scarf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarfing', 'p. pr. & vb. n.', 'of Scarf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'v. t.', 'To throw on loosely; to put on like a scarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'v. t.', 'To dress with a scarf, or as with a scarf; to cover with\n   a loose wrapping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'v. t.', 'To form a scarf on the end or edge of, as for a joint in\n   timber, metal rods, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'v. t.', 'To unite, as two pieces of timber or metal, by a scarf\n   joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'n.', 'In a piece which is to be united to another by a scarf\n   joint, the part of the end or edge that is tapered off, rabbeted, or\n   notched so as to be thinner than the rest of the piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarf', 'n.', 'A scarf joint.');
