  INSERT INTO words (word, word_type, description) VALUES  (    'Scarifier', 'n.', 'One who scarifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarifier', 'n.', 'The instrument used for scarifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarifier', 'n.', 'An implement for stripping and loosening the soil,\n   without bringing up a fresh surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarified', 'imp. & p. p.', 'of Scarify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarifying', 'p. pr. & vb. n.', 'of Scarify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarify', 'v. t.', 'To scratch or cut the skin of; esp. (Med.), to make\n   small incisions in, by means of a lancet or scarificator, so as to draw\n   blood from the smaller vessels without opening a large vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarify', 'v. t.', 'To stir the surface soil of, as a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scariose', 'a.', 'Alt. of Scarious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarious', 'a.', 'Thin, dry, membranous, and not green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarlatina', 'n.', 'Scarlet fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarless', 'a.', 'Free from scar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarlet', 'n.', 'A deep bright red tinged with orange or yellow, -- of many\n   tints and shades; a vivid or bright red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarlet', 'n.', 'Cloth of a scarlet color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarlet', 'a.', 'Of the color called scarlet; as, a scarlet cloth or\n   thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarlet', 'v. t.', 'To dye or tinge with scarlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarmage', 'n.', 'Alt. of Scarmoge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarmoge', 'n.', 'A slight contest; a skirmish. See Skirmish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarn', 'n.', 'Dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaroid', 'a.', 'Of or pertaining to the Scaridae, a family of marine\n   fishes including the parrot fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarp', 'n.', 'A band in the same position as the bend sinister, but only\n   half as broad as the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarp', 'n.', 'The slope of the ditch nearest the parapet; the escarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarp', 'n.', 'A steep descent or declivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarped', 'imp. & p. p.', 'of Scarp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarping', 'p. pr. & vb. n.', 'of Scarp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarp', 'v. t.', 'To cut down perpendicularly, or nearly so; as, to scarp\n   the face of a ditch or a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarring', 'n.', 'A scar; a mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarry', 'a.', 'Bearing scars or marks of wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarry', 'a.', 'Like a scar, or rocky eminence; containing scars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scarus', 'n.', 'A Mediterranean food fish (Sparisoma scarus) of excellent\n   quality and highly valued by the Romans; -- called also parrot fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scary', 'n.', 'Barren land having only a thin coat of grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scary', 'a.', 'Subject to sudden alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scary', 'a.', 'Causing fright; alarming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scasely', 'adv.', 'Scarcely; hardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scat', 'interj.', 'Go away; begone; away; -- chiefly used in driving off a\n   cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scat', 'n.', 'Alt. of Scatt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatt', 'n.', 'Tribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scat', 'n.', 'A shower of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatch', 'n.', 'A kind of bit for the bridle of a horse; -- called also\n   scatchmouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatches', 'n. pl.', 'Stilts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scate', 'n.', 'See Skate, for the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatebrous', 'a.', 'Abounding with springs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scath', 'v.', 'Harm; damage; injury; hurt; waste; misfortune.');
