  INSERT INTO words (word, word_type, description) VALUES  (    'Scath', 'v. t.', 'To do harm to; to injure; to damage; to waste; to\n   destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scathful', 'a.', 'Harmful; doing damage; pernicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scathless', 'a.', 'Unharmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scathly', 'a.', 'Injurious; scathful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scattered', 'imp. & p. p.', 'of Scatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scattering', 'p. pr. & vb. n.', 'of Scatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatter', 'v. t.', 'To strew about; to sprinkle around; to throw down\n   loosely; to deposit or place here and there, esp. in an open or sparse\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatter', 'v. t.', 'To cause to separate in different directions; to reduce\n   from a close or compact to a loose or broken order; to dissipate; to\n   disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatter', 'v. t.', 'Hence, to frustrate, disappoint, and overthrow; as, to\n   scatter hopes, plans, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatter', 'v. i.', 'To be dispersed or dissipated; to disperse or separate;\n   as, clouds scatter after a storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatter-brain', 'n.', 'A giddy or thoughtless person; one incapable of\n   concentration or attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatter-brained', 'a.', 'Giddy; thoughtless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scattered', 'a.', 'Dispersed; dissipated; sprinkled, or loosely spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scattered', 'a.', 'Irregular in position; having no regular order; as,\n   scattered leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scattergood', 'n.', 'One who wastes; a spendthrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scattering', 'a.', 'Going or falling in various directions; not united or\n   aggregated; divided among many; as, scattering votes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scattering', 'n.', 'Act of strewing about; something scattered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatteringly', 'adv.', 'In a scattering manner; dispersedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scatterling', 'n.', 'One who has no fixed habitation or residence; a\n   vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaturient', 'a.', 'Gushing forth; full to overflowing; effusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaturiginous', 'a.', 'Abounding with springs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaup', 'n.', 'A bed or stratum of shellfish; scalp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaup', 'n.', 'A scaup duck. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scauper', 'n.', 'A tool with a semicircular edge, -- used by engravers to\n   clear away the spaces between the lines of an engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scaur', 'n.', 'A precipitous bank or rock; a scar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scavage', 'n.', 'A toll or duty formerly exacted of merchant strangers by\n   mayors, sheriffs, etc., for goods shown or offered for sale within\n   their precincts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scavenge', 'v. t.', 'To cleanse, as streets, from filth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scavenger', 'v.', 'A person whose employment is to clean the streets of a\n   city, by scraping or sweeping, and carrying off the filth. The name is\n   also applied to any animal which devours refuse, carrion, or anything\n   injurious to health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scazon', 'n.', 'A choliamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scelerat', 'n.', 'A villain; a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scelestic', 'a.', 'Evil; wicked; atrocious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scelet', 'n.', 'A mummy; a skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scena', 'n.', 'A scene in an opera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scena', 'n.', 'An accompanied dramatic recitative, interspersed with\n   passages of melody, or followed by a full aria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenario', 'n.', 'A preliminary sketch of the plot, or main incidents, of\n   an opera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenary', 'n.', 'Scenery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'n.', 'The structure on which a spectacle or play is exhibited; the\n   part of a theater in which the acting is done, with its adjuncts and\n   decorations; the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'n.', 'The decorations and fittings of a stage, representing the\n   place in which the action is supposed to go on; one of the slides, or\n   other devices, used to give an appearance of reality to the action of a\n   play; as, to paint scenes; to shift the scenes; to go behind the\n   scenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'n.', 'So much of a play as passes without change of locality or\n   time, or important change of character; hence, a subdivision of an act;\n   a separate portion of a play, subordinate to the act, but differently\n   determined in different plays; as, an act of four scenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'n.', 'The place, time, circumstance, etc., in which anything\n   occurs, or in which the action of a story, play, or the like, is laid;\n   surroundings amid which anything is set before the imagination; place\n   of occurrence, exhibition, or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'n.', 'An assemblage of objects presented to the view at once; a\n   series of actions and events exhibited in their connection; a\n   spectacle; a show; an exhibition; a view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'n.', 'A landscape, or part of a landscape; scenery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'n.', 'An exhibition of passionate or strong feeling before others;\n   often, an artifical or affected action, or course of action, done for\n   effect; a theatrical display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scene', 'v. t.', 'To exhibit as a scene; to make a scene of; to display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sceneful', 'a.', 'Having much scenery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenemen', 'pl. ', 'of Sceneman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sceneman', 'n.', 'The man who manages the movable scenes in a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenery', 'n.', 'Assemblage of scenes; the paintings and hangings\n   representing the scenes of a play; the disposition and arrangement of\n   the scenes in which the action of a play, poem, etc., is laid;\n   representation of place of action or occurence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenery', 'n.', 'Sum of scenes or views; general aspect, as regards variety\n   and beauty or the reverse, in a landscape; combination of natural\n   views, as woods, hills, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sceneshifter', 'n.', 'One who moves the scenes in a theater; a sceneman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenic', 'a.', 'Alt. of Scenical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenical', 'a.', 'Of or pertaining to scenery; of the nature of scenery;\n   theatrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenograph', 'n.', 'A perspective representation or general view of an\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenographic', 'a.', 'Alt. of Scenographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenographical', 'a.', 'Of or pertaining to scenography; drawn in\n   perspective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenography', 'n.', 'The art or act of representing a body on a perspective\n   plane; also, a representation or description of a body, in all its\n   dimensions, as it appears to the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scented', 'imp. & p. p.', 'of Scent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scenting', 'p. pr. & vb. n.', 'of Scent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scent', 'v. t.', 'To perceive by the olfactory organs; to smell; as, to\n   scent game, as a hound does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scent', 'v. t.', 'To imbue or fill with odor; to perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scent', 'v. i.', 'To have a smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scent', 'v. i.', 'To hunt animals by means of the sense of smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scent', 'n.', 'That which, issuing from a body, affects the olfactory\n   organs of animals; odor; smell; as, the scent of an orange, or of a\n   rose; the scent of musk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scent', 'n.', 'Specifically, the odor left by an animal on the ground in\n   passing over it; as, dogs find or lose the scent; hence, course of\n   pursuit; track of discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scent', 'n.', 'The power of smelling; the sense of smell; as, a hound of\n   nice scent; to divert the scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scentful', 'a.', 'Full of scent or odor; odorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scentful', 'a.', 'Of quick or keen smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scentingly', 'adv.', 'By scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scentless', 'a.', 'Having no scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scepsis', 'n.', 'Skepticism; skeptical philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scepter', 'n.', 'Alt. of Sceptre');
