  INSERT INTO words (word, word_type, description) VALUES  (    'Schemata', 'pl. ', 'of Schema');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schemas', 'pl. ', 'of Schema');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schema', 'n.', 'An outline or image universally applicable to a general\n   conception, under which it is likely to be presented to the mind; as,\n   five dots in a line are a schema of the number five; a preceding and\n   succeeding event are a schema of cause and effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schematic', 'a.', 'Of or pertaining to a scheme or a schema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schematism', 'n.', 'Combination of the aspects of heavenly bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schematism', 'n.', 'Particular form or disposition of a thing; an\n   exhibition in outline of any systematic arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schematist', 'n.', 'One given to forming schemes; a projector; a schemer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schematize', 'v. i.', 'To form a scheme or schemes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheme', 'n.', 'A combination of things connected and adjusted by design; a\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheme', 'n.', 'A plan or theory something to be done; a design; a project;\n   as, to form a scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheme', 'n.', 'Any lineal or mathematical diagram; an outline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheme', 'n.', 'A representation of the aspects of the celestial bodies for\n   any moment or at a given event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schemed', 'imp. & p. p.', 'of Scheme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheming', 'p. pr. & vb. n.', 'of Scheme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheme', 'v. t.', 'To make a scheme of; to plan; to design; to project; to\n   plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheme', 'v. i.', 'To form a scheme or schemes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schemeful', 'a.', 'Full of schemes or plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schemer', 'n.', 'One who forms schemes; a projector; esp., a plotter; an\n   intriguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scheming', 'a.', 'Given to forming schemes; artful; intriguing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schemist', 'n.', 'A schemer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schene', 'n.', 'An Egyptian or Persian measure of length, varying from\n   thirty-two to sixty stadia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schenkbeer', 'n.', 'A mild German beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scherbet', 'n.', 'See Sherbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scherif', 'n.', 'See Sherif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scherzando', 'adv.', 'In a playful or sportive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scherzo', 'n.', 'A playful, humorous movement, commonly in 3-4 measure,\n   which often takes the place of the old minuet and trio in a sonata or a\n   symphony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schesis', 'n.', 'General state or disposition of the body or mind, or of\n   one thing with regard to other things; habitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schesis', 'n.', 'A figure of speech whereby the mental habitude of an\n   adversary or opponent is feigned for the purpose of arguing against\n   him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schetic', 'a.', 'Alt. of Schetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schetical', 'a.', 'Of or pertaining to the habit of the body;\n   constitutional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schiedam', 'n.', 'Holland gin made at Schiedam in the Netherlands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schiller', 'n.', 'The peculiar bronzelike luster observed in certain\n   minerals, as hypersthene, schiller spar, etc. It is due to the presence\n   of minute inclusions in parallel position, and is sometimes of\n   secondary origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schilerization', 'n.', 'The act or process of producing schiller in a\n   mineral mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schilling', 'n.', 'Any one of several small German and Dutch coins, worth\n   from about one and a half cents to about five cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schindylesis', 'n.', 'A form of articulation in which one bone is received\n   into a groove or slit in another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schirrhus', 'n.', 'See Scirrhus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schism', 'n.', 'Division or separation; specifically (Eccl.), permanent\n   division or separation in the Christian church; breach of unity among\n   people of the same religious faith; the offense of seeking to produce\n   division in a church without justifiable cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schisma', 'n.', 'An interval equal to half a comma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schismatic', 'a.', 'Of or pertaining to schism; implying schism; partaking\n   of the nature of schism; tending to schism; as, schismatic opinions or\n   proposals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schismatic', 'n.', 'One who creates or takes part in schism; one who\n   separates from an established church or religious communion on account\n   of a difference of opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schismatical', 'a.', 'Same as Schismatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schismatized', 'imp. & p. p.', 'of Schismatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schismatizing', 'p. pr. & vb. n.', 'of Schismatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schismatize', 'v. i.', 'To take part in schism; to make a breach of\n   communion in the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schismless', 'a.', 'Free from schism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schist', 'n.', 'Any crystalline rock having a foliated structure (see\n   Foliation) and hence admitting of ready division into slabs or slates.\n   The common kinds are mica schist, and hornblendic schist, consisting\n   chiefly of quartz with mica or hornblende and often feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schistaceous', 'a.', 'Of a slate color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schistic', 'a.', 'Schistose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schistose', 'a.', 'Alt. of Schistous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schistous', 'a.', 'Of or pertaining to schist; having the structure of a\n   schist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schistosity', 'n.', 'The quality or state of being schistose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizo-', '', 'A combining form denoting division or cleavage; as,\n   schizogenesis, reproduction by fission or cell division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizocarp', 'n.', 'A dry fruit which splits at maturity into several\n   closed one-seeded portions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizocoele', 'n.', 'See Enterocoele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizocoelous', 'a.', 'Pertaining to, or of the nature of, a schizocoele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizogenesis', 'n.', 'Reproduction by fission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizognath', 'n.', 'Any bird with a schizognathous palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizognathae', 'n. pl.', 'The schizognathous birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizognathism', 'n.', 'The condition of having a schizognathous palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizognathous', 'a.', 'Having the maxillo-palatine bones separate from\n   each other and from the vomer, which is pointed in front, as in the\n   gulls, snipes, grouse, and many other birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizomycetes', 'n. pl.', 'An order of Schizophyta, including the so-called\n   fission fungi, or bacteria. See Schizophyta, in the Supplement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizonemertea', 'n. pl.', 'A group of nemerteans comprising those having a\n   deep slit along each side of the head. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizopelmous', 'a.', 'Having the two flexor tendons of the toes entirely\n   separate, and the flexor hallucis going to the first toe only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizophyte', 'n.', 'One of a class of vegetable organisms, in the\n   classification of Cohn, which includes all of the inferior forms that\n   multiply by fission, whether they contain chlorophyll or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizopod', 'n.', 'one of the Schizopoda. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizopod', 'a.', 'Alt. of Schizopodous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizopodous', 'a.', 'Of or pertaining to a schizopod, or the Schizopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schizopoda', 'n. pl.', 'A division of shrimplike Thoracostraca in which\n   each of the thoracic legs has a long fringed upper branch (exopodite)\n   for swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scizorhinal', 'a.', 'Having the nasal bones separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scizorhinal', 'a.', 'Having the anterior nostrils prolonged backward in the\n   form of a slit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schlich', 'n.', 'The finer portion of a crushed ore, as of gold, lead, or\n   tin, separated by the water in certain wet processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schmelze', 'n.', 'A kind of glass of a red or ruby color, made in Bohemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schnapps', 'n.', 'Holland gin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schneiderian', 'a.', 'Discovered or described by C. V. Schneider, a German\n   anatomist of the seventeenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoharie grit', '', 'The formation belonging to the middle of the three\n   subdivisions of the Corniferous period in the American Devonian system;\n   -- so called from Schoharie, in New York, where it occurs. See the\n   Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholar', 'n.', 'One who attends a school; one who learns of a teacher; one\n   under the tuition of a preceptor; a pupil; a disciple; a learner; a\n   student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholar', 'n.', 'One engaged in the pursuits of learning; a learned person;\n   one versed in any branch, or in many branches, of knowledge; a person\n   of high literary or scientific attainments; a savant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholar', 'n.', 'A man of books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholar', 'n.', 'In English universities, an undergraduate who belongs to\n   the foundation of a college, and receives support in part from its\n   revenues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholarity', 'n.', 'Scholarship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholarlike', 'a.', 'Scholarly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholarly', 'a.', 'Like a scholar, or learned person; showing the qualities\n   of a scholar; as, a scholarly essay or critique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholarly', 'adv.', 'In a scholarly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholarship', 'n.', 'The character and qualities of a scholar; attainments\n   in science or literature; erudition; learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholarship', 'n.', 'Literary education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholarship', 'n.', 'Maintenance for a scholar; a foundation for the\n   support of a student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholastic', 'a.', 'Pertaining to, or suiting, a scholar, a school, or\n   schools; scholarlike; as, scholastic manners or pride; scholastic\n   learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholastic', 'a.', 'Of or pertaining to the schoolmen and divines of the\n   Middle Ages (see Schoolman); as, scholastic divinity or theology;\n   scholastic philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholastic', 'a.', 'Hence, characterized by excessive subtilty, or\n   needlessly minute subdivisions; pedantic; formal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholastic', 'n.', 'One who adheres to the method or subtilties of the\n   schools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholastic', 'n.', 'See the Note under Jesuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholastical', 'a. & n.', 'Scholastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholastically', 'adv.', 'In a scholastic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholasticism', 'n.', 'The method or subtilties of the schools of\n   philosophy; scholastic formality; scholastic doctrines or philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholia', 'n. pl.', 'See Scholium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholiast', 'n.', 'A maker of scholia; a commentator or annotator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholiastic', 'a.', 'Of or pertaining to a scholiast, or his pursuits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholiaze', 'v. i.', 'To write scholia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholical', 'a.', 'Scholastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholion', 'n.', 'A scholium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholia', 'pl. ', 'of Scholium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholiums', 'pl. ', 'of Scholium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholium', 'n.', 'A marginal annotation; an explanatory remark or comment;\n   specifically, an explanatory comment on the text of a classic author by\n   an early grammarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholium', 'n.', 'A remark or observation subjoined to a demonstration or a\n   train of reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholy', 'n.', 'A scholium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scholy', 'v. i. & t.', 'To write scholia; to annotate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'A shoal; a multitude; as, a school of fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'A place for learned intercourse and instruction; an\n   institution for learning; an educational establishment; a place for\n   acquiring knowledge and mental training; as, the school of the\n   prophets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'A place of primary instruction; an establishment for the\n   instruction of children; as, a primary school; a common school; a\n   grammar school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'A session of an institution of instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'One of the seminaries for teaching logic, metaphysics, and\n   theology, which were formed in the Middle Ages, and which were\n   characterized by academical disputations and subtilties of reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'The room or hall in English universities where the\n   examinations for degrees and honors are held.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'An assemblage of scholars; those who attend upon\n   instruction in a school of any kind; a body of pupils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'The disciples or followers of a teacher; those who hold a\n   common doctrine, or accept the same teachings; a sect or denomination\n   in philosophy, theology, science, medicine, politics, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'The canons, precepts, or body of opinion or practice,\n   sanctioned by the authority of a particular class or age; as, he was a\n   gentleman of the old school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'n.', 'Figuratively, any means of knowledge or discipline; as, the\n   school of experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooled', 'imp. & p. p.', 'of School');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooling', 'p. pr. & vb. n.', 'of School');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'v. t.', 'To train in an institution of learning; to educate at a\n   school; to teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School', 'v. t.', 'To tutor; to chide and admonish; to reprove; to subject\n   to systematic discipline; to train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolbook', 'n.', 'A book used in schools for learning lessons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolboy', 'n.', 'A boy belonging to, or attending, a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooldame', 'n.', 'A schoolmistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolery', 'n.', 'Something taught; precepts; schooling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolfellow', 'n.', 'One bred at the same school; an associate in school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolgirl', 'n.', 'A girl belonging to, or attending, a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolhouse', 'n.', 'A house appropriated for the use of a school or\n   schools, or for instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooling', 'n.', 'Instruction in school; tuition; education in an\n   institution of learning; act of teaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooling', 'n.', 'Discipline; reproof; reprimand; as, he gave his son a\n   good schooling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooling', 'n.', 'Compensation for instruction; price or reward paid to an\n   instructor for teaching pupils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooling', 'a.', 'Collecting or running in schools or shoals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolmaam', 'n.', 'A schoolmistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolmaid', 'n.', 'A schoolgirl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolmen', 'pl. ', 'of Schoolman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolman', 'n.', 'One versed in the niceties of academical disputation or\n   of school divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolmaster', 'n.', 'The man who presides over and teaches a school; a\n   male teacher of a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolmaster', 'n.', 'One who, or that which, disciplines and directs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolmate', 'n.', 'A pupil who attends the same school as another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolmistress', 'n.', 'A woman who governs and teaches a school; a female\n   school-teacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolroom', 'n.', 'A room in which pupils are taught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolship', 'n.', 'A vessel employed as a nautical training school, in\n   which naval apprentices receive their education at the expense of the\n   state, and are trained for service as sailors. Also, a vessel used as a\n   reform school to which boys are committed by the courts to be\n   disciplined, and instructed as mariners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'School-teacher', 'n.', 'One who teaches or instructs a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schoolward', 'adv.', 'Toward school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooner', 'n.', 'Originally, a small, sharp-built vessel, with two masts\n   and fore-and-aft rig. Sometimes it carried square topsails on one or\n   both masts and was called a topsail schooner. About 1840, longer\n   vessels with three masts, fore-and-aft rigged, came into use, and since\n   that time vessels with four masts and even with six masts, so rigged,\n   are built. Schooners with more than two masts are designated\n   three-masted schooners, four-masted schooners, etc. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schooner', 'n.', 'A large goblet or drinking glass, -- used for lager beer\n   or ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schorl', 'n.', 'Black tourmaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schorlaceous', 'a.', 'Partaking of the nature and character of schorl;\n   resembling schorl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schorlous', 'a.', 'Schorlaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schorly', 'a.', 'Pertaining to, or containing, schorl; as, schorly granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schottish', 'n.', 'Alt. of Schottische');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schottische', 'n.', 'A Scotch round dance in 2-4 time, similar to the\n   polka, only slower; also, the music for such a dance; -- not to be\n   confounded with the Ecossaise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schreibersite', 'n.', 'A mineral occurring in steel-gray flexible folia. It\n   contains iron, nickel, and phosphorus, and is found only in meteoric\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schrode', 'n.', 'See Scrod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schwanns sheath', '', 'The neurilemma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schwanns white substance', '', 'The substance of the medullary sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schwanpan', 'n.', 'Chinese abacus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schweitzerkase', 'n.', 'Gruyere cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schwenkfelder', 'n.', 'Alt. of Schwenkfeldian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Schwenkfeldian', 'n.', 'A member of a religious sect founded by Kaspar von\n   Schwenkfeld, a Silesian reformer who disagreed with Luther, especially\n   on the deification of the body of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciaenoid', 'a.', 'Of or pertaining to the Sciaenidae, a family of marine\n   fishes which includes the meagre, the squeteague, and the kingfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciagraph', 'n.', 'An old term for a vertical section of a building; --\n   called also sciagraphy. See Vertical section, under Section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciagraph', 'n.', 'A radiograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciagraphical', 'a.', 'Pertaining to sciagraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciagraphy', 'n.', 'The art or science of projecting or delineating shadows\n   as they fall in nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciagraphy', 'n.', 'Same as Sciagraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciamachy', 'n.', 'See Sciomachy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciatheric', 'a.', 'Alt. of Sciatherical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciatherical', 'a.', 'Belonging to a sundial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciatic', 'a.', 'Of or pertaining to the hip; in the region of, or\n   affecting, the hip; ischial; ischiatic; as, the sciatic nerve, sciatic\n   pains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciatic', 'n.', 'Sciatica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciatica', 'n.', 'Neuralgia of the sciatic nerve, an affection\n   characterized by paroxysmal attacks of pain in the buttock, back of the\n   thigh, or in the leg or foot, following the course of the branches of\n   the sciatic nerve. The name is also popularly applied to various\n   painful affections of the hip and the parts adjoining it. See Ischiadic\n   passion, under Ischiadic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciatical', 'a.', 'Sciatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciatically', 'adv.', 'With, or by means of, sciatica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scibboleth', 'n.', 'Shibboleth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Science', 'n.', 'Knowledge; knowledge of principles and causes; ascertained\n   truth of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Science', 'n.', 'Accumulated and established knowledge, which has been\n   systematized and formulated with reference to the discovery of general\n   truths or the operation of general laws; knowledge classified and made\n   available in work, life, or the search for truth; comprehensive,\n   profound, or philosophical knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Science', 'n.', 'Especially, such knowledge when it relates to the physical\n   world and its phenomena, the nature, constitution, and forces of\n   matter, the qualities and functions of living tissues, etc.; -- called\n   also natural science, and physical science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Science', 'n.', 'Any branch or department of systematized knowledge\n   considered as a distinct field of investigation or object of study; as,\n   the science of astronomy, of chemistry, or of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Science', 'n.', 'Art, skill, or expertness, regarded as the result of\n   knowledge of laws and principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Science', 'v. t.', 'To cause to become versed in science; to make skilled;\n   to instruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scient', 'a.', 'Knowing; skillful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scienter', 'adv.', 'Knowingly; willfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciential', 'a.', 'Pertaining to, or producing, science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scientific', 'a.', 'Of or pertaining to science; used in science; as,\n   scientific principles; scientific apparatus; scientific observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scientific', 'a.', 'Agreeing with, or depending on, the rules or principles\n   of science; as, a scientific classification; a scientific arrangement\n   of fossils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scientific', 'a.', 'Having a knowledge of science, or of a science;\n   evincing science or systematic knowledge; as, a scientific chemist; a\n   scientific reasoner; a scientific argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scientifical', 'a.', 'Scientific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scientifically', 'adv.', 'In a scientific manner; according to the rules or\n   principles of science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scientist', 'n.', 'One learned in science; a scientific investigator; one\n   devoted to scientific study; a savant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scilicet', 'adv.', 'To wit; namely; videlicet; -- often abbreviated to sc.,\n   or ss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scillain', 'n.', 'A glucoside extracted from squill (Scilla) as a light\n   porous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scillitin', 'n.', 'A bitter principle extracted from the bulbs of the\n   squill (Scilla), and probably consisting of a complex mixture of\n   several substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scimiter', 'n.', 'Alt. of Scimitar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scimitar', 'n.', 'A saber with a much curved blade having the edge on the\n   convex side, -- in use among Mohammedans, esp., the Arabs and persians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scimitar', 'n.', 'A long-handled billhook. See Billhook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scincoid', 'a.', 'Of or pertaining to the family Scincidae, or skinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scincoid', 'n.', 'A scincoidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scincoidea', 'n. pl.', 'A tribe of lizards including the skinks. See Skink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scincoidian', 'n.', 'Any one of numerous species of lizards of the family\n   Scincidae or tribe Scincoidea. The tongue is not extensile. The body\n   and tail are covered with overlapping scales, and the toes are\n   margined. See Illust. under Skink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciniph', 'n.', 'Some kind of stinging or biting insect, as a flea, a gnat,\n   a sandfly, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scink', 'n.', 'A skink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scink', 'n.', 'A slunk calf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintilla', 'n.', 'A spark; the least particle; an iota; a tittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillant', 'a.', 'Emitting sparks, or fine igneous particles; sparkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillated', 'imp. & p. p.', 'of Scintillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillating', 'p. pr. & vb. n.', 'of Scintillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillate', 'v. i.', 'To emit sparks, or fine igneous particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillate', 'v. i.', 'To sparkle, as the fixed stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillation', 'n.', 'The act of scintillating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillation', 'n.', 'A spark or flash emitted in scintillating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillous', 'a.', 'Scintillant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scintillously', 'adv.', 'In a scintillant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciography', 'n.', 'See Sciagraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciolism', 'n.', 'The knowledge of a sciolist; superficial knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciolist', 'n.', 'One who knows many things superficially; a pretender to\n   science; a smatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciolistic', 'a.', 'Of or pertaining to sciolism, or a sciolist; partaking\n   of sciolism; resembling a sciolist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciolous', 'a.', 'Knowing superficially or imperfectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciomachy', 'n.', 'A fighting with a shadow; a mock contest; an imaginary\n   or futile combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciomancy', 'n.', 'Divination by means of shadows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scion', 'n.', 'A shoot or sprout of a plant; a sucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scion', 'n.', 'A piece of a slender branch or twig cut for grafting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scion', 'n.', 'Hence, a descendant; an heir; as, a scion of a royal stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scioptic', 'a.', 'Of or pertaining to an optical arrangement for forming\n   images in a darkened room, usually called scioptic ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciopticon', 'n.', 'A kind of magic lantern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scioptics', 'n.', 'The art or process of exhibiting luminous images,\n   especially those of external objects, in a darkened room, by\n   arrangements of lenses or mirrors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scioptric', 'a.', 'Scioptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciot', 'a.', 'Of or pertaining to the island Scio (Chio or Chios).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciot', 'n.', 'A native or inhabitant of Scio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciotheric', 'a.', 'Of or pertaining to a sundial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scious', 'a.', 'Knowing; having knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scire facias', '', 'A judicial writ, founded upon some record, and\n   requiring the party proceeded against to show cause why the party\n   bringing it should not have advantage of such record, or (as in the\n   case of scire facias to repeal letters patent) why the record should\n   not be annulled or vacated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scirrhoid', 'a.', 'Resembling scirrhus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scirrhosity', 'n.', 'A morbid induration, as of a gland; state of being\n   scirrhous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scirrhous', 'a.', 'Proceeding from scirrhus; of the nature of scirrhus;\n   indurated; knotty; as, scirrhous affections; scirrhous disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scirrhi', 'pl. ', 'of Scirrhus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scirrhuses', 'pl. ', 'of Scirrhus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scirrhus', 'n.', 'An indurated organ or part; especially, an indurated\n   gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scirrhus', 'n.', 'A cancerous tumor which is hard, translucent, of a gray\n   or bluish color, and emits a creaking sound when incised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciscitation', 'n.', 'The act of inquiring; inquiry; demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scise', 'v. i.', 'To cut; to penetrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissel', 'n.', 'The clippings of metals made in various mechanical\n   operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissel', 'n.', 'The slips or plates of metal out of which circular blanks\n   have been cut for the purpose of coinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissible', 'a.', 'Capable of being cut or divided by a sharp instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissil', 'n.', 'See Scissel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissile', 'a.', 'Capable of being cut smoothly; scissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scission', 'n.', 'The act of dividing with an instrument having a sharp\n   edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissiparity', 'n.', 'Reproduction by fission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissor', 'v. t.', 'To cut with scissors or shears; to prepare with the aid\n   of scissors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissors', 'n. pl.', 'A cutting instrument resembling shears, but smaller,\n   consisting of two cutting blades with handles, movable on a pin in the\n   center, by which they are held together. Often called a pair of\n   scissors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissorsbill', 'n.', 'See Skimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissorstail', 'n.', 'A tyrant flycatcher (Milvulus forficatus) of the\n   Southern United States and Mexico, which has a deeply forked tail. It\n   is light gray above, white beneath, salmon on the flanks, and fiery red\n   at the base of the crown feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissors-tailed', 'a.', 'Having the outer feathers much the longest, the\n   others decreasing regularly to the median ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scissure', 'n.', 'A longitudinal opening in a body, made by cutting; a\n   cleft; a fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scitamineous', 'a.', 'Of or pertaining to a natural order of plants\n   (Scitamineae), mostly tropical herbs, including the ginger, Indian\n   shot, banana, and the plants producing turmeric and arrowroot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciurine', 'a.', 'Of or pertaining to the Squirrel family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciurine', 'n.', 'A rodent of the Squirrel family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciuroid', 'a.', 'Resembling the tail of a squirrel; -- generally said of\n   branches which are close and dense, or of spikes of grass like barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciuromorpha', 'n. pl.', 'A tribe of rodents containing the squirrels and\n   allied animals, such as the gophers, woodchucks, beavers, and others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sciurus', 'n.', 'A genus of rodents comprising the common squirrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclaundre', 'n.', 'Slander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclav', 'n.', 'Alt. of Sclave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclave', 'n.', 'Same as Slav.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclavic', 'a.', 'Same as Slavic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclavism', 'n.', 'Same as Slavism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclavonian', 'a. & n.', 'Same as Slavonian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclavonic', 'a.', 'Same as Slavonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclender', 'a.', 'Slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleragogy', 'n.', 'Severe discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerema', 'n.', 'Induration of the cellular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerenchyma', 'n.', 'Vegetable tissue composed of short cells with\n   thickened or hardened walls, as in nutshells and the gritty parts of a\n   pear. See Sclerotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerenchyma', 'n.', 'The hard calcareous deposit in the tissues of\n   Anthozoa, constituting the stony corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerenchymatous', 'a.', 'Pertaining to, or composed of, sclerenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerenchyme', 'n.', 'Sclerenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleriasis', 'n.', 'A morbid induration of the edge of the eyelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleriasis', 'n.', 'Induration of any part, including scleroderma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerite', 'n.', 'A hard chitinous or calcareous process or corpuscle,\n   especially a spicule of the Alcyonaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleritis', 'n.', 'See Sclerotitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerobase', 'n.', 'The calcareous or hornlike coral forming the central\n   stem or axis of most compound alcyonarians; -- called also foot\n   secretion. See Illust. under Gorgoniacea, and Coenenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleroderm', 'n.', 'One of a tribe of plectognath fishes (Sclerodermi)\n   having the skin covered with hard scales, or plates, as the cowfish and\n   the trunkfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleroderm', 'n.', 'One of the Sclerodermata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleroderm', 'n.', 'Hardened, or bony, integument of various animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleroderma', 'n.', 'A disease of adults, characterized by a diffuse\n   rigidity and hardness of the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerodermata', 'n. pl.', 'The stony corals; the Madreporaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerodermic', '', 'Alt. of Sclerodermous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerodermous', '', 'Having the integument, or skin, hard, or covered with\n   hard plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerodermous', '', 'Of or pertaining to the Sclerodermata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerodermite', 'n.', 'The hard integument of Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerodermite', 'n.', 'Sclerenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerogen', 'n.', 'The thickening matter of woody cells; lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerogenous', 'a.', 'Making or secreting a hard substance; becoming hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleroid', 'a.', 'Having a hard texture, as nutshells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleroma', 'n.', 'Induration of the tissues. See Sclerema, Scleroderma, and\n   Sclerosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerometer', 'n.', 'An instrument for determining with accuracy the degree\n   of hardness of a mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerosed', 'a.', 'Affected with sclerosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerosis', 'n.', 'Induration; hardening; especially, that form of\n   induration produced in an organ by increase of its interstitial\n   connective tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerosis', 'n.', 'Hardening of the cell wall by lignification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scleroskeleton', 'n.', 'That part of the skeleton which is developed in\n   tendons, ligaments, and aponeuroses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotal', 'a.', 'Sclerotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotal', 'n.', 'The optic capsule; the sclerotic coat of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotic', 'a.', 'Hard; firm; indurated; -- applied especially in anatomy\n   to the firm outer coat of the eyeball, which is often cartilaginous and\n   sometimes bony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotic', 'a.', 'Of or pertaining to the sclerotic coat of the eye;\n   sclerotical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotic', 'a.', 'Affected with sclerosis; sclerosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotic', 'n.', 'The sclerotic coat of the eye. See Illust. of Eye (d).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotic', 'a.', 'Pertaining to, or designating, an acid obtained from\n   ergot or the sclerotium of a fungus growing on rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotical', 'a.', 'Sclerotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotitis', 'n.', 'Inflammation of the sclerotic coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotia', 'pl. ', 'of Sclerotium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotium', 'n.', 'A hardened body formed by certain fungi, as by the\n   Claviceps purpurea, which produces ergot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotium', 'n.', 'The mature or resting stage of a plasmodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerotome', 'n.', 'One of the bony, cartilaginous, or membranous\n   partitions which separate the myotomes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sclerous', 'a.', 'Hard; indurated; sclerotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoat', 'v. t.', 'To prop; to scotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scobby', 'n.', 'The chaffinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scobiform', 'a.', 'Having the form of, or resembling, sawdust or raspings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scobs', 'n. sing. & pl.', 'Raspings of ivory, hartshorn, metals, or other\n   hard substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scobs', 'n. sing. & pl.', 'The dross of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoff', 'n.', 'Derision; ridicule; mockery; derisive or mocking expression\n   of scorn, contempt, or reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoff', 'n.', 'An object of scorn, mockery, or derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoffed', 'imp. & p. p.', 'of Scoff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoffing', 'p. pr. & vb. n.', 'of Scoff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoff', 'n.', 'To show insolent ridicule or mockery; to manifest contempt\n   by derisive acts or language; -- often with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoff', 'v. t.', 'To treat or address with derision; to assail scornfully;\n   to mock at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoffer', 'n.', 'One who scoffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoffery', 'n.', 'The act of scoffing; scoffing conduct; mockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoffingly', 'adv.', 'In a scoffing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoke', 'n.', 'Poke (Phytolacca decandra).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolay', 'v. i.', 'See Scoley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolded', 'imp. & p. p.', 'of Scold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolding', 'p. pr. & vb. n.', 'of Scold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scold', 'v. i.', 'To find fault or rail with rude clamor; to brawl; to\n   utter harsh, rude, boisterous rebuke; to chide sharply or coarsely; --\n   often with at; as, to scold at a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scold', 'v. t.', 'To chide with rudeness and clamor; to rate; also, to\n   rebuke or reprove with severity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scold', 'n.', 'One who scolds, or makes a practice of scolding; esp., a\n   rude, clamorous woman; a shrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scold', 'n.', 'A scolding; a brawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolder', 'n.', 'One who scolds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolder', 'n.', 'The oyster catcher; -- so called from its shrill cries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolder', 'n.', 'The old squaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolding', '', 'a. & n. from Scold, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoldingly', 'adv.', 'In a scolding manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scole', 'n.', 'School.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolecida', 'n. pl.', 'Same as Helminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolecite', 'n.', 'A zeolitic mineral occuring in delicate radiating groups\n   of white crystals. It is a hydrous silicate of alumina and lime. Called\n   also lime mesotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolecomorpha', 'n. pl.', 'Same as Scolecida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoleces', 'pl. ', 'of Scolex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolex', 'n.', 'The embryo produced directly from the egg in a metagenetic\n   series, especially the larva of a tapeworm or other parasitic worm. See\n   Illust. of Echinococcus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolex', 'n.', 'One of the Scolecida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoley', 'v. i.', 'To go to school; to study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoliosis', 'n.', 'A lateral curvature of the spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolithus', 'n.', 'A tubular structure found in Potsdam sandstone, and\n   believed to be the fossil burrow of a marine worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scollop', 'n. & v.', 'See Scallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolopacine', 'a.', 'Of or pertaining to the Scolopacidae, or Snipe family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolopendra', 'n.', 'A genus of venomous myriapods including the centipeds.\n   See Centiped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolopendra', 'n.', 'A sea fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolopendrine', 'a.', 'Like or pertaining to the Scolopendra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scolytid', 'n.', 'Any one of numerous species of small bark-boring beetles\n   of the genus Scolytus and allied genera. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scomber', 'n.', 'A genus of acanthopterygious fishes which includes the\n   common mackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scomberoid', 'a. & n.', 'Same as Scombroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scombriformes', 'n. pl.', 'A division of fishes including the mackerels,\n   tunnies, and allied fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scombroid', 'a.', 'Like or pertaining to the Mackerel family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scombroid', 'n.', 'Any fish of the family Scombridae, of which the mackerel\n   (Scomber) is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scomfish', 'v. t. & i.', 'To suffocate or stifle; to smother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scomfit', 'n. & v.', 'Discomfit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scomm', 'n.', 'A buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scomm', 'n.', 'A flout; a jeer; a gibe; a taunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A fortification, or work for defense; a fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A hut for protection and shelter; a stall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A piece of armor for the head; headpiece; helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'Fig.: The head; the skull; also, brains; sense;\n   discretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A poll tax; a mulct or fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A protection for a light; a lantern or cased support for\n   a candle; hence, a fixed hanging or projecting candlestick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'Hence, the circular tube, with a brim, in a candlestick,\n   into which the candle is inserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A squinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A fragment of a floe of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'p. p.', 'A fixed seat or shelf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconced', 'imp. & p. p.', 'of Sconce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconcing', 'p. pr. & vb. n.', 'of Sconce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'v. t.', 'To shut up in a sconce; to imprison; to insconce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconce', 'v. t.', 'To mulct; to fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sconcheon', 'n.', 'A squinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scone', 'n.', 'A cake, thinner than a bannock, made of wheat or barley or\n   oat meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'A large ladle; a vessel with a long handle, used for dipping\n   liquids; a utensil for bailing boats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'A deep shovel, or any similar implement for digging out and\n   dipping or shoveling up anything; as, a flour scoop; the scoop of a\n   dredging machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'A spoon-shaped instrument, used in extracting certain\n   substances or foreign bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'A place hollowed out; a basinlike cavity; a hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'A sweep; a stroke; a swoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'The act of scooping, or taking with a scoop or ladle; a\n   motion with a scoop, as in dipping or shoveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scooped', 'imp. & p. p.', 'of Scoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scooping', 'p. pr. & vb. n.', 'of Scoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'To take out or up with, a scoop; to lade out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'To empty by lading; as, to scoop a well dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoop', 'n.', 'To make hollow, as a scoop or dish; to excavate; to dig out;\n   to form by digging or excavation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scooper', 'n.', 'One who, or that which, scoops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scooper', 'n.', 'The avocet; -- so called because it scoops up the mud to\n   obtain food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoot', 'v. i.', 'To walk fast; to go quickly; to run hastily away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoparin', 'n.', 'A yellow gelatinous or crystalline substance found in\n   broom (Cytisus scoparius) accompanying sparteine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopate', 'a.', 'Having the surface closely covered with hairs, like a\n   brush.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-scope', '', 'A combining form usually signifying an instrument for viewing\n   (with the eye) or observing (in any way); as in microscope, telescope,\n   altoscope, anemoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scope', 'n.', 'That at which one aims; the thing or end to which the mind\n   directs its view; that which is purposed to be reached or accomplished;\n   hence, ultimate design, aim, or purpose; intention; drift; object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scope', 'n.', 'Room or opportunity for free outlook or aim; space for\n   action; amplitude of opportunity; free course or vent; liberty; range\n   of view, intent, or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scope', 'n.', 'Extended area.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scope', 'n.', 'Length; extent; sweep; as, scope of cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scope', 'v. t.', 'To look at for the purpose of evaluation; usually with\n   out; as, to scope out the area as a camping site.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopeline', 'a.', 'Scopeloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopeloid', 'a.', 'Like or pertaining to fishes of the genus Scopelus, or\n   family Scopelodae, which includes many small oceanic fishes, most of\n   which are phosphorescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopeloid', 'n.', 'Any fish of the family Scopelidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopiferous', 'a.', 'Bearing a tuft of brushlike hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopiform', 'a.', 'Having the form of a broom or besom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopiped', 'n.', 'Same as Scopuliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoppet', 'v. t.', 'To lade or dip out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scops owl', '', 'Any one of numerous species of small owls of the genus\n   Scops having ear tufts like those of the horned owls, especially the\n   European scops owl (Scops giu), and the American screech owl (S. asio).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoptic', 'a.', 'Alt. of Scoptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoptical', 'a.', 'Jesting; jeering; scoffing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopulas', 'pl. ', 'of Scopula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopulae', 'pl. ', 'of Scopula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopula', 'n.', 'A peculiar brushlike organ found on the foot of spiders\n   and used in the construction of the web.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopula', 'n.', 'A special tuft of hairs on the leg of a bee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopuliped', 'n.', 'Any species of bee which has on the hind legs a brush\n   of hairs used for collecting pollen, as the hive bees and bumblebees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scopulous', 'a.', 'Full of rocks; rocky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorbute', 'n.', 'Scurvy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorbutic', 'a.', 'Alt. of Scorbutical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorbutical', 'a.', 'Of or pertaining to scurvy; of the nature of, or\n   resembling, scurvy; diseased with scurvy; as, a scorbutic person;\n   scorbutic complaints or symptoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorbutus', 'n.', 'Scurvy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorce', 'n.', 'Barter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorched', 'imp. & p. p.', 'of Scorch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorching', 'p. pr. & vb. n.', 'of Scorch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorch', 'v. t.', 'To burn superficially; to parch, or shrivel, the surface\n   of, by heat; to subject to so much heat as changes color and texture\n   without consuming; as, to scorch linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorch', 'v. t.', 'To affect painfully with heat, or as with heat; to dry\n   up with heat; to affect as by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorch', 'v. t.', 'To burn; to destroy by, or as by, fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorch', 'v. i.', 'To be burnt on the surface; to be parched; to be dried\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorch', 'v. i.', 'To burn or be burnt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorching', 'a.', 'Burning; parching or shriveling with heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'A notch or incision; especially, one that is made as a tally\n   mark; hence, a mark, or line, made for the purpose of account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'An account or reckoning; account of dues; bill; hence,\n   indebtedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'Account; reason; motive; sake; behalf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'The number twenty, as being marked off by a special score or\n   tally; hence, in pl., a large number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'A distance of twenty yards; -- a term used in ancient\n   archery and gunnery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'A weight of twenty pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'The number of points gained by the contestants, or either of\n   them, in any game, as in cards or cricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'A line drawn; a groove or furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'The original and entire draught, or its transcript, of a\n   composition, with the parts for all the different instruments or voices\n   written on staves one above another, so that they can be read at a\n   glance; -- so called from the bar, which, in its early use, was drawn\n   through all the parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scored', 'imp. & p. p.', 'of Score');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoring', 'p. pr. & vb. n.', 'of Score');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'v. t.', 'To mark with lines, scratches, or notches; to cut notches\n   or furrows in; to notch; to scratch; to furrow; as, to score timber for\n   hewing; to score the back with a lash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'v. t.', 'Especially, to mark with significant lines or notches,\n   for indicating or keeping account of something; as, to score a tally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'v. t.', 'To mark or signify by lines or notches; to keep record or\n   account of; to set down; to record; to charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'v. t.', 'To engrave, as upon a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'v. t.', 'To make a score of, as points, runs, etc., in a game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'v. t.', 'To write down in proper order and arrangement; as, to\n   score an overture for an orchestra. See Score, n., 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Score', 'n.', 'To mark with parallel lines or scratches; as, the rocks of\n   New England and the Western States were scored in the drift epoch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorer', 'n.', 'One who, or that which, scores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoriae', 'pl. ', 'of Scoria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoria', 'n.', 'The recrement of metals in fusion, or the slag rejected\n   after the reduction of metallic ores; dross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoria', 'n.', 'Cellular slaggy lava; volcanic cinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoriac', 'a.', 'Scoriaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoriaceous', 'a.', 'Of or pertaining to scoria; like scoria or the\n   recrement of metals; partaking of the nature of scoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorie', 'n.', 'The young of any gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorification', 'n.', 'The act, process, or result of scorifying, or\n   reducing to a slag; hence, the separation from earthy matter by means\n   of a slag; as, the scorification of ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorifier', 'n.', 'One who, or that which, scorifies; specifically, a small\n   flat bowl-shaped cup used in the first heating in assaying, to remove\n   the earth and gangue, and to concentrate the gold and silver in a lead\n   button.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoriform', 'a.', 'In the form of scoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorified', 'imp. & p. p.', 'of Scorify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorifying', 'p. pr. & vb. n.', 'of Scorify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorify', 'v. t.', 'To reduce to scoria or slag; specifically, in assaying,\n   to fuse so as to separate the gangue and earthy material, with borax,\n   lead, soda, etc., thus leaving the gold and silver in a lead button;\n   hence, to separate from, or by means of, a slag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorious', 'a.', 'Scoriaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorn', 'n.', 'Extreme and lofty contempt; haughty disregard; that disdain\n   which springs from the opinion of the utter meanness and unworthiness\n   of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorn', 'n.', 'An act or expression of extreme contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorn', 'n.', 'An object of extreme disdain, contempt, or derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorned', 'imp. & p. p.', 'of Scorn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoring', 'p. pr. & vb. n.', 'of Scorn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorn', 'n.', 'To hold in extreme contempt; to reject as unworthy of\n   regard; to despise; to contemn; to disdain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorn', 'n.', 'To treat with extreme contempt; to make the object of\n   insult; to mock; to scoff at; to deride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorn', 'v. i.', 'To scoff; to mock; to show contumely, derision, or\n   reproach; to act disdainfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorner', 'n.', 'One who scorns; a despiser; a contemner; specifically, a\n   scoffer at religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scornful', 'a.', 'Full of scorn or contempt; contemptuous; disdainful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scornful', 'a.', 'Treated with scorn; exciting scorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorny', 'a.', 'Deserving scorn; paltry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorodite', 'n.', 'A leek-green or brownish mineral occurring in\n   orthorhombic crystals. It is a hydrous arseniate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpaenoid', 'a.', 'Of or pertaining to the family Scorpaenidae, which\n   includes the scorpene, the rosefish, the California rockfishes, and\n   many other food fishes. [Written also scorpaenid.] See Illust. under\n   Rockfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpene', 'n.', 'A marine food fish of the genus Scorpaena, as the\n   European hogfish (S. scrofa), and the California species (S. guttata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorper', 'n.', 'Same as Scauper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpiones', 'pl. ', 'of Scorpio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpio', 'n.', 'A scorpion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpio', 'n.', 'The eighth sign of the zodiac, which the sun enters about\n   the twenty-third day of October, marked thus [/] in almanacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpio', 'n.', 'A constellation of the zodiac containing the bright star\n   Antares. It is drawn on the celestial globe in the figure of a\n   scorpion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpiodea', 'n. pl.', 'Same as Scorpiones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpioid', 'a.', 'Alt. of Scorpioidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpioidal', 'a.', 'Having the inflorescence curved or circinate at the\n   end, like a scorpions tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpion', 'n.', 'Any one of numerous species of pulmonate arachnids of the\n   order Scorpiones, having a suctorial mouth, large claw-bearing palpi,\n   and a caudal sting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpion', 'n.', 'The pine or gray lizard (Sceloporus undulatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpion', 'n.', 'The scorpene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpion', 'n.', 'A painful scourge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpion', 'n.', 'A sign and constellation. See Scorpio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpion', 'n.', 'An ancient military engine for hurling stones and other\n   missiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpiones', 'n. pl.', 'A division of arachnids comprising the scorpions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpionidea', 'n. pl.', 'Same as Scorpiones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorpionwort', 'n.', 'A leguminous plant (Ornithopus scorpioides) of\n   Southern Europe, having slender curved pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorse', 'n.', 'Barter; exchange; trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorse', 'v. t.', 'To barter or exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorse', 'v. t.', 'To chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scorse', 'v. i.', 'To deal for the purchase of anything; to practice\n   barter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scortatory', 'a.', 'Pertaining to lewdness or fornication; lewd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scot', 'n.', 'A name for a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scot', 'n.', 'A native or inhabitant of Scotland; a Scotsman, or Scotchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scot', 'n.', 'A portion of money assessed or paid; a tax or contribution; a\n   mulct; a fine; a shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotal', 'n.', 'Alt. of Scotale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotale', 'n.', 'The keeping of an alehouse by an officer of a forest, and\n   drawing people to spend their money for liquor, for fear of his\n   displeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch', 'a.', 'Of or pertaining to Scotland, its language, or its\n   inhabitants; Scottish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch', 'n.', 'The dialect or dialects of English spoken by the people of\n   Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch', 'n.', 'Collectively, the people of Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotched', 'imp. & p. p.', 'of Scotch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotching', 'p. pr. & vb. n.', 'of Scotch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch', 'v. t.', 'To shoulder up; to prop or block with a wedge, chock,\n   etc., as a wheel, to prevent its rolling or slipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch', 'n.', 'A chock, wedge, prop, or other support, to prevent\n   slipping; as, a scotch for a wheel or a log on inclined ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch', 'v. t.', 'To cut superficially; to wound; to score.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch', 'n.', 'A slight cut or incision; a score.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotch-hopper', 'n.', 'Hopscotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotching', 'n.', 'Dressing stone with a pick or pointed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotchmen', 'pl. ', 'of Scotchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotchman', 'n.', 'A native or inhabitant of Scotland; a Scot; a Scotsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotchman', 'n.', 'A piece of wood or stiff hide placed over shrouds and\n   other rigging to prevent chafe by the running gear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoter', 'n.', 'Any one of several species of northern sea ducks of the\n   genus Oidemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scot-free', 'a.', 'Free from payment of scot; untaxed; hence, unhurt;\n   clear; safe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoth', 'v. t.', 'To clothe or cover up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotia', 'n.', 'A concave molding used especially in classical\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotia', 'n.', 'Scotland');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotist', 'n.', 'A follower of (Joannes) Duns Scotus, the Franciscan\n   scholastic (d. 1308), who maintained certain doctrines in philosophy\n   and theology, in opposition to the Thomists, or followers of Thomas\n   Aquinas, the Dominican scholastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotograph', 'n.', 'An instrument for writing in the dark, or without\n   seeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotoma', 'n.', 'Scotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotomy', 'n.', 'Dizziness with dimness of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotomy', 'n.', 'Obscuration of the field of vision due to the appearance\n   of a dark spot before the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotoscope', 'n.', 'An instrument that discloses objects in the dark or in\n   a faint light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scots', 'a.', 'Of or pertaining to the Scotch; Scotch; Scottish; as, Scots\n   law; a pound Scots (1s. 8d.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotsman', 'n.', 'See Scotchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scottering', 'n.', 'The burning of a wad of pease straw at the end of\n   harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotticism', 'n.', 'An idiom, or mode of expression, peculiar to Scotland\n   or Scotchmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scotticize', 'v. t.', 'To cause to become like the Scotch; to make\n   Scottish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scottish', 'a.', 'Of or pertaining to the inhabitants of Scotland, their\n   country, or their language; as, Scottish industry or economy; a\n   Scottish chief; a Scottish dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoundrel', 'n.', 'A mean, worthless fellow; a rascal; a villain; a man\n   without honor or virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoundrel', 'a.', 'Low; base; mean; unprincipled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoundreldom', 'n.', 'The domain or sphere of scoundrels; scoundrels,\n   collectively; the state, ideas, or practices of scoundrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoundrelism', 'n.', 'The practices or conduct of a scoundrel; baseness;\n   rascality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scoured', 'imp. & p. p.', 'of Scour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scouring', 'p. pr. & vb. n.', 'of Scour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. t.', 'To rub hard with something rough, as sand or Bristol\n   brick, especially for the purpose of cleaning; to clean by friction; to\n   make clean or bright; to cleanse from grease, dirt, etc., as articles\n   of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. t.', 'To purge; as, to scour a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. t.', 'To remove by rubbing or cleansing; to sweep along or off;\n   to carry away or remove, as by a current of water; -- often with off or\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. t.', 'To pass swiftly over; to brush along; to traverse or\n   search thoroughly; as, to scour the coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. i.', 'To clean anything by rubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. i.', 'To cleanse anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. i.', 'To be purged freely; to have a diarrhoea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'v. i.', 'To run swiftly; to rove or range in pursuit or search of\n   something; to scamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scour', 'n.', 'Diarrhoea or dysentery among cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourage', 'n.', 'Refuse water after scouring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourer', 'n.', 'One who, or that which, scours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourer', 'n.', 'A rover or footpad; a prowling robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourge', 'n.', 'A lash; a strap or cord; especially, a lash used to\n   inflict pain or punishment; an instrument of punishment or discipline;\n   a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourge', 'n.', 'Hence, a means of inflicting punishment, vengeance, or\n   suffering; an infliction of affliction; a punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourged', 'imp. & p. p.', 'of Scourge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourging', 'p. pr. & vb. n.', 'of Scourge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourge', 'n.', 'To whip severely; to lash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourge', 'n.', 'To punish with severity; to chastise; to afflict, as for\n   sins or faults, and with the purpose of correction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourge', 'n.', 'To harass or afflict severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourger', 'n.', 'One who scourges or punishes; one who afflicts severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scourse', 'v. t.', 'See Scorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scouse', 'n.', 'A sailors dish. Bread scouse contains no meat; lobscouse\n   contains meat, etc. See Lobscouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'n.', 'A swift sailing boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'n.', 'A projecting rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'v. t.', 'To reject with contempt, as something absurd; to treat\n   with ridicule; to flout; as, to scout an idea or an apology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'n.', 'A person sent out to gain and bring in tidings; especially,\n   one employed in war to gain information of the movements and condition\n   of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'n.', 'A college students or undergraduates servant; -- so called\n   in Oxford, England; at Cambridge called a gyp; and at Dublin, a skip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'n.', 'A fielder in a game for practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'n.', 'The act of scouting or reconnoitering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scouted', 'imp. & p. p.', 'of Scout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scouting', 'p. pr. & vb. n.', 'of Scout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'v. t.', 'To observe, watch, or look for, as a scout; to follow for\n   the purpose of observation, as a scout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'v. t.', 'To pass over or through, as a scout; to reconnoiter; as,\n   to scout a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scout', 'v. i.', 'To go on the business of scouting, or watching the\n   motions of an enemy; to act as a scout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scovel', 'n.', 'A mop for sweeping ovens; a malkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scow', 'n.', 'A large flat-bottomed boat, having broad, square ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scow', 'v. t.', 'To transport in a scow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowled', 'imp. & p. p.', 'of Scowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowling', 'p. pr. & vb. n.', 'of Scowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowl', 'v. i.', 'To wrinkle the brows, as in frowning or displeasure; to\n   put on a frowning look; to look sour, sullen, severe, or angry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowl', 'v. i.', 'Hence, to look gloomy, dark, or threatening; to lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowl', 'v. t.', 'To look at or repel with a scowl or a frown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowl', 'v. t.', 'To express by a scowl; as, to scowl defiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowl', 'n.', 'The wrinkling of the brows or face in frowing; the\n   expression of displeasure, sullenness, or discontent in the\n   countenance; an angry frown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowl', 'n.', 'Hence, gloom; dark or threatening aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scowlingly', 'adv.', 'In a scowling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrabbed eggs', '', 'A Lenten dish, composed of eggs boiled hard, chopped,\n   and seasoned with butter, salt, and pepper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrabbled', 'imp. & p. p.', 'of Scrabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrabbling', 'p. pr. & vb. n.', 'of Scrabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrabble', 'v. t.', 'To scrape, paw, or scratch with the hands; to proceed\n   by clawing with the hands and feet; to scramble; as, to scrabble up a\n   cliff or a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrabble', 'v. t.', 'To make irregular, crooked, or unmeaning marks; to\n   scribble; to scrawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrabble', 'v. t.', 'To mark with irregular lines or letters; to scribble;\n   as, to scrabble paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrabble', 'n.', 'The act of scrabbling; a moving upon the hands and knees;\n   a scramble; also, a scribble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraber', 'n.', 'The Manx shearwater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraber', 'n.', 'The black guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraffle', 'v. i.', 'To scramble or struggle; to wrangle; also, to be\n   industrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrag', 'n.', 'Something thin, lean, or rough; a bony piece; especially, a\n   bony neckpiece of meat; hence, humorously or in contempt, the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrag', 'n.', 'A rawboned person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrag', 'n.', 'A ragged, stunted tree or branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scragged', 'a.', 'Rough with irregular points, or a broken surface;\n   scraggy; as, a scragged backbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scragged', 'a.', 'Lean and rough; scraggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraggedness', 'n.', 'Quality or state of being scragged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraggily', 'adv.', 'In a scraggy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scragginess', 'n.', 'The quality or state of being scraggy; scraggedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraggy', 'superl.', 'Rough with irregular points; scragged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraggy', 'superl.', 'Lean and rough; scragged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scragly', 'a.', 'See Scraggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrag-necked', 'a.', 'Having a scraggy neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrambled', 'imp. & p. p.', 'of Scramble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrambling', 'p. pr. & vb. n.', 'of Scramble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scramble', 'v. i.', 'To clamber with hands and knees; to scrabble; as, to\n   scramble up a cliff; to scramble over the rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scramble', 'v. i.', 'To struggle eagerly with others for something thrown\n   upon the ground; to go down upon all fours to seize something; to catch\n   rudely at what is desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scramble', 'v. t.', 'To collect by scrambling; as, to scramble up wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scramble', 'v. t.', 'To prepare (eggs) as a dish for the table, by stirring\n   the yolks and whites together while cooking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scramble', 'n.', 'The act of scrambling, climbing on all fours, or\n   clambering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scramble', 'n.', 'The act of jostling and pushing for something desired;\n   eager and unceremonious struggle for what is thrown or held out; as, a\n   scramble for office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrambler', 'n.', 'One who scrambles; one who climbs on all fours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrambler', 'n.', 'A greedy and unceremonious contestant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrambling', 'a.', 'Confused and irregular; awkward; scambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scranched', 'imp. & p. p.', 'of Scranch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scranching', 'p. pr. & vb. n.', 'of Scranch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scranch', 'v. t.', 'To grind with the teeth, and with a crackling sound; to\n   craunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scranky', 'a.', 'Thin; lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrannel', 'a.', 'Slight; thin; lean; poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scranny', 'a.', 'Thin; lean; meager; scrawny; scrannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrap', 'v. t.', 'Something scraped off; hence, a small piece; a bit; a\n   fragment; a detached, incomplete portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrap', 'v. t.', 'Specifically, a fragment of something written or printed;\n   a brief excerpt; an unconnected extract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrap', 'v. t.', 'The crisp substance that remains after drying out animal\n   fat; as, pork scraps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrap', 'v. t.', 'Same as Scrap iron, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrapbook', 'n.', 'A blank book in which extracts cut from books and papers\n   may be pasted and kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraped', 'imp. & p. p.', 'of Scrape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraping', 'p. pr. & vb. n.', 'of Scrape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. t.', 'To rub over the surface of (something) with a sharp or\n   rough instrument; to rub over with something that roughens by removing\n   portions of the surface; to grate harshly over; to abrade; to make\n   even, or bring to a required condition or form, by moving the sharp\n   edge of an instrument breadthwise over the surface with pressure,\n   cutting away excesses and superfluous parts; to make smooth or clean;\n   as, to scrape a bone with a knife; to scrape a metal plate to an even\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. t.', 'To remove by rubbing or scraping (in the sense above).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. t.', 'To collect by, or as by, a process of scraping; to\n   gather in small portions by laborious effort; hence, to acquire\n   avariciously and save penuriously; -- often followed by together or up;\n   as, to scrape money together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. t.', 'To express disapprobation of, as a play, or to silence,\n   as a speaker, by drawing the feet back and forth upon the floor; --\n   usually with down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. i.', 'To rub over the surface of anything with something which\n   roughens or removes it, or which smooths or cleans it; to rub harshly\n   and noisily along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. i.', 'To occupy ones self with getting laboriously; as, he\n   scraped and saved until he became rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. i.', 'To play awkwardly and inharmoniously on a violin or like\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'v. i.', 'To draw back the right foot along the ground or floor\n   when making a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'n.', 'The act of scraping; also, the effect of scraping, as a\n   scratch, or a harsh sound; as, a noisy scrape on the floor; a scrape of\n   a pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'n.', 'A drawing back of the right foot when bowing; also, a bow\n   made with that accompaniment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrape', 'n.', 'A disagreeable and embarrassing predicament out of which\n   one can not get without undergoing, as it were, a painful rubbing or\n   scraping; a perplexity; a difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrapepenny', 'n.', 'One who gathers and hoards money in trifling sums; a\n   miser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'An instrument with which anything is scraped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'An instrument by which the soles of shoes are cleaned from\n   mud and the like, by drawing them across it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'An instrument drawn by oxen or horses, used for scraping\n   up earth in making or repairing roads, digging cellars, canals etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'An instrument having two or three sharp sides or edges,\n   for cleaning the planks, masts, or decks of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'In the printing press, a board, or blade, the edge of\n   which is made to rub over the tympan sheet and thus produce the\n   impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'One who scrapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'One who plays awkwardly on a violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraper', 'n.', 'One who acquires avariciously and saves penuriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraping', 'n.', 'The act of scraping; the act or process of making even,\n   or reducing to the proper form, by means of a scraper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraping', 'n.', 'Something scraped off; that which is separated from a\n   substance, or is collected by scraping; as, the scraping of the street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraping', 'a.', 'Resembling the act of, or the effect produced by, one\n   who, or that which, scrapes; as, a scraping noise; a scraping miser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrappily', 'adv.', 'In a scrappy manner; in scraps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrappy', 'a.', 'Consisting of scraps; fragmentary; lacking unity or\n   consistency; as, a scrappy lecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrat', 'v. t.', 'To scratch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrat', 'v. i.', 'To rake; to search.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrat', 'n.', 'An hermaphrodite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratched', 'imp. & p. p.', 'of Scratch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratching', 'p. pr. & vb. n.', 'of Scratch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'v. t.', 'To rub and tear or mark the surface of with something\n   sharp or ragged; to scrape, roughen, or wound slightly by drawing\n   something pointed or rough across, as the claws, the nails, a pin, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'v. t.', 'To write or draw hastily or awkwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'v. t.', 'To cancel by drawing one or more lines through, as the\n   name of a candidate upon a ballot, or of a horse in a list; hence, to\n   erase; to efface; -- often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'v. t.', 'To dig or excavate with the claws; as, some animals\n   scratch holes, in which they burrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'v. i.', 'To use the claws or nails in tearing or in digging; to\n   make scratches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'v. i.', 'To score, not by skillful play but by some fortunate\n   chance of the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'n.', 'A break in the surface of a thing made by scratching, or\n   by rubbing with anything pointed or rough; a slight wound, mark,\n   furrow, or incision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'n.', 'A line across the prize ring; up to which boxers are\n   brought when they join fight; hence, test, trial, or proof of courage;\n   as, to bring to the scratch; to come up to the scratch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'n.', 'Minute, but tender and troublesome, excoriations, covered\n   with scabs, upon the heels of horses which have been used where it is\n   very wet or muddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'n.', 'A kind of wig covering only a portion of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'n.', 'A shot which scores by chance and not as intended by the\n   player; a fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch', 'a.', 'Made, done, or happening by chance; arranged with little\n   or no preparation; determined by circumstances; haphazard; as, a\n   scratch team; a scratch crew for a boat race; a scratch shot in\n   billiards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratchback', 'n.', 'A toy which imitates the sound of tearing cloth, --\n   used by drawing it across the back of unsuspecting persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratchbrush', 'n.', 'A stiff wire brush for cleaning iron castings and\n   other metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratch coat', '', 'The first coat in plastering; -- called also\n   scratchwork. See Pricking-up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratcher', 'n.', 'One who, or that which, scratches; specifically (Zool.),\n   any rasorial bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratching', 'adv.', 'With the action of scratching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratchweed', 'n.', 'Cleavers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratchwork', 'n.', 'See Scratch coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scratchy', 'a.', 'Characterized by scratches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scraw', 'n.', 'A turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawl', 'v. i.', 'See Crawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawled', 'imp. & p. p.', 'of Scrawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawling', 'p. pr. & vb. n.', 'of Scrawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawl', 'v. t.', 'To draw or mark awkwardly and irregularly; to write\n   hastily and carelessly; to scratch; to scribble; as, to scrawl a\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawl', 'v. i.', 'To write unskillfully and inelegantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawl', 'n.', 'Unskillful or inelegant writing; that which is unskillfully\n   or inelegantly written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawler', 'n.', 'One who scrawls; a hasty, awkward writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrawny', 'a.', 'Meager; thin; rawboned; bony; scranny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scray', 'n.', 'A tern; the sea swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screable', 'a.', 'Capable of being spit out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screaked', 'imp. & p. p.', 'of Screak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screaking', 'p. pr. & vb. n.', 'of Screak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screak', 'v.', 'To utter suddenly a sharp, shrill sound; to screech; to\n   creak, as a door or wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screak', 'n.', 'A creaking; a screech; a shriek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screamed', 'imp. & p. p.', 'of Scream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screaming', 'p. pr. & vb. n.', 'of Scream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scream', 'v. i.', 'To cry out with a shrill voice; to utter a sudden, sharp\n   outcry, or shrill, loud cry, as in fright or extreme pain; to shriek;\n   to screech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scream', 'n.', 'A sharp, shrill cry, uttered suddenly, as in terror or in\n   pain; a shriek; a screech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screamer', 'n.', 'Any one of three species of South American birds\n   constituting the family Anhimidae, and the suborder Palamedeae. They\n   have two spines on each wing, and the head is either crested or horned.\n   They are easily tamed, and then serve as guardians for other poultry.\n   The crested screamers, or chajas, belong to the genus Chauna. The\n   horned screamer, or kamichi, is Palamedea cornuta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screaming', 'a.', 'Uttering screams; shrieking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screaming', 'a.', 'Having the nature of a scream; like a scream; shrill;\n   sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scree', 'n.', 'A pebble; a stone; also, a heap of stones or rocky debris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screeched', 'imp. & p. p.', 'of Screech');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screeching', 'p. pr. & vb. n.', 'of Screech');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screech', 'v.', 'To utter a harsh, shrill cry; to make a sharp outcry, as\n   in terror or acute pain; to scream; to shriek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screech', 'n.', 'A harsh, shrill cry, as of one in acute pain or in fright;\n   a shriek; a scream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screechers', 'n. pl.', 'The picarian birds, as distinguished from the\n   singing birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screechy', 'a.', 'Like a screech; shrill and harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screed', 'n.', 'A strip of plaster of the thickness proposed for the coat,\n   applied to the wall at intervals of four or five feet, as a guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screed', 'n.', 'A wooden straightedge used to lay across the plaster\n   screed, as a limit for the thickness of the coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screed', 'n.', 'A fragment; a portion; a shred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screed', 'n.', 'A breach or rent; a breaking forth into a loud, shrill\n   sound; as, martial screeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screed', 'n.', 'An harangue; a long tirade on any subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screen', 'n.', 'Anything that separates or cuts off inconvenience, injury,\n   or danger; that which shelters or conceals from view; a shield or\n   protection; as, a fire screen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screen', 'n.', 'A dwarf wall or partition carried up to a certain height\n   for separation and protection, as in a church, to separate the aisle\n   from the choir, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screen', 'n.', 'A surface, as that afforded by a curtain, sheet, wall,\n   etc., upon which an image, as a picture, is thrown by a magic lantern,\n   solar microscope, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screen', 'n.', 'A long, coarse riddle or sieve, sometimes a revolving\n   perforated cylinder, used to separate the coarser from the finer parts,\n   as of coal, sand, gravel, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screened', 'imp. & p. p.', 'of Screen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screening', 'p. pr. & vb. n.', 'of Screen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screen', 'v. t.', 'To provide with a shelter or means of concealment; to\n   separate or cut off from inconvenience, injury, or danger; to shelter;\n   to protect; to protect by hiding; to conceal; as, fruits screened from\n   cold winds by a forest or hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screen', 'v. t.', 'To pass, as coal, gravel, ashes, etc., through a screen\n   in order to separate the coarse from the fine, or the worthless from\n   the valuable; to sift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screenings', 'n. pl.', 'The refuse left after screening sand, coal, ashes,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'A cylinder, or a cylindrical perforation, having a\n   continuous rib, called the thread, winding round it spirally at a\n   constant inclination, so as to leave a continuous spiral groove between\n   one turn and the next, -- used chiefly for producing, when revolved,\n   motion or pressure in the direction of its axis, by the sliding of the\n   threads of the cylinder in the grooves between the threads of the\n   perforation adapted to it, the former being distinguished as the\n   external, or male screw, or, more usually the screw; the latter as the\n   internal, or female screw, or, more usually, the nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'Specifically, a kind of nail with a spiral thread and a head\n   with a nick to receive the end of the screw-driver. Screws are much\n   used to hold together pieces of wood or to fasten something; -- called\n   also wood screws, and screw nails. See also Screw bolt, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'Anything shaped or acting like a screw; esp., a form of\n   wheel for propelling steam vessels. It is placed at the stern, and\n   furnished with blades having helicoidal surfaces to act against the\n   water in the manner of a screw. See Screw propeller, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'A steam vesel propelled by a screw instead of wheels; a\n   screw steamer; a propeller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'An extortioner; a sharp bargainer; a skinflint; a niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'An instructor who examines with great or unnecessary\n   severity; also, a searching or strict examination of a student by an\n   instructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'A small packet of tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'An unsound or worn-out horse, useful as a hack, and commonly\n   of good appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'A straight line in space with which a definite linear\n   magnitude termed the pitch is associated (cf. 5th Pitch, 10 (b)). It is\n   used to express the displacement of a rigid body, which may always be\n   made to consist of a rotation about an axis combined with a translation\n   parallel to that axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'n.', 'An amphipod crustacean; as, the skeleton screw (Caprella).\n   See Sand screw, under Sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screwed', 'imp. & p. p.', 'of Screw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screwing', 'p. pr. & vb. n.', 'of Screw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'v. t.', 'To turn, as a screw; to apply a screw to; to press,\n   fasten, or make firm, by means of a screw or screws; as, to screw a\n   lock on a door; to screw a press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'v. t.', 'To force; to squeeze; to press, as by screws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'v. t.', 'Hence: To practice extortion upon; to oppress by\n   unreasonable or extortionate exactions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'v. t.', 'To twist; to distort; as, to screw his visage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'v. t.', 'To examine rigidly, as a student; to subject to a severe\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'v. i.', 'To use violent mans in making exactions; to be oppressive\n   or exacting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw', 'v. i.', 'To turn ones self uneasily with a twisting motion; as,\n   he screws about in his chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw-cutting', 'a.', 'Adapted for forming a screw by cutting; as, a\n   screw-cutting lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screw-driver', 'n.', 'A tool for turning screws so as to drive them into\n   their place. It has a thin end which enters the nick in the head of the\n   screw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screwer', 'n.', 'One who, or that which, screws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Screwing', '', 'a. & n. from Screw, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribable', 'a.', 'Capable of being written, or of being written upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribatious', 'a.', 'Skillful in, or fond of, writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbet', 'n.', 'A painters pencil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribble', 'v. t.', 'To card coarsely; to run through the scribbling\n   machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbled', 'imp. & p. p.', 'of Scribble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbling', 'p. pr. & vb. n.', 'of Scribble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribble', 'v. t.', 'To write hastily or carelessly, without regard to\n   correctness or elegance; as, to scribble a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribble', 'v. t.', 'To fill or cover with careless or worthless writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribble', 'v. i.', 'To write without care, elegance, or value; to scrawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribble', 'n.', 'Hasty or careless writing; a writing of little value; a\n   scrawl; as, a hasty scribble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribblement', 'n.', 'A scribble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbler', 'n.', 'One who scribbles; a petty author; a writer of no\n   reputation; a literary hack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbler', 'n.', 'A scribbling machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbling', 'n.', 'The act or process of carding coarsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbling', 'a.', 'Writing hastily or poorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribbling', 'n.', 'The act of writing hastily or idly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribblingly', 'adv.', 'In a scribbling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribe', 'n.', 'One who writes; a draughtsman; a writer for another;\n   especially, an offical or public writer; an amanuensis or secretary; a\n   notary; a copyist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribe', 'n.', 'A writer and doctor of the law; one skilled in the law and\n   traditions; one who read and explained the law to the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribed', 'imp. & p. p.', 'of Scribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribing', 'p. pr. & vb. n.', 'of Scribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribe', 'v. t.', 'To write, engrave, or mark upon; to inscribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribe', 'v. t.', 'To cut (anything) in such a way as to fit closely to a\n   somewhat irregular surface, as a baseboard to a floor which is out of\n   level, a board to the curves of a molding, or the like; -- so called\n   because the workman marks, or scribe, with the compasses the line that\n   he afterwards cuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribe', 'v. t.', 'To score or mark with compasses or a scribing iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribe', 'v. i.', 'To make a mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scriber', 'n.', 'A sharp-pointed tool, used by joiners for drawing lines on\n   stuff; a marking awl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scribism', 'n.', 'The character and opinions of a Jewish scribe in the time\n   of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrid', 'n.', 'A screed; a shred; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scriggle', 'v. i.', 'To wriggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrim', 'n.', 'A kind of light cotton or linen fabric, often woven in\n   openwork patterns, -- used for curtains, etc,; -- called also India\n   scrim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrim', 'n.', 'Thin canvas glued on the inside of panels to prevent\n   shrinking, checking, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimer', 'n.', 'A fencing master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimmage', 'n.', 'Formerly, a skirmish; now, a general row or confused\n   fight or struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimmage', 'n.', 'The struggle in the rush lines after the ball is put in\n   play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimped', 'imp. & p. p.', 'of Scrimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimping', 'p. pr. & vb. n.', 'of Scrimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimp', 'v. t.', 'To make too small or short; to limit or straiten; to put\n   on short allowance; to scant; to contract; to shorten; as, to scrimp\n   the pattern of a coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimp', 'a.', 'Short; scanty; curtailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimp', 'n.', 'A pinching miser; a niggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimping', '', 'a. & n. from Scrimp, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimpingly', 'adv.', 'In a scrimping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimpness', 'n.', 'The state of being scrimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimption', 'n.', 'A small portion; a pittance; a little bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimshaw', 'v. t.', 'To ornament, as shells, ivory, etc., by engraving,\n   and (usually) rubbing pigments into the incised lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrimshaw', 'n.', 'A shell, a whales tooth, or the like, that is\n   scrimshawed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrine', 'n.', 'A chest, bookcase, or other place, where writings or\n   curiosities are deposited; a shrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scringed', 'imp. & p. p.', 'of Scrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scringing', 'p. pr. & vb. n.', 'of Scrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrine', 'v. i.', 'To cringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrip', 'n.', 'A small bag; a wallet; a satchel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrip', 'n.', 'A small writing, certificate, or schedule; a piece of paper\n   containing a writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrip', 'n.', 'A preliminary certificate of a subscription to the capital\n   of a bank, railroad, or other company, or for a share of other joint\n   property, or a loan, stating the amount of the subscription and the\n   date of the payment of the installments; as, insurance scrip, consol\n   scrip, etc. When all the installments are paid, the scrip is exchanged\n   for a bond share certificate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrip', 'n.', 'Paper fractional currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrippage', 'n.', 'The contents of a scrip, or wallet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Script', 'n.', 'A writing; a written document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Script', 'n.', 'Type made in imitation of handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Script', 'n.', 'An original instrument or document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Script', 'n.', 'Written characters; style of writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scriptoria', 'pl. ', 'of Scriptorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scriptorium', 'n.', 'In an abbey or monastery, the room set apart for\n   writing or copying manuscripts; in general, a room devoted to writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scriptory', 'a.', 'Of or pertaining to writing; expressed in writing; used\n   in writing; as, scriptory wills; a scriptory reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scriptural', 'a.', 'Contained in the Scriptures; according to the\n   Scriptures, or sacred oracles; biblical; as, a scriptural doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripturalism', 'n.', 'The quality or state of being scriptural; literal\n   adherence to the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripturalist', 'n.', 'One who adheres literally to the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripturally', 'adv.', 'In a scriptural manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripturalness', 'n.', 'Quality of being scriptural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripture', 'n.', 'Anything written; a writing; a document; an inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripture', 'n.', 'The books of the Old and the new Testament, or of either\n   of them; the Bible; -- used by way of eminence or distinction, and\n   chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripture', 'n.', 'A passage from the Bible;; a text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripturian', 'n.', 'A Scripturist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scripturist', 'n.', 'One who is strongly attached to, or versed in, the\n   Scriptures, or who endeavors to regulate his life by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrit', 'n.', 'Writing; document; scroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scritch', 'n.', 'A screech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrivener', 'n.', 'A professional writer; one whose occupation is to draw\n   contracts or prepare writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrivener', 'n.', 'One whose business is to place money at interest; a\n   broker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrivener', 'n.', 'A writing master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrobiculae', 'pl. ', 'of Scrobicula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrobicula', 'n.', 'One of the smooth areas surrounding the tubercles of a\n   sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrobicular', 'a.', 'Pertaining to, or surrounding, scrobiculae; as,\n   scrobicular tubercles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrobiculate', 'a.', 'Alt. of Scrobiculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrobiculated', 'a.', 'Having numerous small, shallow depressions or\n   hollows; pitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrod', 'n.', 'Alt. of Scrode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrode', 'n.', 'A young codfish, especially when cut open on the back and\n   dressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scroddled ware', '', 'Mottled pottery made from scraps of differently\n   colored clays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrofula', 'n.', 'A constitutional disease, generally hereditary,\n   especially manifested by chronic enlargement and cheesy degeneration of\n   the lymphatic glands, particularly those of the neck, and marked by a\n   tendency to the development of chronic intractable inflammations of the\n   skin, mucous membrane, bones, joints, and other parts, and by a\n   diminution in the power of resistance to disease or injury and the\n   capacity for recovery. Scrofula is now generally held to be tuberculous\n   in character, and may develop into general or local tuberculosis\n   (consumption).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrofulide', 'n.', 'Any affection of the skin dependent on scrofula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrofulous', 'a.', 'Pertaining to scrofula, or partaking of its nature; as,\n   scrofulous tumors; a scrofulous habit of body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrofulous', 'a.', 'Diseased or affected with scrofula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrog', 'n.', 'A stunted shrub, bush, or branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scroggy', 'a.', 'Abounding in scrog; also, twisted; stunted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scroll', 'n.', 'A roll of paper or parchment; a writing formed into a roll;\n   a schedule; a list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scroll', 'n.', 'An ornament formed of undulations giving off spirals or\n   sprays, usually suggestive of plant form. Roman architectural ornament\n   is largely of some scroll pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scroll', 'n.', 'A mark or flourish added to a persons signature, intended\n   to represent a seal, and in some States allowed as a substitute for a\n   seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scroll', 'n.', 'Same as Skew surface. See under Skew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrolled', 'a.', 'Formed like a scroll; contained in a scroll; adorned with\n   scrolls; as, scrolled work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrophularia', 'n.', 'A genus of coarse herbs having small flowers in\n   panicled cymes; figwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrophulariaceous', 'a.', 'Of or pertaining to a very large natural order\n   of gamopetalous plants (Scrophulariaceae, or Scrophularineae), usually\n   having irregular didynamous flowers and a two-celled pod. The order\n   includes the mullein, foxglove, snapdragon, figwort, painted cup,\n   yellow rattle, and some exotic trees, as the Paulownia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrotal', 'a.', 'Of or pertaining to the scrotum; as, scrotal hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrotiform', 'a.', 'Purse-shaped; pouch-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrotocele', 'n.', 'A rupture or hernia in the scrotum; scrotal hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrotum', 'n.', 'The bag or pouch which contains the testicles; the cod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrouge', 'v. t.', 'To crowd; to squeeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrow', 'n.', 'A scroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrow', 'n.', 'A clipping from skins; a curriers cuttings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scroyle', 'n.', 'A mean fellow; a wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubbed', 'imp. & p. p.', 'of Scrub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubbing', 'p. pr. & vb. n.', 'of Scrub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'v. t.', 'To rub hard; to wash with rubbing; usually, to rub with a\n   wet brush, or with something coarse or rough, for the purpose of\n   cleaning or brightening; as, to scrub a floor, a doorplate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'v. i.', 'To rub anything hard, especially with a wet brush; to\n   scour; hence, to be diligent and penurious; as, to scrub hard for a\n   living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'n.', 'One who labors hard and lives meanly; a mean fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'n.', 'Something small and mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'n.', 'A worn-out brush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'n.', 'A thicket or jungle, often specified by the name of the\n   prevailing plant; as, oak scrub, palmetto scrub, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'n.', 'One of the common live stock of a region of no particular\n   breed or not of pure breed, esp. when inferior in size, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrub', 'a.', 'Mean; dirty; contemptible; scrubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubbed', 'a.', 'Dwarfed or stunted; scrubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubber', 'n.', 'One who, or that which, scrubs; esp., a brush used in\n   scrubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubber', 'n.', 'A gas washer. See under Gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubboard', 'n.', 'A baseboard; a mopboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubby', 'superl.', 'Of the nature of scrub; small and mean; stunted in\n   growth; as, a scrubby cur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrubstone', 'n.', 'A species of calciferous sandstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruff', 'n.', 'Scurf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruff', 'n.', 'The nape of the neck; the loose outside skin, as of the\n   back of the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrummage', 'n.', 'See Scrimmage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrumptious', 'a.', 'Nice; particular; fastidious; excellent; fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrunch', 'v. t. & v. i.', 'To scranch; to crunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruple', 'n.', 'A weight of twenty grains; the third part of a dram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruple', 'n.', 'Hence, a very small quantity; a particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruple', 'n.', 'Hesitation as to action from the difficulty of determining\n   what is right or expedient; unwillingness, doubt, or hesitation\n   proceeding from motives of conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupled', 'imp. & p. p.', 'of Scruple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skrupling', 'p. pr. & vb. n.', 'of Scruple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruple', 'v. i.', 'To be reluctant or to hesitate, as regards an action,\n   on account of considerations of conscience or expedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruple', 'v. t.', 'To regard with suspicion; to hesitate at; to question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruple', 'v. t.', 'To excite scruples in; to cause to scruple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupler', 'n.', 'One who scruples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupulist', 'n.', 'A scrupler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruou-lize', 'v. t.', 'To perplex with scruples; to regard with scruples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupulosity', 'n.', 'The quality or state of being scruppulous; doubt;\n   doubtfulness respecting decision or action; caution or tenderness from\n   the far of doing wrong or ofending; nice regard to exactness and\n   propierty; precision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupulous', 'a.', 'Full ofscrupules; inclined to scruple; nicely doubtful;\n   hesitating to determine or to act, from a fear of offending or of doing\n   wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupulous', 'a.', 'Careful; cautious; exact; nice; as, scrupulous\n   abstinence from labor; scrupulous performance of duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupulous', 'a.', 'Given to making objections; captious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrupulous', 'a.', 'Liable to be doubted; doubtful; nice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutable', 'a.', 'Discoverable by scrutiny, inquiry, or critical\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutation', 'n.', 'Search; scrutiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutator', 'n.', 'One who scrutinizes; a close examiner or inquirer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutineer', 'n.', 'A scrutinizer; specifically, an examiner of votes, as\n   at an election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutinized', 'imp. & p. p.', 'of Scrutinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutinizing', 'p. pr. & vb. n.', 'of Scrutinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutinize', 'v. t.', 'To examine closely; to inspect or observe with\n   critical attention; to regard narrowly; as, to scrutinize the measures\n   of administration; to scrutinize the conduct or motives of individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutinize', 'v. i.', 'To make scrutiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutinizer', 'n.', 'One who scrutinizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutinous', 'a.', 'Closely examining, or inquiring; careful; sctrict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutiny', 'n.', 'Close examination; minute inspection; critical\n   observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutiny', 'n.', 'An examination of catechumens, in the last week of Lent,\n   who were to receive baptism on Easter Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutiny', 'n.', 'A ticket, or little paper billet, on which a vote is\n   written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutiny', 'n.', 'An examination by a committee of the votes given at an\n   election, for the purpose of correcting the poll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutiny', 'v. t.', 'To scrutinize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scrutoire', 'n.', 'A escritoire; a writing desk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scruze', 'v. t.', 'To squeeze, compress, crush, or bruise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scry', 'v. t.', 'To descry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scry', 'v.', 'A flock of wild fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scry', 'n.', 'A cry or shout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scudded', 'imp. & p. p.', 'of Scud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scudding', 'p. pr. & vb. n.', 'of Scud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'v. i.', 'To move swiftly; especially, to move as if driven forward\n   by something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'v. i.', 'To be driven swiftly, or to run, before a gale, with\n   little or no sail spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'v. t.', 'To pass over quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'n.', 'The act of scudding; a driving along; a rushing with\n   precipitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'n.', 'Loose, vapory clouds driven swiftly by the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'n.', 'A slight, sudden shower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'n.', 'A small flight of larks, or other birds, less than a flock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scud', 'n.', 'Any swimming amphipod crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuddle', 'v. i.', 'To run hastily; to hurry; to scuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scudi', 'pl. ', 'of Scudo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scudo', 'n.', 'A silver coin, and money of account, used in Italy and\n   Sicily, varying in value, in different parts, but worth about 4\n   shillings sterling, or about 96 cents; also, a gold coin worth about\n   the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scudo', 'n.', 'A gold coin of Rome, worth 64 shillings 11 pence sterling,\n   or about $ 15.70.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuff', 'n.', 'The back part of the neck; the scruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffed', 'imp. & p. p.', 'of Scuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffing', 'p. pr. & vb. n.', 'of Scuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuff', 'v. i.', 'To walk without lifting the feet; to proceed with a\n   scraping or dragging movement; to shuffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffled', 'imp. & p. p.', 'of Scuffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffling', 'p. pr. & vb. n.', 'of Scuffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffle', 'v. i.', 'To strive or struggle with a close grapple; to wrestle\n   in a rough fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffle', 'v. i.', 'Hence, to strive or contend tumultuously; to struggle\n   confusedly or at haphazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffle', 'n.', 'A rough, haphazard struggle, or trial of strength; a\n   disorderly wrestling at close quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffle', 'n.', 'Hence, a confused contest; a tumultuous struggle for\n   superiority; a fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffle', 'n.', 'A childs pinafore or bib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffle', 'n.', 'A garden hoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffler', 'n.', 'One who scuffles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuffler', 'n.', 'An agricultural implement resembling a scarifier, but\n   usually lighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scug', 'v. i.', 'To hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scug', 'n.', 'A place of shelter; the declivity of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculk', '', 'Alt. of Sculker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculker', '', 'See Skulk, Skulker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'n.', 'The skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'n.', 'A shoal of fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'n.', 'A boat; a cockboat. See Sculler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'n.', 'One of a pair of short oars worked by one person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'n.', 'A single oar used at the stern in propelling a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'n.', 'The common skua gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculled', 'imp. & p. p.', 'of Scull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculling', 'p. pr. & vb. n.', 'of Scull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'v. t.', 'To impel (a boat) with a pair of sculls, or with a single\n   scull or oar worked over the stern obliquely from side to side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scull', 'v. i.', 'To impel a boat with a scull or sculls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculler', 'n.', 'A boat rowed by one man with two sculls, or short oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculler', 'n.', 'One who sculls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculleries', 'pl. ', 'of Scullery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scullery', 'n.', 'A place where dishes, kettles, and culinary utensils, are\n   cleaned and kept; also, a room attached to the kitchen, where the\n   coarse work is done; a back kitchen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scullery', 'n.', 'Hence, refuse; filth; offal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scullion', 'n.', 'A scalion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scullion', 'n.', 'A servant who cleans pots and kettles, and does other\n   menial services in the kitchen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scullionly', 'a.', 'Like a scullion; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculp', 'v. t.', 'To sculpture; to carve; to engrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpin', 'n.', 'Any one of numerous species of marine cottoid fishes of\n   the genus Cottus, or Acanthocottus, having a large head armed with\n   sharp spines, and a broad mouth. They are generally mottled with\n   yellow, brown, and black. Several species are found on the Atlantic\n   coasts of Europe and America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpin', 'n.', 'A large cottoid market fish of California (Scorpaenichthys\n   marmoratus); -- called also bighead, cabezon, scorpion, salpa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpin', 'n.', 'The dragonet, or yellow sculpin, of Europe (Callionymus\n   lura).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculptile', 'a.', 'Formed by carving; graven; as, sculptile images.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculptor', 'n.', 'One who sculptures; one whose occupation is to carve\n   statues, or works of sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculptor', 'n.', 'Hence, an artist who designs works of sculpture, his\n   first studies and his finished model being usually in a plastic\n   material, from which model the marble is cut, or the bronze is cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculptress', 'n.', 'A female sculptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculptural', 'a.', 'Of or pertaining to sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpture', 'n.', 'The art of carving, cutting, or hewing wood, stone,\n   metal, etc., into statues, ornaments, etc., or into figures, as of men,\n   or other things; hence, the art of producing figures and groups,\n   whether in plastic or hard materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpture', 'n.', 'Carved work modeled of, or cut upon, wood, stone, metal,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculptured', 'imp. & p. p.', 'of Sculpture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpturing', 'p. pr. & vb. n.', 'of Sculpture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpture', 'v. t.', 'To form with the chisel on, in, or from, wood, stone,\n   or metal; to carve; to engrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sculpturesque', 'a.', 'After the manner of sculpture; resembling, or\n   relating to, sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scum', 'v.', 'The extraneous matter or impurities which rise to the surface\n   of liquids in boiling or fermentation, or which form on the surface by\n   other means; also, the scoria of metals in a molten state; dross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scum', 'v.', 'refuse; recrement; anything vile or worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scummed', 'imp. & p. p.', 'of Scum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumming', 'p. pr. & vb. n.', 'of Scum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scum', 'v. t.', 'To take the scum from; to clear off the impure matter from\n   the surface of; to skim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scum', 'v. t.', 'To sweep or range over the surface of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scum', 'v. i.', 'To form a scum; to become covered with scum. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumber', 'v. i.', 'To void excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumber', 'n.', 'Dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumbled', 'imp. & p. p.', 'of Scumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumbling', 'p. pr. & vb. n.', 'of Scumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumble', 'v. t.', 'To cover lighty, as a painting, or a drawing, with a\n   thin wash of opaque color, or with color-crayon dust rubbed on with the\n   stump, or to make any similar additions to the work, so as to produce a\n   softened effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumbling', 'n.', 'A mode of obtaining a softened effect, in painting and\n   drawing, by the application of a thin layer of opaque color to the\n   surface of a painting, or part of the surface, which is too bright in\n   color, or which requires harmonizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumbling', 'n.', 'In crayon drawing, the use of the stump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumbling', 'n.', 'The color so laid on. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scummer', 'v. i.', 'To scumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scummer', 'n.', 'Excrement; scumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scummer', 'n.', 'An instrument for taking off scum; a skimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumming', 'n.', 'The act of taking off scum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scumming', 'n.', 'That which is scummed off; skimmings; scum; -- used\n   chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scummy', 'a.', 'Covered with scum; of the nature of scum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scunner', 'v. t.', 'To cause to loathe, or feel disgust at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scunner', 'v. i.', 'To have a feeling of loathing or disgust; hence, to\n   have dislike, prejudice, or reluctance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scunner', 'n.', 'A feeling of disgust or loathing; a strong prejudice;\n   abhorrence; as, to take a scunner against some one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scup', 'n.', 'A swing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scup', 'n.', 'A marine sparoid food fish (Stenotomus chrysops, or S.\n   argyrops), common on the Atlantic coast of the United States. It\n   appears bright silvery when swimming in the daytime, but shows broad\n   blackish transverse bands at night and when dead. Called also porgee,\n   paugy, porgy, scuppaug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuppaug', 'n.', 'See 2d Scup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scupper', 'v.', 'An opening cut through the waterway and bulwarks of a\n   ship, so that water falling on deck may flow overboard; -- called also\n   scupper hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuppernong', 'n.', 'An American grape, a form of Vitis vulpina, found in\n   the Southern Atlantic States, and often cultivated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scur', 'v. i.', 'To move hastily; to scour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurf', 'n.', 'Thin dry scales or scabs upon the body; especially, thin\n   scales exfoliated from the cuticle, particularly of the scalp;\n   dandruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurf', 'n.', 'Hence, the foul remains of anything adherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurf', 'n.', 'Anything like flakes or scales adhering to a surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurf', 'n.', 'Minute membranous scales on the surface of some leaves, as\n   in the goosefoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurff', 'n.', 'The bull trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurfiness', 'n.', 'Quality or state of being scurfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurfiness', 'n.', 'Scurf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurfy', 'superl.', 'Having or producing scurf; covered with scurf;\n   resembling scurf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurrier', 'n.', 'One who scurries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurrile', 'a.', 'Such as befits a buffoon or vulgar jester; grossly\n   opprobrious or loudly jocose in language; scurrilous; as, scurrile\n   taunts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurrility', 'n.', 'The quality or state of being scurrile or scurrilous;\n   mean, vile, or obscene jocularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurrility', 'n.', 'That which is scurrile or scurrilous; gross or obscene\n   language; low buffoonery; vulgar abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurrilous', 'a.', 'Using the low and indecent language of the meaner sort\n   of people, or such as only the license of buffoons can warrant; as, a\n   scurrilous fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurrilous', 'a.', 'Containing low indecency or abuse; mean; foul; vile;\n   obscenely jocular; as, scurrilous language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurrit', 'n.', 'the lesser tern (Sterna minuta).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurry', 'v. i.', 'To hasten away or along; to move rapidly; to hurry; as,\n   the rabbit scurried away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurry', 'n.', 'Act of scurring; hurried movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurvily', 'adv.', 'In a scurvy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurviness', 'n.', 'The quality or state of being scurvy; vileness;\n   meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurvy', 'n.', 'Covered or affected with scurf or scabs; scabby; scurfy;\n   specifically, diseased with the scurvy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurvy', 'n.', 'Vile; mean; low; vulgar; contemptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scurvy', 'n.', 'A disease characterized by livid spots, especially about\n   the thighs and legs, due to extravasation of blood, and by spongy gums,\n   and bleeding from almost all the mucous membranes. It is accompanied by\n   paleness, languor, depression, and general debility. It is occasioned\n   by confinement, innutritious food, and hard labor, but especially by\n   lack of fresh vegetable food, or confinement for a long time to a\n   limited range of food, which is incapable of repairing the waste of the\n   system. It was formerly prevalent among sailors and soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scut', 'n.', 'The tail of a hare, or of a deer, or other animal whose tail\n   is short, sp. when carried erect; hence, sometimes, the animal itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuta', 'n. pl.', 'See Scutum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutage', 'n.', 'Shield money; commutation of service for a sum of money.\n   See Escuage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutal', 'a.', 'Of or pertaining to a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutate', 'a.', 'Buckler-shaped; round or nearly round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutate', 'a.', 'Protected or covered by bony or horny plates, or large\n   scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutched', 'imp. & p. p.', 'of Scutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutching', 'p. pr. & vb. n.', 'of Scutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutch', 'v. t.', 'To beat or whip; to drub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutch', 'v. t.', 'To separate the woody fiber from (flax, hemp, etc.) by\n   beating; to swingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutch', 'v. t.', 'To loosen and dress the fiber of (cotton or silk) by\n   beating; to free (fibrous substances) from dust by beating and blowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutch', 'n.', 'A wooden instrument used in scutching flax and hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutch', 'n.', 'The woody fiber of flax; the refuse of scutched flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutcheon', 'n.', 'An escutcheon; an emblazoned shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutcheon', 'n.', 'A small plate of metal, as the shield around a keyhole.\n   See Escutcheon, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutcheoned', 'a.', 'Emblazoned on or as a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutcher', 'n.', 'One who scutches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutcher', 'n.', 'An implement or machine for scutching hemp, flax, or\n   cotton; etc.; a scutch; a scutching machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutch grass', '', 'A kind of pasture grass (Cynodon Dactylon). See Bermuda\n   grass: also Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scute', 'n.', 'A small shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scute', 'n.', 'An old French gold coin of the value of 3s. 4d. sterling, or\n   about 80 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scute', 'n.', 'A bony scale of a reptile or fish; a large horny scale on\n   the leg of a bird, or on the belly of a snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutella', 'n. pl.', 'See Scutellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutelle', 'pl. ', 'of Scutella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutella', 'n.', 'See Scutellum, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutellate', 'a.', 'Alt. of Scutellated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutellated', 'a.', 'Formed like a plate or salver; composed of platelike\n   surfaces; as, the scutellated bone of a sturgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutellated', 'a.', 'Having the tarsi covered with broad transverse scales,\n   or scutella; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutellation', 'n.', 'the entire covering, or mode of arrangement, of\n   scales, as on the legs and feet of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutelliform', 'a.', 'Scutellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutelliform', 'a.', 'Having the form of a scutellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutelliplantar', 'a.', 'Having broad scutella on the front, and small\n   scales on the posterior side, of the tarsus; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutella', 'pl. ', 'of Scutellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutellum', 'n.', 'A rounded apothecium having an elevated rim formed of\n   the proper thallus, the fructification of certain lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutellum', 'n.', 'The third of the four pieces forming the upper part of a\n   thoracic segment of an insect. It follows the scutum, and is followed\n   by the small postscutellum; a scutella. See Thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutellum', 'n.', 'One of the transverse scales on the tarsi and toes of\n   birds; a scutella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutibranch', 'a.', 'Scutibranchiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutibranch', 'n.', 'One of the Scutibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutibranchia', 'n. pl.', 'Same as Scutibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutibranchian', 'n.', 'One of the Scutibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutibranchiata', 'n. pl.', 'An order of gastropod Mollusca having a heart\n   with two auricles and one ventricle. The shell may be either spiral or\n   shieldlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutibranchiate', 'a.', 'Having the gills protected by a shieldlike shell;\n   of or pertaining to the Scutibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutibranchiate', 'n.', 'One of the Scutibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutiferous', 'a.', 'Carrying a shield or buckler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutiform', 'a.', 'Shield-shaped; scutate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutiger', 'n.', 'Any species of chilopod myriapods of the genus Scutigera.\n   They sometimes enter buildings and prey upon insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutiped', 'a.', 'Having the anterior surface of the tarsus covered with\n   scutella, or transverse scales, in the form of incomplete bands\n   terminating at a groove on each side; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'n.', 'A broad, shallow basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'n.', 'A wide-mouthed vessel for holding coal: a coal hod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'v. i.', 'To run with affected precipitation; to hurry; to\n   bustle; to scuddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'n.', 'A quick pace; a short run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'n.', 'A small opening in an outside wall or covering, furnished\n   with a lid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'n.', 'A small opening or hatchway in the deck of a ship, large\n   enough to admit a man, and with a lid for covering it, also, a like\n   hole in the side or bottom of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'n.', 'An opening in the roof of a house, with a lid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'n.', 'The lid or door which covers or closes an opening in a\n   roof, wall, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttled', 'imp. & p. p.', 'of Scuttle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttling', 'p. pr. & vb. n.', 'of Scuttle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'v. t.', 'To cut a hole or holes through the bottom, deck, or\n   sides of (as of a ship), for any purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuttle', 'v. t.', 'To sink by making holes through the bottom of; as, to\n   scuttle a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scuta', 'pl. ', 'of Scutum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutum', 'n.', 'An oblong shield made of boards or wickerwork covered with\n   leather, with sometimes an iron rim; -- carried chiefly by the\n   heavy-armed infantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutum', 'n.', 'A penthouse or awning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutum', 'n.', 'The second and largest of the four parts forming the upper\n   surface of a thoracic segment of an insect. It is preceded by the\n   prescutum and followed by the scutellum. See the Illust. under Thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scutum', 'n.', 'One of the two lower valves of the operculum of a barnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scybala', 'n. pl.', 'Hardened masses of feces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scye', 'n.', 'Arm scye, a cutters term for the armhole or part of the\n   armhole of the waist of a garnment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyle', 'v. t.', 'To hide; to secrete; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scylla', 'n.', 'A dangerous rock on the Italian coast opposite the whirpool\n   Charybdis on the coast of Sicily, -- both personified in classical\n   literature as ravenous monsters. The passage between them was formerly\n   considered perilous; hence, the saying \"Between Scylla and Charybdis,\"\n   signifying a great peril on either hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyllaea', 'n.', 'A genus of oceanic nudibranchiate mollusks having the\n   small branched gills situated on the upper side of four fleshy lateral\n   lobes, and on the median caudal crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyllarian', 'n.', 'One of a family (Scyllaridae) of macruran Crustacea,\n   remarkable for the depressed form of the body, and the broad, flat\n   antennae. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyllite', 'n.', 'A white crystalline substance of a sweetish taste,\n   resembling inosite and metameric with dextrose. It is extracted from\n   the kidney of the dogfish (of the genus Scylium), the shark, and the\n   skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scymetar', 'n.', 'See Scimiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphae', 'pl. ', 'of Scypha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scypha', 'n.', 'See Scyphus, 2 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphiform', 'a.', 'Cup-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphistomata', 'pl. ', 'of Scyphistoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphistomae', 'pl. ', 'of Scyphistoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphistoma', 'n.', 'The young attached larva of Discophora in the stage\n   when it resembles a hydroid, or actinian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphobranchii', 'n. pl.', 'An order of fishes including the blennioid and\n   gobioid fishes, and other related families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphomeduse', 'n. pl.', 'Same as Acraspeda, or Discophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphophori', 'n. pl.', 'An order of fresh-water fishes inhabiting tropical\n   Africa. They have rudimentary electrical organs on each side of the\n   tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphi', 'pl. ', 'of Scyphus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphus', 'n.', 'A kind of large drinking cup, -- used by Greeks and\n   Romans, esp. by poor folk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphus', 'n.', 'The cup of a narcissus, or a similar appendage to the\n   corolla in other flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scyphus', 'n.', 'A cup-shaped stem or podetium in lichens. Also called\n   scypha. See Illust. of Cladonia pyxidata, under Lichen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythe', 'n.', 'An instrument for mowing grass, grain, or the like, by\n   hand, composed of a long, curving blade, with a sharp edge, made fast\n   to a long handle, called a snath, which is bent into a form convenient\n   for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythe', 'n.', 'A scythe-shaped blade attached to ancient war chariots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythe', 'v. t.', 'To cut with a scythe; to cut off as with a scythe; to\n   mow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythed', 'a.', 'Armed scythes, as a chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythemen', 'pl. ', 'of Scytheman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scytheman', 'n.', 'One who uses a scythe; a mower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythestone', 'n.', 'A stone for sharpening scythes; a whetstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythewhet', 'n.', 'Wilsons thrush; -- so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythian', 'a.', 'Of or pertaining to Scythia (a name given to the northern\n   part of Asia, and Europe adjoining to Asia), or its language or\n   inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythian', 'n.', 'A native or inhabitant of Scythia; specifically\n   (Ethnol.), one of a Slavonic race which in early times occupied Eastern\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scythian', 'n.', 'The language of the Scythians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Scytodermata', 'n. pl.', 'Same as Holothurioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sdan', 'v. & n.', 'Disdain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sdeath', 'interj.', 'An exclamation expressive of impatience or anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sdeign', 'v. t.', 'To disdain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea', 'n.', 'One of the larger bodies of salt water, less than an ocean,\n   found on the earths surface; a body of salt water of second rank,\n   generally forming part of, or connecting with, an ocean or a larger\n   sea; as, the Mediterranean Sea; the Sea of Marmora; the North Sea; the\n   Carribean Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea', 'n.', 'An inland body of water, esp. if large or if salt or brackish;\n   as, the Caspian Sea; the Sea of Aral; sometimes, a small fresh-water\n   lake; as, the Sea of Galilee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea', 'n.', 'The ocean; the whole body of the salt water which covers a\n   large part of the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea', 'n.', 'The swell of the ocean or other body of water in a high wind;\n   motion of the waters surface; also, a single wave; a billow; as, there\n   was a high sea after the storm; the vessel shipped a sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea', 'n.', 'A great brazen laver in the temple at Jerusalem; -- so called\n   from its size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea', 'n.', 'Fig.: Anything resembling the sea in vastness; as, a sea of\n   glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea acorn', '', 'An acorn barnacle (Balanus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea adder', '', 'The European fifteen-spined stickleback (Gasterosteus\n   spinachia); -- called also bismore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea adder', '', 'The European tanglefish, or pipefish (Syngnathus acus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea anchor', '', 'See Drag sail, under 4th Drag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea amenone', '', 'Any one of numerous species of soft-bodied Anthozoa,\n   belonging to the order Actrinaria; an actinian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea ape', '', 'The thrasher shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea ape', '', 'The sea otter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea apple', '', 'The fruit of a West Indian palm (Manicaria Plukenetii),\n   often found floating in the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea arrow', '', 'A squid of the genus Ommastrephes. See Squid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bank', '', 'The seashore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bank', '', 'A bank or mole to defend against the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-bar', 'n.', 'A tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea barrow', '', 'A sea purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bass', '', 'A large marine food fish (Serranus, / Centropristis,\n   atrarius) which abounds on the Atlantic coast of the United States. It\n   is dark bluish, with black bands, and more or less varied with small\n   white spots and blotches. Called also, locally, blue bass, black sea\n   bass, blackfish, bluefish, and black perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bass', '', 'A California food fish (Cynoscion nobile); -- called also\n   white sea bass, and sea salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bat', '', 'See Batfish (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seabeach', 'n.', 'A beach lying along the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bean', '', 'Same as Florida bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bear', '', 'Any fur seal. See under Fur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bear', '', 'The white bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seabeard', 'n.', 'A green seaweed (Cladophora rupestris) growing in dense\n   tufts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea beast', '', 'Any large marine mammal, as a seal, walrus, or cetacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bird', '', 'Any swimming bird frequenting the sea; a sea fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea blite', '', 'A plant (Suaeda maritima) of the Goosefoot family, growing\n   in salt marches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-blubber', 'n.', 'A jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaboard', 'n.', 'The seashore; seacoast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaboard', 'a.', 'Bordering upon, or being near, the sea; seaside;\n   seacoast; as, a seaboard town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaboard', 'adv.', 'Toward the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaboat', '', 'A boat or vessel adapted to the open sea; hence, a vessel\n   considered with reference to her power of resisting a storm, or\n   maintaining herself in a heavy sea; as, a good sea boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaboat', '', 'A chitin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seabord', 'n. & a.', 'See Seaboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-bordering', 'a.', 'Bordering on the sea; situated beside the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-born', 'a.', 'Born of the sea; produced by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-born', 'a.', 'Born at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seabound', 'a.', 'Bounded by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bow', '', 'See Marine rainbow, under Rainbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea boy', '', 'A boy employed on shipboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea breach', '', 'A breaking or overflow of a bank or a dike by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bream', '', 'Any one of several species of sparoid fishes, especially\n   the common European species (Pagellus centrodontus), the Spanish (P.\n   Oweni), and the black sea bream (Cantharus lineatus); -- called also\n   old wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea brief', '', 'Same as Sea letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea bug', '', 'A chiton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-built', 'a.', 'Built at, in, or by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea butterfly', '', 'A pteropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cabbage', '', 'See Sea kale, under Kale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea calf', '', 'The common seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea canary', '', 'The beluga, or white whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea captain', '', 'The captain of a vessel that sails upon the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea card', '', 'Mariners card, or compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea catfish', '', 'Alt. of Sea cat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cat', '', 'The wolf fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cat', '', 'Any marine siluroid fish, as Aelurichthys marinus, and\n   Arinus felis, of the eastern coast of the United States. Many species\n   are found on the coasts of Central and South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea chart', '', 'A chart or map on which the lines of the shore, islands,\n   shoals, harbors, etc., are delineated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea chickweed', '', 'A fleshy plant (Arenaria peploides) growing in large\n   tufts in the sands of the northern Atlantic seacoast; -- called also\n   sea sandwort, and sea purslane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea clam', '', 'Any one of the large bivalve mollusks found on the open\n   seacoast, especially those of the family Mactridae, as the common\n   American species. (Mactra, / Spisula, solidissima); -- called also\n   beach clam, and surf clam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea coal', '', 'Coal brought by sea; -- a name by which mineral coal was\n   formerly designated in the south of England, in distinction from\n   charcoal, which was brought by land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seacoast', 'n.', 'The shore or border of the land adjacent to the sea or\n   ocean. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cob', '', 'The black-backed gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cock', '', 'In a steamship, a cock or valve close to the vessels side,\n   for closing a pipe which communicates with the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cock', '', 'The black-bellied plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cock', '', 'A gurnard, as the European red gurnard (Trigla pini).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cocoa', '', 'A magnificent palm (Lodoicea Sechellarum) found only in\n   the Seychelles Islands. The fruit is an immense two-lobed nut. It was\n   found floating in the Indian Ocean before the tree was known, and\n   called sea cocoanut, and double cocoanut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea colander', '', 'A large blackfish seaweed (Agarum Turneri), the frond\n   of which is punctured with many little holes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea colewort', '', 'Sea cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea compass', '', 'The mariners compass. See under Compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea coot', '', 'A scoter duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea corn', '', 'A yellow cylindrical mass of egg capsule of certain species\n   of whelks (Buccinum), which resembles an ear of maize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cow', '', 'The mantee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cow', '', 'The dugong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cow', '', 'The walrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crawfish', '', 'Alt. of Sea crayfish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crayfish', '', 'Any crustacean of the genus Palinurus and allied\n   genera, as the European spiny lobster (P. vulgaris), which is much used\n   as an article of food. See Lobster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crow', '', 'The chough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crow', '', 'The cormorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crow', '', 'The blackheaded pewit, and other gulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crow', '', 'The skua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crow', '', 'The razorbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea crow', '', 'The coot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea cucumber', '', 'Any large holothurian, especially one of those\n   belonging to the genus Pentacta, or Cucumaria, as the common American\n   and European species. (P. frondosa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dace', '', 'The European sea perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea daffodil', '', 'A European amarylidaceous plant (Pancratium maritimum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea devil', '', 'Any very large ray, especially any species of the genus\n   Manta or Cepholoptera, some of which become more than twenty feet\n   across and weigh several tons. See also Ox ray, under Ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea devil', '', 'Any large cephalopod, as a large Octopus, or a giant squid\n   (Architeuthis). See Devilfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea devil', '', 'The angler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dog', '', 'The dogfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dog', '', 'The common seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dog', '', 'An old sailor; a salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dotterel', '', 'The turnstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dove', '', 'The little auk, or rotche. See Illust. of Rotche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dragon', '', 'A dragonet, or sculpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea dragon', '', 'The pegasus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea drake', '', 'The pewit gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea duck', '', 'Any one of numerous species of ducks which frequent the\n   seacoasts and feed mainly on fishes and mollusks. The scoters, eiders,\n   old squaw, and ruddy duck are examples. They may be distinguished by\n   the lobate hind toe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea eagle', '', 'Any one of several species of fish-eating eagles of the\n   genus Haliaeetus and allied genera, as the North Pacific sea eagle. (H.\n   pelagicus), which has white shoulders, head, rump, and tail; the\n   European white-tailed eagle (H. albicilla); and the Indian white-tailed\n   sea eagle, or fishing eagle (Polioaetus ichthyaetus). The bald eagle\n   and the osprey are also sometimes classed as sea eagles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea eagle', '', 'The eagle ray. See under Ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-ear', 'n.', 'Any species of ear-shaped shells of the genus Haliotis.\n   See Abalone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea eel', '', 'The conger eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea egg', '', 'A sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea elephant', '', 'A very large seal (Macrorhinus proboscideus) of the\n   Antarctic seas, much hunted for its oil. It sometimes attains a length\n   of thirty feet, and is remarkable for the prolongation of the nose of\n   the adult male into an erectile elastic proboscis, about a foot in\n   length. Another species of smaller size (M. angustirostris) occurs on\n   the coast of Lower California, but is now nearly extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea fan', '', 'Any gorgonian which branches in a fanlike form, especially\n   Gorgonia flabellum of Florida and the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seafarer', 'n.', 'One who follows the sea as a business; a mariner; a\n   sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seafaring', 'a.', 'Following the business of a mariner; as, a seafaring\n   man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea feather', '', 'Any gorgonian which branches in a plumelike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea fennel', '', 'Samphire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea fern', '', 'Any gorgonian which branches like a fern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea fight', '', 'An engagement between ships at sea; a naval battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea fir', '', 'A sertularian hydroid, especially Sertularia abietina, which\n   branches like a miniature fir tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea flewer', '', 'A sea anemone, or any related anthozoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea foam', '', 'Foam of sea water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea foam', '', 'Meerschaum; -- called also sea froth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea fowl', '', 'Any bird which habitually frequents the sea, as an auk,\n   gannet, gull, tern, or petrel; also, all such birds, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea fox', '', 'The thrasher shark. See Thrasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea froth', '', 'See Sea foam, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-gate', 'n.', 'Alt. of Sea-gait');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-gait', 'n.', 'A long, rolling swell of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea gauge', '', 'See under Gauge, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea gherkin', '', 'Alt. of Sea girkin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea girkin', '', 'Any small holothurian resembling in form a gherkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea ginger', '', 'A hydroid coral of the genus Millepora, especially M.\n   alcicornis, of the West Indies and Florida. So called because it stings\n   the tongue like ginger. See Illust. under Millepore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea girdles', '', 'A kind of kelp (Laminaria digitata) with palmately cleft\n   fronds; -- called also sea wand, seaware, and tangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seagirt', 'a.', 'Surrounded by the water of the sea or ocean; as, a seagirt\n   isle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea god', '', 'A marine deity; a fabulous being supposed to live in, or\n   have dominion over, the sea, or some particular sea or part of the sea,\n   as Neptune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea goddess', '', 'A goddess supposed to live in or reign over the sea, or\n   some part of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seagoing', 'a.', 'Going upon the sea; especially, sailing upon the deep\n   sea; -- used in distinction from coasting or river, as applied to\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea goose', '', 'A phalarope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea gown', '', 'A gown or frock with short sleeves, formerly worn by\n   mariners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea grape', '', 'The gulf weed. See under Gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea grape', '', 'A shrubby plant (Coccoloba uvifera) growing on the sandy\n   shores of tropical America, somewhat resembling the grapevine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea grape', '', 'The clusters of gelatinous egg capsules of a squid\n   (Loligo).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea grass', '', 'Eelgrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea green', '', 'The green color of sea water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-green', 'a.', 'Of a beautiful bluish green color, like sea water on\n   soundings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea gudgeon', '', 'The European black goby (Gobius niger).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea gull', '', 'Any gull living on the seacoast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seah', 'n.', 'A Jewish dry measure containing one third of an an ephah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea hare', '', 'Any tectibranchiate mollusk of the genus Aplysia. See\n   Aplysia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea hawk', '', 'A jager gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea heath', '', 'A low perennial plant (Frankenia laevis) resembling heath,\n   growing along the seashore in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea hedgehog', '', 'A sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea hen', '', 'the common guillemot; -- applied also to various other sea\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea hog', '', 'The porpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea holly', '', 'An evergeen seashore plant (Eryngium maritimum). See\n   Eryngium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea holm', '', 'A small uninhabited island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea holm', '', 'Sea holly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea horse', '', 'A fabulous creature, half horse and half fish, represented\n   in classic mythology as driven by sea dogs or ridden by the Nereids. It\n   is also depicted in heraldry. See Hippocampus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea horse', '', 'The walrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea horse', '', 'Any fish of the genus Hippocampus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea hulver', '', 'Sea holly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-island', 'a.', 'Of or pertaining to certain islands along the coast of\n   South Carolina and Georgia; as, sea-island cotton, a superior cotton of\n   long fiber produced on those islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea jelly', '', 'A medusa, or jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seak', 'n.', 'Soap prepared for use in milling cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea kale', '', 'See under Kale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea king', '', 'One of the leaders among the Norsemen who passed their\n   lives in roving the seas in search of plunder and adventures; a Norse\n   pirate chief. See the Note under Viking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'n.', 'Any aquatic carnivorous mammal of the families Phocidae and\n   Otariidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'n.', 'An engraved or inscribed stamp, used for marking an\n   impression in wax or other soft substance, to be attached to a\n   document, or otherwise used by way of authentication or security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'n.', 'Wax, wafer, or other tenacious substance, set to an\n   instrument, and impressed or stamped with a seal; as, to give a deed\n   under hand and seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'n.', 'That which seals or fastens; esp., the wax or wafer placed on\n   a letter or other closed paper, etc., to fasten it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'n.', 'That which confirms, ratifies, or makes stable; that which\n   authenticates; that which secures; assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'n.', 'An arrangement for preventing the entrance or return of gas\n   or air into a pipe, by which the open end of the pipe dips beneath the\n   surface of water or other liquid, or a deep bend or sag in the pipe is\n   filled with the liquid; a draintrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sealed', 'imp. & p. p.', 'of Seal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skaling', 'p. pr. & vb. n.', 'of Seal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. t.', 'To set or affix a seal to; hence, to authenticate; to\n   confirm; to ratify; to establish; as, to seal a deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. t.', 'To mark with a stamp, as an evidence of standard\n   exactness, legal size, or merchantable quality; as, to seal weights and\n   measures; to seal silverware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. t.', 'To fasten with a seal; to attach together with a wafer,\n   wax, or other substance causing adhesion; as, to seal a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. t.', 'Hence, to shut close; to keep close; to make fast; to keep\n   secure or secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. t.', 'To fix, as a piece of iron in a wall, with cement,\n   plaster, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. t.', 'To close by means of a seal; as, to seal a drainpipe with\n   water. See 2d Seal, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. t.', 'Among the Mormons, to confirm or set apart as a second or\n   additional wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal', 'v. i.', 'To affix ones seal, or a seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea laces', '', 'A kind of seaweed (Chorda Filum) having blackish cordlike\n   fronds, often many feet long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lamprey', '', 'The common lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea language', '', 'The peculiar language or phraseology of seamen;\n   sailors cant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lark', '', 'The rock pipit (Anthus obscurus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lark', '', 'Any one of several small sandpipers and plovers, as the\n   ringed plover, the turnstone, the dunlin, and the sanderling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lavender', '', 'See Marsh rosemary, under Marsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lawyer', '', 'The gray snapper. See under Snapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seal-brown', 'a.', 'Of a rich dark brown color, like the fur of the fur\n   seal after it is dyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea legs', '', 'Legs able to maintain their possessor upright in stormy\n   weather at sea, that is, ability stand or walk steadily on deck when a\n   vessel is rolling or pitching in a rough sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lemon', '', 'Any one of several species of nudibranchiate mollusks of\n   the genus Doris and allied genera, having a smooth, thick, convex\n   yellow body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea leopard', '', 'Any one of several species of spotted seals, especially\n   Ogmorhinus leptonyx, and Leptonychotes Weddelli, of the Antarctic\n   Ocean. The North Pacific sea leopard is the harbor seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sealer', 'n.', 'One who seals; especially, an officer whose duty it is to\n   seal writs or instruments, to stamp weights and measures, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sealer', 'n.', 'A mariner or a vessel engaged in the business of capturing\n   seals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea letter', '', 'The customary certificate of national character which\n   neutral merchant vessels are bound to carry in time of war; a passport\n   for a vessel and cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lettuce', '', 'The green papery fronds of several seaweeds of the genus\n   Ulva, sometimes used as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea level', '', 'The level of the surface of the sea; any surface on the\n   same level with the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sealgh', 'n.', 'Alt. of Selch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selch', 'n.', 'A seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lily', '', 'A crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sealing wax', '', 'A compound of the resinous materials, pigments, etc.,\n   used as a material for seals, as for letters, documents, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea lion', '', 'Any one of several large species of seals of the family\n   Otariidae native of the Pacific Ocean, especially the southern sea lion\n   (Otaria jubata) of the South American coast; the northern sea lion\n   (Eumetopias Stelleri) found from California to Japan; and the black, or\n   California, sea lion (Zalophus Californianus), which is common on the\n   rocks near San Francisco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea loach', '', 'The three-bearded rockling. See Rockling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea louse', '', 'Any one of numerous species of isopod crustaceans of\n   Cymothoa, Livoneca, and allied genera, mostly parasites on fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'Grease; tallow; lard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'The fold or line formed by sewing together two pieces of\n   cloth or leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'Hence, a line of junction; a joint; a suture, as on a ship, a\n   floor, or other structure; the line of union, or joint, of two boards,\n   planks, metal plates, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'A thin layer or stratum; a narrow vein between two thicker\n   strata; as, a seam of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'A line or depression left by a cut or wound; a scar; a\n   cicatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamed', 'imp. & p. p.', 'of Seam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaming', 'p. pr. & vb. n.', 'of Seam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'v. t.', 'To form a seam upon or of; to join by sewing together; to\n   unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'v. t.', 'To mark with something resembling a seam; to line; to\n   scar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'v. t.', 'To make the appearance of a seam in, as in knitting a\n   stocking; hence, to knit with a certain stitch, like that in such\n   knitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'v. i.', 'To become ridgy; to crack open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'A denomination of weight or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'The quantity of eight bushels of grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seam', 'n.', 'The quantity of 120 pounds of glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-maid', 'n.', 'The mermaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-maid', 'n.', 'A sea nymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-mail', 'n.', 'A gull; the mew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamen', 'pl. ', 'of Seaman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaman', 'n.', 'A merman; the male of the mermaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamen', 'pl. ', 'of Seaman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaman', 'n.', 'One whose occupation is to assist in the management of\n   ships at sea; a mariner; a sailor; -- applied both to officers and\n   common mariners, but especially to the latter. Opposed to landman, or\n   landsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamanlike', 'a.', 'Having or showing the skill of a practical seaman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamanship', 'n.', 'The skill of a good seaman; the art, or skill in the\n   art, of working a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea mantis', '', 'A squilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea marge', '', 'Land which borders on the sea; the seashore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamark', 'n.', 'Any elevated object on land which serves as a guide to\n   mariners; a beacon; a landmark visible from the sea, as a hill, a tree,\n   a steeple, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea mat', '', 'Any bryozoan of the genus Flustra or allied genera which\n   form frondlike corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea maw', '', 'The sea mew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamed', 'a.', 'Out of condition; not in good condition; -- said of a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-mell', 'n.', 'The sea mew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea mew', '', 'A gull; the mew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea mile', '', 'A geographical mile. See Mile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea milkwort', '', 'A low, fleshy perennial herb (Glaux maritima) found\n   along northern seashores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaming', 'n.', 'The act or process of forming a seam or joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaming', 'n.', 'The cord or rope at the margin of a seine, to which the\n   meshes of the net are attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamless', 'a.', 'Without a seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea monk', '', 'See Monk seal, under Monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea monster', '', 'Any large sea animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea moss', '', 'Any branched marine bryozoan resembling moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea mouse', '', 'A dorsibranchiate annelid, belonging to Aphrodite and\n   allied genera, having long, slender, hairlike setae on the sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea mouse', '', 'The dunlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamster', 'n.', 'One who sews well, or whose occupation is to sew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamstress', 'n.', 'A woman whose occupation is sewing; a needlewoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamstressy', 'n.', 'The business of a seamstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea mud', '', 'A rich slimy deposit in salt marshes and along the seashore,\n   sometimes used as a manure; -- called also sea ooze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seamy', 'a.', 'Having a seam; containing seams, or showing them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sean', 'n.', 'A seine. See Seine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seance', 'n.', 'A session, as of some public body; especially, a meeting of\n   spiritualists to receive spirit communication, so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea needle', '', 'See Garfish (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea nettle', '', 'A jellyfish, or medusa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seannachie', 'n.', 'A bard among the Highlanders of Scotland, who preserved\n   and repeated the traditions of the tribes; also, a genealogist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea onion', '', 'The officinal squill. See Squill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea ooze', '', 'Same as Sea mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea orange', '', 'A large American holothurian (Lophothuria Fabricii)\n   having a bright orange convex body covered with finely granulated\n   scales. Its expanded tentacles are bright red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-orb', 'n.', 'A globefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea otter', '', 'An aquatic carnivore (Enhydris lutris, / marina) found in\n   the North Pacific Ocean. Its fur is highly valued, especially by the\n   Chinese. It is allied to the common otter, but is larger, with feet\n   more decidedly webbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea owl', '', 'The lumpfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pad', '', 'The puffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea partridge', '', 'The gilthead (Crenilabrus melops), a fish of the\n   British coasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pass', '', 'A document carried by neutral merchant vessels in time of\n   war, to show their nationality; a sea letter or passport. See Passport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea peach', '', 'A beautiful American ascidian (Cynthia, / Halocynthia,\n   pyriformis) having the size, form, velvety surface, and color of a ripe\n   peach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pear', '', 'A pedunculated ascidian of the genus Boltonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-pen', 'n.', 'A pennatula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea perch', '', 'The European bass (Roccus, / Labrax, lupus); -- called\n   also sea dace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea perch', '', 'The cunner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea perch', '', 'The sea bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea perch', '', 'The name is applied also to other species of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pheasant', '', 'The pintail duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pie', '', 'The oyster catcher, a limicoline bird of the genus\n   Haematopus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pie', '', 'A dish of crust or pastry and meat or fish, etc., cooked\n   together in alternate layers, -- a common food of sailors; as, a\n   three-decker sea pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seapiece', 'n.', 'A picture representing a scene at sea; a marine picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea piet', '', 'See 1st Sea pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pig', '', 'A porpoise or dolphin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pig', '', 'A dugong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pigeon', '', 'The common guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pike', '', 'The garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pike', '', 'A large serranoid food fish (Centropomus undecimalis) found\n   on both coasts of America; -- called also robalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pike', '', 'The merluce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pincushion', '', 'A sea purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pincushion', '', 'A pentagonal starfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pink', '', 'See Thrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea plover', '', 'the black-bellied plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea poacher', '', 'Alt. of Sea poker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea poker', '', 'The lyrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pool', '', 'A pool of salt water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea poppy', '', 'The horn poppy. See under Horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea porcupine', '', 'Any fish of the genus Diodon, and allied genera, whose\n   body is covered with spines. See Illust. under Diodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pork', '', 'An American compound ascidian (Amoraecium stellatum) which\n   forms large whitish masses resembling salt pork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaport', 'n.', 'A port on the seashore, or one accessible for seagoing\n   vessels. Also used adjectively; as, a seaport town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seapoy', 'n.', 'See Sepoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pudding', '', 'Any large holothurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea purse', '', 'The horny egg case of a skate, and of certain sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea purslane', '', 'See under Purslane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pye', '', 'See 1st Sea pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea pyot', '', 'See 1st Sea pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea quail', '', 'The turnstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaquake', 'n.', 'A quaking of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sear', 'a.', 'Alt. of Sere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sere', 'a.', '[OE. seer, AS. sear (assumed) fr. searian to wither; akin to\n   D. zoor dry, LG. soor, OHG. sor/n to to wither, Gr. a\"y`ein to parch,\n   to dry, Skr. /ush (for sush) to dry, to wither, Zend hush to dry. 152.\n   Cf. Austere, Sorrel, a.] Dry; withered; no longer green; -- applied to\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seared', 'imp. & p. p.', 'of Sear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searing', 'p. pr. & vb. n.', 'of Sear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sear', 'a.', 'To wither; to dry up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sear', 'a.', 'To burn (the surface of) to dryness and hardness; to\n   cauterize; to expose to a degree of heat such as changes the color or\n   the hardness and texture of the surface; to scorch; to make callous;\n   as, to sear the skin or flesh. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sear', 'n.', 'The catch in a gunlock by which the hammer is held cocked or\n   half cocked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea rat', '', 'A pirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea rat', '', 'The chimaera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea raven', '', 'An American cottoid fish (Hemitripterus Americanus) allied\n   to the sculpins, found on the northeren Atlantic coasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea raven', '', 'The cormorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searce', 'n.', 'A fine sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searce', 'v. t.', 'To sift; to bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcer', 'n.', 'One who sifts or bolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcer', 'n.', 'A searce, or sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searched', 'imp. & p. p.', 'of Search');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searching', 'p. pr. & vb. n.', 'of Search');
  INSERT INTO words (word, word_type, description) VALUES  (    'Search', 'v. t.', 'To look over or through, for the purpose of finding\n   something; to examine; to explore; as, to search the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Search', 'v. t.', 'To inquire after; to look for; to seek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Search', 'v. t.', 'To examine or explore by feeling with an instrument; to\n   probe; as, to search a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Search', 'v. t.', 'To examine; to try; to put to the test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Search', 'v. i.', 'To seek; to look for something; to make inquiry,\n   exploration, or examination; to hunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Search', 'v. t.', 'The act of seeking or looking for something; quest;\n   inquiry; pursuit for finding something; examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searchable', 'a.', 'Capable of being searched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searchableness', 'n.', 'Quality of being searchable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcher', 'n.', 'One who, or that which, searhes or examines; a seeker; an\n   inquirer; an examiner; a trier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcher', 'n.', 'Formerly, an officer in London appointed to examine the\n   bodies of the dead, and report the cause of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcher', 'n.', 'An officer of the customs whose business it is to search\n   ships, merchandise, luggage, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcher', 'n.', 'An inspector of leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcher', 'n.', 'An instrument for examining the bore of a cannon, to\n   detect cavities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcher', 'n.', 'An implement for sampling butter; a butter trier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcher', 'n.', 'An instrument for feeling after calculi in the bladder,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searching', 'a.', 'Exploring thoroughly; scrutinizing; penetrating; trying;\n   as, a searching discourse; a searching eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searchless', 'a.', 'Impossible to be searched; inscrutable; impenetrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcloth', 'n.', 'Cerecloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searcloth', 'v. t.', 'To cover, as a sore, with cerecloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seared', 'a.', 'Scorched; cauterized; hence, figuratively, insensible; not\n   susceptible to moral influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Searedness', 'n.', 'The state of being seared or callous; insensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea reed', '', 'The sea-sand reed. See under Reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea risk', '', 'Risk of injury, destruction, or loss by the sea, or while\n   at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea robber', '', 'A pirate; a sea rover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea robin', '', 'See under Robin, and Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea rocket', '', 'See under Rocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea room', '', 'Room or space at sea for a vessel to maneuver, drive, or\n   scud, without peril of running ashore or aground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea rover', '', 'One that cruises or roves the sea for plunder; a sea\n   robber; a pirate; also, a piratical vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-roving', 'a.', 'Cruising at random on the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea salmon', '', 'A young pollock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea salmon', '', 'The spotted squeteague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea salmon', '', 'See Sea bass (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea salt', '', 'Common salt, obtained from sea water by evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea sandpiper', '', 'The purple sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea sandwort', '', 'See Sea chickweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea saurian', 'n.', 'Any marine saurian; esp. (Paleon.) the large extinct\n   species of Mosasaurus, Icthyosaurus, Plesiosaurus, and related genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seascape', 'n.', 'A picture representing a scene at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea scorpion', '', 'A European sculpin (Cottus scorpius) having the head\n   armed with short spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea scorpion', '', 'The scorpene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea scurf', '', 'Any bryozoan which forms rounded or irregular patches of\n   coral on stones, seaweeds, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea serpent', '', 'Any marine snake. See Sea snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea serpent', '', 'A large marine animal of unknown nature, often reported\n   to have been seen at sea, but never yet captured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seashell', 'n.', 'The shell of any marine mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seashore', 'n.', 'The coast of the sea; the land that lies adjacent to the\n   sea or ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seashore', 'n.', 'All the ground between the ordinary highwater and\n   low-water marks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasick', 'a.', 'Affected with seasickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasickness', 'n.', 'The peculiar sickness, characterized by nausea and\n   prostration, which is caused by the pitching or rolling of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaside', 'n.', 'The land bordering on, or adjacent to, the sea; the\n   seashore. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea slater', '', 'Any isopod crustacean of the genus Ligia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea slug', '', 'A holothurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea slug', '', 'A nudibranch mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea snail', '', 'A small fish of the genus Liparis, having a ventral\n   sucker. It lives among stones and seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea snail', '', 'Any small creeping marine gastropod, as the species of\n   Littorina, Natica, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea snake', '', 'Any one of many species of venomous aquatic snakes of the\n   family Hydrophidae, having a flattened tail and living entirely in the\n   sea, especially in the warmer parts of the Indian and Pacific Oceans.\n   They feed upon fishes, and are mostly of moderate size, but some\n   species become eight or ten feet long and four inches broad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea snipe', '', 'A sandpiper, as the knot and dunlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea snipe', '', 'The bellows fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'n.', 'One of the divisions of the year, marked by alternations in\n   the length of day and night, or by distinct conditions of temperature,\n   moisture, etc., caused mainly by the relative position of the earth\n   with respect to the sun. In the north temperate zone, four seasons,\n   namely, spring, summer, autumn, and winter, are generally recognized.\n   Some parts of the world have three seasons, -- the dry, the rainy, and\n   the cold; other parts have but two, -- the dry and the rainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'n.', 'Hence, a period of time, especially as regards its fitness\n   for anything contemplated or done; a suitable or convenient time;\n   proper conjuncture; as, the season for planting; the season for rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'n.', 'A period of time not very long; a while; a time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'n.', 'That which gives relish; seasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasoned', 'imp. & p. p.', 'of Season');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasoning', 'p. pr. & vb. n.', 'of Season');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'To render suitable or appropriate; to prepare; to fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'To fit for any use by time or habit; to habituate; to\n   accustom; to inure; to ripen; to mature; as, to season one to a\n   climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'Hence, to prepare by drying or hardening, or removal of\n   natural juices; as, to season timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'To fit for taste; to render palatable; to give zest or\n   relish to; to spice; as, to season food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'Hence, to fit for enjoyment; to render agrecable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'To qualify by admixture; to moderate; to temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'To imbue; to tinge or taint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. t.', 'To copulate with; to impregnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. i.', 'To become mature; to grow fit for use; to become adapted\n   to a climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. i.', 'To become dry and hard, by the escape of the natural\n   juices, or by being penetrated with other substance; as, timber seasons\n   in the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Season', 'v. i.', 'To give token; to savor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasonable', 'a.', 'Occurring in good time, in due season, or in proper\n   time for the purpose; suitable to the season; opportune; timely; as, a\n   seasonable supply of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasonage', 'n.', 'A seasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasonal', 'a.', 'Of or pertaining to the seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasoner', 'n.', 'One who, or that which, seasons, or gives a relish; a\n   seasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasoning', 'n.', 'The act or process by which anything is seasoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasoning', 'n.', 'That which is added to any species of food, to give it a\n   higher relish, as salt, spices, etc.; a condiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasoning', 'n.', 'Hence, something added to enhance enjoyment or relieve\n   dullness; as, wit is the seasoning of conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seasonless', 'a.', 'Without succession of the seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea spider', '', 'Any maioid crab; a spider crab. See Maioid, and Spider\n   crab, under Spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea spider', '', 'Any pycnogonid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea squirt', '', 'An ascidian. See Illust. under Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea star', '', 'A starfish, or brittle star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea surgeon', '', 'A surgeon fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea swallow', '', 'The common tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea swallow', '', 'The storm petrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea swallow', '', 'The gannet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea swallow', '', 'See Cornish chough, under Chough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'n.', 'The place or thing upon which one sits; hence; anything made\n   to be sat in or upon, as a chair, bench, stool, saddle, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'n.', 'The place occupied by anything, or where any person or thing\n   is situated, resides, or abides; a site; an abode, a station; a post; a\n   situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'n.', 'That part of a thing on which a person sits; as, the seat of\n   a chair or saddle; the seat of a pair of pantaloons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'n.', 'A sitting; a right to sit; regular or appropriate place of\n   sitting; as, a seat in a church; a seat for the season in the opera\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'n.', 'Posture, or way of sitting, on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'n.', 'A part or surface on which another part or surface rests; as,\n   a valve seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seated', 'imp. & p. p.', 'of Seat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seating', 'p. pr. & vb. n.', 'of Seat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'v. t.', 'To place on a seat; to cause to sit down; as, to seat\n   ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'v. t.', 'To cause to occupy a post, site, situation, or the like;\n   to station; to establish; to fix; to settle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'v. t.', 'To assign a seat to, or the seats of; to give a sitting\n   to; as, to seat a church, or persons in a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'v. t.', 'To fix; to set firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'v. t.', 'To settle; to plant with inhabitants; as to seat a\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'v. t.', 'To put a seat or bottom in; as, to seat a chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seat', 'v. i.', 'To rest; to lie down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea tang', '', 'A kind of seaweed; tang; tangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea term', '', 'A term used specifically by seamen; a nautical word or\n   phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea thief', '', 'A pirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea thongs', '', 'A kind of blackish seaweed (Himanthalia lorea) found on\n   the northern coasts of the Atlantic. It has a thonglike forking process\n   rising from a top-shaped base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seating', 'n.', 'The act of providong with a seat or seats; as, the seating\n   of an audience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seating', 'n.', 'The act of making seats; also, the material for making\n   seats; as, cane seating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea titling', '', 'The rock pipit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seatless', 'a.', 'Having no seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea toad', '', 'A sculpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea toad', '', 'A toadfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea toad', '', 'The angler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea trout', '', 'Any one of several species of true trouts which descend\n   rivers and enter the sea after spawning, as the European bull trout and\n   salmon trout, and the eastern American spotted trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea trout', '', 'The common squeteague, and the spotted squeteague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea trout', '', 'A California fish of the family Chiridae, especially\n   Hexagrammus decagrammus; -- called also spotted rock trout. See Rock\n   trout, under Rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea trout', '', 'A California sciaenoid fish (Cynoscion nobilis); -- called\n   also white sea bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea trumpet', '', 'A great blackish seaweed of the Southern Ocean, having a\n   hollow and expanding stem and a pinnate frond, sometimes twenty feet\n   long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea trumpet', '', 'Any large marine univalve shell of the genus Triton. See\n   Triton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea turn', '', 'A breeze, gale, or mist from the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea turtle', '', 'Any one of several very large species of chelonians\n   having the feet converted into paddles, as the green turtle, hawkbill,\n   loggerhead, and leatherback. They inhabit all warm seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea turtle', '', 'The sea pigeon, or guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea unicorn', '', 'The narwhal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea urchin', '', 'Any one of numerous species of echinoderms of the order\n   Echinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seave', 'n.', 'A rush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seavy', 'a.', 'Overgrown with rushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wall', '', 'A wall, or embankment, to resist encroachments of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea-walled', 'a.', 'Surrounded, bounded, or protected by the sea, as if by\n   a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seawan', 'n.', 'Alt. of Seawant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seawant', 'n.', 'The name used by the Algonquin Indians for the shell beads\n   which passed among the Indians as money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seawand', '', 'See Sea girdles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaward', 'a.', 'Directed or situated toward the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaward', 'adv.', 'Toward the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaware', 'n.', 'Seaweed; esp., coarse seaweed. See Ware, and Sea girdles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaweed', 'n.', 'Popularly, any plant or plants growing in the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaweed', 'n.', 'Any marine plant of the class Algae, as kelp, dulse,\n   Fucus, Ulva, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea whip', '', 'A gorgonian having a simple stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea widgeon', '', 'The scaup duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea widgeon', '', 'The pintail duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seawives', 'pl. ', 'of Seawife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seawife', 'n.', 'A European wrasse (Labrus vetula).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea willow', '', 'A gorgonian coral with long flexible branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wing', '', 'A wing shell (Avicula).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea withwind', '', 'A kind of bindweed (Convolvulus Soldanella) growing on\n   the seacoast of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wolf', '', 'The wolf fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wolf', '', 'The European sea perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wolf', '', 'The sea elephant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wolf', '', 'A sea lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea woodcock', '', 'The bar-tailed godwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wood louse', '', 'A sea slater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wormwood', '', 'A European species of wormwood (Artemisia maritima)\n   growing by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaworthiness', 'n.', 'The state or quality of being seaworthy, or able to\n   resist the ordinary violence of wind and weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seaworthy', 'a.', 'Fit for a voyage; worthy of being trusted to transport a\n   cargo with safety; as, a seaworthy ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sea wrack', '', 'See Wrack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebaceous', 'a.', 'Pertaining to, or secreting, fat; composed of fat;\n   having the appearance of fat; as, the sebaceous secretions of some\n   plants, or the sebaceous humor of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebacic', 'a.', 'Of or pertaining to fat; derived from, or resembling, fat;\n   specifically, designating an acid (formerly called also sebic, and\n   pyroleic, acid), obtained by the distillation or saponification of\n   certain oils (as castor oil) as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebat', 'n.', 'The eleventh month of the ancient Hebrew year, approximately\n   corresponding with February.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebate', 'n.', 'A salt of sebacic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebesten', 'n.', 'The mucilaginous drupaceous fruit of two East Indian\n   trees (Cordia Myxa, and C. latifolia), sometimes used medicinally in\n   pectoral diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebic', 'a.', 'See Sebacic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebiferous', 'a.', 'Producing vegetable tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebiferous', 'a.', 'Producing fat; sebaceous; as, the sebiferous, or\n   sebaceous, glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sebiparous', 'a.', 'Same as Sebiferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seborrhea', 'n.', 'A morbidly increased discharge of sebaceous matter upon\n   the skin; stearrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secale', 'n.', 'A genus of cereal grasses including rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secancy', 'n.', 'A cutting; an intersection; as, the point of secancy of\n   one line by another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secant', 'a.', 'Cutting; divivding into two parts; as, a secant line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secant', 'a.', 'A line that cuts another; especially, a straight line\n   cutting a curve in two or more points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secant', 'a.', 'A right line drawn from the center of a circle through one\n   end of a circular arc, and terminated by a tangent drawn from the other\n   end; the number expressing the ratio line of this line to the radius of\n   the circle. See Trigonometrical function, under Function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secco', 'a.', 'Dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seceded', 'imp. & p. p.', 'of Secede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seceding', 'p. pr. & vb. n.', 'of Secede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secede', 'v. i.', 'To withdraw from fellowship, communion, or association;\n   to separate ones self by a solemn act; to draw off; to retire;\n   especially, to withdraw from a political or religious body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seceder', 'n.', 'One who secedes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seceder', 'n.', 'One of a numerous body of Presbyterians in Scotland who\n   seceded from the communion of the Established Church, about the year\n   1733, and formed the Secession Church, so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secerned', 'imp. & p. p.', 'of Secern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secerning', 'p. pr. & vb. n.', 'of Secern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secern', 'v. t.', 'To separate; to distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secern', 'v. t.', 'To secrete; as, mucus secerned in the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secernent', 'a.', 'Secreting; secretory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secernent', 'n.', 'That which promotes secretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secernent', 'n.', 'A vessel in, or by means of, which the process of\n   secretion takes place; a secreting vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secernment', 'n.', 'The act or process of secreting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secess', 'n.', 'Retirement; retreat; secession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secession', 'n.', 'The act of seceding; separation from fellowship or\n   association with others, as in a religious or political organization;\n   withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secession', 'n.', 'The withdrawal of a State from the national Union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secessionism', 'n.', 'The doctrine or policy of secession; the tenets of\n   secession; the tenets of secessionists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secessionist', 'n.', 'One who upholds secession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secessionist', 'n.', 'One who holds to the belief that a State has the\n   right to separate from the Union at its will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seche', 'v. t. & i.', 'To seek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sechium', 'n.', 'The edible fruit of a West Indian plant (Sechium edule) of\n   the Gourd family. It is soft, pear-shaped, and about four inches long,\n   and contains a single large seed. The root of the plant resembles a\n   yam, and is used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seck', 'a.', 'Barren; unprofitable. See Rent seck, under Rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seckel', 'n.', 'A small reddish brown sweet and juicy pear. It originated\n   on a farm near Philadelphia, afterwards owned by a Mr. Seckel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secle', 'n.', 'A century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secluded', 'imp. & p. p.', 'of Seclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secluding', 'p. pr. & vb. n.', 'of Seclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seclude', 'v. t.', 'To shut up apart from others; to withdraw into, or\n   place in, solitude; to separate from society or intercourse with\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seclude', 'v. t.', 'To shut or keep out; to exclude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seclusion', 'n.', 'The act of secluding, or the state of being secluded;\n   separation from society or connection; a withdrawing; privacy; as, to\n   live in seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seclusive', 'a.', 'Tending to seclude; keeping in seclusion; secluding;\n   sequestering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'Immediately following the first; next to the first in order\n   of place or time; hence, occuring again; another; other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'Next to the first in value, power, excellence, dignity, or\n   rank; secondary; subordinate; inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'Being of the same kind as another that has preceded;\n   another, like a protype; as, a second Cato; a second Troy; a second\n   deluge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'n.', 'One who, or that which, follows, or comes after; one next\n   and inferior in place, time, rank, importance, excellence, or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'n.', 'One who follows or attends another for his support and aid;\n   a backer; an assistant; specifically, one who acts as anothers aid in\n   a duel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'n.', 'Aid; assistance; help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'n.', 'An article of merchandise of a grade inferior to the best;\n   esp., a coarse or inferior kind of flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'The sixtieth part of a minute of time or of a minute of\n   space, that is, the second regular subdivision of the degree; as, sound\n   moves about 1,140 English feet in a second; five minutes and ten\n   seconds north of this place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'In the duodecimal system of mensuration, the twelfth part\n   of an inch or prime; a line. See Inch, and Prime, n., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'n.', 'The interval between any tone and the tone which is\n   represented on the degree of the staff next above it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'n.', 'The second part in a concerted piece; -- often popularly\n   applied to the alto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seconded', 'imp. & p. p.', 'of Second');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seconding', 'p. pr. & vb. n.', 'of Second');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'To follow in the next place; to succeed; to alternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'To follow or attend for the purpose of assisting; to\n   support; to back; to act as the second of; to assist; to forward; to\n   encourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second', 'a.', 'Specifically, to support, as a motion or proposal, by\n   adding ones voice to that of the mover or proposer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondarily', 'adv.', 'In a secondary manner or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondarily', 'adv.', 'Secondly; in the second place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondariness', 'n.', 'The state of being secondary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'a.', 'Suceeding next in order to the first; of second place,\n   origin, rank, rank, etc.; not primary; subordinate; not of the first\n   order or rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'a.', 'Acting by deputation or delegated authority; as, the\n   work of secondary hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'a.', 'Possessing some quality, or having been subject to some\n   operation (as substitution), in the second degree; as, a secondary\n   salt, a secondary amine, etc. Cf. primary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'a.', 'Subsequent in origin; -- said of minerals produced by\n   alteertion or deposition subsequent to the formation of the original\n   rocks mass; also of characters of minerals (as secondary cleavage,\n   etc.) developed by pressure or other causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'a.', 'Pertaining to the second joint of the wing of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'a.', 'Dependent or consequent upon another disease; as,\n   Brights disease is often secondary to scarlet fever. (b) Occuring in\n   the second stage of a disease; as, the secondary symptoms of syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondaries', 'pl. ', 'of Secondary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'n.', 'One who occupies a subordinate, inferior, or auxiliary\n   place; a delegate deputy; one who is second or next to the chief\n   officer; as, the secondary, or undersheriff of the city of London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'n.', 'A secondary circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'n.', 'A satellite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondary', 'n.', 'A secondary quill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second-class', 'a.', 'Of the rank or degree below the best highest;\n   inferior; second-rate; as, a second-class house; a second-class\n   passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seconder', 'n.', 'One who seconds or supports what another attempts,\n   affirms, moves, or proposes; as, the seconder of an enterprise or of a\n   motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondhand', 'a.', 'Not original or primary; received from another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondhand', 'a.', 'Not new; already or previously or used by another; as,\n   a secondhand book, garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondly', 'adv.', 'In the second place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secondo', 'n.', 'The second part in a concerted piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second-rate', 'a.', 'Of the second size, rank, quality, or value; as, a\n   second-rate ship; second-rate cloth; a second-rate champion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second-sight', 'n.', 'The power of discerning what is not visible to the\n   physical eye, or of foreseeing future events, esp. such as are of a\n   disastrous kind; the capacity of a seer; prophetic vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Second-sighted', 'a.', 'Having the power of second-sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secre', 'a.', 'Secret; secretive; faithful to a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secre', 'n.', 'A secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrecies', 'pl. ', 'of Secrecy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrecy', 'n.', 'The state or quality of being hidden; as, his movements\n   were detected in spite of their secrecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrecy', 'n.', 'That which is concealed; a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrecy', 'n.', 'Seclusion; privacy; retirement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrecy', 'n.', 'The quality of being secretive; fidelity to a secret;\n   forbearance of disclosure or discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrely', 'adv.', 'Secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secreness', 'n.', 'Secrecy; privacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'a.', 'Hidden; concealed; as, secret treasure; secret plans; a\n   secret vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'a.', 'Withdraw from general intercourse or notice; in retirement\n   or secrecy; secluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'a.', 'Faithful to a secret; not inclined to divulge or betray\n   confidence; secretive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'a.', 'Separate; distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'a.', 'Something studiously concealed; a thing kept from general\n   knowledge; what is not revealed, or not to be revealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'a.', 'A thing not discovered; what is unknown or unexplained; a\n   mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'a.', 'The parts which modesty and propriety require to be\n   concealed; the genital organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secret', 'v. t.', 'To keep secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretage', 'n.', 'A process in which mercury, or some of its salts, is\n   employed to impart the property of felting to certain kinds of furs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretarial', 'a.', 'Of or pertaining to a secretary; befitting a\n   secretary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretariat', 'n.', 'Alt. of Secretariate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretariate', 'n.', 'The office of a secretary; the place where a\n   secretary transacts business, keeps records, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretaries', 'pl. ', 'of Secretary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretary', 'n.', 'One who keeps, or is intrusted with, secrets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretary', 'n.', 'A person employed to write orders, letters, dispatches,\n   public or private papers, records, and the like; an official scribe,\n   amanuensis, or writer; one who attends to correspondence, and transacts\n   other business, for an association, a public body, or an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretary', 'n.', 'An officer of state whose business is to superintend and\n   manage the affairs of a particular department of government, and who is\n   usually a member of the cabinet or advisory council of the chief\n   executive; as, the secretary of state, who conducts the correspondence\n   and attends to the relations of a government with foreign courts; the\n   secretary of the treasury, who manages the department of finance; the\n   secretary of war, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretary', 'n.', 'A piece of furniture, with conveniences for writing and\n   for the arrangement of papers; an escritoire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretary', 'n.', 'The secretary bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretaryship', 'n.', 'The office, or the term of office, of a secretary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secreted', 'imp. & p. p.', 'of Secrete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secreting', 'p. pr. & vb. n.', 'of Secrete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrete', 'v. t.', 'To deposit in a place of hiding; to hide; to conceal;\n   as, to secrete stolen goods; to secrete ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrete', 'v. t.', 'To separate from the blood and elaborate by the process\n   of secretion; to elaborate and emit as a secretion. See Secretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretion', 'n.', 'The act of secreting or concealing; as, the secretion of\n   dutiable goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretion', 'n.', 'The act of secreting; the process by which material is\n   separated from the blood through the agency of the cells of the various\n   glands and elaborated by the cells into new substances so as to form\n   the various secretions, as the saliva, bile, and other digestive\n   fluids. The process varies in the different glands, and hence are\n   formed the various secretions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretion', 'n.', 'Any substance or fluid secreted, or elaborated and\n   emitted, as the gastric juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretist', 'n.', 'A dealer in secrets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretitious', 'a.', 'Parted by animal secretion; as, secretitious humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretive', 'a.', 'Tending to secrete, or to keep secret or private; as, a\n   secretive disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretiveness', 'n.', 'The quality of being secretive; disposition or\n   tendency to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretiveness', 'n.', 'The faculty or propensity which impels to reserve,\n   secrecy, or concealment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretly', 'adv.', 'In a secret manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretness', 'n.', 'The state or quality of being secret, hid, or\n   concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretness', 'n.', 'Secretiveness; concealment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secrete-metory', 'a.', 'Causing secretion; -- said of nerves which go to\n   glands and influence secretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretory', 'a.', 'Secreting; performing, or connected with, the office\n   secretion; secernent; as, secretory vessels, nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secretory', 'n.', 'A secretory vessel; a secernent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sect', 'n.', 'A cutting; a scion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sect', 'n.', 'Those following a particular leader or authority, or attached\n   to a certain opinion; a company or set having a common belief or\n   allegiance distinct from others; in religion, the believers in a\n   particular creed, or upholders of a particular practice; especially, in\n   modern times, a party dissenting from an established church; a\n   denomination; in philosophy, the disciples of a particular master; a\n   school; in society and the state, an order, rank, class, or party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectant', 'n.', 'One of the portions of space bounded by the three\n   coordinate planes. Specif. (Crystallog.), one of the parts of a crystal\n   into which it is divided by the axial planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectarian', 'n.', 'Pertaining to a sect, or to sects; peculiar to a sect;\n   bigotedly attached to the tenets and interests of a denomination; as,\n   sectarian principles or prejudices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectarian', 'n.', 'One of a sect; a member or adherent of a special school,\n   denomination, or religious or philosophical party; one of a party in\n   religion which has separated itself from established church, or which\n   holds tenets different from those of the prevailing denomination in a\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectarianism', 'n.', 'The quality or character of a sectarian; devotion to\n   the interests of a party; excess of partisan or denominational zeal;\n   adherence to a separate church organization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectarianize', 'v. t.', 'To imbue with sectarian feelings; to subject to\n   the control of a sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectarism', 'n.', 'Sectarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectarist', 'n.', 'A sectary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectaries', 'pl. ', 'of Sectary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectary', 'n.', 'A sectarian; a member or adherent of a sect; a follower or\n   disciple of some particular teacher in philosophy or religion; one who\n   separates from an established church; a dissenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectator', 'n.', 'A follower; a disciple; an adherent to a sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectile', 'a.', 'Capable of being cut; specifically (Min.), capable of\n   being severed by the knife with a smooth cut; -- said of minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectility', 'n.', 'The state or quality of being sectile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'The act of cutting, or separation by cutting; as, the\n   section of bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'A part separated from something; a division; a portion; a\n   slice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'A distinct part or portion of a book or writing; a\n   subdivision of a chapter; the division of a law or other writing; a\n   paragraph; an article; hence, the character /, often used to denote\n   such a division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'A distinct part of a country or people, community, class,\n   or the like; a part of a territory separated by geographical lines, or\n   of a people considered as distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'One of the portions, of one square mile each, into which\n   the public lands of the United States are divided; one thirty-sixth\n   part of a township. These sections are subdivided into quarter sections\n   for sale under the homestead and preemption laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'The figure made up of all the points common to a\n   superficies and a solid which meet, or to two superficies which meet,\n   or to two lines which meet. In the first case the section is a\n   superficies, in the second a line, and in the third a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'A division of a genus; a group of species separated by\n   some distinction from others of the same genus; -- often indicated by\n   the sign /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'A part of a musical period, composed of one or more\n   phrases. See Phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Section', 'n.', 'The description or representation of anything as it would\n   appear if cut through by any intersecting plane; depiction of what is\n   beyond a plane passing through, or supposed to pass through, an object,\n   as a building, a machine, a succession of strata; profile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectional', 'a.', 'Of or pertaining to a sections or distinct part of\n   larger body or territory; local.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectional', 'a.', 'Consisting of sections, or capable of being divided into\n   sections; as, a sectional steam boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectionalism', 'n.', 'A disproportionate regard for the interests peculiar\n   to a section of the country; local patriotism, as distinguished from\n   national.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectionality', 'n.', 'The state or quality of being sectional;\n   sectionalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectionalize', 'v. t.', 'To divide according to gepgraphical sections or\n   local interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectionally', 'adv.', 'In a sectional manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectionize', 'v. t.', 'To form into sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectism', 'n.', 'Devotion to a sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectist', 'n.', 'One devoted to a sect; a soetary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectiuncle', 'n.', 'A little or petty sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sector', 'n.', 'A part of a circle comprehended between two radii and the\n   included arc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sector', 'n.', 'A mathematical instrument, consisting of two rulers\n   connected at one end by a joint, each arm marked with several scales,\n   as of equal parts, chords, sines, tangents, etc., one scale of each\n   kind on each arm, and all on lines radiating from the common center of\n   motion. The sector is used for plotting, etc., to any scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sector', 'n.', 'An astronomical instrument, the limb of which embraces a\n   small portion only of a circle, used for measuring differences of\n   declination too great for the compass of a micrometer. When it is used\n   for measuring zenith distances of stars, it is called a zenith sector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectoral', 'a.', 'Of or pertaining to a sector; as, a sectoral circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectorial', 'a.', 'Adapted for cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sectorial', 'n.', 'A sectorial, or carnassial, tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'a.', 'Coming or observed once in an age or a century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'a.', 'Pertaining to an age, or the progress of ages, or to a\n   long period of time; accomplished in a long progress of time; as,\n   secular inequality; the secular refrigeration of the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'a.', 'Of or pertaining to this present world, or to things not\n   spiritual or holy; relating to temporal as distinguished from eternal\n   interests; not immediately or primarily respecting the soul, but the\n   body; worldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'a.', 'Not regular; not bound by monastic vows or rules; not\n   confined to a monastery, or subject to the rules of a religious\n   community; as, a secular priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'a.', 'Belonging to the laity; lay; not clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'n.', 'A secular ecclesiastic, or one not bound by monastic\n   rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'n.', 'A church official whose functions are confined to the\n   vocal department of the choir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secular', 'n.', 'A layman, as distinguished from a clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularism', 'n.', 'The state or quality of being secular; a secular\n   spirit; secularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularism', 'n.', 'The tenets or principles of the secularists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularist', 'n.', 'One who theoretically rejects every form of religious\n   faith, and every kind of religious worship, and accepts only the facts\n   and influences which are derived from the present life; also, one who\n   believes that education and other matters of civil policy should be\n   managed without the introduction of a religious element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularity', 'n.', 'Supreme attention to the things of the present life;\n   worldliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularization', 'n.', 'The act of rendering secular, or the state of being\n   rendered secular; conversion from regular or monastic to secular;\n   conversion from religious to lay or secular possession and uses; as,\n   the secularization of church property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularized', 'imp. & p. p.', 'of Secularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularizing', 'p. pr. & vb. n.', 'of Secularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularize', 'v. t.', 'To convert from regular or monastic into secular;\n   as, to secularize a priest or a monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularize', 'v. t.', 'To convert from spiritual or common use; as, to\n   secularize a church, or church property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularize', 'v. t.', 'To make worldly or unspiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularly', 'adv.', 'In a secular or worldly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secularness', 'n.', 'The quality or state of being secular; worldliness;\n   worldly-minded-ness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secund', 'a.', 'Arranged on one side only, as flowers or leaves on a stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secundate', 'v. t.', 'To make prosperous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secundation', 'n.', 'Prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secundine', 'n.', 'The second coat, or integument, of an ovule, lying\n   within the primine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secundine', 'n.', 'The afterbirth, or placenta and membranes; -- generally\n   used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secundo-geniture', 'n.', 'A right of inheritance belonging to a second son;\n   a property or possession so inherited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securable', 'a.', 'That may be secured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'a.', 'Free from fear, care, or anxiety; easy in mind; not feeling\n   suspicion or distrust; confident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'a.', 'Overconfident; incautious; careless; -- in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'a.', 'Confident in opinion; not entertaining, or not having\n   reason to entertain, doubt; certain; sure; -- commonly with of; as,\n   secure of a welcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'a.', 'Net exposed to danger; safe; -- applied to persons and\n   things, and followed by against or from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secured', 'imp. & p. p.', 'of Secure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securing', 'p. pr. & vb. n.', 'of Secure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'v. t.', 'To make safe; to relieve from apprehensions of, or\n   exposure to, danger; to guard; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'v. t.', 'To put beyond hazard of losing or of not receiving; to\n   make certain; to assure; to insure; -- frequently with against or from,\n   rarely with of; as, to secure a creditor against loss; to secure a debt\n   by a mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'v. t.', 'To make fast; to close or confine effectually; to render\n   incapable of getting loose or escaping; as, to secure a prisoner; to\n   secure a door, or the hatches of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secure', 'v. t.', 'To get possession of; to make ones self secure of; to\n   acquire certainly; as, to secure an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securely', 'adv.', 'In a secure manner; without fear or apprehension;\n   without danger; safely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securement', 'n.', 'The act of securing; protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Secureness', 'n.', 'The condition or quality of being secure; exemption\n   from fear; want of vigilance; security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securer', 'n.', 'One who, or that which, secures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securifera', 'n. pl.', 'The Serrifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securiform', 'a.', 'Having the form of an ax hatchet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securipalp', 'n.', 'One of a family of beetles having the maxillary palpi\n   terminating in a hatchet-shaped joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Securities', 'pl. ', 'of Security');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'The condition or quality of being secure; secureness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'Freedom from apprehension, anxiety, or care; confidence\n   of power of safety; hence, assurance; certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'Hence, carelessness; negligence; heedlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'Freedom from risk; safety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'That which secures or makes safe; protection; guard;\n   defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'Something given, deposited, or pledged, to make certain\n   the fulfillment of an obligation, the performance of a contract, the\n   payment of a debt, or the like; surety; pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'One who becomes surety for another, or engages himself\n   for the performance of anothers obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Security', 'n.', 'An evidence of debt or of property, as a bond, a\n   certificate of stock, etc.; as, government securities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedan', 'n.', 'A portable chair or covered vehicle for carrying a single\n   person, -- usually borne on poles by two men. Called also sedan chair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedate', 'a.', 'Undisturbed by passion or caprice; calm; tranquil; serene;\n   not passionate or giddy; composed; staid; as, a sedate soul, mind, or\n   temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedation', 'n.', 'The act of calming, or the state of being calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedative', 'a.', 'Tending to calm, moderate, or tranquilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedative', 'a.', 'allaying irritability and irritation; assuaging pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedative', 'n.', 'A remedy which allays irritability and irritation, and\n   irritative activity or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedent', 'a.', 'Sitting; inactive; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedentarily', 'adv.', 'In a sedentary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedentariness', 'n.', 'Quality of being sedentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedentary', 'a.', 'Accustomed to sit much or long; as, a sedentary man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedentary', 'a.', 'Characterized by, or requiring, much sitting; as, a\n   sedentary employment; a sedentary life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedentary', 'a.', 'Inactive; motionless; sluggish; hence, calm; tranquil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedentary', 'a.', 'Caused by long sitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedentary', 'a.', 'Remaining in one place, especially when firmly attached\n   to some object; as, the oyster is a sedentary mollusk; the barnacles\n   are sedentary crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sederunt', 'n.', 'A sitting, as of a court or other body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedge', 'n.', 'Any plant of the genus Carex, perennial, endogenous herbs,\n   often growing in dense tufts in marshy places. They have triangular\n   jointless stems, a spiked inflorescence, and long grasslike leaves\n   which are usually rough on the margins and midrib. There are several\n   hundred species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedge', 'n.', 'A flock of herons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedged', 'a.', 'Made or composed of sedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedgy', 'a.', 'Overgrown with sedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedilia', 'n. pl.', 'Seats in the chancel of a church near the altar for\n   the officiating clergy during intervals of service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sediment', 'n.', 'The matter which subsides to the bottom, frrom water or\n   any other liquid; settlings; lees; dregs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sediment', 'n.', 'The material of which sedimentary rocks are formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedimental', 'a.', 'Sedimentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedimentary', 'a.', 'Of or pertaining to sediment; formed by sediment;\n   containing matter that has subsided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedimentation', 'n.', 'The act of depositing a sediment; specifically\n   (Geol.), the deposition of the material of which sedimentary rocks are\n   formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedition', 'n.', 'The raising of commotion in a state, not amounting to\n   insurrection; conduct tending to treason, but without an overt act;\n   excitement of discontent against the government, or of resistance to\n   lawful authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedition', 'n.', 'Dissension; division; schism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seditionary', 'n.', 'An inciter or promoter of sedition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seditious', 'a.', 'Of or pertaining to sedition; partaking of the nature\n   of, or tending to excite, sedition; as, seditious behavior; seditious\n   strife; seditious words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seditious', 'a.', 'Disposed to arouse, or take part in, violent opposition\n   to lawful authority; turbulent; factious; guilty of sedition; as,\n   seditious citizens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedlitz', 'a.', 'Same as Seidlitz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seduced', 'imp. & p. p.', 'of Seduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seducing', 'p. pr. & vb. n.', 'of Seduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seduce', 'v. t.', 'To draw aside from the path of rectitude and duty in any\n   manner; to entice to evil; to lead astray; to tempt and lead to\n   iniquity; to corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seduce', 'v. t.', 'Specifically, to induce to surrender chastity; to\n   debauch by means of solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seducement', 'n.', 'The act of seducing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seducement', 'n.', 'The means employed to seduce, as flattery, promises,\n   deception, etc.; arts of enticing or corrupting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seducer', 'n.', 'One who, or that which, seduces; specifically, one who\n   prevails over the chastity of a woman by enticements and persuasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seducible', 'a.', 'Capable of being seduced; corruptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seducing', 'a.', 'Seductive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seduction', 'n.', 'The act of seducing; enticement to wrong doing;\n   specifically, the offense of inducing a woman to consent to unlawful\n   sexual intercourse, by enticements which overcome her scruples; the\n   wrong or crime of persuading a woman to surrender her chastity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seduction', 'n.', 'That which seduces, or is adapted to seduce; means of\n   leading astray; as, the seductions of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seductive', 'a.', 'Tending to lead astray; apt to mislead by flattering\n   appearances; tempting; alluring; as, a seductive offer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seductively', 'adv.', 'In a seductive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seductress', 'n.', 'A woman who seduces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedulity', 'n.', 'The quality or state of being sedulous; diligent and\n   assiduous application; constant attention; unremitting industry;\n   sedulousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedulous', 'a.', 'Diligent in application or pursuit; constant, steady, and\n   persevering in business, or in endeavors to effect an object; steadily\n   industrious; assiduous; as, the sedulous bee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sedum', 'n.', 'A genus of plants, mostly perennial, having succulent leaves\n   and cymose flowers; orpine; stonecrop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'n.', 'A seat; a site; a place where sovereign power is exercised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'n.', 'Specifically: (a) The seat of episcopal power; a diocese; the\n   jurisdiction of a bishop; as, the see of New York. (b) The seat of an\n   archibishop; a province or jurisdiction of an archibishop; as, an\n   archiepiscopal see. (c) The seat, place, or office of the pope, or\n   Roman pontiff; as, the papal see. (d) The pope or his court at Rome;\n   as, to appeal to the see of Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Saw', 'imp.', 'of See');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seen', 'p. p.', 'of See');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeing', 'p. pr. & vb. n.', 'of See');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. t.', 'To perceive by the eye; to have knowledge of the existence\n   and apparent qualities of by the organs of sight; to behold; to descry;\n   to view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. t.', 'To perceive by mental vision; to form an idea or conception\n   of; to note with the mind; to observe; to discern; to distinguish; to\n   understand; to comprehend; to ascertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. t.', 'To follow with the eyes, or as with the eyes; to watch; to\n   regard attentivelly; to look after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. t.', 'To have an interview with; especially, to make a call upon;\n   to visit; as, to go to see a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. t.', 'To fall in with; to have intercourse or communication with;\n   hence, to have knowledge or experience of; as, to see military service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. t.', 'To accompany in person; to escort; to wait upon; as, to see\n   one home; to see one aboard the cars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. i.', 'To have the power of sight, or of perceiving by the proper\n   organs; to possess or employ the sense of vision; as, he sees\n   distinctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. i.', 'Figuratively: To have intellectual apprehension; to\n   perceive; to know; to understand; to discern; -- often followed by a\n   preposition, as through, or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'See', 'v. i.', 'To be attentive; to take care; to give heed; -- generally\n   with to; as, to see to the house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'pl. ', 'of Seed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeds', 'pl. ', 'of Seed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'n.', 'A ripened ovule, consisting of an embryo with one or more\n   integuments, or coverings; as, an apple seed; a currant seed. By\n   germination it produces a new plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'n.', 'Any small seedlike fruit, though it may consist of a\n   pericarp, or even a calyx, as well as the seed proper; as, parsnip\n   seed; thistle seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'n.', 'The generative fluid of the male; semen; sperm; -- not used\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'n.', 'That from which anything springs; first principle; original;\n   source; as, the seeds of virtue or vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'n.', 'The principle of production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'n.', 'Progeny; offspring; children; descendants; as, the seed of\n   Abraham; the seed of David.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'n.', 'Race; generation; birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeded', 'imp. & p. p.', 'of Seed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeding', 'p. pr. & vb. n.', 'of Seed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'v. t.', 'To sprinkle with seed; to plant seeds in; to sow; as, to\n   seed a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed', 'v. t.', 'To cover thinly with something scattered; to ornament with\n   seedlike decorations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedbox', 'n.', 'A capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedbox', 'n.', 'A plant (Ludwigia alternifolia) which has somewhat cubical\n   or box-shaped capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedcake', 'n.', 'A sweet cake or cooky containing aromatic seeds, as\n   caraway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedcod', 'n.', 'A seedlip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeder', 'n.', 'One who, or that which, sows or plants seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seediness', 'n.', 'The quality or state of being seedy, shabby, or worn\n   out; a state of wretchedness or exhaustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seed-lac', 'n.', 'A species of lac. See the Note under Lac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedless', 'a.', 'Without seed or seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedling', 'n.', 'A plant reared from the seed, as distinguished from one\n   propagated by layers, buds, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedlip', 'n.', 'Alt. of Seedlop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedlop', 'n.', 'A vessel in which a sower carries the seed to be\n   scattered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedman', 'See', 'Seedsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedness', 'n.', 'Seedtime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedsmen', 'pl. ', 'of Seedsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedsman', 'n.', 'A sower; one who sows or scatters seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedsman', 'n.', 'A person who deals in seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedtime', 'n.', 'The season proper for sowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedy', 'superl.', 'Abounding with seeds; bearing seeds; having run to\n   seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedy', 'superl.', 'Having a peculiar flavor supposed to be derived from\n   the weeds growing among the vines; -- said of certain kinds of French\n   brandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seedy', 'superl.', 'Old and worn out; exhausted; spiritless; also, poor and\n   miserable looking; shabbily clothed; shabby looking; as, he looked\n   seedy coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeing', 'conj. ', 'but originally a present participle)) In view of the\n   fact (that); considering; taking into account (that); insmuch as;\n   since; because; -- followed by a dependent clause; as, he did well,\n   seeing that he was so young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek', 'a.', 'Sick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sought', 'imp. & p. p.', 'of Seek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeking', 'p. pr. & vb. n.', 'of Seek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek', 'v. t.', 'To go in search of; to look for; to search for; to try to\n   find.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek', 'v. t.', 'To inquire for; to ask for; to solicit; to bessech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek', 'v. t.', 'To try to acquire or gain; to strive after; to aim at; as,\n   to seek wealth or fame; to seek ones life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek', 'v. t.', 'To try to reach or come to; to go to; to resort to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek', 'v. i.', 'To make search or inquiry: to endeavor to make discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeker', 'n.', 'One who seeks; that which is used in seeking or searching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeker', 'n.', 'One of a small heterogeneous sect of the 17th century, in\n   Great Britain, who professed to be seeking the true church, ministry,\n   and sacraments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek-no-further', 'n.', 'A kind of choice winter apple, having a subacid\n   taste; -- formerly called go-no-further.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seek-sorrow', 'n.', 'One who contrives to give himself vexation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeled', 'imp. & p. p.', 'of Seel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeling', 'p. pr. & vb. n.', 'of Seel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seel', 'v. t.', 'To close the eyes of (a hawk or other bird) by drawing\n   through the lids threads which were fastened over the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seel', 'v. t.', 'Hence, to shut or close, as the eyes; to blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seel', 'v. i.', 'To incline to one side; to lean; to roll, as a ship at\n   sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seel', 'n.', 'Alt. of Seeling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeling', 'n.', 'The rolling or agitation of a ship in a storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seel', 'n.', 'Good fortune; favorable opportunity; prosperity. [Obs.] \"So\n   have I seel\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seel', 'n.', 'Time; season; as, hay seel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seelily', 'adv.', 'In a silly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seely', 'a.', 'See Silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemed', 'imp. & p. p.', 'of Seem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeming', 'p. pr. & vb. n.', 'of Seem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seem', 'a.', 'To appear, or to appear to be; to have a show or semblance;\n   to present an appearance; to look; to strike ones apprehension or\n   fancy as being; to be taken as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seem', 'v. t.', 'To befit; to beseem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemer', 'n.', 'One who seems; one who carries or assumes an appearance or\n   semblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeming', 'a.', 'Having a semblance, whether with or without reality;\n   apparent; specious; befitting; as, seeming friendship; seeming truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeming', 'n.', 'Appearance; show; semblance; fair appearance;\n   speciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeming', 'n.', 'Apprehension; judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemingly', 'adv.', 'In appearance; in show; in semblance; apparently;\n   ostensibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemingness', 'n.', 'Semblance; fair appearance; plausibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemless', 'a.', 'Unseemly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemlily', 'adv.', 'In a seemly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemliness', 'n.', 'The quality or state of being seemly: comeliness;\n   propriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemly', 'v. i.', 'Suited to the object, occasion, purpose, or character;\n   suitable; fit; becoming; comely; decorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemly', 'superl.', 'In a decent or suitable manner; becomingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seemlyhed', 'n.', 'Comely or decent appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seen', '', 'p. p. of See.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seen', 'a.', 'Versed; skilled; accomplished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seep', 'v. i.', 'Alt. of Sipe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipe', 'v. i.', 'To run or soak through fine pores and interstices; to\n   ooze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seepage', 'n.', 'Alt. of Sipage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipage', 'n.', 'Water that seeped or oozed through a porous soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seepy', 'a.', 'Alt. of Sipy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipy', 'a.', 'Oozy; -- applied to land under cultivation that is not well\n   drained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seer', 'a.', 'Sore; painful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seer', 'n.', 'One who sees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seer', 'n.', 'A person who foresees events; a prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeress', 'n.', 'A female seer; a prophetess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seerfish', 'n.', 'A scombroid food fish of Madeira (Cybium Commersonii).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seerhand', 'n.', 'A kind of muslin of a texture between nainsook and mull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seership', 'n.', 'The office or quality of a seer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seersucker', 'n.', 'A light fabric, originally made in the East Indies, of\n   silk and linen, usually having alternating stripes, and a slightly\n   craped or puckered surface; also, a cotton fabric of similar\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seerwood', 'n.', 'Dry wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesaw', 'n.', 'A play among children in which they are seated upon the\n   opposite ends of a plank which is balanced in the middle, and move\n   alternately up and down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesaw', 'n.', 'A plank or board adjusted for this play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesaw', 'n.', 'A vibratory or reciprocating motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesaw', 'n.', 'Same as Crossruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesawad', 'imp. & p. p.', 'of Seesaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesawing', 'p. pr. & vb. n.', 'of Seesaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesaw', 'v. i.', 'To move with a reciprocating motion; to move backward\n   and forward, or upward and downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesaw', 'v. t.', 'To cause to move backward and forward in seesaw fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seesaw', 'a.', 'Moving up and down, or to and fro; having a reciprocating\n   motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seet', 'imp.', 'Sate; sat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seeth', '', 'imp. of Seethe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seethed', 'imp.', 'of Seethe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sod', '', 'of Seethe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seethed', 'p. p.', 'of Seethe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodden', '', 'of Seethe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seething', 'p. pr. & vb. n.', 'of Seethe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seethe', 'n.', 'To decoct or prepare for food in hot liquid; to boil; as,\n   to seethe flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seethe', 'v. i.', 'To be a state of ebullition or violent commotion; to be\n   hot; to boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seether', 'n.', 'A pot for boiling things; a boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seg', 'n.', 'Sedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seg', 'n.', 'The gladen, and other species of Iris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seg', 'n.', 'A castrated bull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segar', 'n.', 'See Cigar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seggar', 'n.', 'A case or holder made of fire clay, in which fine pottery\n   is inclosed while baking in the kin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segge', 'n.', 'The hedge sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segment', 'n.', 'One of the parts into which any body naturally separates\n   or is divided; a part divided or cut off; a section; a portion; as, a\n   segment of an orange; a segment of a compound or divided leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segment', 'n.', 'A part cut off from a figure by a line or plane;\n   especially, that part of a circle contained between a chord and an arc\n   of that circle, or so much of the circle as is cut off by the chord;\n   as, the segment acb in the Illustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segment', 'n.', 'A piece in the form of the sector of a circle, or part of\n   a ring; as, the segment of a sectional fly wheel or flywheel rim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segment', 'n.', 'A segment gear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segment', 'n.', 'One of the cells or division formed by segmentation, as in\n   egg cleavage or in fissiparous cell formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segment', 'n.', 'One of the divisions, rings, or joints into which many\n   animal bodies are divided; a somite; a metamere; a somatome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segment', 'v. i.', 'To divide or separate into parts in growth; to undergo\n   segmentation, or cleavage, as in the segmentation of the ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segmental', 'a.', 'Relating to, or being, a segment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segmental', 'a.', 'Of or pertaining to the segments of animals; as, a\n   segmental duct; segmental papillae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segmental', 'a.', 'Of or pertaining to the segmental organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segmentation', 'n.', 'The act or process of dividing into segments;\n   specifically (Biol.), a self-division into segments as a result of\n   growth; cell cleavage; cell multiplication; endogenous cell formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segmented', 'a.', 'Divided into segments or joints; articulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segnitude', 'n.', 'Alt. of Segnity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segnity', 'n.', 'Sluggishness; dullness; inactivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segno', 'n.', 'A sign. See Al segno, and Dal segno.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sego', 'n.', 'A liliaceous plant (Calochortus Nuttallii) of Western North\n   America, and its edible bulb; -- so called by the Ute Indians and the\n   Mormons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregate', 'a.', 'Separate; select.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregate', 'a.', 'Separated from others of the same kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregated', 'imp. & p. p.', 'of Segregate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregating', 'p. pr. & vb. n.', 'of Segregate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregate', 'v. t.', 'To separate from others; to set apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregate', 'v. i.', 'To separate from a mass, and collect together about\n   centers or along lines of fracture, as in the process of\n   crystallization or solidification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregation', 'n.', 'The act of segregating, or the state of being\n   segregated; separation from others; a parting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Segregation', 'n.', 'Separation from a mass, and gathering about centers or\n   into cavities at hand through cohesive attraction or the crystallizing\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seiches', 'n. pl.', 'Local oscillations in level observed in the case of\n   some lakes, as Lake Geneva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seid', 'n.', 'A descendant of Mohammed through his daughter Fatima and\n   nephew Ali.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seidlitz', 'a.', 'Of or pertaining to Seidlitz, a village in Bohemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigh', '', 'obs. imp. sing. of See. Saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigneurial', 'a.', 'Of or pertaining to the lord of a manor; manorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigneurial', 'a.', 'Vested with large powers; independent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seignior', 'n.', 'A lord; the lord of a manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seignior', 'n.', 'A title of honor or of address in the South of Europe,\n   corresponding to Sir or Mr. in English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigniorage', 'n.', 'Something claimed or taken by virtue of sovereign\n   prerogative; specifically, a charge or toll deducted from bullion\n   brought to a mint to be coined; the difference between the cost of a\n   mass of bullion and the value as money of the pieces coined from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigniorage', 'n.', 'A share of the receipts of a business taken in payment\n   for the use of a right, as a copyright or a patent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seignioral', 'a.', 'Of or pertaining to a seignior; seigneurial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seignioralty', 'n.', 'The territory or authority of a seignior, or lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigniorial', 'a.', 'Same as Seigneurial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigniorize', 'v. t.', 'To lord it over.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ies', 'pl. ', 'of Seigniory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigniory', 'n.', 'The power or authority of a lord; dominion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seigniory', 'n.', 'The territory over which a lord holds jurisdiction; a\n   manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seine', 'n.', 'A large net, one edge of which is provided with sinkers, and\n   the other with floats. It hangs vertically in the water, and when its\n   ends are brought together or drawn ashore incloses the fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seiner', 'n.', 'One who fishes with a seine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seining', 'n.', 'Fishing with a seine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seint', 'n.', 'A girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seint', 'n.', 'A saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seintuary', 'n.', 'Sanctuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seirfish', 'n.', 'Same as Seerfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seirospore', 'n.', 'One of several spores arranged in a chain as in certain\n   algae of the genus Callithamnion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seise', 'v. t.', 'See Seize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seisin', 'n.', 'See Seizin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismic', 'a.', 'Alt. of Seismal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismal', 'a.', 'Of or pertaining to an earthquake; caused by an\n   earthquake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismograph', 'n.', 'An apparatus for registering the shocks and undulatory\n   motions of earthquakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismographic', 'a.', 'Of or pertaining to a seismograph; indicated by a\n   seismograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismography', 'n.', 'A writing about, or a description of, earthquakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismography', 'n.', 'The art of registering the shocks and undulatory\n   movements of earthquakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismological', 'a.', 'Of or pertaining to seismology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismology', 'n.', 'The science of earthquakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismometer', 'n.', 'An instrument for measuring the direction, duration,\n   and force of earthquakes and like concussions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismometric', 'a.', 'Of or pertaining to seismometry, or seismometer; as,\n   seismometric instruments; seismometric measurements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismometry', 'n.', 'The mensuration of such phenomena of earthquakes as\n   can be expressed in numbers, or by their relation to the coordinates of\n   space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seismoscope', 'n.', 'A seismometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seity', 'n.', 'Something peculiar to ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizable', 'a.', 'That may be seized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seized', 'imp. & p. p.', 'of Seize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizing', 'p. pr. & vb. n.', 'of Seize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seize', 'v. t.', 'To fall or rush upon suddenly and lay hold of; to gripe\n   or grasp suddenly; to reach and grasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seize', 'v. t.', 'To take possession of by force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seize', 'v. t.', 'To invade suddenly; to take sudden hold of; to come upon\n   suddenly; as, a fever seizes a patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seize', 'v. t.', 'To take possession of by virtue of a warrant or other\n   legal authority; as, the sheriff seized the debtors goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seize', 'v. t.', 'To fasten; to fix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seize', 'v. t.', 'To grap with the mind; to comprehend fully and\n   distinctly; as, to seize an idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seize', 'v. t.', 'To bind or fasten together with a lashing of small stuff,\n   as yarn or marline; as, to seize ropes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizer', 'n.', 'One who, or that which, seizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizin', 'n.', 'Possession; possession of an estate of froehold. It may be\n   either in deed or in law; the former when there is actual possession,\n   the latter when there is a right to such possession by construction of\n   law. In some of the United States seizin means merely ownership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizin', 'n.', 'The act of taking possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizin', 'n.', 'The thing possessed; property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizing', 'n.', 'The act of taking or grasping suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizing', 'n.', 'The operation of fastening together or lashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizing', 'n.', 'The cord or lashing used for such fastening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizor', 'n.', 'One who seizes, or takes possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizure', 'n.', 'The act of seizing, or the state of being seized; sudden\n   and violent grasp or gripe; a taking into possession; as, the seizure\n   of a thief, a property, a throne, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizure', 'n.', 'Retention within ones grasp or power; hold; possession;\n   ownership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seizure', 'n.', 'That which is seized, or taken possession of; a thing laid\n   hold of, or possessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sejant', 'a.', 'Alt. of Sejeant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sejeant', 'a.', 'Sitting, as a lion or other beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sejein', 'v. t.', 'To separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sejunction', 'n.', 'The act of disjoining, or the state of being disjoined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sejungible', 'a.', 'Capable of being disjoined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seke', 'a.', 'Sick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seke', 'v. t. & i.', 'To seek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sekes', 'n.', 'A place in a pagan temple in which the images of the deities\n   were inclosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selachian', 'n.', 'One of the Selachii. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selachii', 'n. pl.', 'An order of elasmobranchs including the sharks and\n   rays; the Plagiostomi. Called also Selacha, Selache, and Selachoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selachoidei', 'n. pl.', 'Same as Selachii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selachostomi', 'n. pl.', 'A division of ganoid fishes which includes the\n   paddlefish, in which the mouth is armed with small teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selaginella', 'n.', 'A genus of cryptogamous plants resembling Lycopodia,\n   but producing two kinds of spores; also, any plant of this genus. Many\n   species are cultivated in conservatories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selah', 'n.', 'A word of doubtful meaning, occuring frequently in the\n   Psalms; by some, supposed to signify silence or a pause in the musical\n   performance of the song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selcouth', 'n.', 'Rarely known; unusual; strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seld', 'a.', 'Rare; uncommon; unusual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seld', 'adv.', 'Rarely; seldom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selden', 'adv.', 'Seldom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seldem', 'superl', 'Rarely; not often; not frequently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seldom', 'a.', 'Rare; infrequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seldomness', 'n.', 'Rareness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seldseen', 'a.', 'Seldom seen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seldshewn', 'a.', 'Rarely shown or exhibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Select', 'a.', 'Taken from a number by preferance; picked out as more\n   valuable or exellent than others; of special value or exellence; nicely\n   chosen; selected; choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selected', 'imp. & p. p.', 'of Select');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selecting', 'p. pr. & vb. n.', 'of Select');
  INSERT INTO words (word, word_type, description) VALUES  (    'Select', 'v. t.', 'To choose and take from a number; to take by preference\n   from among others; to pick out; to cull; as, to select the best authors\n   for perusal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selectedly', 'adv.', 'With care and selection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selection', 'n.', 'The act of selecting, or the state of being selected;\n   choice, by preference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selection', 'n.', 'That which is selected; a collection of things chosen;\n   as, a choice selection of books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selective', 'a.', 'Selecting; tending to select.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selectmen', 'pl. ', 'of Selectman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selectman', 'n.', 'One of a board of town officers chosen annually in the\n   New England States to transact the general public business of the town,\n   and have a kind of executive authority. The number is usually from\n   three to seven in each town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selectness', 'n.', 'The quality or state of being select.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selector', 'n.', 'One who selects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenate', 'n.', 'A salt of selenic acid; -- formerly called also\n   seleniate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenhydric', 'a.', 'Of, pertaining to, or designating, hydrogen selenide,\n   H2Se, regarded as an acid analogous to sulphydric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenic', 'a.', 'Of or pertaining to selenium; derived from, or containing,\n   selenium; specifically, designating those compounds in which the\n   element has a higher valence as contrasted with selenious compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenide', 'n.', 'A binary compound of selenium, or a compound regarded as\n   binary; as, ethyl selenide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seleniferous', 'a.', 'Containing, or impregnated with, selenium; as,\n   seleniferous pyrites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenio-', '', 'A combining form (also used adjectively) denoting the\n   presence of selenium or its compounds; as, selenio-phosphate, a\n   phosphate having selenium in place of all, or a part, of the oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenious', 'a.', 'Of, pertaining to, or containing, selenium;\n   specifically, designating those compounds in which the element has a\n   lower valence as contrasted with selenic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenite', 'n.', 'A salt of selenious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenite', 'n.', 'A variety of gypsum, occuring in transparent crystals or\n   crystalline masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenitic', 'a.', 'Alt. of Selenitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenitical', 'a.', 'Of or pertaining to selenite; resembling or containing\n   selenite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenium', 'n.', 'A nonmetallic element of the sulphur group, and analogous\n   to sulphur in its compounds. It is found in small quantities with\n   sulphur and some sulphur ores, and obtained in the free state as a dark\n   reddish powder or crystalline mass, or as a dark metallic-looking\n   substance. It exhibits under the action of light a remarkable variation\n   in electric conductivity, and is used in certain electric apparatus.\n   Symbol Se. Atomic weight 78.9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seleniuret', 'n.', 'A selenide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seleniureted', 'a.', 'Combined with selenium as in a selenide; as,\n   seleniureted hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenecentric', 'a.', 'As seen or estimated from the center of the moon;\n   with the moon central.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenograph', 'n.', 'A picture or delineation of the moons surface, or of\n   any part of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenographer', 'n.', 'One skilled in selenography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenographic', 'a.', 'Alt. of Selenographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenographical', 'a.', 'Of or pertaining to selenography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenographist', 'n.', 'A selenographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenography', 'n.', 'The science that treats of the physical features of\n   the moon; -- corresponding to physical geography in respect to the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenonium', 'n.', 'A hypothetical radical of selenium, analogous to\n   sulphonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selenology', 'n.', 'That branch of astronomy which treats of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self', 'a.', 'Same; particular; very; identical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selves', 'pl. ', 'of Self');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self', 'n.', 'The individual as the object of his own reflective\n   consciousness; the man viewed by his own cognition as the subject of\n   all his mental phenomena, the agent in his own activities, the subject\n   of his own feelings, and the possessor of capacities and character; a\n   person as a distinct individual; a being regarded as having\n   personality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self', 'n.', 'Hence, personal interest, or love of private interest;\n   selfishness; as, self is his whole aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self', 'n.', 'Personification; embodiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abased', 'a.', 'Humbled by consciousness of inferiority, unworthiness,\n   guilt, or shame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abasement', 'n.', 'Degradation of ones self by ones own act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abasement', 'n.', 'Humiliation or abasement proceeding from\n   consciousness of inferiority, guilt, or shame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abasing', 'a.', 'Lowering or humbling ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abhorrence', 'n.', 'Abhorrence of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abnegation', 'n.', 'Self-denial; self-renunciation; self-sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abuse', 'n.', 'The abuse of ones own self, powers, or faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abuse', 'n.', 'Self-deception; delusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-abuse', 'n.', 'Masturbation; onanism; self-pollution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-accused', 'a.', 'Accused by ones self or by ones conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-acting', 'a.', 'Acting of or by ones self or by itself; -- said\n   especially of a machine or mechanism which is made to perform of or for\n   itself what is usually done by human agency; automatic; as, a\n   self-acting feed apparatus; a self-acting mule; a self-acting press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-action', 'n.', 'Action by, or originating in, ones self or itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-active', 'a.', 'Acting of ones self or of itself; acting without\n   depending on other agents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-activity', 'n.', 'The quality or state of being self-active;\n   self-action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-adjusting', 'a.', 'Capable of assuming a desired position or condition\n   with relation to other parts, under varying circumstances, without\n   requiring to be adjusted by hand; -- said of a piece in machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-admiration', 'n.', 'Admiration of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-affairs', 'n. pl.', 'Ones own affairs; ones private business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-affrighted', 'a.', 'Frightened at or by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-aggrandizement', 'n.', 'The aggrandizement of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-annihilated', 'a.', 'Annihilated by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-annihilation', 'n.', 'Annihilation by ones own acts; annihilation of\n   ones desires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-applause', 'n.', 'Applause of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-applying', 'a.', 'Applying to or by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-approving', 'a.', 'Approving ones own action or character by ones\n   own judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-asserting', 'a.', 'asserting ones self, or ones own rights or\n   claims; hence, putting ones self forward in a confident or assuming\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-assertion', 'n.', 'The act of asserting ones self, or ones own\n   rights or claims; the quality of being self-asserting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-assertive', 'a.', 'Disposed to self-assertion; self-asserting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-assumed', 'a.', 'Assumed by ones own act, or without authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-assured', 'a.', 'Assured by or of ones self; self-reliant;\n   complacent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-banished', 'a.', 'Exiled voluntarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-begetten', 'a.', 'Begotten by ones self, or ones own powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-bern', 'a.', 'Born or produced by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-centered', 'a.', 'Alt. of Self-centred');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-centred', 'a.', 'Centered in itself, or in ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-centering', 'a.', 'Alt. of Self-centring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-centring', 'a.', 'Centering in ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-centration', 'n.', 'The quality or state of being self-centered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-charity', 'n.', 'Self-love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-color', 'n.', 'A color not mixed or variegated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-celored', 'a.', 'Being of a single color; -- applied to flowers,\n   animals, and textile fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-command', 'n.', 'Control over ones own feelings, temper, etc.;\n   self-control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-commune', 'n.', 'Self-communion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-communicative', 'a.', 'Imparting or communicating by its own powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-communion', 'n.', 'Communion with ones self; thoughts about ones\n   self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-complacency', 'n.', 'The quality of being self-complacent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-complacent', 'a.', 'Satisfied with ones own character, capacity, and\n   doings; self-satisfied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-conceit', 'n.', 'Conceit of ones self; an overweening opinion of\n   ones powers or endowments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-conceited', 'a.', 'Having an overweening opinion of ones own powers,\n   attainments; vain; conceited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-concern', 'n.', 'Concern for ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-condemnation', 'n.', 'Condemnation of ones self by ones own\n   judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-confidence', 'n.', 'The quality or state of being self-confident;\n   self-reliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-confident', 'a.', 'Confident of ones own strength or powers; relying\n   on ones judgment or ability; self-reliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-conjugate', 'a.', 'Having the two things that are conjugate parts of\n   the same figure; as, self-conjugate triangles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-conscious', 'a.', 'Conscious of ones acts or state as belonging to,\n   or originating in, ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-conscious', 'a.', 'Conscious of ones self as an object of the\n   observation of others; as, the speaker was too self-conscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-consciousness', 'n.', 'The quality or state of being self-conscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-considering', 'a.', 'Considering in ones own mind; deliberating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-consistency', 'n.', 'The quality or state of being self-consistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-cconsistent', 'a.', 'Consistent with ones self or with itself; not\n   deviation from the ordinary standard by which the conduct is guided;\n   logically consistent throughout; having each part consistent with the\n   rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-consuming', 'a.', 'Consuming ones self or itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-contained', 'a.', 'Having self-control; reserved; uncommunicative;\n   wholly engrossed in ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-contained', 'a.', 'Having all the essential working parts connected by\n   a bedplate or framework, or contained in a case, etc., so that mutual\n   relations of the parts do not depend upon fastening outside of the\n   machine itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-contradiction', 'n.', 'The act of contradicting ones self or itself;\n   repugnancy in conceptions or in terms; a proposition consisting of two\n   members, one of which contradicts the other; as, to be and not to be at\n   the same time is a self-contradiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-contradictory', 'a.', 'Contradicting ones self or itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-control', 'n.', 'Control of ones self; restraint exercised over ones\n   self; self-command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-convicted', 'a.', 'Convicted by ones own consciousness, knowledge,\n   avowal, or acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-conviction', 'n.', 'The act of convicting ones self, or the state of\n   being self-convicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-created', 'a.', 'Created by ones self; not formed or constituted by\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-culture', 'n.', 'Culture, training, or education of ones self by\n   ones own efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-deceit', 'n.', 'The act of deceiving ones self, or the state of being\n   self-deceived; self-deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-deceived', 'a.', 'Deceived or misled respecting ones self by ones\n   own mistake or error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-deception', 'n.', 'Self-deceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-defence', 'n.', 'See Self-defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-defense', 'n.', 'The act of defending ones own person, property, or\n   reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-defensive', 'a.', 'Defending, or tending to defend, ones own person,\n   property, or reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-degradation', 'n.', 'The act of degrading ones self, or the state of\n   being so degraded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-delation', 'n.', 'Accusation of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-delusion', 'n.', 'The act of deluding ones self, or the state of\n   being thus deluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-denial', 'n.', 'The denial of ones self; forbearing to gratify ones\n   own desires; self-sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-denying', 'a.', 'Refusing to gratify ones self; self-sacrificing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-dependent', 'a.', 'Dependent on ones self; self-depending;\n   self-reliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-depending', 'a.', 'Depending on ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-depraved', 'a.', 'Corrupted or depraved by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-destroyer', 'n.', 'One who destroys himself; a suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-destruction', 'n.', 'The destruction of ones self; self-murder;\n   suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-destructive', 'a.', 'Destroying, or tending to destroy, ones self or\n   itself; rucidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-determination', 'n.', 'Determination by ones self; or, determination\n   of ones acts or states without the necessitating force of motives; --\n   applied to the voluntary or activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-determining', 'a.', 'Capable of self-determination; as, the\n   self-determining power of will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-devised', 'a.', 'Devised by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-devoted', 'a.', 'Devoted in person, or by ones own will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-devotement', 'n.', 'Self-devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-devotion', 'n.', 'The act of devoting ones self, or the state of\n   being self-devoted; willingness to sacrifice ones own advantage or\n   happiness for the sake of others; self-sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-devouring', 'a.', 'Devouring ones self or itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-diffusive', 'a.', 'Having power to diffuse itself; diffusing itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-discipline', 'n.', 'Correction or government of ones self for the\n   sake of improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-distrust', 'n.', 'Want of confidence in one self; diffidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-educated', 'a.', 'Educated by ones own efforts, without instruction,\n   or without pecuniary assistance from others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-elective', 'a.', 'Having the right of electing ones self, or, as a\n   body, of electing its own members.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-enjoyment', 'n.', 'Enjoyment of ones self; self-satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-esteem', 'n.', 'The holding a good opinion of ones self;\n   self-complacency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-estimation', 'n.', 'The act of estimating ones self; self-esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-evidence', 'n.', 'The quality or state of being self-evident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-evident', 'a.', 'Evident without proof or reasoning; producing\n   certainty or conviction upon a bare presentation to the mind; as, a\n   self-evident proposition or truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-evolution', 'n.', 'Evolution of ones self; development by inherent\n   quality or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-exaltation', 'n.', 'The act of exalting ones self, or the state of\n   being so exalted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-examinant', 'n.', 'One who examines himself; one given to\n   self-examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-examination', 'n.', 'An examination into ones own state, conduct, and\n   motives, particularly in regard to religious feelings and duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-existence', 'n.', 'Inherent existence; existence possessed by virtue\n   of a beings own nature, and independent of any other being or cause;\n   -- an attribute peculiar to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-existent', 'a.', 'Existing of or by himself,independent of any other\n   being or cause; -- as, God is the only self-existent being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'self-explaining', 'a.', 'Explaining itself; capable of being understood\n   without explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-exposure', 'n.', 'The act of exposing ones self; the state of being\n   so exposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-fertilization', 'n.', 'The fertilization of a flower by pollen from\n   the same flower and without outer aid; autogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-fertilized', 'a.', 'Fertilized by pollen from the same flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-glorious', 'a.', 'Springing from vainglory or vanity; vain; boastful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-government', 'n.', 'The act of governing ones self, or the state of\n   being governed by ones self; self-control; self-command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-government', 'n.', 'Hence, government of a community, state, or nation\n   by the joint action of the mass of people constituting such a civil\n   body; also, the state of being so governed; democratic government;\n   democracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-gratulation', 'n.', 'Gratulation of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-heal', 'n.', 'A blue-flowered labiate plant (Brunella vulgaris); the\n   healall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-healing', 'a.', 'Having the power or property of healing itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-help', 'n.', 'The act of aiding ones self, without depending on the\n   aid of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-homicide', 'n.', 'The act of killing ones self; suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfhood', 'n.', 'Existence as a separate self, or independent person;\n   conscious personality; individuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-ignorance',    'n.',    'Ignorance of ones own character, powers, and\n   limitations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-ignorant',    'a.',    'Ignorant of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-imparting',    'a.',    'Imparting by ones own, or by its own, powers and\n   will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-importance',    'n.',    'An exaggerated estimate of ones own importance or\n   merit, esp. as manifested by the conduct or manners; self-conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-important',    'a.',    'Having or manifesting an exaggerated idea of ones\n   own importance or merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-imposed',    'a.',    'Voluntarily taken on ones self; as, self-imposed\n   tasks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-imposture',    'n.',    'Imposture practiced on ones self; self-deceit.');
