INSERT INTO words (word, word_type, description)VALUES  (    'Self-indignation',    'n.',    'Indignation at ones own character or actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-indulgence', 'n.', 'Indulgence of ones appetites, desires, or\n   inclinations; -- the opposite of self-restraint, and self-denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-indulgent', 'a.', 'Indulging ones appetites, desires, etc., freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-interest', 'n.', 'Private interest; the interest or advantage of ones\n   self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-interested', 'a.', 'Particularly concerned for ones own interest or\n   happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-involution', 'n.', 'Involution in ones self; hence, abstraction of\n   thought; reverie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfish', 'a.', 'Caring supremely or unduly for ones self; regarding ones\n   own comfort, advantage, etc., in disregard, or at the expense, of those\n   of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfish', 'a.', 'Believing or teaching that the chief motives of human\n   action are derived from love of self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfishly', 'adv.', 'In a selfish manner; with regard to private interest\n   only or chiefly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfishness', 'n.', 'The quality or state of being selfish; exclusive\n   regard to ones own interest or happiness; that supreme self-love or\n   self-preference which leads a person to direct his purposes to the\n   advancement of his own interest, power, or happiness, without regarding\n   those of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfism', 'n.', 'Concentration of ones interests on ones self; self-love;\n   selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfist', 'n.', 'A selfish person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-justifier', 'n.', 'One who excuses or justifies himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-kindled', 'a.', 'Kindled of itself, or without extraneous aid or\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-knowing', 'a.', 'Knowing ones self, or ones own character, powers,\n   and limitations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-knowing', 'a.', 'Knowing of itself, without help from another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-knowledge', 'n.', 'Knowledge of ones self, or of ones own character,\n   powers, limitations, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfless', 'a.', 'Having no regard to self; unselfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selflessness', 'n.', 'Quality or state of being selfless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-life', 'n.', 'Life for ones self; living solely or chiefly for ones\n   own pleasure or good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-love', 'n.', 'The love of ones self; desire of personal happiness;\n   tendency to seek ones own benefit or advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-luminous', 'a.', 'Possessing in itself the property of emitting light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-made', 'a.', 'Made by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-mettle', 'n.', 'Inborn mettle or courage; ones own temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-motion', 'n.', 'Motion given by inherent power, without external\n   impulse; spontaneus or voluntary motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-moved', 'a.', 'Moved by inherent power., without the aid of external\n   impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-moving', 'a.', 'Moving by inherent power, without the aid of external\n   impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-murder', 'a.', 'Suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-murderer', 'n.', 'A suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-neglecting', 'n.', 'A neglecting of ones self, or of ones own\n   interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfness', 'n.', 'Selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-one', 'a.', 'Secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-opinion', 'n.', 'Opinion, especially high opinion, of ones self; an\n   overweening estimate of ones self or of ones own opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-opinioned', 'a.', 'Having a high opinion of ones self; opinionated;\n   conceited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-opininating', 'a.', 'Beginning wwith, or springing from, ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-partiality', 'n.', 'That partiality to himself by which a man\n   overrates his own worth when compared with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-perplexed', 'a.', 'Perplexed by doubts originating in ones own mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-posited', 'a.', 'Disposed or arranged by an action originating in\n   ones self or in itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-positing', 'a.', 'The act of disposing or arranging ones self or\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-possessed', 'a.', 'Composed or tranquill in mind, manner, etc.;\n   undisturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-possession', 'n.', 'The possession of ones powers; calmness;\n   self-command; presence of mind; composure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-praise', 'n.', 'Praise of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-preservation', 'n.', 'The preservation of ones self from destruction\n   or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-propagating', 'a.', 'Propagating by ones self or by itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-registering', 'a.', 'Registering itself; -- said of any instrument so\n   contrived as to record its own indications of phenomena, whether\n   continuously or at stated times, as at the maxima and minima of\n   variations; as, a self-registering anemometer or barometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-regulated', 'a.', 'Regulated by ones self or by itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-regulative', 'a.', 'Tending or serving to regulate ones self or\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reliance', 'n.', 'Reliance on ones own powers or judgment;\n   self-trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reliant', 'a.', 'Reliant upon ones self; trusting to ones own powers\n   or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-renunciation', 'n.', 'The act of renouncing, or setting aside, ones\n   own wishes, claims, etc.; self-sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-repellency', 'n.', 'The quality or state of being self-repelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-repelling', 'a.', 'Made up of parts, as molecules or atoms, which\n   mutually repel each other; as, gases are self-repelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-repetition', 'n.', 'Repetition of ones self or of ones acts; the\n   saying or doing what one has already said or done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reproach', 'n.', 'The act of reproaching ones self; censure by ones\n   own conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reproached', 'a.', 'Reproached by ones own conscience or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reproaching', 'a.', 'Reproaching ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reproof', 'n.', 'The act of reproving ones self; censure of ones\n   conduct by ones own judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reproved', 'a.', 'Reproved by ones own conscience or ones own sense\n   of guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reproving', 'a.', 'Reproving ones self; reproving by consciousness of\n   guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reprovingly', 'adv.', 'In a self-reproving way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-repugnant', 'a.', 'Self-contradictory; inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-repulsive', 'a.', 'Self-repelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-respect', 'n.', 'Respect for ones self; regard for ones character;\n   laudable self-esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-restrained', 'a.', 'Restrained by ones self or itself; restrained by\n   ones own power or will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-restraint', 'n.', 'Restraint over ones self; self-control;\n   self-command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-reverence', 'n.', 'A reverent respect for ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-righteous', 'a.', 'Righteous in ones own esteem; pharisaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-righteousness', 'n.', 'The quality or state of being self-righteous;\n   pharisaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-sacrifice', 'n.', 'The act of sacrificing ones self, or ones\n   interest, for others; self-devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-sacrificing', 'a.', 'Yielding up ones own interest, ffeelings, etc;\n   sacrificing ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selfsame', 'a.', 'Precisely the same; the very same; identical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-satisfaction', 'n.', 'The quality or state of being self-satisfied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-satisfied', 'a.', 'Satisfied with ones self or ones actions;\n   self-complacent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-satisfying', 'a.', 'Giving satisfaction to ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-seeker', 'n.', 'One who seeks only his own interest, advantage, or\n   pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-seeking', 'a.', 'Seeking ones own interest or happiness; selfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-seeking', 'n.', 'The act or habit of seeking ones own interest or\n   happiness; selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-slaughter', 'n.', 'Suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-sufficiency', 'n.', 'The quality or state of being self-sufficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-sufficient', 'a.', 'Sufficient for ones self without external aid or\n   cooperation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-sufficient', 'a.', 'Having an overweening confidence in ones own\n   abilities or worth; hence, haughty; overbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-sufficing', 'a.', 'Sufficing for ones self or for itself, without\n   needing external aid; self-sufficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-suspended', 'a.', 'Suspended by ones self or by itself; balanced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-suspicious', 'a.', 'Suspicious or distrustful of ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-taught', 'a.', 'Taught by ones own efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-tormentor', 'n.', 'One who torments himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-torture', 'n.', 'The act of inflicting pain on ones self; pain\n   inflicted on ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-trust', 'n.', 'Faith in ones self; self-reliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Self-uned', 'a.', 'One with itself; separate from others.');
