  INSERT INTO words (word, word_type, description) VALUES  (    'Seljuckian', 'n.', 'A member of the family of Seljuk; an adherent of that\n   family, or subject of its government; (pl.) the dynasty of Turkish\n   sultans sprung from Seljuk.');
