  INSERT INTO words (word, word_type, description) VALUES  (    'Semi-Arian', 'a.', 'Of or pertaining to Semi-Arianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi-Arianism', 'n.', 'The doctrines or tenets of the Semi-Arians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiaxis', 'n.', 'One half of the axis of an /llipse or other figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibarbarian', 'a.', 'Half barbarous; partially civilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibarbarian', 'n.', 'One partly civilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibarbaric', 'a.', 'Half barbarous or uncivilized; as, semibarbaric\n   display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibarbarism', 'n.', 'The quality or state of being half barbarous or\n   uncivilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibarbarous', 'a.', 'Half barbarous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibreve', 'n.', 'A note of half the time or duration of the breve; -- now\n   usually called a whole note. It is the longest note in general use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibrief', 'n.', 'A semibreve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semibull', 'n.', 'A bull issued by a pope in the period between his\n   election and coronation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicalcareous', 'a.', 'Half or partially calcareous; as, a semicalcareous\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicalcined', 'a.', 'Half calcined; as, semicalcined iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicastrate', 'v. t.', 'To deprive of one testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicentennial', 'a.', 'Of or pertaining to half of a century, or a period\n   of fifty years; as, a semicentennial commemoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicentennial', 'n.', 'A fiftieth anniversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semichaotic', 'a.', 'Partially chaotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semichorus', 'n.', 'A half chorus; a passage to be sung by a selected\n   portion of the voices, as the female voices only, in contrast with the\n   full choir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi-Christianized', 'a.', 'Half Christianized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicircle', 'n.', 'The half of a circle; the part of a circle bounded by\n   its diameter and half of its circumference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicircle', 'n.', 'A semicircumference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicircle', 'n.', 'A body in the form of half of a circle, or half of a\n   circumference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicircle', 'n.', 'An instrument for measuring angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicircled', 'a.', 'Semicircular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicircular', 'a.', 'Having the form of half of a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi circumference', 'n.', 'Half of a circumference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicirque', 'n.', 'A semicircular hollow or opening among trees or hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicolon', 'n.', 'The punctuation mark [;] indicating a separation between\n   parts or members of a sentence more distinct than that marked by a\n   comma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicolumn', 'n.', 'A half column; a column bisected longitudinally, or\n   along its axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicolumnar', 'a.', 'Like a semicolumn; flat on one side and round on the\n   other; imperfectly columnar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicompact', 'a.', 'Half compact; imperfectly indurated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiconscious', 'a.', 'Half conscious; imperfectly conscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicope', 'n.', 'A short cope, or an inferier kind of cope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi crustaceous', 'a.', 'Half crustaceous; partially crustaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicrystalline', 'a.', 'Half crystalline; -- said of certain cruptive\n   rocks composed partly of crystalline, partly of amorphous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicubical', 'a.', 'Of or pertaining to the square root of the cube of a\n   quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicubium', 'n.', 'Alt. of Semicupium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicupium', 'n.', 'A half bath, or one that covers only the lewer\n   extremities and the hips; a sitz-bath; a half bath, or hip bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicylindric', 'a.', 'Alt. of Semicylyndrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semicylyndrical', 'a.', 'Half cylindrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semideistical', 'a.', 'Half deisticsl; bordering on deism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidemiquaver', 'n.', 'A demisemiquaver; a thirty-second note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidetached', 'a.', 'Half detached; partly distinct or separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiameter', 'n.', 'Half of a diameter; a right line, or the length of a\n   right line, drawn from the center of a circle, a sphere, or other\n   curved figure, to its circumference or periphery; a radius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiapason', 'n.', 'An imperfect octave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiapente', 'n.', 'An imperfect or diminished fifth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiaphaneity', 'n.', 'Half or imperfect transparency; translucency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiaphanous', 'a.', 'Half or imperfectly transparent; translucent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiatessaron', 'n.', 'An imperfect or diminished fourth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiditone', 'n.', 'A lesser third, having its terms as 6 to 5; a\n   hemiditone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiurnal', 'a.', 'Pertaining to, or accomplished in, half a day, or\n   twelve hours; occurring twice every day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidiurnal', 'a.', 'Pertaining to, or traversed in, six hours, or in half\n   the time between the rising and setting of a heavenly body; as, a\n   semidiurnal arc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidome', 'n.', 'A roof or ceiling covering a semicircular room or recess,\n   or one of nearly that shape, as the apse of a church, a niche, or the\n   like. It is approximately the quarter of a hollow sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidouble', 'n.', 'An office or feast celebrated with less solemnity than\n   the double ones. See Double, n., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semidouble', 'a.', 'Having the outermost stamens converted into petals,\n   while the inner ones remain perfect; -- said of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semifable', 'n.', 'That which is part fable and part truth; a mixture of\n   truth and fable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiflexed', 'a.', 'Half bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semifloret', 'n.', 'See Semifloscule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semifloscular', 'a.', 'Semiflosculous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semifloscule', 'n.', 'A floscule, or florest, with its corolla prolonged\n   into a strap-shaped petal; -- called also semifloret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiflosculous', 'a.', 'Having all the florets ligulate, as in the\n   dandelion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semifluid', 'a.', 'Imperfectly fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semifluid', 'n.', 'A semifluid substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiform', 'n.', 'A half form; an imperfect form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiformed', 'a.', 'Half formed; imperfectly formed; as, semiformed\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiglutin', 'n.', 'A peptonelike body, insoluble in alcohol, formed by\n   boiling collagen or gelatin for a long time in water. Hemicollin, a\n   like body, is also formed at the same time, and differs from semiglutin\n   by being partly soluble in alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semihistorical', 'a.', 'Half or party historical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semihoral', 'a.', 'Half-hourly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiindurated', 'a.', 'Imperfectly indurated or hardened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilapidified', 'a.', 'Imperfectly changed into stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilens', 'n.', 'The half of a lens divided along a plane passing through\n   its axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilenticular', 'a.', 'Half lenticular or convex; imperfectly resembling a\n   lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiligneous', 'a.', 'Half or partially ligneous, as a stem partly woody\n   and partly herbaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiliquid', 'a.', 'Half liquid; semifluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiliquidity', 'n.', 'The quality or state of being semiliquid; partial\n   liquidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilogical', 'a.', 'Half logical; partly logical; said of fallacies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilor', 'n.', 'A yellowish alloy of copper and zinc. See Simplor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilunar', 'a.', 'Shaped like a half moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilunar', 'n.', 'The semilunar bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilunary', 'a.', 'Semilunar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilunate', 'a.', 'Semilunar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semilune', 'n.', 'The half of a lune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semimetal', 'n.', 'An element possessing metallic properties in an inferior\n   degree and not malleable, as arsenic, antimony, bismuth, molybdenum,\n   uranium, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semimetallic', 'a.', 'Of or pertaining to a semimetal; possessing metallic\n   properties in an inferior degree; resembling metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semimonthly', 'a.', 'Coming or made twice in a month; as, semimonthly\n   magazine; a semimonthly payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semimonthly', 'n.', 'Something done or made every half month; esp., a\n   semimonthly periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semimonthly', 'adv.', 'In a semimonthly manner; at intervals of half a\n   month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiimute', 'a.', 'Having the faculty of speech but imperfectly developed\n   or partially lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semimute', 'n.', 'A semimute person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminal', 'a.', 'Pertaining to, containing, or consisting of, seed or\n   semen; as, the seminal fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminal', 'a.', 'Contained in seed; holding the relation of seed, source,\n   or first principle; holding the first place in a series of developed\n   results or consequents; germinal; radical; primary; original; as,\n   seminal principles of generation; seminal virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminal', 'n.', 'A seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminality', 'n.', 'The quality or state of being seminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminarian', 'n.', 'Alt. of Seminarist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminarist', 'n.', 'A member of, or one educated in, a seminary;\n   specifically, an ecclesiastic educated for the priesthood in a\n   seminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminaries', 'pl. ', 'of Seminary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminary', 'n.', 'A piece of ground where seed is sown for producing plants\n   for transplantation; a nursery; a seed plat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminary', 'n.', 'Hence, the place or original stock whence anything is\n   brought or produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminary', 'n.', 'A place of education, as a scool of a high grade, an\n   academy, college, or university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminary', 'n.', 'Seminal state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminary', 'n.', 'Fig.: A seed bed; a source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminary', 'n.', 'A Roman Catholic priest educated in a foreign seminary; a\n   seminarist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminary', 'a.', 'Belonging to seed; seminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminated', 'imp. & p. p.', 'of Seminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminating', 'p. pr. & vb. n.', 'of Seminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminate', 'v. t.', 'To sow; to spread; to propagate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semination', 'n.', 'The act of sowing or spreading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semination', 'n.', 'Natural dispersion of seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semined', 'a.', 'Thickly covered or sown, as with seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminiferous', 'a.', 'Seed-bearing; producing seed; pertaining to, or\n   connected with, the formation of semen; as, seminiferous cells or\n   vesicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminific', 'a.', 'Alt. of Semnifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semnifical', 'a.', 'Forming or producing seed, or the male generative\n   product of animals or of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminification', 'n.', 'Propagation from seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminist', 'n.', 'A believer in the old theory that the newly created being\n   is formed by the admixture of the seed of the male with the supposed\n   seed of the female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminoles', 'n. pl.', 'A tribe of Indians who formerly occupied Florida,\n   where some of them still remain. They belonged to the Creek\n   Confideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminose', 'n.', 'A carbohydrate of the glucose group found in the\n   thickened endosperm of certain seeds, and extracted as yellow sirup\n   having a sweetish-bitter taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminude', 'a.', 'Partially nude; half naked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seminymph', 'n.', 'The pupa of insects which undergo only a slight change\n   in passing to the imago state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semioccasionally', 'adv.', 'Once in a while; on rare occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiofficial', 'a.', 'Half official; having some official authority or\n   importance; as, a semiofficial statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiography', '', 'Alt. of Semiological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiology', '', 'Alt. of Semiological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiological', '', 'Same as Semeiography, Semeiology, Semeiological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiopacous', 'a.', 'Semiopaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiopal', 'n.', 'A variety of opal not possessing opalescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiopaque', 'a.', 'Half opaque; only half transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiorbicular', 'a.', 'Having the shape of a half orb or sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiotic', 'a.', 'Same as Semeiotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiotics', 'n.', 'Same as Semeiotics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semioval', 'a.', 'Half oval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiovate', 'a.', 'Half ovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semioxygenated', 'a.', 'Combined with oxygen only in part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semipagan', 'a.', 'Half pagan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semipalmate', 'a.', 'Alt. of Semipalmated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semipalmated', 'a.', 'Having the anterior toes joined only part way down\n   with a web; half-webbed; as, a semipalmate bird or foot. See Illust. k\n   under Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiparabola', 'n.', 'One branch of a parabola, being terminated at the\n   principal vertex of the curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiped', 'n.', 'A half foot in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semipedal', 'a.', 'Containing a half foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi-Pelagian', 'n.', 'A follower of John Cassianus, a French monk (died\n   about 448), who modified the doctrines of Pelagius, by denying human\n   merit, and maintaining the necessity of the Spirits influence, while,\n   on the other hand, he rejected the Augustinian doctrines of election,\n   the inability of man to do good, and the certain perseverance of the\n   saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi-Pelagian', 'a.', 'Of or pertaining to the Semi-Pelagians, or their\n   tenets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi-Pelagianism', 'n.', 'The doctrines or tenets of the Semi-Pelagians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semipellucid', 'a.', 'Half clear, or imperfectly transparent; as, a\n   semipellucid gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semipellucidity', 'n.', 'The qualiti or state of being imperfectly\n   transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semipenniform', 'a.', 'Half or partially penniform; as, a semipenniform\n   muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semopermanent', 'n.', 'Half or partly permanent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiperspicuous', 'a.', 'Half transparent; imperfectly clear; semipellucid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiphlogisticated', 'a.', 'Partially impregnated with phlogiston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiplume', 'n.', 'A feather which has a plumelike web, with the shaft of\n   an ordinary feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiprecious', 'a.', 'Somewhat precious; as, semiprecious stones or metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiproof', 'n.', 'Half proof; evidence from the testimony of a single\n   witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi pupa', 'n.', 'The young of an insect in a stage between the larva and\n   pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiquadrate', 'n.', 'Alt. of Semiquartile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiquartile', 'n.', 'An aspect of the planets when distant from each other\n   the half of a quadrant, or forty-five degrees, or one sign and a half.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiquaver', 'n.', 'A note of half the duration of the quaver; -- now\n   usually called a sixsteenth note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiquintile', 'n.', 'An aspect of the planets when distant from each other\n   half of the quintile, or thirty-six degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semirecondite', 'a.', 'Half hidden or half covered; said of the head of an\n   insect when half covered by the shield of the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiring', 'n.', 'One of the incomplete rings of the upper part of the\n   bronchial tubes of most birds. The semerings form an essential part of\n   the syrinx, or musical organ, of singing birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semisavage', 'a.', 'Half savage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semisavage', 'n.', 'One who is half savage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semi-Saxon', 'a.', 'Half Saxon; -- specifically applied to the language\n   intermediate between Saxon and English, belonging to the period\n   1150-1250.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semisextile', 'n.', 'An aspect of the planets when they are distant from\n   each other the twelfth part of a circle, or thirty degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semisolid', 'a.', 'Partially solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semisoun', 'n.', 'A half sound; a low tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semispheric', 'a.', 'Alt. of Semispherical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semispherical', 'a.', 'Having the figure of a half sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semispheroidal', 'a.', 'Formed like a half spheroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semisteel', 'n.', 'Puddled steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitae', 'pl. ', 'of Semita');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semita', 'n.', 'A fasciole of a spatangoid sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitangent', 'n.', 'The tangent of half an arc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semite', 'n.', 'One belonging to the Semitic race. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiterete', 'a.', 'Half terete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitertian', 'a.', 'Having the characteristics of both a tertian and a\n   quotidian intermittent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitertian', 'n.', 'An intermittent combining the characteristics of a\n   tertian and a quotidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitic', 'a.', 'Of or pertaining to Shem or his descendants; belonging to\n   that division of the Caucasian race which includes the Arabs, Jews, and\n   related races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitism', 'n.', 'A Semitic idiom; a word of Semitic origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitone', 'n.', 'Half a tone; -- the name commonly applied to the smaller\n   intervals of the diatonic scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitonic', 'a.', 'Of or pertaining to a semitone; consisting of a\n   semitone, or of semitones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitransept', 'n.', 'The half of a transept; as, the north semitransept of\n   a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitranslucent', 'a.', 'Slightly clear; transmitting light in a slight\n   degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitransparency', 'n.', 'Imperfect or partial transparency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semitransparent', 'a.', 'Half or imperfectly transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiverticillate', 'a.', 'Partially verticillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivif', 'a.', 'Only half alive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivitreous', 'a.', 'Partially vitreous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivitrification', 'n.', 'The quality or state of being semivitrified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivitrification', 'n.', 'A substance imperfectly vitrified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivitrified', 'a.', 'Half or imperfectly vitrified; partially converted\n   into glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivocal', 'a.', 'Of or pertaining to a semivowel; half cocal; imperfectly\n   sounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivowel', 'n.', 'A sound intermediate between a vowel and a consonant, or\n   partaking of the nature of both, as in the English w and y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semivowel', 'n.', 'The sign or letter representing such a sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiweekly', 'a.', 'Coming, or made, or done, once every half week; as, a\n   semiweekly newspaper; a semiweekly trip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiweekly', 'n.', 'That which comes or happens once every half week, esp.\n   a semiweekly periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semiweekly', 'adv.', 'At intervals of half a week each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semolella', 'n.', 'See Semolina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semolina', 'n.', 'The fine, hard parts of wheat, rounded by the attrition\n   of the millstones, -- used in cookery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semolino', 'n.', 'Same as Semolina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semoule', 'n.', 'Same as Semolina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempervirent', 'a.', 'Always fresh; evergreen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempervive', 'n.', 'The houseleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempervivum', 'n.', 'A genus of fleshy-leaved plants, of which the\n   houseleek (Sempervivum tectorum) is the commonest species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempiternal', 'a.', 'Of neverending duration; everlasting; endless; having\n   beginning, but no end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempiternal', 'a.', 'Without beginning or end; eternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempiterne', 'a.', 'Sempiternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempiternity', 'n.', 'Future duration without end; the relation or state of\n   being sempiternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempre', 'adv.', 'Always; throughout; as, sempre piano, always soft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempster', 'n.', 'A seamster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempstress', 'n.', 'A seamstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sempstressy', 'n.', 'Seamstressy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semster', 'n.', 'A seamster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semuncia', 'n.', 'A Roman coin equivalent to one twenty-fourth part of a\n   Roman pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sen', 'n.', 'A Japanese coin, worth about one half of a cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sen', 'adv., prep., & conj.', 'Since.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senary', 'a.', 'Of six; belonging to six; containing six.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senate', 'n.', 'An assembly or council having the highest deliberative and\n   legislative functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senate', 'n.', 'A body of elders appointed or elected from among the nobles\n   of the nation, and having supreme legislative authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senate', 'n.', 'The upper and less numerous branch of a legislature in\n   various countries, as in France, in the United States, in most of the\n   separate States of the United States, and in some Swiss cantons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senate', 'n.', 'In general, a legislative body; a state council; the\n   legislative department of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senate', 'n.', 'The governing body of the Universities of Cambridge and\n   London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senate', 'n.', 'In some American colleges, a council of elected students,\n   presided over by the president of the college, to which are referred\n   cases of discipline and matters of general concern affecting the\n   students.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senator', 'n.', 'A member of a senate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senator', 'n.', 'A member of the kings council; a kings councilor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senatorial', 'a.', 'Of or pertaining to a senator, or a senate; becoming to\n   a senator, or a senate; as, senatorial duties; senatorial dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senatorial', 'a.', 'Entitled to elect a senator, or by senators; as, the\n   senatorial districts of a State.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senatorially', 'adv.', 'In a senatorial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senatorian', 'a.', 'Senatorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senatorious', 'a.', 'Senatorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senatorship', 'n.', 'The office or dignity of a senator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senatusconsult', 'n.', 'A decree of the Roman senate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sent', 'imp. & p. p.', 'of Send');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sending', 'p. pr. & vb. n.', 'of Send');
  INSERT INTO words (word, word_type, description) VALUES  (    'Send', 'v. t.', 'To cause to go in any manner; to dispatch; to commission\n   or direct to go; as, to send a messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Send', 'v. t.', 'To give motion to; to cause to be borne or carried; to\n   procure the going, transmission, or delivery of; as, to send a message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Send', 'v. t.', 'To emit; to impel; to cast; to throw; to hurl; as, to send\n   a ball, an arrow, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Send', 'v. t.', 'To cause to be or to happen; to bestow; to inflict; to\n   grant; -- sometimes followed by a dependent proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Send', 'v. i.', 'To dispatch an agent or messenger to convey a message, or\n   to do an errand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Send', 'v. i.', 'To pitch; as, the ship sends forward so violently as to\n   endanger her masts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Send', 'n.', 'The impulse of a wave by which a vessel is carried bodily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sendal', 'n.', 'A light thin stuff of silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sender', 'n.', 'One who sends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senecas', 'n. pl.', 'A tribe of Indians who formerly inhabited a part of\n   Western New York. This tribe was the most numerous and most warlike of\n   the Five Nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senecio', 'n.', 'A very large genus of composite plants including the\n   groundsel and the golden ragwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senectitude', 'n.', 'Old age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senega', 'n.', 'Seneca root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senegal', 'n.', 'Gum senegal. See under Gum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senegin', 'n.', 'A substance extracted from the rootstock of the Polygala\n   Senega (Seneca root), and probably identical with polygalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senescence', 'n.', 'The state of growing old; decay by time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senescent', 'a.', 'Growing old; decaying with the lapse of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seneschal', 'n.', 'An officer in the houses of princes and dignitaries, in\n   the Middle Ages, who had the superintendence of feasts and domestic\n   ceremonies; a steward. Sometimes the seneschal had the dispensing of\n   justice, and was given high military commands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seneschalship', 'n.', 'The office, dignity, or jurisdiction of a seneschal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senge', 'v. t.', 'To singe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sengreen', 'n.', 'The houseleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senile', 'a.', 'Of or pertaining to old age; proceeding from, or\n   characteristic of, old age; affected with the infirmities of old age;\n   as, senile weakness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senility', 'n.', 'The quality or state of being senile; old age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senior', 'a.', 'More advanced than another in age; prior in age; elder;\n   hence, more advanced in dignity, rank, or office; superior; as, senior\n   member; senior counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senior', 'a.', 'Belonging to the final year of the regular course in\n   American colleges, or in professional schools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senior', 'n.', 'A person who is older than another; one more advanced in\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senior', 'n.', 'One older in office, or whose entrance upon office was\n   anterior to that of another; one prior in grade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senior', 'n.', 'An aged person; an older.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senior', 'n.', 'One in the fourth or final year of his collegiate course at\n   an American college; -- originally called senior sophister; also, one\n   in the last year of the course at a professional schools or at a\n   seminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seniority', 'n.', 'The quality or state of being senior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seniorize', 'v. i.', 'To exercise authority; to rule; to lord it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seniory', 'n.', 'Seniority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senna', 'n.', 'The leaves of several leguminous plants of the genus Cassia.\n   (C. acutifolia, C. angustifolia, etc.). They constitute a valuable but\n   nauseous cathartic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senna', 'n.', 'The plants themselves, native to the East, but now\n   cultivated largely in the south of Europe and in the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sennachy', 'n.', 'See Seannachie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sennet', 'n.', 'A signal call on a trumpet or cornet for entrance or exit\n   on the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sennet', 'n.', 'The barracuda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sennight', 'n.', 'The space of seven nights and days; a week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sennit', 'n.', 'A braided cord or fabric formed by plaiting together rope\n   yarns or other small stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sennit', 'n.', 'Plaited straw or palm leaves for making hats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senocular', 'a.', 'Having six eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senonian', 'a.', 'In european geology, a name given to the middle division\n   of the Upper Cretaceous formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seor', 'n.', 'A Spanish title of courtesy corresponding to the English Mr.\n   or Sir; also, a gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seora', 'n.', 'A Spanish title of courtesy given to a lady; Mrs.; Madam;\n   also, a lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seorita', 'n.', 'A Spanish title of courtesy given to a young lady; Miss;\n   also, a young lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sens', 'adv.', 'Since.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensated', 'imp. & p. p.', 'of Sensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensating', 'p. pr. & vb. n.', 'of Sensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensate', 'v. t.', 'To feel or apprehend more or less distinctly through a\n   sense, or the senses; as, to sensate light, or an odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensate', 'a.', 'Alt. of Sensated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensated', 'a.', 'Felt or apprehended through a sense, or the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensation', 'n.', 'An impression, or the consciousness of an impression,\n   made upon the central nervous organ, through the medium of a sensory or\n   afferent nerve or one of the organs of sense; a feeling, or state of\n   consciousness, whether agreeable or disagreeable, produced either by an\n   external object (stimulus), or by some change in the internal state of\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensation', 'n.', 'A purely spiritual or psychical affection; agreeable or\n   disagreeable feelings occasioned by objects that are not corporeal or\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensation', 'n.', 'A state of excited interest or feeling, or that which\n   causes it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensational', 'a.', 'Of or pertaining to sensation; as, sensational nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensational', 'a.', 'Of or pertaining to sensationalism, or the doctrine\n   that sensation is the sole origin of knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensational', 'a.', 'Suited or intended to excite temporarily great\n   interest or emotion; melodramatic; emotional; as, sensational plays or\n   novels; sensational preaching; sensational journalism; a sensational\n   report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensationalism', 'n.', 'The doctrine held by Condillac, and by some\n   ascribed to Locke, that our ideas originate solely in sensation, and\n   consist of sensations transformed; sensualism; -- opposed to\n   intuitionalism, and rationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensationalism', 'n.', 'The practice or methods of sensational writing or\n   speaking; as, the sensationalism of a novel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensationalist', 'n.', 'An advocate of, or believer in, philosophical\n   sensationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensationalist', 'n.', 'One who practices sensational writing or speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'A faculty, possessed by animals, of perceiving external\n   objects by means of impressions made upon certain organs (sensory or\n   sense organs) of the body, or of perceiving changes in the condition of\n   the body; as, the senses of sight, smell, hearing, taste, and touch.\n   See Muscular sense, under Muscular, and Temperature sense, under\n   Temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'Perception by the sensory organs of the body; sensation;\n   sensibility; feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'Perception through the intellect; apprehension;\n   recognition; understanding; discernment; appreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'Sound perception and reasoning; correct judgment; good\n   mental capacity; understanding; also, that which is sound, true, or\n   reasonable; rational meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'That which is felt or is held as a sentiment, view, or\n   opinion; judgment; notion; opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'Meaning; import; signification; as, the true sense of\n   words or phrases; the sense of a remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'Moral perception or appreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'One of two opposite directions in which a line, surface,\n   or volume, may be supposed to be described by the motion of a point,\n   line, or surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensed', 'imp. & p. p.', 'of Sense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensing', 'p. pr. & vb. n.', 'of Sense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sense', 'v. t.', 'To perceive by the senses; to recognize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senseful', 'a.', 'Full of sense, meaning, or reason; reasonable; judicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senseless', 'a.', 'Destitute of, deficient in, or contrary to, sense;\n   without sensibility or feeling; unconscious; stupid; foolish; unwise;\n   unreasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibilities', 'pl. ', 'of Sensibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibility', 'n.', 'The quality or state of being sensible, or capable of\n   sensation; capacity to feel or perceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibility', 'n.', 'The capacity of emotion or feeling, as distinguished\n   from the intellect and the will; peculiar susceptibility of impression,\n   pleasurable or painful; delicacy of feeling; quick emotion or sympathy;\n   as, sensibility to pleasure or pain; sensibility to shame or praise;\n   exquisite sensibility; -- often used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibility', 'n.', 'Experience of sensation; actual feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibility', 'n.', 'That quality of an instrument which makes it indicate\n   very slight changes of condition; delicacy; as, the sensibility of a\n   balance, or of a thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'a.', 'Capable of being perceived by the senses; apprehensible\n   through the bodily organs; hence, also, perceptible to the mind; making\n   an impression upon the sense, reason, or understanding; ////// heat;\n   sensible resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'a.', 'Having the capacity of receiving impressions from\n   external objects; capable of perceiving by the instrumentality of the\n   proper organs; liable to be affected physsically or mentally;\n   impressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'a.', 'Hence: Liable to impression from without; easily\n   affected; having nice perception or acute feeling; sensitive; also,\n   readily moved or affected by natural agents; delicate; as, a sensible\n   thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'a.', 'Perceiving or having perception, either by the senses or\n   the mind; cognizant; perceiving so clearly as to be convinced;\n   satisfied; persuaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'a.', 'Having moral perception; capable of being affected by\n   moral good or evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'a.', 'Possessing or containing sense or reason; giftedwith, or\n   characterized by, good or common sense; intelligent; wise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'n.', 'Sensation; sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'n.', 'That which impresses itself on the sense; anything\n   perceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensible', 'n.', 'That which has sensibility; a sensitive being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibleness', 'n.', 'The quality or state of being sensible; sensibility;\n   appreciation; capacity of perception; susceptibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibleness', 'n.', 'Intelligence; reasonableness; good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibly', 'adv.', 'In a sensible manner; so as to be perceptible to the\n   senses or to the mind; appreciably; with perception; susceptibly;\n   sensitively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensibly', 'adv.', 'With intelligence or good sense; judiciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensifacient', 'a.', 'Converting into sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensiferous', 'a.', 'Exciting sensation; conveying sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensific', 'a.', 'Exciting sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensificatory', 'a.', 'Susceptible of, or converting into, sensation; as,\n   the sensificatory part of a nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensigenous', 'a.', 'Causing or exciting sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensism', 'n.', 'Same as Sensualism, 2 & 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensist', 'n.', 'One who, in philosophy, holds to sensism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitive', 'a.', 'Having sense of feeling; possessing or exhibiting the\n   capacity of receiving impressions from external objects; as, a\n   sensitive soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitive', 'a.', 'Having quick and acute sensibility, either to the action\n   of external objects, or to impressions upon the mind and feelings;\n   highly susceptible; easily and acutely affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitive', 'a.', 'Having a capacity of being easily affected or moved; as,\n   a sensitive thermometer; sensitive scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitive', 'a.', 'Readily affected or changed by certain appropriate\n   agents; as, silver chloride or bromide, when in contact with certain\n   organic substances, is extremely sensitive to actinic rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitive', 'a.', 'Serving to affect the sense; sensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitive', 'a.', 'Of or pertaining to sensation; depending on sensation;\n   as, sensitive motions; sensitive muscular motions excited by\n   irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitivity', 'n.', 'The quality or state of being sensitive; -- used\n   chiefly in science and the arts; as, the sensitivity of iodized silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitize', 'v. t.', 'To render sensitive, or susceptible of being easily\n   acted on by the actinic rays of the sun; as, sensitized paper or plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitizer', 'n.', 'An agent that sensitizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensitory', 'n.', 'See Sensory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensive', 'a.', 'Having sense or sensibility; sensitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensor', 'a.', 'Sensory; as, the sensor nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensorial', 'a.', 'Of or pertaining to the sensorium; as, sensorial\n   faculties, motions, powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensoriums', 'pl. ', 'of Sensorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensoria', 'pl. ', 'of Sensorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensorium', 'n.', 'The seat of sensation; the nervous center or centers to\n   which impressions from the external world must be conveyed before they\n   can be perceived; the place where external impressions are localized,\n   and transformed into sensations, prior to being reflected to other\n   parts of the organism; hence, the whole nervous system, when animated,\n   so far as it is susceptible of common or special sensations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensori-volitional', 'a.', 'Concerned both in sensation and volition; --\n   applied to those nerve fibers which pass to and from the cerebro-spinal\n   axis, and are respectively concerned in sensation and volition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensories', 'pl. ', 'of Sensery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensery', 'n.', 'Same as Sensorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensory', 'a.', 'Of or pertaining to the sensorium or sensation; as,\n   sensory impulses; -- especially applied to those nerves and nerve\n   fibers which convey to a nerve center impulses resulting in sensation;\n   also sometimes loosely employed in the sense of afferent, to indicate\n   nerve fibers which convey impressions of any kind to a nerve center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensual', 'a.', 'Pertaining to, consisting in, or affecting, the sense, or\n   bodily organs of perception; relating to, or concerning, the body, in\n   distinction from the spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensual', 'a.', 'Hence, not spiritual or intellectual; carnal; fleshly;\n   pertaining to, or consisting in, the gratification of the senses, or\n   the indulgence of appetites; wordly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensual', 'a.', 'Devoted to the pleasures of sense and appetite; luxurious;\n   voluptuous; lewd; libidinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensual', 'a.', 'Pertaining or peculiar to the philosophical doctrine of\n   sensualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualism', 'n.', 'The condition or character of one who is sensual;\n   subjection to sensual feelings and appetite; sensuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualism', 'n.', 'The doctrine that all our ideas, or the operations of\n   the understanding, not only originate in sensation, but are transformed\n   sensations, copies or relics of sensations; sensationalism; sensism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualism', 'n.', 'The regarding of the gratification of the senses as the\n   highest good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualist', 'n.', 'One who is sensual; one given to the indulgence of the\n   appetites or senses as the means of happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualist', 'n.', 'One who holds to the doctrine of sensualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualistic', 'a.', 'Sensual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualistic', 'a.', 'Adopting or teaching the doctrines of sensualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensuality', 'n.', 'The quality or state of being sensual; devotedness to\n   the gratification of the bodily appetites; free indulgence in carnal or\n   sensual pleasures; luxuriousness; voluptuousness; lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualization', 'n.', 'The act of sensualizing, or the state of being\n   sensualized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualized', 'imp. & p. p.', 'of Sensualize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualizing', 'p. pr. & vb. n.', 'of Sensualize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualize', 'v. t.', 'To make sensual; to subject to the love of sensual\n   pleasure; to debase by carnal gratifications; to carnalize; as,\n   sensualized by pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensually', 'adv.', 'In a sensual manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensualness', 'n.', 'Sensuality; fleshliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensuism', 'n.', 'Sensualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensuosity', 'n.', 'The quality or state of being sensuous; sensuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensuous', 'a.', 'Of or pertaining to the senses, or sensible objects;\n   addressing the senses; suggesting pictures or images of sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sensuous', 'a.', 'Highly susceptible to influence through the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sent', 'v. & n.', 'See Scent, v. & n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sent', '', 'obs. 3d pers. sing. pres. of Send, for sendeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sent', '', 'imp. & p. p. of Send.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'n.', 'Sense; meaning; significance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'n.', 'An opinion; a decision; a determination; a judgment,\n   especially one of an unfavorable nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'n.', 'A philosophical or theological opinion; a dogma; as,\n   Summary of the Sentences; Book of the Sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'n.', 'In civil and admiralty law, the judgment of a court\n   pronounced in a cause; in criminal and ecclesiastical courts, a\n   judgment passed on a criminal by a court or judge; condemnation\n   pronounced by a judgical tribunal; doom. In common law, the term is\n   exclusively used to denote the judgment in criminal cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'n.', 'A short saying, usually containing moral instruction; a\n   maxim; an axiom; a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'n.', 'A combination of words which is complete as expressing a\n   thought, and in writing is marked at the close by a period, or full\n   point. See Proposition, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentenced', 'imp. & p. p.', 'of Sentence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentencing', 'p. pr. & vb. n.', 'of Sentence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'v. t.', 'To pass or pronounce judgment upon; to doom; to\n   condemn to punishment; to prescribe the punishment of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'v. t.', 'To decree or announce as a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentence', 'v. t.', 'To utter sententiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentencer', 'n.', 'One who pronounced a sentence or condemnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'sentential', 'a.', 'Comprising sentences; as, a sentential translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'sentential', 'a.', 'Of or pertaining to a sentence, or full period; as, a\n   sentential pause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sententially', 'adv.', 'In a sentential manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sententiarist', 'n.', 'A sententiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sententiary', 'n.', 'One who read lectures, or commented, on the Sentences\n   of Peter Lombard, Bishop of Paris (1159-1160), a school divine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sententiosity', 'n.', 'The quality or state of being sententious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sententious', 'a.', 'Abounding with sentences, axioms, and maxims; full of\n   meaning; terse and energetic in expression; pithy; as, a sententious\n   style or discourse; sententious truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sententious', 'a.', 'Comprising or representing sentences; sentential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentery', 'n.', 'A sentry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senteur', 'n.', 'Scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentience', 'n.', 'Alt. of Sentiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentiency', 'n.', 'The quality or state of being sentient; esp., the\n   quality or state of having sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentient', 'a.', 'Having a faculty, or faculties, of sensation and\n   perception. Specif. (Physiol.), especially sensitive; as, the sentient\n   extremities of nerves, which terminate in the various organs or\n   tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentient', 'n.', 'One who has the faculty of perception; a sentient being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentiently', 'adv.', 'In a sentient or perceptive way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentiment', 'a.', 'A thought prompted by passion or feeling; a state of\n   mind in view of some subject; feeling toward or respecting some person\n   or thing; disposition prompting to action or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentiment', 'a.', 'Hence, generally, a decision of the mind formed by\n   deliberation or reasoning; thought; opinion; notion; judgment; as, to\n   express ones sentiments on a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentiment', 'a.', 'A sentence, or passage, considered as the expression of\n   a thought; a maxim; a saying; a toast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentiment', 'a.', 'Sensibility; feeling; tender susceptibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimental', 'a.', 'Having, expressing, or containing a sentiment or\n   sentiments; abounding with moral reflections; containing a moral\n   reflection; didactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimental', 'a.', 'Inclined to sentiment; having an excess of sentiment\n   or sensibility; indulging the sensibilities for their own sake;\n   artificially or affectedly tender; -- often in a reproachful sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimental', 'a.', 'Addressed or pleasing to the emotions only, usually to\n   the weaker and the unregulated emotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimentalism', 'n.', 'The quality of being sentimental; the character or\n   behavior of a sentimentalist; sentimentality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimentalist', 'n.', 'One who has, or affects, sentiment or fine feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimentality', 'n.', 'The quality or state of being sentimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimentalize', 'v. t.', 'To regard in a sentimental manner; as, to\n   sentimentalize a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimentalize', 'v. i.', 'To think or act in a sentimental manner, or like\n   a sentimentalist; to affect exquisite sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentimentally', 'adv.', 'In a sentimental manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentine', 'n.', 'A place for dregs and dirt; a sink; a sewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentinel', 'n.', 'One who watches or guards; specifically (Mil.), a soldier\n   set to guard an army, camp, or other place, from surprise, to observe\n   the approach of danger, and give notice of it; a sentry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentinel', 'n.', 'Watch; guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentinel', 'n.', 'A marine crab (Podophthalmus vigil) native of the Indian\n   Ocean, remarkable for the great length of its eyestalks; -- called also\n   sentinel crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentineled', 'imp. & p. p.', 'of Sentinel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentinelled', '', 'of Sentinel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentineling', 'p. pr. & vb. n.', 'of Sentinel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentinelling', '', 'of Sentinel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentinel', 'v. t.', 'To watch over like a sentinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentinel', 'v. t.', 'To furnish with a sentinel; to place under the guard\n   of a sentinel or sentinels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentisection', 'n.', 'Painful vivisection; -- opposed to callisection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentires', 'pl. ', 'of Sentry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentry', 'n.', 'A soldier placed on guard; a sentinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sentry', 'n.', 'Guard; watch, as by a sentinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Senza', 'prep.', 'Without; as, senza stromenti, without instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepal', 'n.', 'A leaf or division of the calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepaled', 'a.', 'Having one or more sepals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepaline', 'a.', 'Relating to, or having the nature of, sepals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepalody', 'n.', 'The metamorphosis of other floral organs into sepals or\n   sepaloid bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepaloid', 'a.', 'Like a sepal, or a division of a calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepalous', 'a.', 'Having, or relating to, sepals; -- used mostly in\n   composition. See under Sepal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separability', 'n.', 'Quality of being separable or divisible;\n   divisibility; separableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separable', 'a.', 'Capable of being separated, disjoined, disunited, or\n   divided; as, the separable parts of plants; qualities not separable\n   from the substance in which they exist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separated', 'imp. & p. p.', 'of Separate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separating', 'p. pr. & vb. n.', 'of Separate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separate', 'v. t.', 'To disunite; to divide; to disconnect; to sever; to\n   part in any manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separate', 'v. t.', 'To come between; to keep apart by occupying the space\n   between; to lie between; as, the Mediterranean Sea separates Europe and\n   Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separate', 'v. t.', 'To set apart; to select from among others, as for a\n   special use or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separate', 'v. i.', 'To part; to become disunited; to be disconnected; to\n   withdraw from one another; as, the family separated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separate', 'p. a.', 'Divided from another or others; disjoined;\n   disconnected; separated; -- said of things once connected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separate', 'p. a.', 'Unconnected; not united or associated; distinct; --\n   said of things that have not been connected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separate', 'p. a.', 'Disunited from the body; disembodied; as, a separate\n   spirit; the separate state of souls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatical', 'a.', 'Of or pertaining to separatism in religion;\n   schismatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separating', 'a.', 'Designed or employed to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separation', 'n.', 'The act of separating, or the state of being separated,\n   or separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separation', 'n.', 'Chemical analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separation', 'n.', 'Divorce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separation', 'n.', 'The operation of removing water from steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatism', 'n.', 'The character or act of a separatist; disposition to\n   withdraw from a church; the practice of so withdrawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatist', 'n.', 'One who withdraws or separates himself; especially, one\n   who withdraws from a church to which he has belonged; a seceder from an\n   established church; a dissenter; a nonconformist; a schismatic; a\n   sectary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatistic', 'a.', 'Of or pertaining to separatists; characterizing\n   separatists; schismatical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separative', 'a.', 'Causing, or being to cause, separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separator', 'n.', 'One who, or that which, separates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separator', 'n.', 'A device for depriving steam of particles of water mixed\n   with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separator', 'n.', 'An apparatus for sorting pulverized ores into grades, or\n   separating them from gangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separator', 'n.', 'An instrument used for spreading apart the threads of\n   the warp in the loom, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatory', 'a.', 'Separative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatory', 'n.', 'An apparatus used in separating, as a separating\n   funnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatory', 'n.', 'A surgical instrument for separating the pericranium\n   from the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-trices', 'pl. ', 'of Separatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    '-trixes', 'pl. ', 'of Separatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Separatrix', 'n.', 'The decimal point; the dot placed at the left of a\n   decimal fraction, to separate it from the whole number which it\n   follows. The term is sometimes also applied to other marks of\n   separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepawn', 'n.', 'See Supawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepelible', 'a.', 'Admitting of burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepelition', 'n.', 'Burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sephen', 'n.', 'A large sting ray of the genus Trygon, especially T. sephen\n   of the Indian Ocean and the Red Sea. The skin is an article of\n   commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepias', 'pl. ', 'of Sepia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepiae', 'pl. ', 'of Sepia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepia', 'n.', 'The common European cuttlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepia', 'n.', 'A genus comprising the common cuttlefish and numerous\n   similar species. See Illustr. under Cuttlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepia', 'n.', 'A pigment prepared from the ink, or black secretion, of the\n   sepia, or cuttlefish. Treated with caustic potash, it has a rich brown\n   color; and this mixed with a red forms Roman sepia. Cf. India ink,\n   under India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepia', 'a.', 'Of a dark brown color, with a little red in its composition;\n   also, made of, or done in, sepia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepic', 'a.', 'Of or pertaining to sepia; done in sepia; as, a sepic\n   drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepidaceous', 'a.', 'Like or pertaining to the cuttlefishes of the genus\n   Sepia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepiment', 'n.', 'Something that separates; a hedge; a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepiolite', 'n.', 'Meerschaum. See Meerschaum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepiostare', 'n.', 'The bone or shell of cuttlefish. See Illust. under\n   Cuttlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepon', 'n.', 'See Supawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepose', 'v. t.', 'To set apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seposit', 'v. t.', 'To set aside; to give up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seposition', 'n.', 'The act of setting aside, or of giving up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepoy', 'n.', 'A native of India employed as a soldier in the service of a\n   European power, esp. of Great Britain; an Oriental soldier disciplined\n   in the European manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seppuku', 'n.', 'Same as Hara-kiri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepsin', 'n.', 'A soluble poison (ptomaine) present in putrid blood. It is\n   also formed in the putrefaction of proteid matter in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepsis', 'n.', 'The poisoning of the system by the introduction of\n   putrescent material into the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sept', 'n.', 'A clan, tribe, or family, proceeding from a common\n   progenitor; -- used especially of the ancient clans in Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septaemia', 'n.', 'Septicaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septal', 'a.', 'Of or pertaining to a septum or septa, as of a coral or a\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septane', 'n.', 'See Heptane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septangle', 'n.', 'A figure which has seven angles; a heptagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septangular', 'a.', 'Heptagonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septaria', 'pl. ', 'of Septarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septarium', 'n.', 'A flattened concretionary nodule, usually of limestone,\n   intersected within by cracks which are often filled with calcite,\n   barite, or other minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septate', 'a.', 'Divided by partition or partitions; having septa; as, a\n   septate pod or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'September', 'n.', 'The ninth month of the year, containing thurty days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septemberer', 'n.', 'A Setembrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septembrist', 'n.', 'An agent in the massacres in Paris, committed in\n   patriotic frenzy, on the 22d of September, 1792.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septemfluous', 'a.', 'Flowing sevenfold; divided into seven streams or\n   currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septempartite', 'a.', 'Divided nearly to the base into seven parts; as, a\n   septempartite leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septemtrioun', 'n.', 'Septentrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septemvirs', 'pl. ', 'of Septemvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septemviri', 'pl. ', 'of Septemvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septemvir', 'n.', 'One of a board of seven men associated in some office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septemvirate', 'n.', 'The office of septemvir; a government by septimvirs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septenary', 'a.', 'Consisting of, or relating to, seven; as, a septenary\n   number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septenary', 'a.', 'Lasting seven years; continuing seven years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septenary', 'n.', 'The number seven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septenate', 'a.', 'Having parts in sevens; heptamerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septennate', 'n.', 'A period of seven years; as, the septennate during\n   which the President of the French Republic holds office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septennial', 'a.', 'Lasting or continuing seven years; as, septennial\n   parliaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septennial', 'a.', 'Happening or returning once in every seven years; as,\n   septennial elections in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septennially', 'adv.', 'Once in seven years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrial', 'a.', 'Septentrional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrio', 'n.', 'The constellation Ursa Major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrion', 'n.', 'The north or northern regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrion', 'a.', 'Alt. of Septentrional');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrional', 'a.', 'Of or pertaining to the north; northern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrionality', 'n.', 'Northerliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrionally', 'adv.', 'Northerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septentrionate', 'v. i.', 'To tend or point toward the north; to north.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septet', 'n.', 'Alt. of Septette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septette', 'n.', 'A set of seven persons or objects; as, a septet of\n   singers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septette', 'n.', 'A musical composition for seven instruments or seven\n   voices; -- called also septuor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septfoil', 'n.', 'A European herb, the tormentil. See Tormentil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septfoil', 'n.', 'An ornamental foliation having seven lobes. Cf.\n   Cinquefoil, Quarterfoil, and Trefoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septfoil', 'n.', 'A typical figure, consisting of seven equal segments of a\n   circle, used to denote the gifts of the Holy Chost, the seven\n   sacraments as recognized by the Roman Catholic Church, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septi-', '', 'A combining form meaning seven; as, septifolious,\n   seven-leaved; septi-lateral, seven-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septic', 'a.', 'Of the seventh degree or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septic', 'n.', 'A quantic of the seventh degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septic', 'a.', 'Alt. of Septical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septical', 'a.', 'Having power to promote putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septic', 'n.', 'A substance that promotes putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septicaemia', 'n.', 'A poisoned condition of the blood produced by the\n   absorption into it of septic or putrescent material; blood poisoning.\n   It is marked by chills, fever, prostration, and inflammation of the\n   different serous membranes and of the lungs, kidneys, and other organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septically', 'adv.', 'In a septic manner; in a manner tending to promote\n   putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septicidal', 'a.', 'Dividing the partitions; -- said of a method of\n   dehiscence in which a pod splits through the partitions and is divided\n   into its component carpels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septicity', 'n.', 'Tendency to putrefaction; septic quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septifarious', 'a.', 'Turned in seven different ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septiferous', 'a.', 'Bearing a partition; -- said of the valves of a\n   capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septiferous', 'a.', 'Conveying putrid poison; as, the virulence of\n   septiferous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septifluous', 'a.', 'Flowing in seven streams; septemfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septifolious', 'a.', 'Having seven leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septiform', 'a.', 'Having the form of a septum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septifragal', 'a.', 'Breaking from the partitions; -- said of a method of\n   dehiscence in which the valves of a pod break away from the partitions,\n   and these remain attached to the common axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septilateral', 'a.', 'Having seven sides; as, a septilateral figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septillion', 'n.', 'According to the French method of numeration (which is\n   followed also in the United States), the number expressed by a unit\n   with twenty-four ciphers annexed. According to the English method, the\n   number expressed by a unit with forty-two ciphers annexed. See\n   Numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septimole', 'n.', 'A group of seven notes to be played in the time of four\n   or six.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septinsular', 'a.', 'Consisting of seven islands; as, the septinsular\n   republic of the Ionian Isles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septisyllable', 'n.', 'A word of seven syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septoic', 'a.', 'See Heptoic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septomaxillary', 'a.', 'Of or pertaining to the nasal septum and the\n   maxilla; situated in the region of these parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septomaxillary', 'n.', 'A small bone between the nasal septum and the\n   maxilla in many reptiles and amphibians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuagenarian', 'n.', 'A person who is seventy years of age; a\n   septuagenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuagenary', 'a.', 'Consisting of seventy; also, seventy years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuagenary', 'n.', 'A septuagenarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuagesima', 'n.', 'The third Sunday before Lent; -- so called because it\n   is about seventy days before Easter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuagesimal', 'a.', 'Consisting of seventy days, years, etc.; reckoned by\n   seventies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuagint', 'n.', 'A Greek version of the Old Testament; -- so called\n   because it was believed to be the work of seventy (or rather of\n   seventy-two) translators.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuary', 'n.', 'Something composed of seven; a week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septulate', 'a.', 'Having imperfect or spurious septa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septula', 'pl. ', 'of Septulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septulum', 'n.', 'A little septum; a division between small cavities or\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septa', 'pl. ', 'of Septum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septum', 'n.', 'A wall separating two cavities; a partition; as, the nasal\n   septum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septum', 'n.', 'A partition that separates the cells of a fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septum', 'n.', 'One of the radial calcareous plates of a coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septum', 'n.', 'One of the transverse partitions dividing the shell of a\n   mollusk, or of a rhizopod, into several chambers. See Illust. under\n   Nautilus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septum', 'n.', 'One of the transverse partitions dividing the body cavity\n   of an annelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuor', 'n.', 'A septet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuple', 'a.', 'Seven times as much; multiplied by seven; sevenfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septupled', 'imp. & p. p.', 'of Septuple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septupling', 'p. pr. & vb. n.', 'of Septuple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Septuple', 'v. t.', 'To multiply by seven; to make sevenfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulcher', 'n.', 'Alt. of Sepulchre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchre', 'n.', 'The place in which the dead body of a human being is\n   interred, or a place set apart for that purpose; a grave; a tomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchered', 'imp. & p. p.', 'of Sepulchre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchred', '', 'of Sepulchre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchering', 'p. pr. & vb. n.', 'of Sepulchre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchring', '', 'of Sepulchre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulcher', 'v. t.', 'Alt. of Sepulchre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchre', 'v. t.', 'To bury; to inter; to entomb; as, obscurely\n   sepulchered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchral', 'a.', 'Of or pertaining to burial, to the grave, or to\n   monuments erected to the memory of the dead; as, a sepulchral stone; a\n   sepulchral inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulchral', 'a.', 'Unnaturally low and grave; hollow in tone; -- said of\n   sound, especially of the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulture', 'n.', 'The act of depositing the dead body of a human being in\n   the grave; burial; interment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sepulture', 'n.', 'A sepulcher; a grave; a place of burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequacious', 'a.', 'Inclined to follow a leader; following; attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequacious', 'a.', 'Hence, ductile; malleable; pliant; manageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequacious', 'a.', 'Having or observing logical sequence; logically\n   consistent and rigorous; consecutive in development or transition of\n   thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequaciousness', 'n.', 'Quality of being sequacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequacity', 'n.', 'Quality or state of being sequacious; sequaciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequel', 'n.', 'That which follows; a succeeding part; continuation; as,\n   the sequel of a mans advantures or history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequel', 'n.', 'Consequence; event; effect; result; as, let the sun cease,\n   fail, or swerve, and the sequel would be ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequel', 'n.', 'Conclusion; inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequelae', 'pl. ', 'of Sequela');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequela', 'n.', 'One who, or that which, follows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequela', 'n.', 'An adherent, or a band or sect of adherents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequela', 'n.', 'That which follows as the logical result of reasoning;\n   inference; conclusion; suggestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequela', 'n.', 'A morbid phenomenon left as the result of a disease; a\n   disease resulting from another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'The state of being sequent; succession; order of\n   following; arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'That which follows or succeeds as an effect; sequel;\n   consequence; result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'Simple succession, or the coming after in time, without\n   asserting or implying causative energy; as, the reactions of chemical\n   agents may be conceived as merely invariable sequences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'Any succession of chords (or harmonic phrase) rising or\n   falling by the regular diatonic degrees in the same scale; a succession\n   of similar harmonic steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'A melodic phrase or passage successively repeated one\n   tone higher; a rosalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'A hymn introduced in the Mass on certain festival days,\n   and recited or sung immediately before the gospel, and after the\n   gradual or introit, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'Three or more cards of the same suit in immediately\n   consecutive order of value; as, ace, king, and queen; or knave, ten,\n   nine, and eight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequence', 'n.', 'All five cards, of a hand, in consecutive order as to\n   value, but not necessarily of the same suit; when of one suit, it is\n   called a sequence flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequent', 'a.', 'Following; succeeding; in continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequent', 'a.', 'Following as an effect; consequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequent', 'n.', 'A follower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequent', 'n.', 'That which follows as a result; a sequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequential', 'a.', 'Succeeding or following in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestered', 'imp. & p. p.', 'of Sequester');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestering', 'p. pr. & vb. n.', 'of Sequester');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'v. t.', 'To separate from the owner for a time; to take from\n   parties in controversy and put into the possession of an indifferent\n   person; to seize or take possession of, as property belonging to\n   another, and hold it till the profits have paid the demand for which it\n   is taken, or till the owner has performed the decree of court, or\n   clears himself of contempt; in international law, to confiscate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'v. t.', 'To cause (one) to submit to the process of\n   sequestration; to deprive (one) of ones estate, property, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'v. t.', 'To set apart; to put aside; to remove; to separate\n   from other things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'v. t.', 'To cause to retire or withdraw into obscurity; to\n   seclude; to withdraw; -- often used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'v. i.', 'To withdraw; to retire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'v. i.', 'To renounce (as a widow may) any concern with the\n   estate of her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'n.', 'Sequestration; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'n.', 'A person with whom two or more contending parties\n   deposit the subject matter of the controversy; one who mediates between\n   two parties; a mediator; an umpire or referee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequester', 'n.', 'Same as Sequestrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestered', 'a.', 'Retired; secluded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestrable', 'a.', 'Capable of being sequestered; subject or liable to\n   sequestration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestral', 'a.', 'Of or pertaining to a sequestrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestrated', 'imp. & p. p.', 'of Sequestrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestrating', 'p. pr. & vb. n.', 'of Sequestrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestrate', 'v. t.', 'To sequester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seguestration', 'n.', 'The act of separating, or setting aside, a thing in\n   controversy from the possession of both the parties that contend for\n   it, to be delivered to the one adjudged entitled to it. It may be\n   voluntary or involuntary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seguestration', 'n.', 'A prerogative process empowering certain\n   commissioners to take and hold a defendants property and receive the\n   rents and profits thereof, until he clears himself of a contempt or\n   performs a decree of the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seguestration', 'n.', 'A kind of execution for a rent, as in the case of a\n   beneficed clerk, of the profits of a benefice, till he shall have\n   satisfied some debt established by decree; the gathering up of the\n   fruits of a benefice during a vacancy, for the use of the next\n   incumbent; the disposing of the goods, by the ordinary, of one who is\n   dead, whose estate no man will meddle with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seguestration', 'n.', 'The seizure of the property of an individual for the\n   use of the state; particularly applied to the seizure, by a belligerent\n   power, of debts due from its subjects to the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seguestration', 'n.', 'The state of being separated or set aside;\n   separation; retirement; seclusion from society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seguestration', 'n.', 'Disunion; disjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestrator', 'n.', 'One who sequesters property, or takes the possession\n   of it for a time, to satisfy a demand out of its rents or profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestrator', 'n.', 'One to whom the keeping of sequestered property is\n   committed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestra', 'pl. ', 'of Sequestrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequestrum', 'n.', 'A portion of dead bone which becomes separated from the\n   sound portion, as in necrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequin', 'n.', 'An old gold coin of Italy and Turkey. It was first struck\n   at Venice about the end of the 13th century, and afterward in the other\n   Italian cities, and by the Levant trade was introduced into Turkey. It\n   is worth about 9s. 3d. sterling, or about $2.25. The different kinds\n   vary somewhat in value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequoia', 'n.', 'A genus of coniferous trees, consisting of two species,\n   Sequoia Washingtoniana, syn. S. gigantea, the \"big tree\" of California,\n   and S. sempervirens, the redwood, both of which attain an immense\n   height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sequoiene', 'n.', 'A hydrocarbon (C13H10) obtained in white fluorescent\n   crystals, in the distillation products of the needles of the California\n   \"big tree\" (Sequoia gigantea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraglio', 'n.', 'An inclosure; a place of separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraglio', 'n.', 'The palace of the Grand Seignior, or Turkish sultan, at\n   Constantinople, inhabited by the sultan himself, and all the officers\n   and dependents of his court. In it are also kept the females of the\n   harem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraglio', 'n.', 'A harem; a place for keeping wives or concubines;\n   sometimes, loosely, a place of licentious pleasure; a house of\n   debauchery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serai', 'n.', 'A palace; a seraglio; also, in the East, a place for the\n   accommodation of travelers; a caravansary, or rest house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seralbumen', 'n.', 'Serum albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serang', 'n.', 'The boatswain of a Lascar or East Ondian crew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serape', 'n.', 'A blanket or shawl worn as an outer garment by the Spanish\n   Americans, as in Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphs', 'pl. ', 'of Seraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphim', 'pl. ', 'of Seraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraph', 'n.', 'One of an order of celestial beings, each having three\n   pairs of wings. In ecclesiastical art and in poetry, a seraph is\n   represented as one of a class of angels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphic', 'a.', 'Alt. of Seraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphical', 'a.', 'Of or pertaining to a seraph; becoming, or suitable to,\n   a seraph; angelic; sublime; pure; refined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphicism', 'n.', 'The character, quality, or state of a seraph;\n   seraphicalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphim', 'n.', 'The Hebrew plural of Seraph. Cf. Cherubim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphina', 'n.', 'A seraphine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraphine', 'n.', 'A wind instrument whose sounding parts are reeds,\n   consisting of a thin tongue of brass playing freely through a slot in a\n   plate. It has a case, like a piano, and is played by means of a similar\n   keybord, the bellows being worked by the foot. The melodeon is a\n   portable variety of this instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serapis', 'n.', 'An Egyptian deity, at first a symbol of the Nile, and so\n   of fertility; later, one of the divinities of the lower world. His\n   worship was introduced into Greece and Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraskier', 'n.', 'A general or commander of land forces in the Turkish\n   empire; especially, the commander-in-chief of minister of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seraskierate', 'n.', 'The office or authority of a seraskier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serbonian', 'a.', 'Relating to the lake of Serbonis in Egypt, which by\n   reason of the sand blowing into it had a deceptive appearance of being\n   solid land, but was a bog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sere', 'a.', 'Dry; withered. Same as Sear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sere', 'n.', 'Claw; talon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serein', 'n.', 'A mist, or very fine rain, which sometimes falls from a\n   clear sky a few moments after sunset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenade', 'n.', 'Music sung or performed in the open air at nights; --\n   usually applied to musical entertainments given in the open air at\n   night, especially by gentlemen, in a spirit of gallantry, under the\n   windows of ladies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenade', 'n.', 'A piece of music suitable to be performed at such times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenaded', 'imp. & p. p.', 'of Serenade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenading', 'p. pr. & vb. n.', 'of Serenade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenade', 'v. t.', 'To entertain with a serenade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenade', 'v. i.', 'To perform a serenade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenader', 'n.', 'One who serenades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenata', 'n.', 'Alt. of Serenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenate', 'n.', 'A piece of vocal music, especially one on an amoreus\n   subject; a serenade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serene', 'a.', 'Bright; clear; unabscured; as, a serene sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serene', 'a.', 'Calm; placid; undisturbed; unruffled; as, a serene aspect;\n   a serene soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serene', 'n.', 'Serenity; clearness; calmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serene', 'n.', 'Evening air; night chill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serene', 'v. t.', 'To make serene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenely', 'adv.', 'In a serene manner; clearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenely', 'adv.', 'With unruffled temper; coolly; calmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sereneness', 'n.', 'Serenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenitude', 'n.', 'Serenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenity', 'n.', 'The quality or state of being serene; clearness and\n   calmness; quietness; stillness; peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serenity', 'n.', 'Calmness of mind; eveness of temper; undisturbed state;\n   coolness; composure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serf', 'v. t.', 'A servant or slave employed in husbandry, and in some\n   countries attached to the soil and transferred with it, as formerly in\n   Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serfage', 'n.', 'Alt. of Serfdom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serfdom', 'n.', 'The state or condition of a serf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serfhood', 'n.', 'Alt. of Serfism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serfism', 'n.', 'Serfage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serge', 'n.', 'A woolen twilled stuff, much used as material for clothing\n   for both sexes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serge', 'n.', 'A large wax candle used in the ceremonies of various\n   churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeancies', 'pl. ', 'of Sergeancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeancy', 'n.', 'The office of a sergeant; sergeantship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeant', 'n.', 'Formerly, in England, an officer nearly answering to the\n   more modern bailiff of the hundred; also, an officer whose duty was to\n   attend on the king, and on the lord high steward in court, to arrest\n   traitors and other offenders. He is now called sergeant-at-arms, and\n   two of these officers, by allowance of the sovereign, attend on the\n   houses of Parliament (one for each house) to execute their commands,\n   and another attends the Court Chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeant', 'n.', 'In a company, battery, or troop, a noncommissioned\n   officer next in rank above a corporal, whose duty is to instruct\n   recruits in discipline, to form the ranks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeant', 'n.', 'A lawyer of the highest rank, answering to the doctor of\n   the civil law; -- called also serjeant at law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeant', 'n.', 'A title sometimes given to the servants of the sovereign;\n   as, sergeant surgeon, that is, a servant, or attendant, surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeant', 'n.', 'The cobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeantcy', 'n.', 'Same as Sergeancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeantry', 'n.', 'See Sergeanty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeantship', 'n.', 'The office of sergeant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sergeanty', 'n.', 'Tenure of lands of the crown by an honorary kind of\n   service not due to any lord, but to the king only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serial', 'a.', 'Of or pertaining to a series; consisting of a series;\n   appearing in successive parts or numbers; as, a serial work or\n   publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serial', 'a.', 'Of or pertaining to rows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serial', 'n.', 'A publication appearing in a series or succession of part;\n   a tale, or other writing, published in successive numbers of a\n   periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seriality', 'n.', 'The quality or state of succession in a series;\n   sequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serially', 'adv.', 'In a series, or regular order; in a serial manner; as,\n   arranged serially; published serially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seriate', 'a.', 'Arranged in a series or succession; pertaining to a\n   series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seriatim', 'adv.', 'In regular order; one after the other; severally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seriation', 'n.', 'Arrangement or position in a series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sericeous', 'a.', 'Of or pertaining to silk; consisting of silk; silky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sericeous', 'a.', 'Covered with very soft hairs pressed close to the\n   surface; as, a sericeous leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sericeous', 'a.', 'Having a silklike luster, usually due to fine, close\n   hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sericin', 'n.', 'A gelatinous nitrogenous material extracted from crude\n   silk and other similar fiber by boiling water; -- called also silk\n   gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sericite', 'n.', 'A kind of muscovite occuring in silky scales having a\n   fibrous structure. It is characteristic of sericite schist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sericterium', 'n.', 'A silk gland, as in the silkworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sericulture', 'n.', 'The raising of silkworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serie', 'n.', 'Series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seriema', 'n.', 'A large South American bird (Dicholophus, / Cariama\n   cristata) related to the cranes. It is often domesticated. Called also\n   cariama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Series', 'n.', 'A number of things or events standing or succeeding in\n   order, and connected by a like relation; sequence; order; course; a\n   succession of things; as, a continuous series of calamitous events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Series', 'n.', 'Any comprehensive group of animals or plants including\n   several subordinate related groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Series', 'n.', 'An indefinite number of terms succeeding one another, each\n   of which is derived from one or more of the preceding by a fixed law,\n   called the law of the series; as, an arithmetical series; a geometrical\n   series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serin', 'n.', 'A European finch (Serinus hortulanus) closely related to the\n   canary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serine', 'n.', 'A white crystalline nitrogenous substance obtained by the\n   action of dilute sulphuric acid on silk gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serio-comic', 'a.', 'Alt. of Serio-comical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serio-comical', 'a.', 'Having a mixture of seriousness and sport; serious\n   and comical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serious', 'a.', 'Grave in manner or disposition; earnest; thoughtful;\n   solemn; not light, gay, or volatile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serious', 'a.', 'Really intending what is said; being in earnest; not\n   jesting or deceiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serious', 'a.', 'Important; weighty; not trifling; grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serious', 'a.', 'Hence, giving rise to apprehension; attended with danger;\n   as, a serious injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seriph', 'n.', 'See Ceriph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serjeant', '', 'Alt. of Serjeantcy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serjeantcy', '', 'See Sergeant, Sergeantcy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermocination', 'n.', 'The making of speeches or sermons; sermonizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermocinator', 'n.', 'One who makes sermons or speeches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermon', 'n.', 'A discourse or address; a talk; a writing; as, the sermons\n   of Chaucer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermon', 'n.', 'Specifically, a discourse delivered in public, usually by a\n   clergyman, for the purpose of religious instruction and grounded on\n   some text or passage of Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermon', 'n.', 'Hence, a serious address; a lecture on ones conduct or\n   duty; an exhortation or reproof; a homily; -- often in a depreciatory\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermon', 'v. i.', 'To speak; to discourse; to compose or deliver a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermon', 'v. t.', 'To discourse to or of, as in a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermon', 'v. t.', 'To tutor; to lecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermoneer', 'n.', 'A sermonizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermoner', 'n.', 'A preacher; a sermonizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonet', 'n.', 'A short sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonic', 'a.', 'Alt. of Sermonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonical', 'a.', 'Like, or appropriate to, a sermon; grave and didactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermoning', 'n.', 'The act of discoursing; discourse; instruction;\n   preaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonish', 'a.', 'Resembling a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonist', 'n.', 'See Sermonizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonized', 'imp. & p. p.', 'of Sermonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonizing', 'p. pr. & vb. n.', 'of Sermonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonize', 'v. i.', 'To compose or write a sermon or sermons; to preach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonize', 'v. i.', 'To inculcate rigid rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonize', 'v. t.', 'To preach or discourse to; to affect or influence by\n   means of a sermon or of sermons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sermonizer', 'n.', 'One who sermonizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serolin', 'n.', 'A peculiar fatty substance found in the blood, probably a\n   mixture of fats, cholesterin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serolin', 'n.', 'A body found in fecal matter and thought to be formed in\n   the intestines from the cholesterin of the bile; -- called also\n   stercorin, and stercolin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seron', 'n.', 'Alt. of Seroon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seroon', 'n.', 'Same as Ceroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serose', 'a.', 'Serous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serosity', 'n.', 'The quality or state of being serous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serosity', 'n.', 'A thin watery animal fluid, as synovial fluid and\n   pericardial fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serotine', 'n.', 'The European long-eared bat (Vesperugo serotinus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serotinous', 'a.', 'Appearing or blossoming later in the season than is\n   customary with allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serous', 'a.', 'Thin; watery; like serum; as the serous fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serous', 'a.', 'Of or pertaining to serum; as, the serous glands,\n   membranes, layers. See Serum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serow', 'n.', 'Alt. of Surrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrow', 'n.', 'The thar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpens', 'n.', 'A constellation represented as a serpent held by\n   Serpentarius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent', 'n.', 'Any reptile of the order Ophidia; a snake, especially a\n   large snake. See Illust. under Ophidia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent', 'n.', 'Fig.: A subtle, treacherous, malicious person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent', 'n.', 'A species of firework having a serpentine motion as it\n   passess through the air or along the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent', 'n.', 'The constellation Serpens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent', 'n.', 'A bass wind instrument, of a loud and coarse tone,\n   formerly much used in military bands, and sometimes introduced into the\n   orchestra; -- so called from its form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpented', 'imp. & p. p.', 'of Serpent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpenting', 'p. pr. & vb. n.', 'of Serpent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent', 'v. i.', 'To wind like a serpent; to crook about; to meander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent', 'v. t.', 'To wind; to encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentaria', 'a.', 'The fibrous aromatic root of the Virginia snakeroot\n   (Aristolochia Serpentaria).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentarius', 'n.', 'A constellation on the equator, lying between Scorpio\n   and Hercules; -- called also Ophiuchus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentiform', 'a.', 'Having the form of a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentigenous', 'a.', 'Bred of a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentine', 'a.', 'Resembling a serpent; having the shape or qualities of\n   a serpent; subtle; winding or turning one way and the other, like a\n   moving serpent; anfractuous; meandering; sinuous; zigzag; as,\n   serpentine braid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentine', 'n.', 'A mineral or rock consisting chiefly of the hydrous\n   silicate of magnesia. It is usually of an obscure green color, often\n   with a spotted or mottled appearance resembling a serpents skin.\n   Precious, or noble, serpentine is translucent and of a rich oil-green\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentine', 'n.', 'A kind of ancient cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentine', 'v. i.', 'To serpentize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentinely', 'adv.', 'In a serpentine manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentinian', 'n.', 'See 2d Ophite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentinize', 'v. t.', 'To convert (a magnesian silicate) into serpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentinous', 'a.', 'Relating to, or like, serpentine; as, a rock\n   serpentinous in character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentize', 'v. i.', 'To turn or bend like a serpent, first in one\n   direction and then in the opposite; to meander; to wind; to serpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentry', 'n.', 'A winding like a serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpentry', 'n.', 'A place inhabited or infested by serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpent-tongued', 'a.', 'Having a forked tongue, like a serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpet', 'n.', 'A basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpette', 'n.', 'A pruning knife with a curved blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpiginous', 'a.', 'Creeping; -- said of lesions which heal over one\n   portion while continuing to advance at another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpigo', 'n.', 'A dry, scaly eruption on the skin; especially, a ringworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpolet', 'n.', 'Wild thyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpulae', 'pl. ', 'of Serpula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpulas', 'pl. ', 'of Serpula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpula', 'n.', 'Any one of numerous species of tubicolous annelids of the\n   genus Serpula and allied genera of the family Serpulidae. They secrete\n   a calcareous tube, which is usually irregularly contorted, but is\n   sometimes spirally coiled. The worm has a wreath of plumelike and often\n   bright-colored gills around its head, and usually an operculum to close\n   the aperture of its tube when it retracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpulian', 'n.', 'Alt. of Serpulidan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpulidan', 'n.', 'A serpula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serpulite', 'n.', 'A fossil serpula shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serr', 'v. t.', 'To crowd, press, or drive together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serranoid', 'n.', 'Any fish of the family Serranidae, which includes the\n   striped bass, the black sea bass, and many other food fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serranoid', 'a.', 'Of or pertaining to the Serranidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrate', 'a.', 'Alt. of Serrated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrated', 'a.', 'Notched on the edge, like a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrated', 'a.', 'Beset with teeth pointing forwards or upwards; as,\n   serrate leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serration', 'n.', 'Condition of being serrate; formation in the shape of a\n   saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serration', 'n.', 'One of the teeth in a serrate or serrulate margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serratirostral', 'a.', 'Having a toothed bill, like that of a toucan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrator', 'n.', 'The ivory gull (Larus eburneus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrature', 'n.', 'A notching, like that between the teeth of a saw, in the\n   edge of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrature', 'n.', 'One of the teeth in a serrated edge; a serration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serricated', 'a.', 'Covered with fine silky down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serricorn', 'a.', 'Having serrated antenn/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serricorn', 'n.', 'Any one of a numerous tribe of beetles (Serricornia).\n   The joints of the antennae are prominent, thus producing a serrate\n   appearance. See Illust. under Antenna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serried', 'a.', 'Crowded; compact; dense; pressed together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrifera', 'n. pl.', 'A division of Hymenoptera comprising the sawflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrirostres', 'n. pl.', 'Same as Lamellirostres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrous', 'a.', 'Like the teeth off a saw; jagged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrula', 'n.', 'The red-breasted merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrulate', 'a.', 'Alt. of Serrulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrulated', 'a.', 'Finely serrate; having very minute teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrulation', 'n.', 'The state of being notched minutely, like a fine saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrulation', 'n.', 'One of the teeth in a serrulate margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serried', 'imp. & p. p.', 'of Serry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serrying', 'p. pr. & vb. n.', 'of Serry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serry', 'v. t.', 'To crowd; to press together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sertularia', 'n.', 'A genus of delicate branching hydroids having small\n   sessile hydrothecae along the sides of the branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sertularian', 'n.', 'Any species of Sertularia, or of Sertularidae, a\n   family of hydroids having branched chitinous stems and simple sessile\n   hydrothecae. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serum', 'n.', 'The watery portion of certain animal fluids, as blood, milk,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serum', 'n.', 'A thin watery fluid, containing more or less albumin,\n   secreted by the serous membranes of the body, such as the pericardium\n   and peritoneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servable', 'a.', 'Capable of being served.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servable', 'a.', 'Capable of being preserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servage', 'n.', 'Serfage; slavery; servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serval', 'n.', 'An African wild cat (Felis serval) of moderate size. It has\n   rather long legs and a tail of moderate length. Its color is tawny,\n   with black spots on the body and rings of black on the tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servaline', 'a.', 'Related to, or resembling, the serval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servant', 'n.', 'One who serves, or does services, voluntarily or on\n   compulsion; a person who is employed by another for menial offices, or\n   for other labor, and is subject to his command; a person who labors or\n   exerts himself for the benefit of another, his master or employer; a\n   subordinate helper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servant', 'n.', 'One in a state of subjection or bondage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servant', 'n.', 'A professed lover or suitor; a gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servant', 'v. t.', 'To subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servantess', 'n.', 'A maidservant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servantry', 'n.', 'A body of servants; servants, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Served', 'imp. & p. p.', 'of Serve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serving', 'p. pr. & vb. n.', 'of Serve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To work for; to labor in behalf of; to exert ones self\n   continuously or statedly for the benefit of; to do service for; to be\n   in the employment of, as an inferior, domestic, serf, slave, hired\n   assistant, official helper, etc.; specifically, in a religious sense,\n   to obey and worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To be subordinate to; to act a secondary part under; to\n   appear as the inferior of; to minister to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To be suitor to; to profess love to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To wait upon; to supply the wants of; to attend;\n   specifically, to wait upon at table; to attend at meals; to supply with\n   food; as, to serve customers in a shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'Hence, to bring forward, arrange, deal, or distribute, as\n   a portion of anything, especially of food prepared for eating; -- often\n   with up; formerly with in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To perform the duties belonging to, or required in or\n   for; hence, to be of use to; as, a curate may serve two churches; to\n   serve ones country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To contribute or conduce to; to promote; to be sufficient\n   for; to satisfy; as, to serve ones turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To answer or be (in the place of something) to; as, a\n   sofa serves one for a seat and a couch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To treat; to behave ones self to; to requite; to act\n   toward; as, he served me very ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To work; to operate; as, to serve the guns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To bring to notice, deliver, or execute, either actually\n   or constructively, in such manner as the law requires; as, to serve a\n   summons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To make legal service opon (a person named in a writ,\n   summons, etc.); as, to serve a witness with a subp/na.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To pass or spend, as time, esp. time of punishment; as,\n   to serve a term in prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To copulate with; to cover; as, a horse serves a mare; --\n   said of the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To lead off in delivering (the ball).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. t.', 'To wind spun yarn, or the like, tightly around (a rope or\n   cable, etc.) so as to protect it from chafing or from the weather. See\n   under Serving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. i.', 'To be a servant or a slave; to be employed in labor or\n   other business for another; to be in subjection or bondage; to render\n   menial service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. i.', 'To perform domestic offices; to be occupied with\n   household affairs; to prepare and dish up food, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. i.', 'To be in service; to do duty; to discharge the\n   requirements of an office or employment. Specifically, to act in the\n   public service, as a soldier, seaman. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. i.', 'To be of use; to answer a purpose; to suffice; to suit;\n   to be convenient or favorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serve', 'v. i.', 'To lead off in delivering the ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Server', 'n.', 'One who serves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Server', 'n.', 'A tray for dishes; a salver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servian', 'a.', 'Of or pertaining to Servia, a kingdom of Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servian', 'n.', 'A native or inhabitant of Servia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', '', 'Alt. of Service');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', '', 'A name given to several trees and shrubs of the genus Pyrus,\n   as Pyrus domestica and P. torminalis of Europe, the various species of\n   mountain ash or rowan tree, and the American shad bush (see Shad bush,\n   under Shad). They have clusters of small, edible, applelike berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'The act of serving; the occupation of a servant; the\n   performance of labor for the benefit of another, or at anothers\n   command; attendance of an inferior, hired helper, slave, etc., on a\n   superior, employer, master, or the like; also, spiritual obedience and\n   love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'The deed of one who serves; labor performed for another;\n   duty done or required; office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'Office of devotion; official religious duty performed;\n   religious rites appropriate to any event or ceremonial; as, a burial\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'Hence, a musical composition for use in churches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'Duty performed in, or appropriate to, any office or\n   charge; official function; hence, specifically, military or naval duty;\n   performance of the duties of a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'Useful office; advantage conferred; that which promotes\n   interest or happiness; benefit; avail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'Profession of respect; acknowledgment of duty owed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'The act and manner of bringing food to the persons who eat\n   it; order of dishes at table; also, a set or number of vessels\n   ordinarily used at table; as, the service was tardy and awkward; a\n   service of plate or glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'The act of bringing to notice, either actually or\n   constructively, in such manner as is prescribed by law; as, the service\n   of a subp/na or an attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'The materials used for serving a rope, etc., as spun yarn,\n   small lines, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'The act of serving the ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Service', 'n.', 'Act of serving or covering. See Serve, v. t., 13.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serviceable', 'a.', 'Doing service; promoting happiness, interest,\n   advantage, or any good; useful to any end; adapted to any good end use;\n   beneficial; advantageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serviceable', 'a.', 'Prepared for rendering service; capable of, or fit\n   for, the performance of duty; hence, active; diligent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serviceage', 'n.', 'Servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servient', 'a.', 'Subordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serviette', 'n.', 'A table napkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servile', 'a.', 'Of or pertaining to a servant or slave; befitting a\n   servant or a slave; proceeding from dependence; hence, meanly\n   submissive; slavish; mean; cringing; fawning; as, servile flattery;\n   servile fear; servile obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servile', 'a.', 'Held in subjection; dependent; enslaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servile', 'a.', 'Not belonging to the original root; as, a servile letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servile', 'a.', 'Not itself sounded, but serving to lengthen the preceeding\n   vowel, as e in tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servile', 'n.', 'An element which forms no part of the original root; --\n   opposed to radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servilely', 'adv.', 'In a servile manner; slavishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servileness', 'n.', 'Quality of being servile; servility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servility', 'n.', 'The quality or state of being servile; servileness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serving', '', 'a. & n. from Serve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servite', 'n.', 'One of the order of the Religious Servants of the Holy\n   Virgin, founded in Florence in 1223.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servifor', 'n.', 'One who serves; a servant; an attendant; one who acts\n   under another; a follower or adherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servifor', 'n.', 'An undergraduate, partly supported by the college funds,\n   whose duty it formerly was to wait at table. A servitor corresponded to\n   a sizar in Cambridge and Dublin universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servitorship', 'n.', 'The office, rank, or condition of a servitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servitude', 'n.', 'The state of voluntary or compulsory subjection to a\n   master; the condition of being bound to service; the condition of a\n   slave; slavery; bondage; hence, a state of slavish dependence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servitude', 'n.', 'Servants, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servitude', 'n.', 'A right whereby one thing is subject to another thing or\n   person for use or convenience, contrary to the common right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serviture', 'n.', 'Servants, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Servitute', 'n.', 'Servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Serye', 'n.', 'A series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesame', 'n.', 'Either of two annual herbaceous plants of the genus Sesamum\n   (S. Indicum, and S. orientale), from the seeds of which an oil is\n   expressed; also, the small obovate, flattish seeds of these plants,\n   sometimes used as food. See Benne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesamoid', 'a.', 'Resembling in shape the seeds of sesame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesamoid', 'a.', 'Of or pertaining to the sesamoid bones or cartilages;\n   sesamoidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesamoid', 'n.', 'A sesamoid bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesamoidal', 'a.', 'Sesamoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesban', 'n.', 'A leguminous shrub (Sesbania aculeata) which furnishes a\n   fiber used for making ropes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesqui-', '', 'A combining form (also used adjectively) denoting that three\n   atoms or equivalents of the substance to the name of which it is\n   prefixed are combined with two of some other element or radical; as,\n   sesquibromide, sesquicarbonate, sesquichloride, sesquioxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquialter', 'a.', 'Sesquialteral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquialter', 'n.', 'Alt. of Sesquialtera');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquialtera', 'n.', 'A stop on the organ, containing several ranks of\n   pipes which reenforce some of the high harmonics of the ground tone,\n   and make the sound more brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquialteral', 'a.', 'Alt. of Sesquialterate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquialterate', 'a.', 'Once and a half times as great as another; having\n   the ratio of one and a half to one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquialterous', 'a.', 'Sesquialteral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquibasic', 'a.', 'Containing, or acting as, a base in the proportions of\n   a sesqui compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquiduplicate', 'a.', 'Twice and a half as great (as another thing);\n   having the ratio of two and a half to one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquioxide', 'n.', 'An oxide containing three atoms of oxygen with two\n   atoms (or radicals) of some other substance; thus, alumina, Al2O3 is a\n   sesquioxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquipedal', 'a.', 'Alt. of Sesquipedalian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquipedalian', 'a.', 'Measuring or containing a foot and a half; as, a\n   sesquipedalian pygmy; -- sometimes humorously applied to long words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquipedalianism', 'n.', 'Alt. of Sesquipedalism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquipedalism', 'n.', 'Sesquipedality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesqyipedality', 'n.', 'The quality or condition of being sesquipedal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesqyipedality', 'n.', 'The use of sesquipedalian words; style\n   characterized by the use of long words; sesquipedalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquiplicate', 'a.', 'Subduplicate of the triplicate; -- a term applied to\n   ratios; thus, a and a are in the sesquiplicate ratio of b and b, when\n   a is to a as the square root of the cube of b is to the square root of\n   the cube of b, or a:a::b3:b3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquisalt', 'n.', 'A salt derived from a sesquioxide base, or made up on\n   the proportions of a sesqui compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquisulphide', 'n.', 'A sulphide, analogous to a sesquioxide, containing\n   three atoms of sulphur to two of the other ingredient; -- formerly\n   called also sesquisulphuret; as, orpiment, As2S3 is arsenic\n   sesquisulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquitertial', 'a.', 'Sesquitertian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquitertian', 'a.', 'Alt. of Sesquitertianal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquitertianal', 'a.', 'Having the ratio of one and one third to one (as 4\n   : 3).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesquitone', 'n.', 'A minor third, or interval of three semitones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sess', 'v. t.', 'To lay a tax upon; to assess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sess', 'n.', 'A tax; an assessment. See Cess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sessa', 'interj.', 'Hurry; run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sessile', 'a.', 'Attached without any sensible projecting support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sessile', 'a.', 'Resting directly upon the main stem or branch, without a\n   petiole or footstalk; as, a sessile leaf or blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sessile', 'a.', 'Permanently attached; -- said of the gonophores of certain\n   hydroids which never became detached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sessile-eyed', 'a.', 'Having eyes which are not elevated on a stalk; --\n   opposed to stalk-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Session', 'n.', 'The act of sitting, or the state of being seated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Session', 'n.', 'The actual sitting of a court, council, legislature, etc.,\n   or the actual assembly of the members of such a body, for the\n   transaction of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Session', 'n.', 'Hence, also, the time, period, or term during which a\n   court, council, legislature, etc., meets daily for business; or, the\n   space of time between the first meeting and the prorogation or\n   adjournment; thus, a session of Parliaments is opened with a speech\n   from the throne, and closed by prorogation. The session of a judicial\n   court is called a term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sessional', 'a.', 'Of or pertaining to a session or sessions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesspool', 'n.', 'Same as Cesspool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sesterce', 'n.', 'A Roman coin or denomination of money, in value the\n   fourth part of a denarius, and originally containing two asses and a\n   half, afterward four asses, -- equal to about two pence sterling, or\n   four cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sestet', 'n.', 'A piece of music composed for six voices or six\n   instruments; a sextet; -- called also sestuor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sestet', 'n.', 'The last six lines of a sonnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sestetto', 'n.', 'A sestet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sestine', 'n.', 'See Sextain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sestuor', 'n.', 'A sestet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'imp. & p. p.', 'of Set');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setting', 'p. pr. & vb. n.', 'of Set');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To cause to sit; to make to assume a specified position or\n   attitude; to give site or place to; to place; to put; to fix; as, to\n   set a house on a stone foundation; to set a book on a shelf; to set a\n   dish on a table; to set a chest or trunk on its bottom or on end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'Hence, to attach or affix (something) to something else, or\n   in or upon a certain place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To make to assume specified place, condition, or\n   occupation; to put in a certain condition or state (described by the\n   accompanying words); to cause to be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To fix firmly; to make fast, permanent, or stable; to\n   render motionless; to give an unchanging place, form, or condition to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To cause to stop or stick; to obstruct; to fasten to a\n   spot; hence, to occasion difficulty to; to embarrass; as, to set a\n   coach in the mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To fix beforehand; to determine; hence, to make unyielding\n   or obstinate; to render stiff, unpliant, or rigid; as, to set ones\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To fix in the ground, as a post or a tree; to plant; as, to\n   set pear trees in an orchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To fix, as a precious stone, in a border of metal; to place\n   in a setting; hence, to place in or amid something which serves as a\n   setting; as, to set glass in a sash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To render stiff or solid; especially, to convert into curd;\n   to curdle; as, to set milk for cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To put into a desired position or condition; to adjust; to\n   regulate; to adapt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To put in order in a particular manner; to prepare; as, to\n   set (that is, to hone) a razor; to set a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To extend and bring into position; to spread; as, to set\n   the sails of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To give a pitch to, as a tune; to start by fixing the\n   keynote; as, to set a psalm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To reduce from a dislocated or fractured state; to replace;\n   as, to set a broken bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To make to agree with some standard; as, to set a watch or\n   a clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To lower into place and fix solidly, as the blocks of cut\n   stone in a structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To stake at play; to wager; to risk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To fit with music; to adapt, as words to notes; to prepare\n   for singing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To determine; to appoint; to assign; to fix; as, to set a\n   time for a meeting; to set a price on a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To adorn with something infixed or affixed; to stud; to\n   variegate with objects placed here and there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To value; to rate; -- with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To point out the seat or position of, as birds, or other\n   game; -- said of hunting dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To establish as a rule; to furnish; to prescribe; to\n   assign; as, to set an example; to set lessons to be learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To suit; to become; as, it sets him ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. t.', 'To compose; to arrange in words, lines, etc.; as, to set\n   type; to set a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To pass below the horizon; to go down; to decline; to sink\n   out of sight; to come to an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To fit music to words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To place plants or shoots in the ground; to plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To be fixed for growth; to strike root; to begin to\n   germinate or form; as, cuttings set well; the fruit has set well (i.\n   e., not blasted in the blossom).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To become fixed or rigid; to be fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To congeal; to concrete; to solidify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To have a certain direction in motion; to flow; to move on;\n   to tend; as, the current sets to the north; the tide sets to the\n   windward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To begin to move; to go out or forth; to start; -- now\n   followed by out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To indicate the position of game; -- said of a dog; as, the\n   dog sets well; also, to hunt game by the aid of a setter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To apply ones self; to undertake earnestly; -- now\n   followed by out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'v. i.', 'To fit or suit one; to sit; as, the coat sets well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'a.', 'Fixed in position; immovable; rigid; as, a set line; a set\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'a.', 'Firm; unchanging; obstinate; as, set opinions or prejudices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'a.', 'Regular; uniform; formal; as, a set discourse; a set battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'a.', 'Established; prescribed; as, set forms of prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'a.', 'Adjusted; arranged; formed; adapted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'The act of setting, as of the sun or other heavenly body;\n   descent; hence, the close; termination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'That which is set, placed, or fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A young plant for growth; as, a set of white thorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'That which is staked; a wager; a venture; a stake; hence, a\n   game at venture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'Permanent change of figure in consequence of excessive strain,\n   as from compression, tension, bending, twisting, etc.; as, the set of a\n   spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A kind of punch used for bending, indenting, or giving shape\n   to, metal; as, a saw set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A piece placed temporarily upon the head of a pile when the\n   latter cannot be reached by the weight, or hammer, except by means of\n   such an intervening piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A short steel spike used for driving the head of a nail below\n   the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A number of things of the same kind, ordinarily used or\n   classed together; a collection of articles which naturally complement\n   each other, and usually go together; an assortment; a suit; as, a set\n   of chairs, of china, of surgical or mathematical instruments, of books,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A number of persons associated by custom, office, common\n   opinion, quality, or the like; a division; a group; a clique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'Direction or course; as, the set of the wind, or of a current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'In dancing, the number of persons necessary to execute a\n   quadrille; also, the series of figures or movements executed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'The deflection of a tooth, or of the teeth, of a saw, which\n   causes the the saw to cut a kerf, or make an opening, wider than the\n   blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A young oyster when first attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'Collectively, the crop of young oysters in any locality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'A series of as many games as may be necessary to enable one\n   side to win six. If at the end of the tenth game the score is a tie,\n   the set is usually called a deuce set, and decided by an application of\n   the rules for playing off deuce in a game. See Deuce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set', 'n.', 'That dimension of the body of a type called by printers the\n   width.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setae', 'pl. ', 'of Seta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seta', 'n.', 'Any slender, more or less rigid, bristlelike organ or part;\n   as the hairs of a caterpillar, the slender spines of a crustacean, the\n   hairlike processes of a protozoan, the bristles or stiff hairs on the\n   leaves of some plants, or the pedicel of the capsule of a moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seta', 'n.', 'One of the movable chitinous spines or hooks of an annelid.\n   They usually arise in clusters from muscular capsules, and are used in\n   locomotion and for defense. They are very diverse in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seta', 'n.', 'One of the spinelike feathers at the base of the bill of\n   certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setaceous', 'a.', 'Set with, or consisting of, bristles; bristly; as, a\n   stiff, setaceous tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setaceous', 'a.', 'Bristelike in form or texture; as, a setaceous feather;\n   a setaceous leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setback', 'n.', 'Offset, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setback', 'n.', 'A backset; a countercurrent; an eddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setback', 'n.', 'A backset; a check; a repulse; a reverse; a relapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setbolt', 'n.', 'An iron pin, or bolt, for fitting planks closely together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setbolt', 'n.', 'A bolt used for forcing another bolt out of its hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setdown', 'n.', 'The humbling of a person by act or words, especially by a\n   retort or a reproof; the retort or the reproof which has such effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setee', 'n.', 'See 2d Settee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seten', '', 'obs. imp. pl. of Sit. Sat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setewale', 'n.', 'See Cetewale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-fair', 'n.', 'In plastering, a particularly good troweled surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setfoil', 'n.', 'See Septfoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sethen', 'adv. & conj.', 'See Since.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sethic', 'a.', 'See Sothic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setiferous', 'a.', 'Producing, or having one or more, bristles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setiform', 'a.', 'Having the form or structure of setae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setiger', 'n.', 'An annelid having setae; a chaetopod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setigerous', 'a.', 'Covered with bristles; having or bearing a seta or\n   setae; setiferous; as, setigerous glands; a setigerous segment of an\n   annelid; specifically (Bot.), tipped with a bristle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setim', 'n.', 'See Shittim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setiparous', 'a.', 'Producing setae; -- said of the organs from which the\n   setae of annelids arise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setireme', 'n.', 'A swimming leg (of an insect) having a fringe of hairs on\n   the margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setness', 'n.', 'The quality or state of being set; formality; obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-off', 'n.', 'That which is set off against another thing; an offset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-off', 'n.', 'That which is used to improve the appearance of anything;\n   a decoration; an ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-off', 'n.', 'A counterclaim; a cross debt or demand; a distinct claim\n   filed or set up by the defendant against the plaintiffs demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-off', 'n.', 'Same as Offset, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-off', 'n.', 'See Offset, 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seton', 'n.', 'A few silk threads or horsehairs, or a strip of linen or the\n   like, introduced beneath the skin by a knife or needle, so as to form\n   an issue; also, the issue so formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setose', 'a.', 'Alt. of Setous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setous', 'a.', 'Thickly set with bristles or bristly hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setout', 'n.', 'A display, as of plate, equipage, etc.; that which is\n   displayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-stitched', 'a.', 'Stitched according to a formal pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sett', 'n.', 'See Set, n., 2 (e) and 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settee', 'n.', 'A long seat with a back, -- made to accommodate several\n   persons at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settee', 'n.', 'A vessel with a very long, sharp prow, carrying two or\n   three masts with lateen sails, -- used in the Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setter', 'n.', 'One who, or that which, sets; -- used mostly in composition\n   with a noun, as typesetter; or in combination with an adverb, as a\n   setter on (or inciter), a setter up, a setter forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setter', 'n.', 'A hunting dog of a special breed originally derived from a\n   cross between the spaniel and the pointer. Modern setters are usually\n   trained to indicate the position of game birds by standing in a fixed\n   position, but originally they indicated it by sitting or crouching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setter', 'n.', 'One who hunts victims for sharpers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setter', 'n.', 'One who adapts words to music in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setter', 'n.', 'An adornment; a decoration; -- with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setter', 'n.', 'A shallow seggar for porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setter', 'v. t.', 'To cut the dewlap (of a cow or an ox), and to insert a\n   seton, so as to cause an issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setterwort', 'n.', 'The bears-foot (Helleborus f/tidus); -- so called\n   because the root was used in settering, or inserting setons into the\n   dewlaps of cattle. Called also pegroots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setting', 'n.', 'The act of one who, or that which, sets; as, the setting\n   of type, or of gems; the setting of the sun; the setting (hardening) of\n   moist plaster of Paris; the setting (set) of a current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setting', 'n.', 'The act of marking the position of game, as a setter does;\n   also, hunting with a setter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setting', 'n.', 'Something set in, or inserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setting', 'n.', 'That in which something, as a gem, is set; as, the gold\n   setting of a jeweled pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'A seat of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'A bench; especially, a bench with a high back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'A place made lower than the rest; a wide step or platform\n   lower than some other part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settled', 'imp. & p. p.', 'of Settle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settling', 'p. pr. & vb. n.', 'of Settle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To place in a fixed or permanent condition; to make firm,\n   steady, or stable; to establish; to fix; esp., to establish in life; to\n   fix in business, in a home, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To establish in the pastoral office; to ordain or install\n   as pastor or rector of a church, society, or parish; as, to settle a\n   minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To cause to be no longer in a disturbed condition; to\n   render quiet; to still; to calm; to compose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To clear of dregs and impurities by causing them to sink;\n   to render pure or clear; -- said of a liquid; as, to settle coffee, or\n   the grounds of coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To restore or bring to a smooth, dry, or passable\n   condition; -- said of the ground, of roads, and the like; as, clear\n   weather settles the roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To cause to sink; to lower; to depress; hence, also, to\n   render close or compact; as, to settle the contents of a barrel or bag\n   by shaking it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To determine, as something which is exposed to doubt or\n   question; to free from unscertainty or wavering; to make sure, firm, or\n   constant; to establish; to compose; to quiet; as, to settle the mind\n   when agitated; to settle questions of law; to settle the succession to\n   a throne; to settle an allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To adjust, as something in discussion; to make up; to\n   compose; to pacify; as, to settle a quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To adjust, as accounts; to liquidate; to balance; as, to\n   settle an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'Hence, to pay; as, to settle a bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'n.', 'To plant with inhabitants; to colonize; to people; as, the\n   French first settled Canada; the Puritans settled New England; Plymouth\n   was settled in 1620.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To become fixed or permanent; to become stationary; to\n   establish ones self or itself; to assume a lasting form, condition,\n   direction, or the like, in place of a temporary or changing state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To fix ones residence; to establish a dwelling place or\n   home; as, the Saxons who settled in Britain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To enter into the married state, or the state of a\n   householder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To be established in an employment or profession; as, to\n   settle in the practice of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To become firm, dry, and hard, as the ground after the\n   effects of rain or frost have disappeared; as, the roads settled late\n   in the spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To become clear after being turbid or obscure; to\n   clarify by depositing matter held in suspension; as, the weather\n   settled; wine settles by standing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To sink to the bottom; to fall to the bottom, as dregs\n   of a liquid, or the sediment of a reserveir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To sink gradually to a lower level; to subside, as the\n   foundation of a house, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To become calm; to cease from agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To adjust differences or accounts; to come to an\n   agreement; as, he has settled with his creditors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settle', 'v. i.', 'To make a jointure for a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settledness', 'n.', 'The quality or state of being settled; confirmed\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'The act of setting, or the state of being settled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'Establishment in life, in business, condition, etc.;\n   ordination or installation as pastor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'The act of peopling, or state of being peopled; act of\n   planting, as a colony; colonization; occupation by settlers; as, the\n   settlement of a new country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'The act or process of adjusting or determining;\n   composure of doubts or differences; pacification; liquidation of\n   accounts; arrangement; adjustment; as, settlement of a controversy, of\n   accounts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'Bestowal, or giving possession, under legal sanction;\n   the act of giving or conferring anything in a formal and permanent\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'A disposition of property for the benefit of some\n   person or persons, usually through the medium of trustees, and for the\n   benefit of a wife, children, or other relatives; jointure granted to a\n   wife, or the act of granting it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'That which settles, or is settled, established, or\n   fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'Matter that subsides; settlings; sediment; lees; dregs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'A colony newly established; a place or region newly\n   settled; as, settlement in the West.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'That which is bestowed formally and permanently; the\n   sum secured to a person; especially, a jointure made to a woman at her\n   marriage; also, in the United States, a sum of money or other property\n   formerly granted to a pastor in additional to his salary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'The gradual sinking of a building, whether by the\n   yielding of the ground under the foundation, or by the compression of\n   the joints or the material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'Fractures or dislocations caused by settlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settlement', 'n.', 'A settled place of abode; residence; a right growing\n   out of residence; legal residence or establishment of a person in a\n   particular parish or town, which entitles him to maintenance if a\n   pauper, and subjects the parish or town to his support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settler', 'n.', 'One who settles, becomes fixed, established, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settler', 'n.', 'Especially, one who establishes himself in a new region or\n   a colony; a colonist; a planter; as, the first settlers of New England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settler', 'n.', 'That which settles or finishes; hence, a blow, etc., which\n   settles or decides a contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settler', 'n.', 'A vessel, as a tub, in which something, as pulverized ore\n   suspended in a liquid, is allowed to settle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settling', 'n.', 'The act of one who, or that which, settles; the act of\n   establishing ones self, of colonizing, subsiding, adjusting, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Settling', 'n.', 'That which settles at the bottom of a liquid; lees;\n   dregs; sediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Set-to', 'n.', 'A contest in boxing, in an argument, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setulae', 'pl. ', 'of Setula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setula', 'n.', 'A small, short hair or bristle; a small seta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setule', 'n.', 'A setula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setulose', 'a.', 'Having small bristles or setae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Setwall', 'n.', 'A plant formerly valued for its restorative qualities\n   (Valeriana officinalis, or V. Pyrenaica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seven', 'a.', 'One more than six; six and one added; as, seven days make\n   one week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seven', 'n.', 'The number greater by one than six; seven units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seven', 'n.', 'A symbol representing seven units, as 7, or vii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sevenfold', 'a.', 'Repeated seven times; having seven thicknesses;\n   increased to seven times the size or amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sevenfold', 'adv.', 'Seven times as much or as often.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sevennight', 'n.', 'A week; any period of seven consecutive days and\n   nights. See Sennight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sevenscore', 'n. & a.', 'Seven times twenty, that is, a hundred and forty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seven-shooter', 'n.', 'A firearm, esp. a pistol, with seven barrels or\n   chambers for cartridges, or one capable of firing seven shots without\n   reloading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeen', 'a.', 'One more than sixteen; ten and seven added; as,\n   seventeen years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeen', 'n.', 'The number greater by one than sixteen; the sum of ten\n   and seven; seventeen units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeen', 'n.', 'A symbol denoting seventeen units, as 17, or xvii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeenth', 'a.', 'Next in order after the sixteenth; coming after\n   sixteen others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeenth', 'a.', 'Constituting or being one of seventeen equal parts\n   into which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeenth', 'n.', 'The next in order after the sixteenth; one coming\n   after sixteen others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeenth', 'n.', 'The quotient of a unit divided by seventeen; one of\n   seventeen equal parts or divisions of one whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventeenth', 'n.', 'An interval of two octaves and a third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventh', 'a.', 'Next in order after the sixth;; coming after six others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventh', 'a.', 'Constituting or being one of seven equal parts into which\n   anything is divided; as, the seventh part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventh', 'n.', 'One next in order after the sixth; one coming after six\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventh', 'n.', 'The quotient of a unit divided by seven; one of seven\n   equal parts into which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventh', 'n.', 'An interval embracing seven diatonic degrees of the scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventh', 'n.', 'A chord which includes the interval of a seventh whether\n   major, minor, or diminished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seven-thirties', 'n. pl.', 'A name given to three several issues of United\n   States Treasury notes, made during the Civil War, in denominations of\n   $50 and over, bearing interest at the rate of seven and three tenths\n   (thirty hundredths) per cent annually. Within a few years they were all\n   redeemed or funded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventhly', 'adv.', 'In the seventh place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventieth', 'a.', 'Next in order after the sixty-ninth; as, a man in the\n   seventieth year of his age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventieth', 'a.', 'Constituting or being one of seventy equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventieth', 'n.', 'One next in order after the sixty-ninth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventieth', 'n.', 'The quotient of a unit divided by seventy; one of\n   seventy equal parts or fractions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventy', 'a.', 'Seven times ten; one more than sixty-nine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventies', 'pl. ', 'of Seventy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventy', 'n.', 'The sum of seven times ten; seventy units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventy', 'n.', 'A symbol representing seventy units, as 70, or lxx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seventy-four', 'n.', 'A naval vessel carrying seventy-four guns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Seven-up', 'n.', 'The game of cards called also all fours, and old sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severed', 'imp. &. p. p.', 'of Sever');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severing', 'p. pr. & vb. n.', 'of Sever');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sever', 'v. t.', 'To separate, as one from another; to cut off from\n   something; to divide; to part in any way, especially by violence, as by\n   cutting, rending, etc.; as, to sever the head from the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sever', 'v. t.', 'To cut or break open or apart; to divide into parts; to\n   cut through; to disjoin; as, to sever the arm or leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sever', 'v. t.', 'To keep distinct or apart; to except; to exempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sever', 'v. t.', 'To disunite; to disconnect; to terminate; as, to sever an\n   estate in joint tenancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sever', 'v. i.', 'To suffer disjunction; to be parted, or rent asunder; to\n   be separated; to part; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sever', 'v. i.', 'To make a separation or distinction; to distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severable', 'a.', 'Capable of being severed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Several', 'a.', 'Separate; distinct; particular; single.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Several', 'a.', 'Diverse; different; various.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Several', 'a.', 'Consisting of a number more than two, but not very many;\n   divers; sundry; as, several persons were present when the event took\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Several', 'adv.', 'By itself; severally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Several', 'n.', 'Each particular taken singly; an item; a detail; an\n   individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Several', 'n.', 'Persons oe objects, more than two, but not very many.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Several', 'n.', 'An inclosed or separate place; inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severalities', 'pl. ', 'of Severality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severality', 'n.', 'Each particular taken singly; distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severalize', 'v. t.', 'To distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severally', 'adv.', 'Separately; distinctly; apart from others;\n   individually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severalty', 'n.', 'A state of separation from the rest, or from all others;\n   a holding by individual right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severance', 'n.', 'The act of severing, or the state of being severed;\n   partition; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severance', 'n.', 'The act of dividing; the singling or severing of two or\n   more that join, or are joined, in one writ; the putting in several or\n   separate pleas or answers by two or more disjointly; the destruction of\n   the unity of interest in a joint estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severe', 'superl.', 'Serious in feeeling or manner; sedate; grave; austere;\n   not light, lively, or cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severe', 'superl.', 'Very strict in judgment, discipline, or government;\n   harsh; not mild or indulgent; rigorous; as, severe criticism; severe\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severe', 'superl.', 'Rigidly methodical, or adherent to rule or principle;\n   exactly conformed to a standard; not allowing or employing unneccessary\n   ornament, amplification, etc.; strict; -- said of style, argument, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severe', 'superl.', 'Sharp; afflictive; distressing; violent; extreme; as,\n   severe pain, anguish, fortune; severe cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severe', 'superl.', 'Difficult to be endured; exact; critical; rigorous;\n   as, a severe test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severities', 'pl. ', 'of Severity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severity', 'n.', 'The quality or state of being severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severity', 'n.', 'Gravity or austerity; extreme strictness; rigor;\n   harshness; as, the severity of a reprimand or a reproof; severity of\n   discipline or government; severity of penalties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severity', 'n.', 'The quality or power of distressing or paining; extreme\n   degree; extremity; intensity; inclemency; as, the severity of pain or\n   anguish; the severity of cold or heat; the severity of the winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severity', 'n.', 'Harshness; cruel treatment; sharpness of punishment; as,\n   severity practiced on prisoners of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severity', 'n.', 'Exactness; rigorousness; strictness; as, the severity of\n   a test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Severy', 'n.', 'A bay or compartment of a vaulted ceiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sevocation', 'n.', 'A calling aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sevres blue', '', 'A very light blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sevres ware', '', 'Porcelain manufactured at Sevres, France, ecpecially in\n   the national factory situated there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sew', 'n.', 'Juice; gravy; a seasoned dish; a delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sew', 'v. t.', 'To follow; to pursue; to sue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewed', 'imp.', 'of Sew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewed', 'p. p.', 'of Sew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewn', '', 'of Sew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewing', 'p. pr. & vb. n.', 'of Sew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sew', 'v. t.', 'To unite or fasten together by stitches, as with a needle\n   and thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sew', 'v. t.', 'To close or stop by ssewing; -- often with up; as, to sew\n   up a rip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sew', 'v. t.', 'To inclose by sewing; -- sometimes with up; as, to sew\n   money in a bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sew', 'v. i.', 'To practice sewing; to work with needle and thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sew', 'v. t.', 'To drain, as a pond, for taking the fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewage', 'n.', 'The contents of a sewer or drain; refuse liquids or matter\n   carried off by sewers');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewage', 'n.', 'Sewerage, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewe', 'v. i.', 'To perform the duties of a sewer. See 3d Sewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewel', 'n.', 'A scarecrow, generally made of feathers tied to a string,\n   hung up to prevent deer from breaking into a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewellel', 'n.', 'A peculiar gregarious burrowing rodent (Haplodon rufus),\n   native of the coast region of the Northwestern United States. It\n   somewhat resembles a muskrat or marmot, but has only a rudimentary\n   tail. Its head is broad, its eyes are small and its fur is brownish\n   above, gray beneath. It constitutes the family Haplodontidae. Called\n   also boomer, showtl, and mountain beaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewen', 'n.', 'A British trout usually regarded as a variety (var.\n   Cambricus) of the salmon trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewer', 'n.', 'One who sews, or stitches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewer', 'n.', 'A small tortricid moth whose larva sews together the edges\n   of a leaf by means of silk; as, the apple-leaf sewer (Phoxopteris\n   nubeculana)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewer', 'n.', 'A drain or passage to carry off water and filth under\n   ground; a subterraneous channel, particularly in cities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewer', 'n.', 'Formerly, an upper servant, or household officer, who set on\n   and removed the dishes at a feast, and who also brought water for the\n   hands of the guests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewerage', 'n.', 'The construction of a sewer or sewers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewerage', 'n.', 'The system of sewers in a city, town, etc.; the general\n   drainage of a city or town by means of sewers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewerage', 'n.', 'The material collected in, and discharged by, sewers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewin', 'n.', 'Same as Sewen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewing', 'n.', 'The act or occupation of one who sews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewing', 'n.', 'That which is sewed with the needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sewster', 'n.', 'A seamstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sex-', '', 'A combining form meaning six; as, sexdigitism; sexennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sex', 'n.', 'The distinguishing peculiarity of male or female in both\n   animals and plants; the physical difference between male and female;\n   the assemblage of properties or qualities by which male is\n   distinguished from female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sex', 'n.', 'One of the two divisions of organic beings formed on the\n   distinction of male and female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sex', 'n.', 'The capability in plants of fertilizing or of being\n   fertilized; as, staminate and pistillate flowers are of opposite sexes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sex', 'n.', 'One of the groups founded on this distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexagenarian', 'n.', 'A person who is sixty years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexagenary', 'a.', 'Pertaining to, or designating, the number sixty;\n   poceeding by sixties; sixty years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexagenary', 'n.', 'Something composed of sixty parts or divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexagenary', 'n.', 'A sexagenarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexagesima', 'n.', 'The second Sunday before Lent; -- so called as being\n   about the sixtieth day before Easter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexagesimal', 'a.', 'Pertaining to, or founded on, the number sixty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexagesimal', 'n.', 'A sexagesimal fraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexangle', 'n.', 'A hexagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexangled', 'a.', 'Alt. of Sexangular');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexangular', 'a.', 'Having six angles; hexagonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexangularly', 'adv.', 'Hexagonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexavalent', 'a.', 'See Sexivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexdigitism', 'n.', 'The state of having six fingers on a hand, or six toes\n   on a foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexdigitist', 'n.', 'One who has six fingers on a hand, or six toes on a\n   foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexed', 'a.', 'Belonging to sex; having sex; distinctively male of female;\n   as, the sexed condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexenary', 'a.', 'Proceeding by sixes; sextuple; -- applied especially to a\n   system of arithmetical computation in which the base is six.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexennial', 'a.', 'Lasting six years, or happening once in six years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexennial', 'n.', 'A sexennial event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexennially', 'adv.', 'Once in six years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexfid', 'a.', 'Alt. of Sexifid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexifid', 'a.', 'Six-cleft; as, a sexfid calyx or nectary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexisyllabic', 'a.', 'Having six syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexisyllable', 'n.', 'A word of six syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexivalent', 'a.', 'Hexavalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexless', 'a.', 'Having no sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexlocular', 'a.', 'Having six cells for seeds; six-celled; as, a\n   sexlocular pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexly', 'a.', 'Pertaining to sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexradiate', 'a.', 'Having six rays; -- said of certain sponge spicules.\n   See Illust. of Spicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sext', 'n.', 'The office for the sixth canonical hour, being a part of the\n   Breviary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sext', 'n.', 'The sixth book of the decretals, added by Pope Boniface VIII.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextain', 'n.', 'A stanza of six lines; a sestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextans', 'n.', 'A Roman coin, the sixth part of an as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextans', 'n.', 'A constellation on the equator south of Leo; the Sextant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextant', 'n.', 'The sixth part of a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextant', 'n.', 'An instrument for measuring angular distances between\n   objects, -- used esp. at sea, for ascertaining the latitude and\n   longitude. It is constructed on the same optical principle as Hadleys\n   quadrant, but usually of metal, with a nicer graduation, telescopic\n   sight, and its arc the sixth, and sometimes the third, part of a\n   circle. See Quadrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextant', 'n.', 'The constellation Sextans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextaries', 'pl. ', 'of Sextary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextary', 'n.', 'An ancient Roman liquid and dry measure, about equal to an\n   English pint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextary', 'n.', 'A sacristy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextet', 'n.', 'Alt. of Sextetto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextetto', 'n.', 'See Sestet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexteyn', 'n.', 'A sacristan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextic', 'a.', 'Of the sixth degree or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextic', 'n.', 'A quantic of the sixth degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextile', 'a.', 'Measured by sixty degrees; fixed or indicated by a\n   distance of sixty degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextile', 'n.', 'The aspect or position of two planets when distant from\n   each other sixty degrees, or two signs. This position is marked thus:\n   /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextillion', 'n.', 'According to the method of numeration (which is\n   followed also in the United States), the number expressed by a unit\n   with twenty-one ciphers annexed. According to the English method, a\n   million raised to the sixth power, or the number expressed by a unit\n   with thirty-six ciphers annexed. See Numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextos', 'pl. ', 'of Sexto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexto', 'n.', 'A book consisting of sheets each of which is folded into six\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextodecimo', 'a.', 'Having sixteen leaves to a sheet; of, or equal to, the\n   size of one fold of a sheet of printing paper when folded so as to make\n   sixteen leaves, or thirty-two pages; as, a sextodecimo volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextodecimos', 'pl. ', 'of Sextodecimo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextodecimo', 'n.', 'A book composed of sheets each of which is folded into\n   sixteen leaves; hence, indicating, more or less definitely, a size of a\n   book; -- usually written 16mo, or 16.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextolet', 'n.', 'A double triplet; a group of six equal notes played in\n   the time of four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexton', 'n.', 'An under officer of a church, whose business is to take\n   care of the church building and the vessels, vestments, etc., belonging\n   to the church, to attend on the officiating clergyman, and to perform\n   other duties pertaining to the church, such as to dig graves, ring the\n   bell, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextoness', 'n.', 'A female sexton; a sextons wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextonry', 'n.', 'Sextonship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextonship', 'n.', 'The office of a sexton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextry', 'n.', 'See Sacristy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextuple', 'a.', 'Six times as much; sixfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sextuple', 'a.', 'Divisible by six; having six beats; as, sixtuple measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexual', 'a.', 'Of or pertaining to sex, or the sexes; distinguishing sex;\n   peculiar to the distinction and office of male or female; relating to\n   the distinctive genital organs of the sexes; proceeding from, or based\n   upon, sex; as, sexual characteristics; sexual intercourse, connection,\n   or commerce; sexual desire; sexual diseases; sexual generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexualist', 'n.', 'One who classifies plants by the sexual method of\n   Linnaeus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexuality', 'n.', 'The quality or state of being distinguished by sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexualize', 'v. t.', 'To attribute sex to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sexually', 'adv.', 'In a sexual manner or relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sey', '', 'Alt. of Seyh');
