  INSERT INTO words (word, word_type, description) VALUES  (    'Shark', 'v. t. & i.', 'A rapacious, artful person; a sharper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shark', 'v. t. & i.', 'Trickery; fraud; petty rapine; as, to live upon the\n   shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shark', 'v. t.', 'To pick or gather indiscriminately or covertly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharked', 'imp. & p. p.', 'of Shark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharking', 'p. pr. & vb. n.', 'of Shark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shark', 'v. i.', 'To play the petty thief; to practice fraud or trickery;\n   to swindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shark', 'v. i.', 'To live by shifts and stratagems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharker', 'n.', 'One who lives by sharking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharking', 'n.', 'Petty rapine; trick; also, seeking a livelihood by shifts\n   and dishonest devices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharock', 'n.', 'An East Indian coin of the value of 12/ pence sterling, or\n   about 25 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Having a very thin edge or fine point; of a nature to\n   cut or pierce easily; not blunt or dull; keen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Terminating in a point or edge; not obtuse or rounded;\n   somewhat pointed or edged; peaked or ridged; as, a sharp hill; sharp\n   features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Affecting the sense as if pointed or cutting, keen,\n   penetrating, acute: to the taste or smell, pungent, acid, sour, as\n   ammonia has a sharp taste and odor; to the hearing, piercing, shrill,\n   as a sharp sound or voice; to the eye, instantaneously brilliant,\n   dazzling, as a sharp flash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'High in pitch; acute; as, a sharp note or tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Raised a semitone in pitch; as, C sharp (C/), which is\n   a half step, or semitone, higher than C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'So high as to be out of tune, or above true pitch; as,\n   the tone is sharp; that instrument is sharp. Opposed in all these\n   senses to flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Very trying to the feelings; piercing; keen; severe;\n   painful; distressing; as, sharp pain, weather; a sharp and frosty air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Cutting in language or import; biting; sarcastic;\n   cruel; harsh; rigorous; severe; as, a sharp rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Of keen perception; quick to discern or distinguish;\n   having nice discrimination; acute; penetrating; sagacious; clever; as,\n   a sharp eye; sharp sight, hearing, or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Eager in pursuit; keen in quest; impatient for\n   gratification; keen; as, a sharp appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Fierce; ardent; fiery; violent; impetuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Keenly or unduly attentive to ones own interest; close\n   and exact in dealing; shrewd; as, a sharp dealer; a sharp customer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Composed of hard, angular grains; gritty; as, sharp\n   sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Steep; precipitous; abrupt; as, a sharp ascent or\n   descent; a sharp turn or curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'superl.', 'Uttered in a whisper, or with the breath alone, without\n   voice, as certain consonants, such as p, k, t, f; surd; nonvocal;\n   aspirated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'adv.', 'To a point or edge; piercingly; eagerly; sharply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'adv.', 'Precisely; exactly; as, we shall start at ten oclock\n   sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'n.', 'A sharp tool or weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'n.', 'The character [/] used to indicate that the note before\n   which it is placed is to be raised a half step, or semitone, in pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'n.', 'A sharp tone or note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'n.', 'A portion of a stream where the water runs very rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'n.', 'A sewing needle having a very slender point; a needle of the\n   most pointed of the three grades, blunts, betweens, and sharps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'n.', 'Same as Middlings, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'n.', 'An expert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharped', 'imp. & p. p.', 'of Sharp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharping', 'p. pr. & vb. n.', 'of Sharp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'v. t.', 'To sharpen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'v. t.', 'To raise above the proper pitch; to elevate the tone of;\n   especially, to raise a half step, or semitone, above the natural tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'v. i.', 'To play tricks in bargaining; to act the sharper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp', 'v. i.', 'To sing above the proper pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp-cut', 'a.', 'Cut sharply or definitely, or so as to make a clear,\n   well-defined impression, as the lines of an engraved plate, and the\n   like; clear-cut; hence, having great distinctness; well-defined; clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sarpened', 'imp. & p. p.', 'of Sharpen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpening', 'p. pr. & vb. n.', 'of Sharpen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To make sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To give a keen edge or fine point to; to make sharper; as,\n   to sharpen an ax, or the teeth of a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To render more quick or acute in perception; to make more\n   ready or ingenious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To make more eager; as, to sharpen mens desires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To make more pungent and intense; as, to sharpen a pain or\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To make biting, sarcastic, or severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To render more shrill or piercing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To make more tart or acid; to make sour; as, the rays of\n   the sun sharpen vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'a.', 'To raise, as a sound, by means of a sharp; to apply a\n   sharp to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpen', 'v. i.', 'To grow or become sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharper', 'n.', 'A person who bargains closely, especially, one who cheats\n   in bargains; a swinder; also, a cheating gamester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpie', 'n.', 'A long, sharp, flat-bottomed boat, with one or two masts\n   carrying a triangular sail. They are often called Fair Haven sharpies,\n   after the place on the coast of Connecticut where they originated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpling', 'n.', 'A stickleback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharply', 'adv.', 'In a sharp manner,; keenly; acutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpness', 'n.', 'The quality or condition of being sharp; keenness;\n   acuteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpsaw', 'n.', 'The great titmouse; -- so called from its harsh call\n   notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp-set', 'a.', 'Eager in appetite or desire of gratification; affected\n   by keen hunger; ravenous; as, an eagle or a lion sharp-set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpshooter', 'n.', 'One skilled in shooting at an object with exactness;\n   a good marksman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharpshooting', 'n.', 'A shooting with great precision and effect; hence, a\n   keen contest of wit or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp-sighted', 'a.', 'Having quick or acute sight; -- used literally and\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharptail', 'n.', 'The pintail duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharptail', 'n.', 'The pintail grouse, or prairie chicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sharp-witted', 'a.', 'Having an acute or nicely discerning mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shash', 'n.', 'The scarf of a turban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shash', 'n.', 'A sash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaster', 'n.', 'Alt. of Shastra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shastra', 'n.', 'A treatise for authoritative instruction among the\n   Hindoos; a book of institutes; especially, a treatise explaining the\n   Vedas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shathmont', 'n.', 'A shaftment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shattered', 'imp. & p. p.', 'of Shatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shattering', 'p. pr. & vb. n.', 'of Shatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shatter', 'v. t.', 'To break at once into many pieces; to dash, burst, or\n   part violently into fragments; to rend into splinters; as, an explosion\n   shatters a rock or a bomb; too much steam shatters a boiler; an oak is\n   shattered by lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shatter', 'v. t.', 'To disorder; to derange; to render unsound; as, to be\n   shattered in intellect; his constitution was shattered; his hopes were\n   shattered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shatter', 'v. t.', 'To scatter about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shatter', 'v. i.', 'To be broken into fragments; to fall or crumble to\n   pieces by any force applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shatter', 'n.', 'A fragment of anything shattered; -- used chiefly or soley\n   in the phrase into shatters; as, to break a glass into shatters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shatter-brained', 'a.', 'Alt. of Shatter-pated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shatter-pated', 'a.', 'Disordered or wandering in intellect; hence,\n   heedless; wild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shattery', 'a.', 'Easily breaking into pieces; not compact; loose of\n   texture; brittle; as, shattery spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', '', 'obs. p. p. of Shave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaved', 'imp.', 'of Shave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaved', 'p. p.', 'of Shave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaven', '', 'of Shave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaving', 'p. pr. & vb. n.', 'of Shave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'To cut or pare off from the surface of a body with a\n   razor or other edged instrument; to cut off closely, as with a razor;\n   as, to shave the beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'To make bare or smooth by cutting off closely the\n   surface, or surface covering, of; especially, to remove the hair from\n   with a razor or other sharp instrument; to take off the beard or hair\n   of; as, to shave the face or the crown of the head; he shaved himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'To cut off thin slices from; to cut in thin slices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'To skim along or near the surface of; to pass close to,\n   or touch lightly, in passing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'To strip; to plunder; to fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. i.', 'To use a razor for removing the beard; to cut closely;\n   hence, to be hard and severe in a bargain; to practice extortion; to\n   cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'A thin slice; a shaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'A cutting of the beard; the operation of shaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'An exorbitant discount on a note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'A premium paid for an extension of the time of delivery\n   or payment, or for the right to vary a stock contract in any\n   particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'A hand tool consisting of a sharp blade with a handle at\n   each end; a drawing knife; a spokeshave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shave', 'v. t.', 'The act of passing very near to, so as almost to graze;\n   as, the bullet missed by a close shave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaveling', 'n.', 'A man shaved; hence, a monk, or other religious; -- used\n   in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaver', 'n.', 'One who shaves; one whose occupation is to shave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaver', 'n.', 'One who is close in bargains; a sharper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaver', 'n.', 'One who fleeces; a pillager; a plunderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaver', 'n.', 'A boy; a lad; a little fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaver', 'n.', 'A tool or machine for shaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaving', 'n.', 'The act of one who, or that which, shaves; specifically,\n   the act of cutting off the beard with a razor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaving', 'n.', 'That which is shaved off; a thin slice or strip pared off\n   with a shave, a knife, a plane, or other cutting instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaw', 'n.', 'A thicket; a small wood or grove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shaw', 'n.', 'The leaves and tops of vegetables, as of potatoes, turnips,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shawfowl', 'n.', 'The representation or image of a fowl made by fowlers to\n   shoot at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shawl', 'n.', 'A square or oblong cloth of wool, cotton, silk, or other\n   textile or netted fabric, used, especially by women, as a loose\n   covering for the neck and shoulders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shawl', 'v. t.', 'To wrap in a shawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shawm', 'n.', 'A wind instrument of music, formerly in use, supposed to\n   have resembled either the clarinet or the hautboy in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shawnees', 'n. pl.', 'A tribe of North American Indians who occupied\n   Western New York and part of Ohio, but were driven away and widely\n   dispersed by the Iroquois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shay', 'n.', 'A chaise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'She', 'obj.', 'This or that female; the woman understood or referred to;\n   the animal of the female sex, or object personified as feminine, which\n   was spoken of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'She', 'obj.', 'A woman; a female; -- used substantively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheading', 'v. t.', 'A tithing, or division, in the Isle of Man, in which\n   there is a coroner, or chief constable. The island is divided into six\n   sheadings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheaf', 'n.', 'A sheave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheaves', 'pl. ', 'of Sheaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheaf', 'n.', 'A quantity of the stalks and ears of wheat, rye, or other\n   grain, bound together; a bundle of grain or straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheaf', 'n.', 'Any collection of things bound together; a bundle;\n   specifically, a bundle of arrows sufficient to fill a quiver, or the\n   allowance of each archer, -- usually twenty-four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheaf', 'v. t.', 'To gather and bind into a sheaf; to make into sheaves;\n   as, to sheaf wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheaf', 'v. i.', 'To collect and bind cut grain, or the like; to make\n   sheaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheafy', 'a.', 'Pertaining to, or consisting of, a sheaf or sheaves;\n   resembling a sheaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheal', 'n.', 'Same as Sheeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheal', 'v. t.', 'To put under a sheal or shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheal', 'v. t.', 'To take the husks or pods off from; to shell; to empty of\n   its contents, as a husk or a pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheal', 'n.', 'A shell or pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shealing', 'n.', 'The outer husk, pod, or shell, as of oats, pease, etc.;\n   sheal; shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shealing', 'n.', 'Same as Sheeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheared', 'imp.', 'of Shear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shore', '', 'of Shear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheared', 'p. p.', 'of Shear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorn', '', 'of Shear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'p. pr. & vb. n.', 'of Shear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'To cut, clip, or sever anything from with shears or a\n   like instrument; as, to shear sheep; to shear cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'To separate or sever with shears or a similar instrument;\n   to cut off; to clip (something) from a surface; as, to shear a fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'To reap, as grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'Fig.: To deprive of property; to fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'To produce a change of shape in by a shear. See Shear,\n   n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'A pair of shears; -- now always used in the plural, but\n   formerly also in the singular. See Shears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'A shearing; -- used in designating the age of sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'An action, resulting from applied forces, which tends to\n   cause two contiguous parts of a body to slide relatively to each other\n   in a direction parallel to their plane of contact; -- also called\n   shearing stress, and tangential stress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. t.', 'A strain, or change of shape, of an elastic body,\n   consisting of an extension in one direction, an equal compression in a\n   perpendicular direction, with an unchanged magnitude in the third\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. i.', 'To deviate. See Sheer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shear', 'v. i.', 'To become more or less completely divided, as a body\n   under the action of forces, by the sliding of two contiguous parts\n   relatively to each other in a direction parallel to their plane of\n   contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearbill', 'n.', 'The black skimmer. See Skimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheard', 'n.', 'See Shard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearer', 'n.', 'One who shears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearer', 'n.', 'A reaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'n.', 'The act or operation of clipping with shears or a\n   shearing machine, as the wool from sheep, or the nap from cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'n.', 'The product of the act or operation of clipping with\n   shears or a shearing machine; as, the whole shearing of a flock; the\n   shearings from cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'n.', 'Same as Shearling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'n.', 'The act or operation of reaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'n.', 'The act or operation of dividing with shears; as, the\n   shearing of metal plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'n.', 'The process of preparing shear steel; tilting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearing', 'n.', 'The process of making a vertical side cutting in working\n   into a face of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearling', 'n.', 'A sheep but once sheared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearmen', 'pl. ', 'of Shearman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearman', 'n.', 'One whose occupation is to shear cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearn', 'n.', 'Dung; excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'A cutting instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'An instrument consisting of two blades, commonly with bevel\n   edges, connected by a pivot, and working on both sides of the material\n   to be cut, -- used for cutting cloth and other substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'A similar instrument the blades of which are extensions of\n   a curved spring, -- used for shearing sheep or skins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'A shearing machine; a blade, or a set of blades, working\n   against a resisting edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'Anything in the form of shears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'A pair of wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'An apparatus for raising heavy weights, and especially for\n   stepping and unstepping the lower masts of ships. It consists of two or\n   more spars or pieces of timber, fastened together near the top,\n   steadied by a guy or guys, and furnished with the necessary tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shears', 'n.', 'The bedpiece of a machine tool, upon which a table or slide\n   rest is secured; as, the shears of a lathe or planer. See Illust. under\n   Lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheartail', 'n.', 'The common tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheartail', 'n.', 'Any one of several species of humming birds of the genus\n   Thaumastura having a long forked tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shearwater', 'n.', 'Any one of numerous species of long-winged oceanic\n   birds of the genus Puffinus and related genera. They are allied to the\n   petrels, but are larger. The Manx shearwater (P. Anglorum), the dusky\n   shearwater (P. obscurus), and the greater shearwater (P. major), are\n   well-known species of the North Atlantic. See Hagdon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheatfish', 'n.', 'A European siluroid fish (Silurus glanis) allied to the\n   cat-fishes. It is the largest fresh-water fish of Europe, sometimes\n   becoming six feet or more in length. See Siluroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheath', 'n.', 'A case for the reception of a sword, hunting knife, or\n   other long and slender instrument; a scabbard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheath', 'n.', 'Any sheathlike covering, organ, or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheath', 'n.', 'The base of a leaf when sheathing or investing a stem or\n   branch, as in grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheath', 'n.', 'One of the elytra of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathbill', 'n.', 'Either one of two species of birds composing the genus\n   Chionis, and family Chionidae, native of the islands of the Antarctic\n   seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathed', 'imp. & p. p.', 'of Sheathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheating', 'p. pr. & vb. n.', 'of Sheathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathe', 'v. t.', 'To put into a sheath, case, or scabbard; to inclose or\n   cover with, or as with, a sheath or case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathe', 'v. t.', 'To fit or furnish, as with a sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathe', 'v. t.', 'To case or cover with something which protects, as thin\n   boards, sheets of metal, and the like; as, to sheathe a ship with\n   copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathe', 'v. t.', 'To obtund or blunt, as acrimonious substances, or sharp\n   particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathed', 'a.', 'Povided with, or inclosed in, sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathed', 'a.', 'Invested by a sheath, or cylindrical membranaceous tube,\n   which is the base of the leaf, as the stalk or culm in grasses;\n   vaginate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheather', 'n.', 'One who sheathes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathfish', 'n.', 'Same as Sheatfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathing', 'p. pr. & a.', 'Inclosing with a sheath; as, the sheathing\n   leaves of grasses; the sheathing stipules of many polygonaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathing', 'n.', 'That which sheathes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathing', 'n.', 'The casing or covering of a ships bottom and sides; the\n   materials for such covering; as, copper sheathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathing', 'n.', 'The first covering of boards on the outside wall of a\n   frame house or on a timber roof; also, the material used for covering;\n   ceiling boards in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathless', 'a.', 'Without a sheath or case for covering; unsheathed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheath-winged', 'a.', 'Having elytra, or wing cases, as a beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheathy', 'a.', 'Forming or resembling a sheath or case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shea tree', '', 'An African sapotaceous tree (Bassia, / Butyrospermum,\n   Parkii), from the seeds of which a substance resembling butter is\n   obtained; the African butter tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheave', 'v.', 'A wheel having a groove in the rim for a rope to work in,\n   and set in a block, mast, or the like; the wheel of a pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheave', 'v. t.', 'To gather and bind into a sheaf or sheaves; hence, to\n   collect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheaved', 'a.', 'Made of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shebander', 'n.', 'A harbor master, or ruler of a port, in the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shebang', 'n.', 'A jocosely depreciative name for a dwelling or shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shebeen', 'n.', 'A low public house; especially, a place where spirits and\n   other excisable liquors are illegally and privately sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shechinah', 'n.', 'See Shekinah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shecklaton', 'n.', 'A kind of gilt leather. See Checklaton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'n.', 'A slight or temporary structure built to shade or shelter\n   something; a structure usually open in front; an outbuilding; a hut;\n   as, a wagon shed; a wood shed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'imp. & p. p.', 'of Shed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shedding', 'p. pr. & vb. n.', 'of Shed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. t.', 'To separate; to divide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. t.', 'To part with; to throw off or give forth from ones self;\n   to emit; to diffuse; to cause to emanate or flow; to pour forth or out;\n   to spill; as, the sun sheds light; she shed tears; the clouds shed\n   rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. t.', 'To let fall; to throw off, as a natural covering of hair,\n   feathers, shell; to cast; as, fowls shed their feathers; serpents shed\n   their skins; trees shed leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. t.', 'To cause to flow off without penetrating; as, a tight\n   roof, or covering of oiled cloth, sheeds water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. t.', 'To sprinkle; to intersperse; to cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. t.', 'To divide, as the warp threads, so as to form a shed, or\n   passageway, for the shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. i.', 'To fall in drops; to pour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'v. i.', 'To let fall the parts, as seeds or fruit; to throw off a\n   covering or envelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'n.', 'A parting; a separation; a division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'n.', 'The act of shedding or spilling; -- used only in composition,\n   as in bloodshed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'n.', 'That which parts, divides, or sheds; -- used in composition,\n   as in watershed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shed', 'n.', 'The passageway between the threads of the warp through which\n   the shuttle is thrown, having a sloping top and bottom made by raising\n   and lowering the alternate threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shedder', 'n.', 'One who, or that which, sheds; as, a shedder of blood; a\n   shedder of tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shedder', 'n.', 'A crab in the act of casting its shell, or immediately\n   afterwards while still soft; -- applied especially to the edible crabs,\n   which are most prized while in this state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shedding', 'n.', 'The act of shedding, separating, or casting off or out;\n   as, the shedding of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shedding', 'n.', 'That which is shed, or cast off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelfa', 'n.', 'Alt. of Shilfa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilfa', 'n.', 'The chaffinch; -- so named from its call note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeling', 'n.', 'A hut or small cottage in an expessed or a retired place\n   (as on a mountain or at the seaside) such as is used by shepherds,\n   fishermen, sportsmen, etc.; a summer cottage; also, a shed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheely', 'n.', 'Same as Sheelfa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheen', 'v. t.', 'Bright; glittering; radiant; fair; showy; sheeny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheen', 'v. i.', 'To shine; to glisten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheen', 'n.', 'Brightness; splendor; glitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheenly', 'adv.', 'Brightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeny', 'a.', 'Bright; shining; radiant; sheen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep', 'n. sing. & pl.', 'Any one of several species of ruminants of the\n   genus Ovis, native of the higher mountains of both hemispheres, but\n   most numerous in Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep', 'n. sing. & pl.', 'A weak, bashful, silly fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep', 'n. sing. & pl.', 'Fig.: The people of God, as being under the\n   government and protection of Christ, the great Shepherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepback', 'n.', 'A rounded knoll of rock resembling the back of a sheep.\n   -- produced by glacial action. Called also roche moutonnee; -- usually\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepberry', 'n.', 'The edible fruit of a small North American tree of the\n   genus Viburnum (V. Lentago), having white flowers in flat cymes; also,\n   the tree itself. Called also nannyberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepbite', 'v. i.', 'To bite or nibble like a sheep; hence, to practice\n   petty thefts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepbiter', 'n.', 'One who practices petty thefts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepcot', 'n.', 'Alt. of Sheepcote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepcote', 'n.', 'A small inclosure for sheep; a pen; a fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep-faced', 'a.', 'Over-bashful; sheepish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepfold', 'n.', 'A fold or pen for sheep; a place where sheep are\n   collected or confined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep-headed', 'a.', 'Silly; simple-minded; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheephook', 'n.', 'A hook fastened to pole, by which shepherds lay hold on\n   the legs or necks of their sheep; a shepherds crook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepish', 'a.', 'Of or pertaining to sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepish', 'a.', 'Like a sheep; bashful; over-modest; meanly or foolishly\n   diffident; timorous to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepmaster', 'n.', 'A keeper or feeder of sheep; also, an owner of sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeprack', 'n.', 'The starling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeps-eye', 'n.', 'A modest, diffident look; a loving glance; -- commonly\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeps-foot', 'n.', 'A printers tool consisting of a metal bar formed\n   into a hammer head at one end and a claw at the other, -- used as a\n   lever and hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepshank', 'n.', 'A hitch by which a rope may be temporarily shortened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepshead', 'n.', 'A large and valuable sparoid food fish (Archosargus, /\n   Diplodus, probatocephalus) found on the Atlantic coast of the United\n   States. It often weighs from ten to twelve pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep-shearer', 'n.', 'One who shears, or cuts off the wool from, sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep-shearing', 'n.', 'Act of shearing sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheep-shearing', 'n.', 'A feast at the time of sheep-shearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepskin', 'n.', 'The skin of a sheep; or, leather prepared from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepskin', 'n.', 'A diploma; -- so called because usually written or\n   printed on parchment prepared from the skin of the sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepsplit', 'n.', 'A split of a sheepskin; one of the thin sections made\n   by splitting a sheepskin with a cutting knife or machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheepy', 'a.', 'Resembling sheep; sheepish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'v. i.', 'Bright; clear; pure; unmixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'v. i.', 'Very thin or transparent; -- applied to fabrics; as,\n   sheer muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'v. i.', 'Being only what it seems to be; obvious; simple; mere;\n   downright; as, sheer folly; sheer nonsense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'v. i.', 'Stright up and down; vertical; prpendicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'adv.', 'Clean; quite; at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'v. t.', 'To shear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheered', 'imp. & p. p.', 'of Sheer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheering', 'p. pr. & vb. n.', 'of Sheer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'v. i.', 'To decline or deviate from the line of the proper course;\n   to turn aside; to swerve; as, a ship sheers from her course; a horse\n   sheers at a bicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'n.', 'The longitudinal upward curvature of the deck, gunwale, and\n   lines of a vessel, as when viewed from the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'n.', 'The position of a vessel riding at single anchor and\n   swinging clear of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'n.', 'A turn or change in a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheer', 'n.', 'Shears See Shear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheerly', 'adv.', 'At once; absolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheerwater', 'n.', 'The shearwater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'In general, a large, broad piece of anything thin, as\n   paper, cloth, etc.; a broad, thin portion of any substance; an expanded\n   superficies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'A broad piece of cloth, usually linen or cotton, used for\n   wrapping the body or for a covering; especially, one used as an article\n   of bedding next to the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'A broad piece of paper, whether folded or unfolded,\n   whether blank or written or printed upon; hence, a letter; a newspaper,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'A single signature of a book or a pamphlet;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'the book itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'A broad, thinly expanded portion of metal or other\n   substance; as, a sheet of copper, of glass, or the like; a plate; a\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'A broad expanse of water, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'A sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'An extensive bed of an eruptive rock intruded between, or\n   overlying, other strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'A rope or chain which regulates the angle of adjustment\n   of a sail in relation in relation to the wind; -- usually attached to\n   the lower corner of a sail, or to a yard or a boom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'The space in the forward or the after part of a boat\n   where there are no rowers; as, fore sheets; stern sheets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeted', 'imp. & p. p.', 'of Sheet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeting', 'p. pr. & vb. n.', 'of Sheet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'To furnish with a sheet or sheets; to wrap in, or cover\n   with, a sheet, or as with a sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet', 'v. t.', 'To expand, as a sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet anchor', 'v. t.', 'A large anchor stowed on shores outside the waist\n   of a vessel; -- called also waist anchor. See the Note under Anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet anchor', 'v. t.', 'Anything regarded as a sure support or dependence\n   in danger; the best hope or refuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet cable', '', 'The cable belonging to the sheet anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheet chain', '', 'A chain sheet cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheetfuls', 'pl. ', 'of Sheetful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheetful', 'n.', 'Enough to fill a sheet; as much as a sheet can hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeting', 'n.', 'Cotton or linen cloth suitable for bed sheets. It is\n   sometimes made of double width.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeting', 'n.', 'A lining of planks or boards (rarely of metal) for\n   protecting an embankment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheeting', 'n.', 'The act or process of forming into sheets, or flat\n   pieces; also, material made into sheets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheik', 'n.', 'The head of an Arab family, or of a clan or a tribe; also,\n   the chief magistrate of an Arab village. The name is also applied to\n   Mohammedan ecclesiastics of a high grade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheil', 'n.', 'Alt. of Sheiling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheiling', 'n.', 'See Sheeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shekel', 'n.', 'An ancient weight and coin used by the Jews and by other\n   nations of the same stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shekel', 'n.', 'A jocose term for money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shekinah', 'n.', 'The visible majesty of the Divine Presence, especially\n   when resting or dwelling between the cherubim on the mercy seat, in the\n   Tabernacle, or in the Temple of Solomon; -- a term used in the Targums\n   and by the later Jews, and adopted by Christians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheld', 'a.', 'Variegated; spotted; speckled; piebald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheldafle', 'n.', 'Alt. of Sheldaple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheldaple', 'n.', 'A chaffinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheldfowl', 'n.', 'The common sheldrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheldrake', 'n.', 'Any one of several species of large Old World ducks of\n   the genus Tadorna and allied genera, especially the European and\n   Asiatic species. (T. cornuta, / tadorna), which somewhat resembles a\n   goose in form and habit, but breeds in burrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheldrake', 'n.', 'Any one of the American mergansers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelduck', 'n.', 'The sheldrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelves', 'pl. ', 'of Shelf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelf', 'v. i.', 'A flat tablet or ledge of any material set horizontally\n   at a distance from the floor, to hold objects of use or ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelf', 'v. i.', 'A sand bank in the sea, or a rock, or ledge of rocks,\n   rendering the water shallow, and dangerous to ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelf', 'v. i.', 'A stratum lying in a very even manner; a flat, projecting\n   layer of rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelf', 'v. i.', 'A piece of timber running the whole length of a vessel\n   inside the timberheads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelfy', 'a.', 'Abounding in shelves; full of dangerous shallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelfy', 'a.', 'Full of strata of rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'A hard outside covering, as of a fruit or an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'The covering, or outside part, of a nut; as, a hazelnut\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'A pod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'The hard covering of an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'The hard calcareous or chitinous external covering of\n   mollusks, crustaceans, and some other invertebrates. In some mollusks,\n   as the cuttlefishes, it is internal, or concealed by the mantle. Also,\n   the hard covering of some vertebrates, as the armadillo, the tortoise,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'Hence, by extension, any mollusks having such a covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'A hollow projectile, of various shapes, adapted for a mortar\n   or a cannon, and containing an explosive substance, ignited with a fuse\n   or by percussion, by means of which the projectile is burst and its\n   fragments scattered. See Bomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'The case which holds the powder, or charge of powder and\n   shot, used with breechloading small arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'Any slight hollow structure; a framework, or exterior\n   structure, regarded as not complete or filled in; as, the shell of a\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'A coarse kind of coffin; also, a thin interior coffin\n   inclosed in a more substantial one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'An instrument of music, as a lyre, -- the first lyre having\n   been made, it is said, by drawing strings over a tortoise shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'An engraved copper roller used in print works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'The husks of cacao seeds, a decoction of which is often used\n   as a substitute for chocolate, cocoa, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'The outer frame or case of a block within which the sheaves\n   revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'n.', 'A light boat the frame of which is covered with thin wood or\n   with paper; as, a racing shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelled', 'imp. & p. p.', 'of Shell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelling', 'p. pr. & vb. n.', 'of Shell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'v. t.', 'To strip or break off the shell of; to take out of the\n   shell, pod, etc.; as, to shell nuts or pease; to shell oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'v. t.', 'To separate the kernels of (an ear of Indian corn, wheat,\n   oats, etc.) from the cob, ear, or husk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'v. t.', 'To throw shells or bombs upon or into; to bombard; as, to\n   shell a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'v. i.', 'To fall off, as a shell, crust, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'v. i.', 'To cast the shell, or exterior covering; to fall out of\n   the pod or husk; as, nuts shell in falling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell', 'v. i.', 'To be disengaged from the ear or husk; as, wheat or rye\n   shells in reaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell-lac', 'n.', 'Alt. of Shellac');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shellac', 'n.', 'See the Note under 2d Lac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shellapple', 'n.', 'See Sheldafle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shellbark', 'n.', 'A species of hickory (Carya alba) whose outer bark is\n   loose and peeling; a shagbark; also, its nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelled', 'a.', 'Having a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheller', 'n.', 'One who, or that which, shells; as, an oyster sheller; a\n   corn sheller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shellfish', 'n.', 'Any aquatic animal whose external covering consists of a\n   shell, either testaceous, as in oysters, clams, and other mollusks, or\n   crustaceous, as in lobsters and crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelling', 'n.', 'Groats; hulled oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shell-less', 'a.', 'Having no shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shellproof', 'a.', 'Capable of resisting bombs or other shells; bombproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shellwork', 'n.', 'Work composed of shells, or adorned with them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelly', 'a.', 'Abounding with shells; consisting of shells, or of a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelter', 'n.', 'That which covers or defends from injury or annoyance; a\n   protection; a screen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelter', 'n.', 'One who protects; a guardian; a defender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelter', 'n.', 'The state of being covered and protected; protection;\n   security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheltered', 'imp. & p. p.', 'of Shelter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheltering', 'p. pr. & vb. n.', 'of Shelter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelter', 'v. t.', 'To be a shelter for; to provide with a shelter; to\n   cover from injury or annoyance; to shield; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelter', 'v. t.', 'To screen or cover from notice; to disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelter', 'v. t.', 'To betake to cover, or to a safe place; -- used\n   reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelter', 'v. i.', 'To take shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelterless', 'a.', 'Destitute of shelter or protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheltery', 'a.', 'Affording shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheltie', 'n.', 'Alt. of Shelty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelty', 'n.', 'A Shetland pony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelve', 'v. t.', 'To furnish with shelves; as, to shelve a closet or a\n   library.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelve', 'v. t.', 'To place on a shelf. Hence: To lay on the shelf; to put\n   aside; to dismiss from service; to put off indefinitely; as, to shelve\n   an officer; to shelve a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelved', 'imp. & p. p.', 'of Shelve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelving', 'p. pr. & vb. n.', 'of Shelve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelve', 'v. i.', 'To incline gradually; to be slopping; as, the bottom\n   shelves from the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelving', 'a.', 'Sloping gradually; inclining; as, a shelving shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelving', 'n.', 'The act of fitting up shelves; as, the job of shelving a\n   closet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelving', 'n.', 'The act of laying on a shelf, or on the shelf; putting\n   off or aside; as, the shelving of a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelving', 'n.', 'Material for shelves; shelves, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shelvy', 'a.', 'Sloping gradually; shelving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shemite', 'n.', 'A descendant of Shem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shemitic', 'a.', 'Alt. of Shemitish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shemitish', 'a.', 'Of or pertaining to Shem, the son of Noah, or his\n   descendants. See Semitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shemitism', 'n.', 'See Semitism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shent', 'imp. & p. p.', 'of Shend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shending', 'p. pr. & vb. n.', 'of Shend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shend', 'n.', 'To injure, mar, spoil, or harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shend', 'n.', 'To blame, reproach, or revile; to degrade, disgrace, or put\n   to shame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shendful', 'a.', 'Destructive; ruinous; disgraceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shendship', 'n.', 'Harm; ruin; also, reproach; disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shent', '', 'obs. 3d pers. sing. pres. of Shend, for shendeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shent', 'v. t.', 'To shend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheol', 'n.', 'The place of departed spirits; Hades; also, the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepen', 'n.', 'A stable; a shippen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherd', 'n.', 'A man employed in tending, feeding, and guarding sheep,\n   esp. a flock grazing at large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherd', 'n.', 'The pastor of a church; one with the religious guidance\n   of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherded', 'imp. & p. p.', 'of Shepherd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherding', 'p. pr. & vb. n.', 'of Shepherd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherd', 'v. t.', 'To tend as a shepherd; to guard, herd, lead, or drive,\n   as a shepherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherdess', 'n.', 'A woman who tends sheep; hence, a rural lass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherdias', 'pl. ', 'of Shepherdia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherdia', 'n.', 'A genus of shrubs having silvery scurfy leaves, and\n   belonging to the same family as Elaeagnus; also, any plant of this\n   genus. See Buffalo berry, under Buffalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherdish', 'n.', 'Resembling a shepherd; suiting a shepherd; pastoral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherdism', 'n.', 'Pastoral life or occupation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherdling', 'n.', 'A little shepherd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepherdly', 'a.', 'Resembling, or becoming to, a shepherd; pastoral;\n   rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shepster', 'n.', 'A seamstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherbet', 'n.', 'A refreshing drink, common in the East, made of the juice\n   of some fruit, diluted, sweetened, and flavored in various ways; as,\n   orange sherbet; lemon sherbet; raspberry sherbet, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherbet', 'n.', 'A flavored water ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherbet', 'n.', 'A preparation of bicarbonate of soda, tartaric acid,\n   sugar, etc., variously flavored, for making an effervescing drink; --\n   called also sherbet powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherd', 'n.', 'A fragment; -- now used only in composition, as in potsherd.\n   See Shard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shereef', 'n.', 'Alt. of Sherif');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherif', 'n.', 'A member of an Arab princely family descended from Mohammed\n   through his son-in-law Ali and daughter Fatima. The Grand Shereef is\n   the governor of Mecca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheriat', 'n.', 'The sacred law of the Turkish empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheriff', 'n.', 'The chief officer of a shire or county, to whom is\n   intrusted the execution of the laws, the serving of judicial writs and\n   processes, and the preservation of the peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheriffalty', 'n.', 'Alt. of Sheriffwick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheriffdom', 'n.', 'Alt. of Sheriffwick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheriffry', 'n.', 'Alt. of Sheriffwick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheriffship', 'n.', 'Alt. of Sheriffwick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheriffwick', 'n.', 'The office or jurisdiction of sheriff. See Shrievalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shern', 'n.', 'See Shearn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherris', 'n.', 'Sherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherry', 'n.', 'A Spanish light-colored dry wine, made in Andalusia. As\n   prepared for commerce it is colored a straw color or a deep amber by\n   mixing with it cheap wine boiled down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sherryvallies', 'n. pl.', 'Trousers or overalls of thick cloth or leather,\n   buttoned on the outside of each leg, and generally worn to protect\n   other trousers when riding on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shet', 'imp.', 'of Shet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shette', '', 'of Shet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shet', 'p. pr.', 'of Shet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shetting', 'p. pr. & vb. n.', 'of Shet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shet', 'v. t. & i.', 'To shut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shete', 'v. t. & i.', 'To shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sheth', 'n.', 'The part of a plow which projects downward beneath the beam,\n   for holding the share and other working parts; -- also called standard,\n   or post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shetland pony', '', 'One of a small, hardy breed of horses, with long mane\n   and tail, which originated in the Shetland Islands; a sheltie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shew', 'v. t. & i.', 'See Show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shew', 'n.', 'Show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shewbread', '', 'See Showbread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shewel', 'n.', 'A scarecrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shewer', 'n.', 'One who shews. See Shower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shewn', '', 'p. p. of Shew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiah', 'n.', 'Same as Shiite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shibboleth', 'n.', 'A word which was made the criterion by which to\n   distinguish the Ephraimites from the Gileadites. The Ephraimites, not\n   being able to pronounce sh, called the word sibboleth. See Judges xii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shibboleth', 'n.', 'Also in an extended sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shibboleth', 'n.', 'Hence, the criterion, test, or watchword of a party; a\n   party cry or pet phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shide', 'n.', 'A thin board; a billet of wood; a splinter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shie', 'v. t.', 'See Shy, to throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shied', '', 'imp. & p. p. of Shy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiel', 'n.', 'A sheeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'A broad piece of defensive armor, carried on the arm, --\n   formerly in general use in war, for the protection of the body. See\n   Buckler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'Anything which protects or defends; defense; shelter;\n   protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'Figuratively, one who protects or defends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'In lichens, a Hardened cup or disk surrounded by a rim and\n   containing the fructification, or asci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'The escutcheon or field on which are placed the bearings in\n   coats of arms. Cf. Lozenge. See Illust. of Escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'A framework used to protect workmen in making an adit under\n   ground, and capable of being pushed along as excavation progresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'A spot resembling, or having the form of, a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'A coin, the old French crown, or ecu, having on one side\n   the figure of a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shielded', 'imp. & p. p.', 'of Shield');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shielding', 'p. pr. & vb. n.', 'of Shield');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'To cover with, or as with, a shield; to cover from danger;\n   to defend; to protect from assault or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'To ward off; to keep off or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield', 'n.', 'To avert, as a misfortune; hence, as a supplicatory\n   exclamation, forbid!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield-bearer', 'n.', 'One who, or that which, carries a shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shield-bearer', 'n.', 'Any small moth of the genus Aspidisca, whose larva\n   makes a shieldlike covering for itself out of bits of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shielddrake', 'n.', 'A sheldrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shieldless', 'a.', 'Destitute of a shield, or of protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shieldtail', 'n.', 'Any species of small burrowing snakes of the family\n   Uropeltidae, native of Ceylon and Southern Asia. They have a small\n   mouth which can not be dilated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shieling', 'n.', 'A hut or shelter for shepherds of fishers. See Sheeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifted', 'imp. & p. p.', 'of Shift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifting', 'p. pr. & vb. n.', 'of Shift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'To divide; to distribute; to apportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'To change the place of; to move or remove from one place\n   to another; as, to shift a burden from one shoulder to another; to\n   shift the blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'To change the position of; to alter the bearings of; to\n   turn; as, to shift the helm or sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'To exchange for another of the same class; to remove and\n   to put some similar thing in its place; to change; as, to shift the\n   clothes; to shift the scenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'To change the clothing of; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'To put off or out of the way by some expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiff', 'v. i.', 'To divide; to distribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiff', 'v. i.', 'To make a change or changes; to change position; to move;\n   to veer; to substitute one thing for another; -- used in the various\n   senses of the transitive verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiff', 'v. i.', 'To resort to expedients for accomplishing a purpose; to\n   contrive; to manage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiff', 'v. i.', 'To practice indirect or evasive methods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiff', 'v. i.', 'To slip to one side of a ship, so as to destroy the\n   equilibrum; -- said of ballast or cargo; as, the cargo shifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'The act of shifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'The act of putting one thing in the place of another, or\n   of changing the place of a thing; change; substitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'Something frequently shifted; especially, a womans\n   under-garment; a chemise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'The change of one set of workmen for another; hence, a\n   spell, or turn, of work; also, a set of workmen who work in turn with\n   other sets; as, a night shift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'In building, the extent, or arrangement, of the\n   overlapping of plank, brick, stones, etc., that are placed in courses\n   so as to break joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'A breaking off and dislocation of a seam; a fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shift', 'v. t.', 'A change of the position of the hand on the finger board,\n   in playing the violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiftable', 'a.', 'Admitting of being shifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifter', 'n.', 'One who, or that which, shifts; one who plays tricks or\n   practices artifice; a cozener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifter', 'n.', 'An assistant to the ships cook in washing, steeping, and\n   shifting the salt provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifter', 'n.', 'An arrangement for shifting a belt sidewise from one\n   pulley to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifter', 'n.', 'A wire for changing a loop from one needle to another, as\n   in narrowing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiftiness', 'n.', 'The quality or state of being shifty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifting', 'a.', 'Changing in place, position, or direction; varying;\n   variable; fickle; as, shifting winds; shifting opinions or principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifting', 'a.', 'Adapted or used for shifting anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiftingly', 'adv.', 'In a shifting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiftless', 'a.', 'Destitute of expedients, or not using successful\n   expedients; characterized by failure, especially by failure to provide\n   for ones own support, through negligence or incapacity; hence, lazy;\n   improvident; thriftless; as, a shiftless fellow; shiftless management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shifty', 'a.', 'Full of, or ready with, shifts; fertile in expedients or\n   contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiite', 'n.', 'Alt. of Shiah');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiah', 'n.', 'A member of that branch of the Mohammedans to which the\n   Persians belong. They reject the first three caliphs, and consider Ali\n   as being the first and only rightful successor of Mohammed. They do not\n   acknowledge the Sunna, or body of traditions respecting Mohammed, as\n   any part of the law, and on these accounts are treated as heretics by\n   the Sunnites, or orthodox Mohammedans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shikaree', 'n.', 'Alt. of Shikari');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shikari', 'n.', 'A sportsman; esp., a native hunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilf', 'n.', 'Straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shill', 'v. t.', 'To shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shill', 'v. t.', 'To put under cover; to sheal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shillalah', 'n.', 'Alt. of Shillelah');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shillelah', 'n.', 'An oaken sapling or cudgel; any cudgel; -- so called\n   from Shillelagh, a place in Ireland of that name famous for its oaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilling', 'n.', 'A silver coin, and money of account, of Great Britain and\n   its dependencies, equal to twelve pence, or the twentieth part of a\n   pound, equivalent to about twenty-four cents of the United States\n   currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilling', 'n.', 'In the United States, a denomination of money, differing\n   in value in different States. It is not now legally recognized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilling', 'n.', 'The Spanish real, of the value of one eight of a dollar,\n   or 12/ cets; -- formerly so called in New York and some other States.\n   See Note under 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shill-I-shall-I', 'adv.', 'Alt. of Shilly-shally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilly-shally', 'adv.', 'In an irresolute, undecided, or hesitating manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilly-shally', 'v. i.', 'To hesitate; to act in an irresolute manner;\n   hence, to occupy ones self with trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shilly-shally', 'n.', 'Irresolution; hesitation; also, occupation with\n   trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiloh', 'n.', 'A word used by Jacob on his deathbed, and interpreted\n   variously, as \"the Messiah,\" or as the city \"Shiloh,\" or as \"Rest.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shily', 'adv.', 'See Shyly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shim', 'n.', 'A kind of shallow plow used in tillage to break the ground,\n   and clear it of weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shim', 'n.', 'A thin piece of metal placed between two parts to make a fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shimmered', 'imp. & p. p.', 'of Shimmer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shimmering', 'p. pr. & vb. n.', 'of Shimmer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shimmer', 'v. i.', 'To shine with a tremulous or intermittent light; to\n   shine faintly; to gleam; to glisten; to glimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shimmer', 'n.', 'A faint, tremulous light; a gleaming; a glimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shimmering', 'n.', 'A gleam or glimmering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shimmy', 'n.', 'A chemise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shin', 'n.', 'The front part of the leg below the knee; the front edge of\n   the shin bone; the lower part of the leg; the shank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shin', 'n.', 'A fish plate for rails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shinned', 'imp. & p. p.', 'of Shin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shinning', 'p. pr. & vb. n.', 'of Shin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shin', 'v. i.', 'To climb a mast, tree, rope, or the like, by embracing it\n   alternately with the arms and legs, without help of steps, spurs, or\n   the like; -- used with up; as, to shin up a mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shin', 'v. i.', 'To run about borrowing money hastily and temporarily, as\n   for the payment of ones notes at the bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shin', 'v. t.', 'To climb (a pole, etc.) by shinning up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shindle', 'n.', 'A shingle; also, a slate for roofing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shindle', 'v. t.', 'To cover or roof with shindles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shindies', 'pl. ', 'of Shindy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shindy', 'n.', 'An uproar or disturbance; a spree; a row; a riot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shindy', 'n.', 'Hockey; shinney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shindy', 'n.', 'A fancy or liking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shone', 'imp. & p. p.', 'of Shine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shined', '', 'of Shine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shining', 'p. pr. & vb. n.', 'of Shine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'v. i.', 'To emit rays of light; to give light; to beam with steady\n   radiance; to exhibit brightness or splendor; as, the sun shines by day;\n   the moon shines by night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'v. i.', 'To be bright by reflection of light; to gleam; to be\n   glossy; as, to shine like polished silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'v. i.', 'To be effulgent in splendor or beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'v. i.', 'To be eminent, conspicuous, or distinguished; to exhibit\n   brilliant intellectual powers; as, to shine in courts; to shine in\n   conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'v. t.', 'To cause to shine, as a light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'v. t.', 'To make bright; to cause to shine by reflected light; as,\n   in hunting, to shine the eyes of a deer at night by throwing a light on\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'n.', 'The quality or state of shining; brightness; luster, gloss;\n   polish; sheen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'n.', 'Sunshine; fair weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'n.', 'A liking for a person; a fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'n.', 'Caper; antic; row.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shine', 'v. i.', 'Shining; sheen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiner', 'n.', 'That which shines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiner', 'n.', 'A luminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiner', 'n.', 'A bright piece of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiner', 'n.', 'Any one of numerous species of small freshwater American\n   cyprinoid fishes, belonging to Notropis, or Minnilus, and allied\n   genera; as the redfin (Notropis megalops), and the golden shiner\n   (Notemigonus chrysoleucus) of the Eastern United States; also loosely\n   applied to various other silvery fishes, as the dollar fish, or\n   horsefish, menhaden, moonfish, sailors choice, and the sparada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiner', 'n.', 'The common Lepisma, or furniture bug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiness', 'n.', 'See Shyness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingle', 'n.', 'Round, water-worn, and loose gravel and pebbles, or a\n   collection of roundish stones, such as are common on the seashore and\n   elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingle', 'n.', 'A piece of wood sawed or rived thin and small, with one\n   end thinner than the other, -- used in covering buildings, especially\n   roofs, the thick ends of one row overlapping the thin ends of the row\n   below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingle', 'n.', 'A sign for an office or a shop; as, to hang out ones\n   shingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingled', 'imp. &. p. p.', 'of Shingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingling', 'p. pr. & vb. n.', 'of Shingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingle', 'v. t.', 'To cover with shingles; as, to shingle a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingle', 'v. t.', 'To cut, as hair, so that the ends are evenly exposed\n   all over the head, as shingles on a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingle', 'v. t.', 'To subject to the process of shindling, as a mass of\n   iron from the pudding furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingler', 'n.', 'One who shingles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingler', 'n.', 'A machine for shingling puddled iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingles', 'n.', 'A kind of herpes (Herpes zoster) which spreads half way\n   around the body like a girdle, and is usually attended with violent\n   neuralgic pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingling', 'n.', 'The act of covering with shingles; shingles,\n   collectively; a covering made of shingles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingling', 'n.', 'The process of expelling scoriae and other impurities by\n   hammering and squeezing, in the production of wrought iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shingly', 'a.', 'Abounding with shingle, or gravel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shinhopple', 'n.', 'The hobblebush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shining', 'a.', 'Emitting light, esp. in a continuous manner; radiant; as,\n   shining lamps; also, bright by the reflection of light; as, shining\n   armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shining', 'a.', 'Splendid; illustrious; brilliant; distinguished;\n   conspicious; as, a shining example of charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shining', 'a.', 'Having the surface smooth and polished; -- said of leaves,\n   the surfaces of shells, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shining', 'n.', 'Emission or reflection of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiningness', 'n.', 'Brightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shinney', 'n.', 'The game of hockey; -- so called because of the liability\n   of the players to receive blows on the shin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shinplaster', 'n.', 'Formerly, a jocose term for a bank note greatly\n   depreciated in value; also, for paper money of a denomination less than\n   a dollar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shinto', 'n.', 'Alt. of Shintiism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shintiism', 'n.', 'One of the two great systems of religious belief in\n   Japan. Its essence is ancestor worship, and sacrifice to dead heroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shintoist', 'n.', 'An adherent of Shintoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shinty', 'n.', 'A Scotch game resembling hockey; also, the club used in the\n   game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiny', 'superl.', 'Bright; luminous; clear; unclouded.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ship', 'n.', 'A suffix denoting state, office, dignity, profession, or\n   art; as in lordship, friendship, chancellorship, stewardship,\n   horsemanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'n.', 'Pay; reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'n.', 'Any large seagoing vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'n.', 'Specifically, a vessel furnished with a bowsprit and three\n   masts (a mainmast, a foremast, and a mizzenmast), each of which is\n   composed of a lower mast, a topmast, and a topgallant mast, and\n   square-rigged on all masts. See Illustation in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'n.', 'A dish or utensil (originally fashioned like the hull of a\n   ship) used to hold incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipped', 'imp. & p. p.', 'of Ship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipping', 'p. pr. & vb. n.', 'of Ship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. t.', 'To put on board of a ship, or vessel of any kind, for\n   transportation; to send by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. t.', 'By extension, in commercial usage, to commit to any\n   conveyance for transportation to a distance; as, to ship freight by\n   railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. t.', 'Hence, to send away; to get rid of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. t.', 'To engage or secure for service on board of a ship; as, to\n   ship seamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. t.', 'To receive on board ship; as, to ship a sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. t.', 'To put in its place; as, to ship the tiller or rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. i.', 'To engage to serve on board of a vessel; as, to ship on a\n   man-of-war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship', 'v. i.', 'To embark on a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipboard', 'n.', 'A ships side; hence, by extension, a ship; -- found\n   chiefly in adverbial phrases; as, on shipboard; a shipboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipbuilder', 'n.', 'A person whose occupation is to construct ships and\n   other vessels; a naval architect; a shipwright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipbuilding', 'n.', 'Naval architecturel the art of constructing ships and\n   other vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipfuls', 'pl. ', 'of Shipful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipful', 'n.', 'As much or as many as a ship will hold; enough to fill a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipholder', 'n.', 'A shipowner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipless', 'a.', 'Destitute of ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiplet', 'n.', 'A little ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipload', 'n.', 'The load, or cargo, of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipmen', 'pl. ', 'of Shipman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipman', 'n.', 'A seaman, or sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipmaster', 'n.', 'The captain, master, or commander of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipmate', 'n.', 'One who serves on board of the same ship with another; a\n   fellow sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipment', 'n.', 'The act or process of shipping; as, he was engaged in the\n   shipment of coal for London; an active shipment of wheat from the West.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipment', 'n.', 'That which is shipped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipowner', 'n.', 'Owner of a ship or ships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shippen', 'n.', 'A stable; a cowhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipper', 'n.', 'One who sends goods from one place to another not in the\n   same city or town, esp. one who sends goods by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipping', 'a.', 'Relating to ships, their ownership, transfer, or\n   employment; as, shiping concerns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipping', 'a.', 'Relating to, or concerned in, the forwarding of goods;\n   as, a shipping clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipping', 'n.', 'The act of one who, or of that which, ships; as, the\n   shipping of flour to Liverpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipping', 'n.', 'The collective body of ships in one place, or belonging\n   to one port, country, etc.; vessels, generally; tonnage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipping', 'n.', 'Navigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shippon', 'n.', 'A cowhouse; a shippen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ship-rigged', 'a.', 'Rigged like a ship, that is, having three masts, each\n   with square sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipshape', 'a.', 'Arranged in a manner befitting a ship; hence, trim;\n   tidy; orderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipshape', 'adv.', 'In a shipshape or seamanlike manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipworm', 'n.', 'Any long, slender, worm-shaped bivalve mollusk of Teredo\n   and allied genera. The shipworms burrow in wood, and are destructive to\n   wooden ships, piles of wharves, etc. See Teredo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwreck', 'n.', 'The breaking in pieces, or shattering, of a ship or\n   other vessel by being cast ashore or driven against rocks, shoals,\n   etc., by the violence of the winds and waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwreck', 'n.', 'A ship wrecked or destroyed upon the water, or the parts\n   of such a ship; wreckage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwreck', 'n.', 'Fig.: Destruction; ruin; irretrievable loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwrecked', 'imp. & p. p.', 'of Shipwreck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwrecking', 'p. pr. & vb. n.', 'of Shipwreck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwreck', 'v. t.', 'To destroy, as a ship at sea, by running ashore or on\n   rocks or sandbanks, or by the force of wind and waves in a tempest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwreck', 'v. t.', 'To cause to experience shipwreck, as sailors or\n   passengers. Hence, to cause to suffer some disaster or loss; to destroy\n   or ruin, as if by shipwreck; to wreck; as, to shipwreck a business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipwright', 'n.', 'One whose occupation is to construct ships; a builder\n   of ships or other vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shipyard', 'n.', 'A yard, place, or inclosure where ships are built or\n   repaired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiraz', 'n.', 'A kind of Persian wine; -- so called from the place whence\n   it is brought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shire', 'n.', 'A portion of Great Britain originally under the supervision\n   of an earl; a territorial division, usually identical with a county,\n   but sometimes limited to a smaller district; as, Wiltshire, Yorkshire,\n   Richmondshire, Hallamshire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shire', 'n.', 'A division of a State, embracing several contiguous\n   townships; a county.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirked', 'imp. & p. p.', 'of Shirk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirking', 'p. pr. & vb. n.', 'of Shirk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirk', 'v. t.', 'To procure by petty fraud and trickery; to obtain by mean\n   solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirk', 'v. t.', 'To avoid; to escape; to neglect; -- implying\n   unfaithfulness or fraud; as, to shirk duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirk', 'v. i.', 'To live by shifts and fraud; to shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirk', 'v. i.', 'To evade an obligation; to avoid the performance of duty,\n   as by running away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirk', 'n.', 'One who lives by shifts and tricks; one who avoids the\n   performance of duty or labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirker', 'n.', 'One who shirks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirky', 'a.', 'Disposed to shirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirl', 'a.', 'Shrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirl', 'n.', 'See Schorl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirley', 'n.', 'The bullfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirr', 'n.', 'A series of close parallel runnings which are drawn up so as\n   to make the material between them set full by gatherings; -- called\n   also shirring, and gauging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirred', 'a.', 'Made or gathered into a shirr; as, a shirred bonnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirred', 'a.', 'Broken into an earthen dish and baked over the fire; --\n   said of eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirt', 'n.', 'A loose under-garment for the upper part of the body, made\n   of cotton, linen, or other material; -- formerly used of the\n   under-garment of either sex, now commonly restricted to that worn by\n   men and boys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirted', 'imp. & p. p.', 'of Shirt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirting', 'p. pr. & vb. n.', 'of Shirt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirt', 'v. t. & i.', 'To cover or clothe with a shirt, or as with a shirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirting', 'n.', 'Cloth, specifically cotton cloth, suitable for making\n   shirts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shirtless', 'a.', 'Not having or wearing a shirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shist', '', 'Alt. of Shistose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shistose', '', 'See Shist, Schistose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittah', 'n.', 'Alt. of Shittah tree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittah tree', 'n.', 'A tree that furnished the precious wood of which the\n   ark, tables, altars, boards, etc., of the Jewish tabernacle were made;\n   -- now believed to have been the wood of the Acacia Seyal, which is\n   hard, fine grained, and yellowish brown in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittim', 'n.', 'Alt. of Shittim wood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittim wood', 'n.', 'The wood of the shittah tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittle', 'n.', 'A shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittle', 'a.', 'Wavering; unsettled; inconstant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittlecock', 'n.', 'A shuttlecock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shittleness', 'n.', 'Instability; inconstancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shive', 'n.', 'A slice; as, a shive of bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shive', 'n.', 'A thin piece or fragment; specifically, one of the scales or\n   pieces of the woody part of flax removed by the operation of breaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shive', 'n.', 'A thin, flat cork used for stopping a wide-mouthed bottle;\n   also, a thin wooden bung for casks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'n.', 'One of the small pieces, or splinters, into which a brittle\n   thing is broken by sudden violence; -- generally used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'n.', 'A thin slice; a shive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'n.', 'A variety of blue slate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'n.', 'A sheave or small wheel in a pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'n.', 'A small wedge, as for fastening the bolt of a window\n   shutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'n.', 'A spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shivered', 'imp. & p. p.', 'of Shiver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shivering', 'p. pr. & vb. n.', 'of Shiver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'v. t.', 'To break into many small pieces, or splinters; to\n   shatter; to dash to pieces by a blow; as, to shiver a glass goblet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'v. i.', 'To separate suddenly into many small pieces or parts; to\n   be shattered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'v. i.', 'To tremble; to vibrate; to quiver; to shake, as from\n   cold or fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'v. t.', 'To cause to shake or tremble, as a sail, by steering\n   close to the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver', 'n.', 'The act of shivering or trembling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiveringly', 'adv.', 'In a shivering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shiver-spar', 'n.', 'A variety of calcite, so called from its slaty\n   structure; -- called also slate spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shivery', 'a.', 'Tremulous; shivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shivery', 'a.', 'Easily broken; brittle; shattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoad', 'n.', 'A train of vein material mixed with rubbish; fragments of\n   ore which have become separated by the action of water or the weather,\n   and serve to direct in the discovery of mines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoading', 'n.', 'The tracing of veins of metal by shoads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoal', 'n.', 'A great multitude assembled; a crowd; a throng; -- said\n   especially of fish; as, a shoal of bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoaled', 'imp. & p. p.', 'of Shoal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoaling', 'p. pr. & vb. n.', 'of Shoal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoal', 'v. i.', 'To assemble in a multitude; to throng; as, the fishes\n   shoaled about the place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoal', 'a.', 'Having little depth; shallow; as, shoal water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoal', 'n.', 'A place where the water of a sea, lake, river, pond, etc.,\n   is shallow; a shallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoal', 'n.', 'A sandbank or bar which makes the water shoal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoal', 'v. i.', 'To become shallow; as, the color of the water shows where\n   it shoals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoal', 'v. t.', 'To cause to become more shallow; to come to a more\n   shallow part of; as, a ship shoals her water by advancing into that\n   which is less deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoaliness', 'n.', 'The quality or state of being shoaly; little depth of\n   water; shallowness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoaling', 'a.', 'Becoming shallow gradually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoaly', 'a.', 'Full of shoals, or shallow places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoar', 'n.', 'A prop. See 3d Shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoat', 'n.', 'A young hog. Same as Shote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'A pile or assemblage of sheaves of grain, as wheat, rye, or\n   the like, set up in a field, the sheaves varying in number from twelve\n   to sixteen; a stook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'A lot consisting of sixty pieces; -- a term applied in some\n   Baltic ports to loose goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'v. t.', 'To collect, or make up, into a shock or shocks; to stook;\n   as, to shock rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'v. i.', 'To be occupied with making shocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'A quivering or shaking which is the effect of a blow,\n   collision, or violent impulse; a blow, impact, or collision; a\n   concussion; a sudden violent impulse or onset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'A sudden agitation of the mind or feelings; a sensation of\n   pleasure or pain caused by something unexpected or overpowering; also,\n   a sudden agitating or overpowering event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'A sudden depression of the vital forces of the entire body,\n   or of a port of it, marking some profound impression produced upon the\n   nervous system, as by severe injury, overpowering emotion, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'The sudden convulsion or contraction of the muscles, with\n   the feeling of a concussion, caused by the discharge, through the\n   animal system, of electricity from a charged body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shocked', 'imp. & p. p.', 'of Shock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shocking', 'p. pr. & vb. n.', 'of Shock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'v.', 'To give a shock to; to cause to shake or waver; hence, to\n   strike against suddenly; to encounter with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'v.', 'To strike with surprise, terror, horror, or disgust; to\n   cause to recoil; as, his violence shocked his associates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'v. i.', 'To meet with a shock; to meet in violent encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'A dog with long hair or shag; -- called also shockdog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'n.', 'A thick mass of bushy hair; as, a head covered with a shock\n   of sandy hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock', 'a.', 'Bushy; shaggy; as, a shock hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shockdog', 'n.', 'See 7th Shock, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock-head', 'a.', 'Shock-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shock-headed', 'a.', 'Having a thick and bushy head of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shocking', 'a.', 'Causing to shake or tremble, as by a blow; especially,\n   causing to recoil with horror or disgust; extremely offensive or\n   disgusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shod', 'imp. & p. p.', 'f Shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoddy', 'v. t.', 'A fibrous material obtained by \"deviling,\" or tearing\n   into fibers, refuse woolen goods, old stockings, rags, druggets, etc.\n   See Mungo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoddy', 'v. t.', 'A fabric of inferior quality made of, or containing a\n   large amount of, shoddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoddy', 'a.', 'Made wholly or in part of shoddy; containing shoddy; as,\n   shoddy cloth; shoddy blankets; hence, colloquially, not genuine; sham;\n   pretentious; as, shoddy aristocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoddyism', 'n.', 'The quality or state of being shoddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shode', 'v. t.', 'The parting of the hair on the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shode', 'v. t.', 'The top of the head; the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shode', '', 'Alt. of Shoding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoding', '', 'See Shoad, Shoading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoder', 'n.', 'A package of gold beaters skins in which gold is subjected\n   to the second process of beating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoes', 'pl. ', 'of Shoe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoon', 'pl. ', 'of Shoe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'A covering for the human foot, usually made of leather,\n   having a thick and somewhat stiff sole and a lighter top. It differs\n   from a boot on not extending so far up the leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'Anything resembling a shoe in form, position, or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'A plate or rim of iron nailed to the hoof of an animal to\n   defend it from injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'A band of iron or steel, or a ship of wood, fastened to the\n   bottom of the runner of a sleigh, or any vehicle which slides on the\n   snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'A drag, or sliding piece of wood or iron, placed under the\n   wheel of a loaded vehicle, to retard its motion in going down a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'The part of a railroad car brake which presses upon the wheel\n   to retard its motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'A trough-shaped or spout-shaped member, put at the bottom of\n   the water leader coming from the eaves gutter, so as to throw the water\n   off from the building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'The trough or spout for conveying the grain from the hopper\n   to the eye of the millstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'An inclined trough in an ore-crushing mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'An iron socket or plate to take the thrust of a strut or\n   rafter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'An iron socket to protect the point of a wooden pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'A plate, or notched piece, interposed between a moving part\n   and the stationary part on which it bears, to take the wear and afford\n   means of adjustment; -- called also slipper, and gib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shod', 'imp. & p. p.', 'of Shoe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoeing', 'p. pr. & vb. n.', 'of Shoe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'To furnish with a shoe or shoes; to put a shoe or shoes on;\n   as, to shoe a horse, a sled, an anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoe', 'n.', 'To protect or ornament with something which serves the\n   purpose of a shoe; to tip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoebill', 'n.', 'A large African wading bird (Balaeniceps rex) allied to\n   the storks and herons, and remarkable for its enormous broad swollen\n   bill. It inhabits the valley of the White Nile. See Illust. (l.) of\n   Beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoeblack', 'n.', 'One who polishes shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoehorn', 'n.', 'Alt. of Shoeing-horn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoeing-horn', 'n.', 'A curved piece of polished horn, wood, or metal used\n   to facilitate the entrance of the foot into a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoeing-horn', 'n.', 'Anything by which a transaction is facilitated; a\n   medium; -- by way of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoeing-horn', 'n.', 'Anything which draws on or allures; an inducement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoeless', 'a.', 'Destitute of shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoemaker', 'n.', 'One whose occupation it is to make shoes and boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoemaker', 'n.', 'The threadfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoemaker', 'n.', 'The runner, 12.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoemaking', 'n.', 'The business of a shoemaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoer', 'n.', 'One who fits shoes to the feet; one who furnishes or puts on\n   shoes; as, a shoer of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shog', 'n.', 'A shock; a jog; a violent concussion or impulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shog', 'v. t.', 'To shake; to shock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shog', 'v. i.', 'To jog; to move on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoggle', 'v. t.', 'To joggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shogun', 'n.', 'A title originally conferred by the Mikado on the military\n   governor of the eastern provinces of Japan. By gradual usurpation of\n   power the Shoguns (known to foreigners as Tycoons) became finally the\n   virtual rulers of Japan. The title was abolished in 1867.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shogunate', 'n.', 'The office or dignity of a Shogun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shola', 'n.', 'See Sola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shole', 'n.', 'A plank fixed beneath an object, as beneath the rudder of a\n   vessel, to protect it from injury; a plank on the ground under the end\n   of a shore or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shole', 'n.', 'See Shoal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shonde', 'n.', 'Harm; disgrace; shame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shone', '', 'imp. & p. p. of Shine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoo', 'interj.', 'Begone; away; -- an expression used in frightening away\n   animals, especially fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooi', 'n.', 'The Richardsons skua (Stercorarius parasiticus);- so called\n   from its cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shook', '', 'imp. & obs. or poet. p. p. of Shake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shook', 'n.', 'A set of staves and headings sufficient in number for one\n   hogshead, cask, barrel, or the like, trimmed, and bound together in\n   compact form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shook', 'n.', 'A set of boards for a sugar box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shook', 'n.', 'The parts of a piece of house furniture, as a bedstead,\n   packed together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shook', 'v. t.', 'To pack, as staves, in a shook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoon', 'n.', 'pl. of Shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoop', '', 'imp. of Shape. Shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'n.', 'An inclined plane, either artificial or natural, down which\n   timber, coal, etc., are caused to slide; also, a narrow passage, either\n   natural or artificial, in a stream, where the water rushes rapidly;\n   esp., a channel, having a swift current, connecting the ends of a bend\n   in the stream, so as to shorten the course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'imp. & p. p.', 'of Shoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooting', 'p. pr. & vb. n.', 'of Shoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotten', '', 'of Shoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To let fly, or cause to be driven, with force, as an\n   arrow or a bullet; -- followed by a word denoting the missile, as an\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To discharge, causing a missile to be driven forth; --\n   followed by a word denoting the weapon or instrument, as an object; --\n   often with off; as, to shoot a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To strike with anything shot; to hit with a missile;\n   often, to kill or wound with a firearm; -- followed by a word denoting\n   the person or thing hit, as an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To send out or forth, especially with a rapid or sudden\n   motion; to cast with the hand; to hurl; to discharge; to emit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To push or thrust forward; to project; to protrude; --\n   often with out; as, a plant shoots out a bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To plane straight; to fit by planing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To pass rapidly through, over, or under; as, to shoot a\n   rapid or a bridge; to shoot a sand bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To variegate as if by sprinkling or intermingling; to\n   color in spots or patches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To cause an engine or weapon to discharge a missile; --\n   said of a person or an agent; as, they shot at a target; he shoots\n   better than he rides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To discharge a missile; -- said of an engine or\n   instrument; as, the gun shoots well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To be shot or propelled forcibly; -- said of a missile;\n   to be emitted or driven; to move or extend swiftly, as if propelled;\n   as, a shooting star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To penetrate, as a missile; to dart with a piercing\n   sensation; as, shooting pains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To feel a quick, darting pain; to throb in pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To germinate; to bud; to sprout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To grow; to advance; as, to shoot up rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To change form suddenly; especially, to solidify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To protrude; to jut; to project; to extend; as, the land\n   shoots into a promontory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'v. i.', 'To move ahead by force of momentum, as a sailing vessel\n   when the helm is put hard alee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'n.', 'The act of shooting; the discharge of a missile; a shot; as,\n   the shoot of a shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'n.', 'A young branch or growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'n.', 'A rush of water; a rapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'n.', 'A vein of ore running in the same general direction as the\n   lode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'n.', 'A weft thread shot through the shed by the shuttle; a pick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoot', 'n.', 'A shoat; a young hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooter', 'n.', 'One who shoots, as an archer or a gunner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooter', 'n.', 'That which shoots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooter', 'n.', 'A firearm; as, a five-shooter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooter', 'n.', 'A shooting star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooting', 'n.', 'The act of one who, or that which, shoots; as, the\n   shooting of an archery club; the shooting of rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooting', 'n.', 'A wounding or killing with a firearm; specifically\n   (Sporting), the killing of game; as, a week of shooting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooting', 'n.', 'A sensation of darting pain; as, a shooting in ones\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooting', 'a.', 'Of or pertaining to shooting; for shooting; darting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shooty', 'a.', 'Sprouting or coming up freely and regularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shop', '', 'imp. of Shape. Shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shop', 'n.', 'A building or an apartment in which goods, wares, drugs,\n   etc., are sold by retail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shop', 'n.', 'A building in which mechanics or artisans work; as, a shoe\n   shop; a car shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopped', 'imp. & p. p.', 'of Shop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopping', 'p. pr. & vb. n.', 'of Shop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shop', 'v. i.', 'To visit shops for the purpose of purchasing goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopboard', 'n.', 'A bench or board on which work is performed; a\n   workbench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopbook', 'n.', 'A book in which a tradesman keeps his accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopboy', 'n.', 'A boy employed in a shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopen', '', 'p. p. of Shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopgirl', 'n.', 'A girl employed in a shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopkeeper', 'n.', 'A trader who sells goods in a shop, or by retail; -- in\n   distinction from one who sells by wholesale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoplifter', 'n.', 'One who steals anything in a shop, or takes goods\n   privately from a shop; one who, under pretense of buying goods, takes\n   occasion to steal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoplifting', 'n.', 'Larceny committed in a shop; the stealing of anything\n   from a shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoplike', 'a.', 'Suiting a shop; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopmaid', 'n.', 'A shopgirl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopmen', 'pl. ', 'of Shopman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopman', 'n.', 'A shopkeeper; a retailer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopman', 'n.', 'One who serves in a shop; a salesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopman', 'n.', 'One who works in a shop or a factory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopper', 'n.', 'One who shops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoppish', 'a.', 'Having the appearance or qualities of a shopkeeper, or\n   shopman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoppy', 'a.', 'Abounding with shops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoppy', 'a.', 'Of or pertaining to shops, or ones own shop or business;\n   as, shoppy talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopshift', 'n.', 'The trick of a shopkeeper; deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopwalker', 'n.', 'One who walks about in a shop as an overseer and\n   director. Cf. Floorwalker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopwomen', 'pl. ', 'of Shopwoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopwoman', 'n.', 'A woman employed in a shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shopworn', 'a.', 'Somewhat worn or damaged by having been kept for a time\n   in a shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorage', 'n.', 'Duty paid for goods brought on shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shore', '', 'imp. of Shear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shore', 'n.', 'A sewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shore', 'n.', 'A prop, as a timber, placed as a brace or support against\n   the side of a building or other structure; a prop placed beneath\n   anything, as a beam, to prevent it from sinking or sagging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shored', 'imp. & p. p.', 'of Shore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoring', 'p. pr. & vb. n.', 'of Shore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shore', 'v. t.', 'To support by a shore or shores; to prop; -- usually with\n   up; as, to shore up a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shore', 'v. t.', 'The coast or land adjacent to a large body of water, as\n   an ocean, lake, or large river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shore', 'v. t.', 'To set on shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoreless', 'a.', 'Having no shore or coast; of indefinite or unlimited\n   extent; as, a shoreless ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoreling', 'n.', 'See Shorling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorer', 'n.', 'One who, or that which, shores or props; a prop; a shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoreward', 'adv.', 'Toward the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoring', 'n.', 'The act of supporting or strengthening with a prop or\n   shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoring', 'n.', 'A system of props; props, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorl', 'a.', 'Alt. of Shorlaceous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorlaceous', 'a.', 'See Schorl, Schorlaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorling', 'n.', 'The skin of a sheen after the fleece is shorn off, as\n   distinct from the morling, or skin taken from the dead sheep; also, a\n   sheep of the first years shearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorling', 'n.', 'A person who is shorn; a shaveling; hence, in contempt, a\n   priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorn', '', 'p. p. of Shear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Not long; having brief length or linear extension; as,\n   a short distance; a short piece of timber; a short flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Not extended in time; having very limited duration; not\n   protracted; as, short breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Limited in quantity; inadequate; insufficient; scanty;\n   as, a short supply of provisions, or of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Insufficiently provided; inadequately supplied;\n   scantily furnished; lacking; not coming up to a resonable, or the\n   ordinary, standard; -- usually with of; as, to be short of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Deficient; defective; imperfect; not coming up, as to a\n   measure or standard; as, an account which is short of the trith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Not distant in time; near at hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Limited in intellectual power or grasp; not\n   comprehensive; narrow; not tenacious, as memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Less important, efficaceous, or powerful; not equal or\n   equivalent; less (than); -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Abrupt; brief; pointed; petulant; as, he gave a short\n   answer to the question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Breaking or crumbling readily in the mouth; crisp; as,\n   short pastry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Brittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'superl.', 'Engaging or engaged to deliver what is not possessed;\n   as, short contracts; to be short of stock. See The shorts, under Short,\n   n., and To sell short, under Short, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'adv.', 'Not prolonged, or relatively less prolonged, in utterance;\n   -- opposed to long, and applied to vowels or to syllables. In English,\n   the long and short of the same letter are not, in most cases, the long\n   and short of the same sound; thus, the i in ill is the short sound, not\n   of i in isle, but of ee in eel, and the e in pet is the short sound of\n   a in pate, etc. See Quantity, and Guide to Pronunciation, //22, 30.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'n.', 'A summary account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'n.', 'The part of milled grain sifted out which is next finer than\n   the bran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'n.', 'Short, inferior hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'n.', 'Breeches; shortclothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'n.', 'A short sound, syllable, or vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'adv.', 'In a short manner; briefly; limitedly; abruptly; quickly;\n   as, to stop short in ones course; to turn short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'v. t.', 'To shorten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short', 'v. i.', 'To fail; to decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortage', 'n.', 'Amount or extent of deficiency, as determined by some\n   requirement or standard; as, a shortage in money accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-breathed', 'a.', 'Having short-breath, or quick respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-breathed', 'a.', 'Having short life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortcake', 'n.', 'An unsweetened breakfast cake shortened with butter or\n   lard, rolled thin, and baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short circuit', '', 'A circuit formed or closed by a conductor of\n   relatively low resistance because shorter or of relatively great\n   conductivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-circuited', 'imp. & p. p.', 'of Short-circuit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-circuiting', 'p. pr. & vb. n.', 'of Short-circuit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-circuit', 'v. t.', 'To join, as the electrodes of a battery or dynamo\n   or any two points of a circuit, by a conductor of low resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortclothes', 'n.', 'Coverings for the legs of men or boys, consisting of\n   trousers which reach only to the knees, -- worn with long stockings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortcoming', 'n.', 'The act of falling, or coming short');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortcoming', 'n.', 'The failure of a crop, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortcoming', 'n.', 'Neglect of, or failure in, performance of duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-dated', 'a.', 'Having little time to run from the date.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortened /', 'imp. & p. p.', 'of Shorten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortening', 'p. pr. & vb. n.', 'of Shorten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorten', 'a.', 'To make short or shorter in measure, extent, or time; as,\n   to shorten distance; to shorten a road; to shorten days of calamity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorten', 'a.', 'To reduce or diminish in amount, quantity, or extent; to\n   lessen; to abridge; to curtail; to contract; as, to shorten work, an\n   allowance of food, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorten', 'a.', 'To make deficient (as to); to deprive; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorten', 'a.', 'To make short or friable, as pastry, with butter, lard,\n   pot liquor, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorten', 'v. i.', 'To become short or shorter; as, the day shortens in\n   northern latitudes from June to December; a metallic rod shortens by\n   cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortener', 'n.', 'One who, or that which, shortens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortening', 'n.', 'The act of making or becoming short or shorter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortening', 'n.', 'That which renders pastry short or friable, as butter,\n   lard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorthand', 'n.', 'A compendious and rapid method or writing by\n   substituting characters, abbreviations, or symbols, for letters, words,\n   etc.; short writing; stenography. See Illust. under Phonography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-handed', 'a.', 'Short of, or lacking the regular number of, servants\n   or helpers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorthead', 'n.', 'A sucking whale less than one year old; -- so called by\n   sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shorthorn', 'a.', 'One of a breed of large, heavy domestic cattle having\n   short horns. The breed was developed in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-jointed', 'a.', 'Having short intervals between the joints; -- said\n   of a plant or an animal, especially of a horse whose pastern is too\n   short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-lived', 'a.', 'Not living or lasting long; being of short\n   continuance; as, a short-lived race of beings; short-lived pleasure;\n   short-lived passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortly', 'adv.', 'In a short or brief time or manner; soon; quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortly', 'adv.', 'In few words; briefly; abruptly; curtly; as, to express\n   ideas more shortly in verse than in prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortness', 'n.', 'The quality or state of being short; want of reach or\n   extension; brevity; deficiency; as, the shortness of a journey; the\n   shortness of the days in winter; the shortness of an essay; the\n   shortness of the memory; a shortness of provisions; shortness of\n   breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortsighted', 'a.', 'Not able to see far; nearsighted; myopic. See Myopic,\n   and Myopia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortsighted', 'a.', 'Fig.: Not able to look far into futurity; unable to\n   understand things deep; of limited intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortsighted', 'a.', 'Having little regard for the future; heedless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-spoken', 'a.', 'Speaking in a quick or short manner; hence, gruff;\n   curt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortstop', 'n.', 'The player stationed in the field bewtween the second\n   and third bases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-waisted', 'a.', 'Having a short waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-winded', 'a.', 'Affected with shortness of breath; having a quick,\n   difficult respiration, as dyspnoic and asthmatic persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shortwing', 'n.', 'Any one of several species of small wrenlike Asiatic\n   birds having short wings and a short tail. They belong to Brachypterix,\n   Callene, and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Short-wited', 'a.', 'Having little wit; not wise; having scanty intellect\n   or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shory', 'a.', 'Lying near the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoshones', 'n. pl.', 'A linguistic family or stock of North American\n   Indians, comprising many tribes, which extends from Montana and Idaho\n   into Mexico. In a restricted sense the name is applied especially to\n   the Snakes, the most northern of the tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', '', 'imp. & p. p. of Shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'a.', 'Woven in such a way as to produce an effect of variegation,\n   of changeable tints, or of being figured; as, shot silks. See Shoot, v.\n   t., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'v. t.', 'A share or proportion; a reckoning; a scot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'pl. ', 'of Shot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shots', 'pl. ', 'of Shot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'n.', 'The act of shooting; discharge of a firearm or other weapon\n   which throws a missile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'n.', 'A missile weapon, particularly a ball or bullet;\n   specifically, whatever is discharged as a projectile from firearms or\n   cannon by the force of an explosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'n.', 'Small globular masses of lead, of various sizes, -- used\n   chiefly for killing game; as, bird shot; buckshot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'n.', 'The flight of a missile, or the distance which it is, or can\n   be, thrown; as, the vessel was distant more than a cannon shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'n.', 'A marksman; one who practices shooting; as, an exellent shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotted', 'imp. & p. p.', 'of Shot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotting', 'p. pr. & vb. n.', 'of Shot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot', 'v. t.', 'To load with shot, as a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot-clog', 'n.', 'A person tolerated only because he pays the shot, or\n   reckoning, for the rest of the company, otherwise a mere clog on them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shote', 'v. t.', 'A fish resembling the trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shote', 'v. t.', 'A young hog; a shoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot-free', 'a.', 'Not to be injured by shot; shot-proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot-free', 'a.', 'Free from charge or expense; hence, unpunished;\n   scot-free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotgun', 'n.', 'A light, smooth-bored gun, often double-barreled,\n   especially designed for firing small shot at short range, and killing\n   small game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shot-proof', 'a.', 'Impenetrable by shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shots', 'n. pl.', 'The refuse of cattle taken from a drove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotted', 'a.', 'Loaded with shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotted', 'a.', 'Having a shot attached; as, a shotten suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotten', 'n.', 'Having ejected the spawn; as, a shotten herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shotten', 'n.', 'Shot out of its socket; dislocated, as a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shough', 'n.', 'A shockdog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shough', 'interj.', 'See Shoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Should', 'imp.', 'Used as an auxiliary verb, to express a conditional or\n   contingent act or state, or as a supposition of an actual fact; also,\n   to express moral obligation (see Shall); e. g.: they should have come\n   last week; if I should go; I should think you could go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'n.', 'The joint, or the region of the joint, by which the fore\n   limb is connected with the body or with the shoulder girdle; the\n   projection formed by the bones and muscles about that joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'n.', 'The flesh and muscles connected with the shoulder joint;\n   the upper part of the back; that part of the human frame on which it is\n   most easy to carry a heavy burden; -- often used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'n.', 'Fig.: That which supports or sustains; support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'n.', 'That which resembles a human shoulder, as any\n   protuberance or projection from the body of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'n.', 'The upper joint of the fore leg and adjacent parts of an\n   animal, dressed for market; as, a shoulder of mutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'n.', 'The angle of a bastion included between the face and\n   flank. See Illust. of Bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'n.', 'An abrupt projection which forms an abutment on an\n   object, or limits motion, etc., as the projection around a tenon at the\n   end of a piece of timber, the part of the top of a type which projects\n   beyond the base of the raised character, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shouldered', 'imp. & p. p.', 'of Shoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shouldering', 'p. pr. & vb. n.', 'of Shoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'v. t.', 'To push or thrust with the shoulder; to push with\n   violence; to jostle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder', 'v. t.', 'To take upon the shoulder or shoulders; as, to\n   shoulder a basket; hence, to assume the burden or responsibility of;\n   as, to shoulder blame; to shoulder a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shouldered', 'a.', 'Having shoulders; -- used in composition; as, a\n   broad-shouldered man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoulder-shotten', 'a.', 'Sprained in the shoulder, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shouted', 'imp. & p. p.', 'of Shout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shouting', 'p. pr. & vb. n.', 'of Shout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shout', 'v. i.', 'To utter a sudden and loud outcry, as in joy, triumph, or\n   exultation, or to attract attention, to animate soldiers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shout', 'v. t.', 'To utter with a shout; to cry; -- sometimes with out; as,\n   to shout, or to shout out, a mans name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shout', 'v. t.', 'To treat with shouts or clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shout', 'n.', 'A loud burst of voice or voices; a vehement and sudden\n   outcry, especially of a multitudes expressing joy, triumph, exultation,\n   or animated courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shouter', 'n.', 'One who shouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoved', 'imp. & p. p.', 'of Shove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoving', 'p. pr. & vb. n.', 'of Shove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shove', 'v. t.', 'To drive along by the direct and continuous application\n   of strength; to push; especially, to push (a body) so as to make it\n   move along the surface of another body; as, to shove a boat on the\n   water; to shove a table across the floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shove', 'v. t.', 'To push along, aside, or away, in a careless or rude\n   manner; to jostle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shove', 'v. i.', 'To push or drive forward; to move onward by pushing or\n   jostling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shove', 'v. i.', 'To move off or along by an act pushing, as with an oar a\n   pole used by one in a boat; sometimes with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shove', 'n.', 'The act of shoving; a forcible push.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shove', '', 'p. p. of Shove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoveboard', 'n.', 'Alt. of Shovegroat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovegroat', 'n.', 'The same as Shovelboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovel', 'v. t.', 'An implement consisting of a broad scoop, or more or\n   less hollow blade, with a handle, used for lifting and throwing earth,\n   coal, grain, or other loose substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoveled', 'imp. & p. p.', 'of Shovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelled', '', 'of Shovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoveling', 'p. pr. & vb. n.', 'of Shovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelling', '', 'of Shovel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovel', 'v. t.', 'To take up and throw with a shovel; as, to shovel earth\n   into a heap, or into a cart, or out of a pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovel', 'v. t.', 'To gather up as with a shovel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelard', 'n.', 'Shoveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelbill', 'n.', 'The shoveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelboard', 'n.', 'A board on which a game is played, by pushing or\n   driving pieces of metal or money to reach certain marks; also, the game\n   itself. Called also shuffleboard, shoveboard, shovegroat, shovelpenny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelboard', 'n.', 'A game played on board ship in which the aim is to\n   shove or drive with a cue wooden disks into divisions chalked on the\n   deck; -- called also shuffleboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoveler', 'n.', 'One who, or that which, shovels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoveler', 'n.', 'A river duck (Spatula clypeata), native of Europe and\n   America. It has a large bill, broadest towards the tip. The male is\n   handsomely variegated with green, blue, brown, black, and white on the\n   body; the head and neck are dark green. Called also broadbill,\n   spoonbill, shovelbill, and maiden duck. The Australian shoveler, or\n   shovel-nosed duck (S. rhynchotis), is a similar species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelfuls', 'pl. ', 'of Shovelful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelful', 'n.', 'As much as a shovel will hold; enough to fill a shovel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelhead', 'n.', 'A shark (Sphryna tiburio) allied to the hammerhead, and\n   native of the warmer parts of the Atlantic and Pacific oceans; --\n   called also bonnet shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelnose', 'n.', 'The common sand shark. See under Snad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelnose', 'n.', 'A small California shark (Heptranchias maculatus),\n   which is taken for its oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelnose', 'n.', 'A Pacific Ocean shark (Hexanchus corinus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovelnose', 'n.', 'A ganoid fish of the Sturgeon family (Scaphirhynchus\n   platyrhynchus) of the Mississippi and Ohio rivers; -- called also white\n   sturgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shovel-nosed', 'a.', 'Having a broad, flat nose; as, the shovel-nosed duck,\n   or shoveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shoven', '', 'p. p. of Shove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showed', 'imp.', 'of Show');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shown', 'p. p.', 'of Show');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showed', '', 'of Show');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showing', 'p. pr. & vb. n.', 'of Show');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'v. t.', 'To exhibit or present to view; to place in sight; to\n   display; -- the thing exhibited being the object, and often with an\n   indirect object denoting the person or thing seeing or beholding; as,\n   to show a house; show your colors; shopkeepers show customers goods\n   (show goods to customers).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'v. t.', 'To exhibit to the mental view; to tell; to disclose; to\n   reveal; to make known; as, to show ones designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'v. t.', 'Specifically, to make known the way to (a person); hence,\n   to direct; to guide; to asher; to conduct; as, to show a person into a\n   parlor; to show one to the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'v. t.', 'To make apparent or clear, as by evidence, testimony, or\n   reasoning; to prove; to explain; also, to manifest; to evince; as, to\n   show the truth of a statement; to show the causes of an event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'v. t.', 'To bestow; to confer; to afford; as, to show favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'v. i.', 'To exhibit or manifest ones self or itself; to appear; to\n   look; to be in appearance; to seem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'v. i.', 'To have a certain appearance, as well or ill, fit or\n   unfit; to become or suit; to appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'n.', 'The act of showing, or bringing to view; exposure to sight;\n   exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'n.', 'That which os shown, or brought to view; that which is\n   arranged to be seen; a spectacle; an exhibition; as, a traveling show;\n   a cattle show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'n.', 'Proud or ostentatious display; parade; pomp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'n.', 'Semblance; likeness; appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'n.', 'False semblance; deceitful appearance; pretense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'n.', 'A discharge, from the vagina, of mucus streaked with blood,\n   occuring a short time before labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Show', 'n.', 'A pale blue flame, at the top of a candle flame, indicating\n   the presence of fire damp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showbread', 'n.', 'Bread of exhibition; loaves to set before God; -- the\n   term used in translating the various phrases used in the Hebrew and\n   Greek to designate the loaves of bread which the priest of the week\n   placed before the Lord on the golden table in the sanctuary. They were\n   made of fine flour unleavened, and were changed every Sabbath. The\n   loaves, twelve in number, represented the twelve tribes of Israel. They\n   were to be eaten by the priests only, and in the Holy Place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'n.', 'One who shows or exhibits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'n.', 'That which shows; a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'n.', 'A fall or rain or hail of short duration; sometimes, but\n   rarely, a like fall of snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'n.', 'That which resembles a shower in falling or passing through\n   the air copiously and rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'n.', 'A copious supply bestowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showered', 'imp. & p. p.', 'of Shower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showering', 'p. pr. & vb. n.', 'of Shower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'v. t.', 'To water with a shower; to //t copiously with rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'v. t.', 'To bestow liberally; to destribute or scatter in\n   /undance; to rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shower', 'v. i.', 'To rain in showers; to fall, as in a hower or showers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showerful', 'a.', 'Full of showers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showeriness', 'n.', 'Quality of being showery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showerless', 'a.', 'Rainless; freo from showers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showery', 'a.', 'Raining in showers; abounding with frequent showers of\n   rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showery', 'a.', 'Of or pertaining to a shower or showers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showily', 'adv.', 'In a showy manner; pompously; with parade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showiness', 'n.', 'The quality or state of being showy; pompousness; great\n   parade; ostentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showing', 'n.', 'Appearance; display; exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showing', 'n.', 'Presentation of facts; statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showish', 'a.', 'Showy; ostentatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showmen', 'pl. ', 'of Showman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showman', 'n.', 'One who exhibits a show; a proprietor of a show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shown', '', 'p. p. of Show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showroom', 'n.', 'A room or apartment where a show is exhibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showroom', 'n.', 'A room where merchandise is exposed for sale, or where\n   samples are displayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Showy', 'a.', 'Making a show; attracting attention; presenting a marked\n   appearance; ostentatious; gay; gaudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrag', 'n.', 'A twig of a tree cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrag', 'v. t.', 'To trim, as trees; to lop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shragger', 'n.', 'One who lops; one who trims trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shram', 'v. t.', 'To cause to shrink or shrivel with cold; to benumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrank', '', 'imp. of Shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrap', 'n.', 'Alt. of Shrape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrape', 'n.', 'A place baited with chaff to entice birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrapnel', 'a.', 'Applied as an appellation to a kind of shell invented by\n   Gen. H. Shrapnel of the British army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrapnel', 'n.', 'A shrapnel shell; shrapnel shells, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shred', 'n.', 'A long, narrow piece cut or torn off; a strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shred', 'n.', 'In general, a fragment; a piece; a particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shred', 'imp. & p. p.', 'of Shred');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shredded', '', 'of Shred');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shredding', 'p. pr. & vb. n.', 'of Shred');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shred', 'n.', 'To cut or tear into small pieces, particularly narrow and\n   long pieces, as of cloth or leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shred', 'n.', 'To lop; to prune; to trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shredcook', 'n.', 'The fieldfare; -- so called from its harsh cry before\n   rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shredding', 'n.', 'The act of cutting or tearing into shreds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shredding', 'n.', 'That which is cut or torn off; a piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shreddy', 'a.', 'Consisting of shreds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shredless', 'a.', 'Having no shreds; without a shred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrew', 'a.', 'Wicked; malicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrew', 'a.', 'Originally, a brawling, turbulent, vexatious person of\n   either sex, but now restricted in use to females; a brawler; a scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrew', 'a.', 'Any small insectivore of the genus Sorex and several allied\n   genera of the family Sorecidae. In form and color they resemble mice,\n   but they have a longer and more pointed nose. Some of them are the\n   smallest of all mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrew', 'a.', 'To beshrew; to curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrewd', 'superl.', 'Inclining to shrew; disposing to curse or scold;\n   hence, vicious; malicious; evil; wicked; mischievous; vexatious; rough;\n   unfair; shrewish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrewd', 'superl.', 'Artful; wily; cunning; arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrewd', 'superl.', 'Able or clever in practical affairs; sharp in\n   business; astute; sharp-witted; sagacious; keen; as, a shrewd observer;\n   a shrewd design; a shrewd reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrewish', 'a.', 'having the qualities of a shrew; having a scolding\n   disposition; froward; peevish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrewmouse', 'n.', 'A shrew; especially, the erd shrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrieked', 'imp. & p. p.', 'of Shriek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrieking', 'p. pr. & vb. n.', 'of Shriek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriek', 'v. i.', 'To utter a loud, sharp, shrill sound or cry, as do some\n   birds and beasts; to scream, as in a sudden fright, in horror or\n   anguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriek', 'v. t.', 'To utter sharply and shrilly; to utter in or with a\n   shriek or shrieks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriek', 'n.', 'A sharp, shrill outcry or scream; a shrill wild cry such as\n   is caused by sudden or extreme terror, pain, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrieker', 'n.', 'One who utters a shriek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrieval', 'a.', 'Of or pertaining to a sheriff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrievalty', 'n.', 'The office, or sphere of jurisdiction, of a sheriff;\n   sheriffalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrieve', 'n.', 'A sheriff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrieve', 'v. t.', 'To shrive; to question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrift', 'n.', 'The act of shriving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrift', 'n.', 'Confession made to a priest, and the absolution consequent\n   upon it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shright', '', 'imp. & p. p. of Shriek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shright', 'n.', 'A shriek; shrieking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrike', 'v. i.', 'Any one of numerous species of oscinine birds of the\n   family Laniidae, having a strong hooked bill, toothed at the tip. Most\n   shrikes are insectivorous, but the common European gray shrike (Lanius\n   excubitor), the great northern shrike (L. borealis), and several\n   others, kill mice, small birds, etc., and often impale them on thorns,\n   and are, on that account called also butcher birds. See under Butcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrill', 'v. i.', 'Acute; sharp; piercing; having or emitting a sharp,\n   piercing tone or sound; -- said of a sound, or of that which produces a\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrill', 'n.', 'A shrill sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrilled', 'imp. & p. p.', 'of Shrill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrilling', 'p. pr. & vb. n.', 'of Shrill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrill', 'v. i.', 'To utter an acute, piercing sound; to sound with a\n   sharp, shrill tone; to become shrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrill', 'v. t.', 'To utter or express in a shrill tone; to cause to make a\n   shrill sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrill-gorged', 'a.', 'Having a throat which produces a shrill note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrillness', 'n.', 'The quality or state of being shrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrill-tongued', 'a.', 'Having a shrill voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrilly', 'adv.', 'In a shrill manner; acutely; with a sharp sound or\n   voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrilly', 'a.', 'Somewhat shrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrimp', 'v. t.', 'To contract; to shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrimp', 'v.', 'Any one of numerous species of macruran Crustacea belonging\n   to Crangon and various allied genera, having a slender body and long\n   legs. Many of them are used as food. The larger kinds are called also\n   prawns. See Illust. of Decapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrimp', 'v.', 'In a more general sense, any species of the macruran tribe\n   Caridea, or any species of the order Schizopoda, having a similar form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrimp', 'v.', 'In a loose sense, any small crustacean, including some\n   amphipods and even certain entomostracans; as, the fairy shrimp, and\n   brine shrimp. See under Fairy, and Brine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrimp', 'v.', 'Figuratively, a little wrinkled man; a dwarf; -- in\n   contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrimper', 'n.', 'One who fishes for shrimps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrine', 'n.', 'A case, box, or receptacle, especially one in which are\n   deposited sacred relics, as the bones of a saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrine', 'n.', 'Any sacred place, as an altar, tromb, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrine', 'n.', 'A place or object hallowed from its history or\n   associations; as, a shrine of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrine', 'v. t.', 'To enshrine; to place reverently, as in a shrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrank', 'imp.', 'of Shrink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrunk', '', 'of Shrink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrunk', 'p. p.', 'of Shrink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrunken', '', 'of Shrink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrinking', 'p. pr. & vb. n.', 'of Shrink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrink', 'v. i.', 'To wrinkle, bend, or curl; to shrivel; hence, to\n   contract into a less extent or compass; to gather together; to become\n   compacted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrink', 'v. i.', 'To withdraw or retire, as from danger; to decline action\n   from fear; to recoil, as in fear, horror, or distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrink', 'v. i.', 'To express fear, horror, or pain by contracting the\n   body, or part of it; to shudder; to quake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrink', 'v. t.', 'To cause to contract or shrink; as, to shrink finnel by\n   imersing it in boiling water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrink', 'v. t.', 'To draw back; to withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrink', 'n.', 'The act shrinking; shrinkage; contraction; also, recoil;\n   withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrinkage', 'n.', 'The act of shrinking; a contraction into less bulk or\n   measurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrinkage', 'n.', 'The amount of such contraction; the bulk or dimension\n   lost by shrinking, as of grain, castings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrinkage', 'n.', 'Decrease in value; depreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrinker', 'n.', 'One who shrinks; one who withdraws from danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrinking', '', 'a. & n. from Shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrinkingly', 'adv.', 'In a shrinking manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrivalty', 'n.', 'Shrievalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrived', 'imp.', 'of Shrive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrove', '', 'of Shrive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriven', 'p. p.', 'of Shrive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrived', '', 'of Shrive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriving', 'p. pr. & vb. n.', 'of Shrive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrive', 'v. t.', 'To hear or receive the confession of; to administer\n   confession and absolution to; -- said of a priest as the agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrive', 'v. t.', 'To confess, and receive absolution; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrive', 'v. i.', 'To receive confessions, as a priest; to administer\n   confession and absolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriveled', 'imp. & p. p.', 'of Shrivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrivelled', '', 'of Shrivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriveling', 'p. pr. & vb. n.', 'of Shrivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrivelling', '', 'of Shrivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrivel', 'v. i.', 'To draw, or be drawn, into wrinkles; to shrink, and\n   form corrugations; as, a leaf shriveles in the hot sun; the skin\n   shrivels with age; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrivel', 'v. t.', 'To cause to shrivel or contract; to cause to shrink\n   onto corruptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriven', '', 'p. p. of Shrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriver', 'n.', 'One who shrives; a confessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shriving', 'n.', 'Shrift; confession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroff', 'n.', 'A banker, or changer of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroffage', 'n.', 'The examination of coins, and the separation of the good\n   from the debased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrood', 'v. t.', 'To trim; to lop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'That which clothes, covers, conceals, or protects; a\n   garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'Especially, the dress for the dead; a winding sheet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'That which covers or shelters like a shroud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'A covered place used as a retreat or shelter, as a cave or\n   den; also, a vault or crypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'The branching top of a tree; foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'A set of ropes serving as stays to support the masts. The\n   lower shrouds are secured to the sides of vessels by heavy iron bolts\n   and are passed around the head of the lower masts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'One of the two annular plates at the periphery of a water\n   wheel, which form the sides of the buckets; a shroud plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrouded', 'imp. & p. p.', 'of Shroud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrouding', 'p. pr. & vb. n.', 'of Shroud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'To cover with a shroud; especially, to inclose in a winding\n   sheet; to dress for the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'n.', 'To cover, as with a shroud; to protect completely; to cover\n   so as to conceal; to hide; to veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'v. i.', 'To take shelter or harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud', 'v. t.', 'To lop. See Shrood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrouded', 'a.', 'Provided with a shroud or shrouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrouding', 'n.', 'The shrouds. See Shroud, n., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroud-laid', 'a.', 'Composed of four strands, and laid right-handed with a\n   heart, or center; -- said of rope. See Illust. under Cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroudless', 'a.', 'Without a shroud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroudy', 'a.', 'Affording shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrove', '', 'imp. of Shrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrove', 'v. i.', 'To join in the festivities of Shrovetide; hence, to make\n   merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrovetide', 'n.', 'The days immediately preceding Ash Widnesday,\n   especially the period between the evening before Quinguagesima Sunday\n   and the morning of Ash Wednesday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shroving', 'n.', 'The festivity of Shrovetide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrow', 'n.', 'A shrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrowd', 'v. t.', 'See Shrood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrub', 'n.', 'A liquor composed of vegetable acid, especially lemon juice,\n   and sugar, with spirit to preserve it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrub', 'n.', 'A woody plant of less size than a tree, and usually with\n   several stems from the same root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrub', 'v. t.', 'To lop; to prune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrubberies', 'pl. ', 'of Shrubbery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrubbery', 'n.', 'A collection of shrubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrubbery', 'n.', 'A place where shrubs are planted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrubbiness', 'n.', 'Quality of being shrubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrubby', 'superl.', 'Full of shrubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrubby', 'superl.', 'Of the nature of a shrub; resembling a shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrubless', 'a.', 'having no shrubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shruff', 'n.', 'Rubbish. Specifically: (a) Dross or refuse of metals.\n   [Obs.] (b) Light, dry wood, or stuff used for fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrugged', 'imp. & p. p.', 'of Shrug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrugging', 'p. pr. & vb. n.', 'of Shrug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrug', 'v. t.', 'To draw up or contract (the shoulders), especially by way\n   of expressing dislike, dread, doubt, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrug', 'v. i.', 'To raise or draw up the shoulders, as in expressing\n   dislike, dread, doubt, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrug', 'n.', 'A drawing up of the shoulders, -- a motion usually\n   expressing dislike, dread, or doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shrunken', '', 'p. p. & a. from Shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuck', 'n.', 'A shock of grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuck', 'n.', 'A shell, husk, or pod; especially, the outer covering of\n   such nuts as the hickory nut, butternut, peanut, and chestnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuck', 'n.', 'The shell of an oyster or clam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shucked', 'imp. & p. p.', 'of Shuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shucking', 'p. pr. & vb. n.', 'of Shuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuck', 'v. t.', 'To deprive of the shucks or husks; as, to shuck walnuts,\n   Indian corn, oysters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shucker', 'n.', 'One who shucks oysters or clams');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuddered', 'imp. & p. p.', 'of Shudder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuddering', 'p. pr. & vb. n.', 'of Shudder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shudder', 'v. i.', 'To tremble or shake with fear, horrer, or aversion; to\n   shiver with cold; to quake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shudder', 'n.', 'The act of shuddering, as with fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shudderingly', 'adv.', 'In a shuddering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shude', 'n.', 'The husks and other refuse of rice mills, used to adulterate\n   oil cake, or linseed cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffled', 'imp. & p. p.', 'of Shuffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffling', 'p. pr. & vb. n.', 'of Shuffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'v. t.', 'To shove one way and the other; to push from one to\n   another; as, to shuffle money from hand to hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'v. t.', 'To mix by pushing or shoving; to confuse; to throw into\n   disorder; especially, to change the relative positions of, as of the\n   cards in a pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'v. t.', 'To remove or introduce by artificial confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'v. i.', 'To change the relative position of cards in a pack; as,\n   to shuffle and cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'v. i.', 'To change ones position; to shift ground; to evade\n   questions; to resort to equivocation; to prevaricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'v. i.', 'To use arts or expedients; to make shift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'v. i.', 'To move in a slovenly, dragging manner; to drag or\n   scrape the feet in walking or dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'n.', 'The act of shuffling; a mixing confusedly; a slovenly,\n   dragging motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffle', 'n.', 'A trick; an artifice; an evasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffleboard', 'n.', 'See Shovelboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shufflecap', 'n.', 'A play performed by shaking money in a hat or cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffler', 'n.', 'One who shuffles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffler', 'n.', 'Either one of the three common American scaup ducks. See\n   Scaup duck, under Scaup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shufflewing', 'n.', 'The hedg sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffling', 'a.', 'Moving with a dragging, scraping step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffling', 'a.', 'Evasive; as, a shuffling excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuffling', 'v.', 'In a shuffling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shug', 'v. i.', 'To writhe the body so as to produce friction against ones\n   clothes, as do those who have the itch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shug', 'v. i.', 'Hence, to crawl; to sneak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shumac', 'n.', 'Sumac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunned', 'imp. & p. p.', 'of Shun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunning', 'p. pr. & vb. n.', 'of Shun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shun', 'v. t.', 'To avoid; to keep clear of; to get out of the way of; to\n   escape from; to eschew; as, to shun rocks, shoals, vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunless', 'a.', 'Not to be shunned; inevitable; unavoidable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunted', 'imp. & p. p.', 'of Shunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunting', 'p. pr. & vb. n.', 'of Shunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. t.', 'To shun; to move from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. t.', 'To cause to move suddenly; to give a sudden start to; to\n   shove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. t.', 'To turn off to one side; especially, to turn off, as a\n   grain or a car upon a side track; to switch off; to shift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. t.', 'To provide with a shunt; as, to shunt a galvanometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. i.', 'To go aside; to turn off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. t.', 'A turning off to a side or short track, that the\n   principal track may be left free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. t.', 'A conducting circuit joining two points in a conductor,\n   or the terminals of a galvanometer or dynamo, so as to form a parallel\n   or derived circuit through which a portion of the current may pass, for\n   the purpose of regulating the amount passing in the main circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunt', 'v. t.', 'The shifting of the studs on a projectile from the deep\n   to the shallow sides of the grooves in its discharge from a shunt gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shunter', 'n.', 'A person employed to shunt cars from one track to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'imp. & p. p.', 'of Shut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shutting', 'p. pr. & vb. n.', 'of Shut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'v. t.', 'To close so as to hinder ingress or egress; as, to shut a\n   door or a gate; to shut ones eyes or mouth.');
