  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'v. t.', 'To preclude; to exclude; to bar out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'v. t.', 'To fold together; to close over, as the fingers; to close\n   by bringing the parts together; as, to shut the hand; to shut a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'v. i.', 'To close itself; to become closed; as, the door shuts; it\n   shuts hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'a.', 'Closed or fastened; as, a shut door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'a.', 'Rid; clear; free; as, to get shut of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'a.', 'Formed by complete closure of the mouth passage, and with the\n   nose passage remaining closed; stopped, as are the mute consonants, p,\n   t, k, b, d, and hard g.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'a.', 'Cut off sharply and abruptly by a following consonant in the\n   same syllable, as the English short vowels, /, /, /, /, /, always are.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'n.', 'The act or time of shutting; close; as, the shut of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'n.', 'A door or cover; a shutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shut', 'n.', 'The line or place where two pieces of metal are united by\n   welding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shute', 'n.', 'Same as Chute, or Shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shutter', 'n.', 'One who shuts or closes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shutter', 'n.', 'A movable cover or screen for a window, designed to shut\n   out the light, to obstruct the view, or to be of some strength as a\n   defense; a blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shutter', 'n.', 'A removable cover, or a gate, for closing an aperture of\n   any kind, as for closing the passageway for molten iron from a ladle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttered', 'a.', 'Furnished with shutters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttle', 'n.', 'An instrument used in weaving for passing or shooting the\n   thread of the woof from one side of the cloth to the other between the\n   threads of the warp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttle', 'n.', 'The sliding thread holder in a sewing machine, which\n   carries the lower thread through a loop of the upper thread, to make a\n   lock stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttle', 'n.', 'A shutter, as for a channel for molten metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttle', 'v. i.', 'To move backwards and forwards, like a shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttlecock', 'n.', 'A cork stuck with feathers, which is to be struck by a\n   battledoor in play; also, the play itself.');
