  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttlecork', 'n.', 'See Shuttlecock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shuttlewise', 'adv.', 'Back and forth, like the movement of a shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shwan-pan', 'n.', 'See Schwan-pan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shy', 'superl.', 'Easily frightened; timid; as, a shy bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shy', 'superl.', 'Reserved; coy; disinclined to familiar approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shy', 'superl.', 'Cautious; wary; suspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shied', 'imp. & p. p.', 'of Shy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shying', 'p. pr. & vb. n.', 'of Shy');
