  INSERT INTO words (word, word_type, description) VALUES  (    'Shy', 'v. t.', 'To throw sidewise with a jerk; to fling; as, to shy a\n   stone; to shy a slipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shy', 'n.', 'A sudden start aside, as by a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shy', 'n.', 'A side throw; a throw; a fling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shyly', 'adv.', 'In a shy or timid manner; not familiarly; with reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shyness', 'n.', 'The quality or state of being shy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Shyster', 'n.', 'A trickish knave; one who carries on any business,\n   especially legal business, in a mean and dishonest way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Si', '', 'A syllable applied, in solmization, to the note B; more recently,\n   to the seventh tone of any major diatonic scale. It was added to\n   Guidos scale by Le Maire about the end of the 17th century.');
