  INSERT INTO words (word, word_type, description) VALUES  (    'Siamang', 'n.', 'A gibbon (Hylobates syndactylus), native of Sumatra. It\n   has the second and third toes partially united by a web.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siamese', 'a.', 'Of or pertaining to Siam, its native people, or their\n   language.');
