  INSERT INTO words (word, word_type, description) VALUES  (    'Siberian', 'a.', 'Of or pertaining to Siberia, a region comprising all\n   northern Asia and belonging to Russia; as, a Siberian winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siberian', 'n.', 'A native or inhabitant of Siberia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibilance', 'n.', 'Alt. of Sibilancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibilancy', 'n.', 'The quality or state of being sibilant; sibilation.');
