  INSERT INTO words (word, word_type, description) VALUES  (    'Sibilation', 'n.', 'Utterance with a hissing sound; also, the sound itself;\n   a hiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibilatory', 'a.', 'Hissing; sibilant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibilous', 'a.', 'Having a hissing sound; hissing; sibilant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibyl', 'n.', 'A woman supposed to be endowed with a spirit of prophecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibyl', 'n.', 'A female fortune teller; a pythoness; a prophetess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibylist', 'n.', 'One who believes in a sibyl or the sibylline prophecies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sibylline', 'a.', 'Pertaining to the sibyls; uttered, written, or composed\n   by sibyls; like the productions of sibyls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sic', 'a.', 'Such.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sic', 'adv.', 'Thus.');
