  INSERT INTO words (word, word_type, description) VALUES  (    'Siccate', 'v. t.', 'To dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siccation', 'n.', 'The act or process of drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siccative', 'a.', 'Drying; causing to dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siccative', 'n.', 'That which promotes drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siccific', 'a.', 'Causing dryness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siccity', 'n.', 'Dryness; aridity; destitution of moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sice', 'n.', 'The number six at dice.');
