  INSERT INTO words (word, word_type, description) VALUES  (    'Sick', 'superl.', 'Having a strong dislike; disgusted; surfeited; -- with\n   of; as, to be sick of flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sick', 'superl.', 'Corrupted; imperfect; impaired; weakned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sick', 'n.', 'Sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sick', 'v. i.', 'To fall sick; to sicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sick-brained', 'a.', 'Disordered in the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickened', 'imp. & p. p.', 'of Sicken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickening', 'p. pr. & vb. n.', 'of Sicken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicken', 'v. t.', 'To make sick; to disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicken', 'v. t.', 'To make qualmish; to nauseate; to disgust; as, to sicken\n   the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicken', 'v. t.', 'To impair; to weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicken', 'v. i.', 'To become sick; to fall into disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicken', 'v. i.', 'To be filled to disgust; to be disgusted or nauseated;\n   to be filled with abhorrence or aversion; to be surfeited or satiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicken', 'v. i.', 'To become disgusting or tedious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicken', 'v. i.', 'To become weak; to decay; to languish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickening', 'a.', 'Causing sickness; specif., causing surfeit or disgust;\n   nauseating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicker', 'v. i.', 'To percolate, trickle, or ooze, as water through a\n   crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicker', 'a.', 'Alt. of Siker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siker', 'a.', 'Sure; certain; trusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicker', 'adv.', 'Alt. of Siker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siker', 'adv.', 'Surely; certainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickerly', 'adv.', 'Alt. of Sikerly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sikerly', 'adv.', 'Surely; securely.');
