  INSERT INTO words (word, word_type, description) VALUES  (    'Sikerness', 'n.', 'The quality or state of being sicker, or certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickish', 'a.', 'Somewhat sick or diseased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickish', 'a.', 'Somewhat sickening; as, a sickish taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickle', 'n.', 'A reaping instrument consisting of a steel blade curved\n   into the form of a hook, and having a handle fitted on a tang. The\n   sickle has one side of the blade notched, so as always to sharpen with\n   a serrated edge. Cf. Reaping hook, under Reap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickle', 'n.', 'A group of stars in the constellation Leo. See Illust. of\n   Leo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicklebill', 'n.', 'Any one of three species of humming birds of the genus\n   Eutoxeres, native of Central and South America. They have a long and\n   strongly curved bill. Called also the sickle-billed hummer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicklebill', 'n.', 'A curlew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicklebill', 'n.', 'A bird of the genus Epimachus and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickled', 'a.', 'Furnished with a sickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicklemen', 'pl. ', 'of Sickleman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickleman', 'n.', 'One who uses a sickle; a reaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickler', 'n.', 'One who uses a sickle; a sickleman; a reaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickless', 'a.', 'Free from sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicklewort', 'n.', 'A plant of the genus Coronilla (C. scorpioides); -- so\n   named from its curved pods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicklewort', 'n.', 'The healall (Brunella vulgaris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicklied', 'a.', 'Made sickly. See Sickly, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickliness', 'n.', 'The quality or state of being sickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickly', 'superl.', 'Somewhat sick; disposed to illness; attended with\n   disease; as, a sickly body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickly', 'superl.', 'Producing, or tending to, disease; as, a sickly\n   autumn; a sickly climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickly', 'superl.', 'Appearing as if sick; weak; languid; pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickly', 'superl.', 'Tending to produce nausea; sickening; as, a sickly\n   smell; sickly sentimentality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickly', 'adv.', 'In a sick manner or condition; ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickly', 'v. t.', 'To make sick or sickly; -- with over, and probably only\n   in the past participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickness', 'n.', 'The quality or state of being sick or diseased; illness;\n   sisease or malady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sickness', 'n.', 'Nausea; qualmishness; as, sickness of stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sicle', 'n.', 'A shekel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sida', 'n.', 'A genus of malvaceous plants common in the tropics. All the\n   species are mucilaginous, and some have tough ligneous fibers which are\n   used as a substitute for hemp and flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siddow', 'a.', 'Soft; pulpy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'The margin, edge, verge, or border of a surface; especially\n   (when the thing spoken of is somewhat oblong in shape), one of the\n   longer edges as distinguished from the shorter edges, called ends; a\n   bounding line of a geometrical figure; as, the side of a field, of a\n   square or triangle, of a river, of a road, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'Any outer portion of a thing considered apart from, and yet\n   in relation to, the rest; as, the upper side of a sphere; also, any\n   part or position viewed as opposite to or contrasted with another; as,\n   this or that side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'One of the halves of the body, of an animals or man, on\n   either side of the mesial plane; or that which pertains to such a half;\n   as, a side of beef; a side of sole leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'The right or left part of the wall or trunk of the body; as,\n   a pain in the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'A slope or declivity, as of a hill, considered as opposed to\n   another slope over the ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'The position of a person or party regarded as opposed to\n   another person or party, whether as a rival or a foe; a body of\n   advocates or partisans; a party; hence, the interest or cause which one\n   maintains against another; a doctrine or view opposed to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'A line of descent traced through one parent as distinguished\n   from that traced through another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'Fig.: Aspect or part regarded as contrasted with some other;\n   as, the bright side of poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'a.', 'Of or pertaining to a side, or the sides; being on the side,\n   or toward the side; lateral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'a.', 'Hence, indirect; oblique; collateral; incidental; as, a side\n   issue; a side view or remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'n.', 'Long; large; extensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sided', 'imp. & p. p.', 'of Side');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siding', 'p. pr.& vb. n.', 'of Side');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'v. i.', 'To lean on one side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'v. i.', 'To embrace the opinions of one party, or engage in its\n   interest, in opposition to another party; to take sides; as, to side\n   with the ministerial party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'v. t.', 'To be or stand at the side of; to be on the side toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'v. t.', 'To suit; to pair; to match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'v. t.', 'To work (a timber or rib) to a certain thickness by\n   trimming the sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side', 'v. t.', 'To furnish with a siding; as, to side a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideboard', 'n.', 'A piece of dining-room furniture having compartments and\n   shelves for keeping or displaying articles of table service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidebone', 'n.', 'A morbid growth or deposit of bony matter and at the\n   sides of the coronet and coffin bone of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sided', 'a.', 'Having (such or so many) sides; -- used in composition; as,\n   one-sided; many-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidehill', 'n.', 'The side or slope of a hill; sloping ground; a descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideling', 'adv.', 'Sidelong; on the side; laterally; also, obliquely;\n   askew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideling', 'a.', 'Inclining to one side; directed toward one side; sloping;\n   inclined; as, sideling ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidelong', 'adv.', 'Laterally; obliquely; in the direction of the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidelong', 'adv.', 'On the side; as, to lay a thing sidelong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidelong', 'a.', 'Lateral; oblique; not being directly in front; as, a\n   sidelong glance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidepiece', 'n.', 'The jamb, or cheek, of an opening in a wall, as of door\n   or window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sider', 'n.', 'One who takes a side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sider', 'n.', 'Cider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideral', 'a.', 'Relating to the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideral', 'a.', 'Affecting unfavorably by the supposed influence of the\n   stars; baleful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderated', 'a.', 'Planet-struck; blasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideration', 'n.', 'The state of being siderated, or planet-struck; esp.,\n   blast in plants; also, a sudden and apparently causeless stroke of\n   disease, as in apoplexy or paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidereal', 'a.', 'Relating to the stars; starry; astral; as, sidereal\n   astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidereal', 'a.', 'Measuring by the apparent motion of the stars;\n   designated, marked out, or accompanied, by a return to the same\n   position in respect to the stars; as, the sidereal revolution of a\n   planet; a sidereal day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderealize', 'v. t.', 'To elevate to the stars, or to the region of the\n   stars; to etherealize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidereous', 'a.', 'Sidereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderite', 'n.', 'Carbonate of iron, an important ore of iron occuring\n   generally in cleavable masses, but also in rhombohedral crystals. It is\n   of a light yellowish brown color. Called also sparry iron, spathic\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderite', 'n.', 'A meteorite consisting solely of metallic iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderite', 'n.', 'An indigo-blue variety of quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderite', 'n.', 'Formerly, magnetic iron ore, or loadstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderite', 'n.', 'Any plant of the genus Sideritis; ironwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderographic', 'a.', 'Alt. of Siderographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderographical', 'a.', 'Of or pertaining to siderography; executed by\n   engraved plates of steel; as, siderographic art; siderographic\n   impressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderographist', 'n.', 'One skilled in siderography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderography', 'n.', 'The art or practice of steel engraving; especially,\n   the process, invented by Perkins, of multiplying facsimiles of an\n   engraved steel plate by first rolling over it, when hardened, a soft\n   steel cylinder, and then rolling the cylinder, when hardened, over a\n   soft steel plate, which thus becomes a facsimile of the original. The\n   process has been superseded by electrotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderolite', 'n.', 'A kind of meteorite. See under Meteorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideromancy', 'n.', 'Divination by burning straws on red-hot iron, and\n   noting the manner of their burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideroscope', 'n.', 'An instrument for detecting small quantities of iron\n   in any substance by means of a very delicate combination of magnetic\n   needles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderosis', 'n.', 'A sort of pneumonia occuring in iron workers, produced\n   by the inhalation of particles of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siderostat', 'n.', 'An apparatus consisting essentially of a mirror moved\n   by clockwork so as to throw the rays of the sun or a star in a fixed\n   direction; -- a more general term for heliostat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideroxylon', 'n.', 'A genus of tropical sapotaceous trees noted for their\n   very hard wood; ironwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidesaddle', 'n.', 'A saddle for women, in which the rider sits with both\n   feet on one side of the animal mounted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidesmen', 'pl. ', 'of Sidesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidesman', 'n.', 'A party man; a partisan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidesman', 'n.', 'An assistant to the churchwarden; a questman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side-taking', 'n.', 'A taking sides, as with a party, sect, or faction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidewalk', 'n.', 'A walk for foot passengers at the side of a street or\n   road; a foot pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sideways', 'adv.', 'Toward the side; sidewise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Side-wheel', 'a.', 'Having a paddle wheel on each side; -- said of steam\n   vessels; as, a side-wheel steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidewinder', 'n.', 'See Horned rattler, under Horned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidewinder', 'n.', 'A heavy swinging blow from the side, which disables an\n   adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidewise', 'adv.', 'On or toward one side; laterally; sideways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siding', 'n.', 'Attaching ones self to a party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siding', 'n.', 'A side track, as a railroad; a turnout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siding', 'n.', 'The covering of the outside wall of a frame house, whether\n   made of weatherboards, vertical boarding with cleats, shingles, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siding', 'n.', 'The thickness of a rib or timber, measured, at right angles\n   with its side, across the curved edge; as, a timber having a siding of\n   ten inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidled', 'imp. & p. p.', 'of Sidle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidling', 'p. pr. & vb. n.', 'of Sidle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sidle', 'v. t.', 'To go or move with one side foremost; to move sidewise;\n   as, to sidle through a crowd or narrow opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'A seat; especially, a royal seat; a throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'Hence, place or situation; seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'Rank; grade; station; estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'Passage of excrements; stool; fecal matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'The sitting of an army around or before a fortified place\n   for the purpose of compelling the garrison to surrender; the\n   surrounding or investing of a place by an army, and approaching it by\n   passages and advanced works, which cover the besiegers from the enemys\n   fire. See the Note under Blockade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'Hence, a continued attempt to gain possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'The floor of a glass-furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'n.', 'A workmans bench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siege', 'v. t.', 'To besiege; to beset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siegework', 'n.', 'A temporary fort or parallel where siege guns are\n   mounted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siemens-Martin process', '', 'See Open-hearth process, etc., under Open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sienite', 'n.', 'See Syenite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sienitic', 'a.', 'See Syenitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sienna', 'n.', 'Clay that is colored red or brown by the oxides of iron or\n   manganese, and used as a pigment. It is used either in the raw state or\n   burnt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siennese', 'a.', 'Of or pertaining to Sienna, a city of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sierra', 'n.', 'A ridge of mountain and craggy rocks, with a serrated or\n   irregular outline; as, the Sierra Nevada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siesta', 'n.', 'A short sleep taken about the middle of the day, or after\n   dinner; a midday nap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sieur', 'n.', 'Sir; -- a title of respect used by the French.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sieva', 'n.', 'A small variety of the Lima bean (Phaseolus lunatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sieve', 'n.', 'A utensil for separating the finer and coarser parts of a\n   pulverized or granulated substance from each other. It consist of a\n   vessel, usually shallow, with the bottom perforated, or made of hair,\n   wire, or the like, woven in meshes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sieve', 'n.', 'A kind of coarse basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sifac', 'n.', 'The white indris of Madagascar. It is regarded by the\n   natives as sacred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sifflement', 'n.', 'The act of whistling or hissing; a whistling sound;\n   sibilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sifilet', 'n.', 'The six-shafted bird of paradise. See Paradise bird, under\n   Paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sifted', 'imp. & p. p.', 'of Sift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sifting', 'p. pr. & vb. n.', 'of Sift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sift', 'v. t.', 'To separate with a sieve, as the fine part of a substance\n   from the coarse; as, to sift meal or flour; to sift powder; to sift\n   sand or lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sift', 'v. t.', 'To separate or part as if with a sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sift', 'v. t.', 'To examine critically or minutely; to scrutinize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sifter', 'n.', 'One who, or that which, sifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sifter', 'n.', 'Any lamellirostral bird, as a duck or goose; -- so called\n   because it sifts or strains its food from the water and mud by means of\n   the lamell/ of the beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sig', 'v. t.', 'Urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigaultian', 'a.', 'Pertaining to Sigault, a French physician. See\n   Symphyseotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigger', 'v. i.', 'Same as');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sighed', 'imp. & p. p.', 'of Sigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sighing', 'p. pr. & vb. n.', 'of Sigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. i.', 'To inhale a larger quantity of air than usual, and\n   immediately expel it; to make a deep single audible respiration,\n   especially as the result or involuntary expression of fatigue,\n   exhaustion, grief, sorrow, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. i.', 'Hence, to lament; to grieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. i.', 'To make a sound like sighing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. t.', 'To exhale (the breath) in sighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. t.', 'To utter sighs over; to lament or mourn over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. t.', 'To express by sighs; to utter in or with sighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. i.', 'A deep and prolonged audible inspiration or respiration of\n   air, as when fatigued or grieved; the act of sighing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh', 'v. i.', 'Figuratively, a manifestation of grief; a lan/ent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigh-born', 'a.', 'Sorrowful; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigher', 'n.', 'One who sighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sighing', 'a.', 'Uttering sighs; grieving; lamenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'The act of seeing; perception of objects by the eye;\n   view; as, to gain sight of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'The power of seeing; the faculty of vision, or of\n   perceiving objects by the instrumentality of the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'The state of admitting unobstructed vision; visibility;\n   open view; region which the eye at one time surveys; space through\n   which the power of vision extends; as, an object within sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'A spectacle; a view; a show; something worth seeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'The instrument of seeing; the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'Inspection; examination; as, a letter intended for the\n   sight of only one person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'Mental view; opinion; judgment; as, in their sight it was\n   harmless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'A small aperture through which objects are to be seen,\n   and by which their direction is settled or ascertained; as, the sight\n   of a quadrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'A small piece of metal, fixed or movable, on the breech,\n   muzzle, center, or trunnion of a gun, or on the breech and the muzzle\n   of a rifle, pistol, etc., by means of which the eye is guided in\n   aiming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'In a drawing, picture, etc., that part of the surface, as\n   of paper or canvas, which is within the frame or the border or margin.\n   In a frame or the like, the open space, the opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'A great number, quantity, or sum; as, a sight of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sighted', 'imp. & p. p.', 'of Sight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sighting', 'p. pr. & vb. n.', 'of Sight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'To get sight of; to see; as, to sight land; to sight a\n   wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'To look at through a sight; to see accurately; as, to\n   sight an object, as a star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. t.', 'To apply sights to; to adjust the sights of; also, to\n   give the proper elevation and direction to by means of a sight; as, to\n   sight a rifle or a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight', 'v. i.', 'To take aim by a sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sighted', 'a.', 'Having sight, or seeing, in a particular manner; -- used\n   in composition; as, long-sighted, short-sighted, quick-sighted,\n   sharp-sighted, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightful', 'a.', 'Easily or clearly seen; distinctly visible; perspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightfulness', 'n.', 'The state of being sightful; perspicuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight-hole', 'n.', 'A hole for looking through; a peephole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sighting', '', 'a. & n. from Sight, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightless', 'a.', 'Wanting sight; without sight; blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightless', 'a.', 'That can not be seen; invisible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightless', 'a.', 'Offensive or unpleasing to the eye; unsightly; as,\n   sightless stains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightliness', 'n.', 'The state of being sightly; comeliness;\n   conspicuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightly', 'a.', 'Pleasing to the sight; comely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightly', 'a.', 'Open to sight; conspicuous; as, a house stands in a\n   sightly place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightproof', 'a.', 'Undiscoverable to sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight-seeing', 'a.', 'Engaged in, or given to, seeing sights; eager for\n   novelties or curiosities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight-seeing', 'n.', 'The act of seeing sights; eagerness for novelties or\n   curiosities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight-seer', 'n.', 'One given to seeing sights or noted things, or eager\n   for novelties or curiosities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sight-shot', 'n.', 'Distance to which the sight can reach or be thrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightsmen', 'pl. ', 'of Sightsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sightsman', 'n.', 'One who reads or performs music readily at first sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigil', 'n.', 'A seal; a signature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigillaria', 'n. pl.', 'Little images or figures of earthenware exposed for\n   sale, or given as presents, on the last two days of the Saturnalia;\n   hence, the last two, or the sixth and seventh, days of the Saturnalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigillaria', 'n.', 'A genus of fossil trees principally found in the coal\n   formation; -- so named from the seallike leaf scars in vertical rows on\n   the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigillarid', 'n.', 'One of an extinct family of cryptagamous trees,\n   including the genus Sigillaria and its allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigillated', 'a.', 'Decorated by means of stamps; -- said of pottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigillative', 'a.', 'Fit to seal; belonging to a seal; composed of wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigilla', 'pl. ', 'of Sigillum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigillum', 'n.', 'A seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigla', 'n. pl.', 'The signs, abbreviations, letters, or characters\n   standing for words, shorthand, etc., in ancient manuscripts, or on\n   coins, medals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigmas', 'pl. ', 'of Sigma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigma', 'n.', 'The Greek letter /, /, or / (English S, or s). It originally\n   had the form of the English C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigmodont', 'n.', 'Any one of a tribe (Sigmodontes) of rodents which\n   includes all the indigenous rats and mice of America. So called from\n   the form of the ridges of enamel on the crowns of the worn molars. Also\n   used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigmoid', 'a.', 'Alt. of Sigmoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigmoidal', 'a.', 'Curved in two directions, like the letter S, or the\n   Greek /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sigmoidally', 'adv.', 'In a sigmoidal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'That by which anything is made known or represented; that\n   which furnishes evidence; a mark; a token; an indication; a proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'A remarkable event, considered by the ancients as indicating\n   the will of some deity; a prodigy; an omen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'An event considered by the Jews as indicating the divine\n   will, or as manifesting an interposition of the divine power for some\n   special end; a miracle; a wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'Something serving to indicate the existence, or preserve the\n   memory, of a thing; a token; a memorial; a monument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'Any symbol or emblem which prefigures, typifles, or\n   represents, an idea; a type; hence, sometimes, a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'A word or a character regarded as the outward manifestation\n   of thought; as, words are the sign of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'A motion, an action, or a gesture by which a thought is\n   expressed, or a command or a wish made known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'Hence, one of the gestures of pantomime, or of a language of\n   a signs such as those used by the North American Indians, or those used\n   by the deaf and dumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'A military emblem carried on a banner or a standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'A lettered board, or other conspicuous notice, placed upon or\n   before a building, room, shop, or office to advertise the business\n   there transacted, or the name of the person or firm carrying it on; a\n   publicly displayed token or notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'The twelfth part of the ecliptic or zodiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'A character indicating the relation of quantities, or an\n   operation performed upon them; as, the sign + (plus); the sign --\n   (minus); the sign of division , and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'An objective evidence of disease; that is, one appreciable by\n   some one other than the patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'Any character, as a flat, sharp, dot, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'That which, being external, stands for, or signifies,\n   something internal or spiritual; -- a term used in the Church of\n   England in speaking of an ordinance considered with reference to that\n   which it represents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signed', 'imp. & p. p.', 'of Sign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signing', 'p. pr. & vb. n.', 'of Sign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'To represent by a sign; to make known in a typical or\n   emblematic manner, in distinction from speech; to signify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'To make a sign upon; to mark with a sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'To affix a signature to; to ratify by hand or seal; to\n   subscribe in ones own handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'To assign or convey formally; -- used with away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'n.', 'To mark; to make distinguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'v. i.', 'To be a sign or omen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'v. i.', 'To make a sign or signal; to communicate directions or\n   intelligence by signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sign', 'v. i.', 'To write ones name, esp. as a token of assent,\n   responsibility, or obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signable', 'a.', 'Suitable to be signed; requiring signature; as, a legal\n   document signable by a particular person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signal', 'n.', 'A sign made for the purpose of giving notice to a person of\n   some occurence, command, or danger; also, a sign, event, or watchword,\n   which has been agreed upon as the occasion of concerted action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signal', 'n.', 'A token; an indication; a foreshadowing; a sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signal', 'a.', 'Noticeable; distinguished from what is ordinary; eminent;\n   remarkable; memorable; as, a signal exploit; a signal service; a signal\n   act of benevolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signal', 'a.', 'Of or pertaining to signals, or the use of signals in\n   conveying information; as, a signal flag or officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signaled', '/', 'or Signalled (imp. & p. p.) of Signal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signaling', 'p. pr. & vb. n.', 'of Signal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalling', '', 'of Signal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signal', 'v. t.', 'To communicate by signals; as, to signal orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signal', 'v. t.', 'To notify by a signals; to make a signal or signals to;\n   as, to signal a fleet to anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalist', 'n.', 'One who makes signals; one who communicates intelligence\n   by means of signals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signality', 'n.', 'The quality or state of being signal or remarkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalized', 'imp. & p. p.', 'of Signalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalizing', 'p. pr. & vb. n.', 'of Signalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalize', 'a.', 'To make signal or eminent; to render distinguished from\n   what is common; to distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalize', 'a.', 'To communicate with by means of a signal; as, a ship\n   signalizes its consort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalize', 'a.', 'To indicate the existence, presence, or fact of, by a\n   signal; as, to signalize the arrival of a steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signally', 'adv.', 'In a signal manner; eminently.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Signalman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalman', 'n.', 'A man whose business is to manage or display signals;\n   especially, one employed in setting the signals by which railroad\n   trains are run or warned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signalment', 'n.', 'The act of signaling, or of signalizing; hence,\n   description by peculiar, appropriate, or characteristic marks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signate', 'v. t.', 'Having definite color markings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signation', 'v. t.', 'Sign given; marking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signatory', 'a.', 'Relating to a seal; used in sealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signatory', 'a.', 'Signing; joining or sharing in a signature; as,\n   signatory powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Signatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signatory', 'n.', 'A signer; one who signs or subscribes; as, a conference\n   of signatories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'A sign, stamp, or mark impressed, as by a seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'Especially, the name of any person, written with his\n   own hand, employed to signify that the writing which precedes accords\n   with his wishes or intentions; a sign manual; an autograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'An outward mark by which internal characteristics\n   were supposed to be indicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'A resemblance between the external characters of a\n   disease and those of some physical agent, for instance, that existing\n   between the red skin of scarlet fever and a red cloth; -- supposed to\n   indicate this agent in the treatment of the disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'The designation of the key (when not C major, or its\n   relative, A minor) by means of one or more sharps or flats at the\n   beginning of the staff, immediately after the clef, affecting all notes\n   of the same letter throughout the piece or movement. Each minor key has\n   the same signature as its relative major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'A letter or figure placed at the bottom of the first\n   page of each sheet of a book or pamphlet, as a direction to the binder\n   in arranging and folding the sheets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'The printed sheet so marked, or the form from which\n   it is printed; as, to reprint one or more signatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'That part of a prescription which contains the\n   directions to the patient. It is usually prefaced by S or Sig. (an\n   abbreviation for the Latin signa, imperative of signare to sign or\n   mark).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signature', 'v. t.', 'To mark with, or as with, a signature or signatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signaturist', 'n.', 'One who holds to the doctrine of signatures impressed\n   upon objects, indicative of character or qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signboard', 'n.', 'A board, placed on or before a shop, office, etc., on\n   which ssome notice is given, as the name of a firm, of a business, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signer', 'n.', 'One who signs or subscribes his name; as, a memorial with a\n   hundred signers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signet', 'n.', 'A seal; especially, in England, the seal used by the\n   sovereign in sealing private letters and grants that pass by bill under\n   the sign manual; -- called also privy signet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signeted', 'a.', 'Stamped or marked with a signet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signifer', 'a.', 'Bearing signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significance', 'n.', 'Alt. of Significancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significancy', 'n.', 'The quality or state of being significant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significancy', 'n.', 'That which is signified; meaning; import; as, the\n   significance of a nod, of a motion of the hand, or of a word or\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significancy', 'n.', 'Importance; moment; weight; consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significant', 'a.', 'Fitted or designed to signify or make known somethingl\n   having a meaning; standing as a sign or token; expressive or\n   suggestive; as, a significant word or sound; a significant look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significant', 'a.', 'Deserving to be considered; important; momentous; as,\n   a significant event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significant', 'n.', 'That which has significance; a sign; a token; a\n   symbol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significantly', 'adv.', 'In a significant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significate', 'n.', 'One of several things signified by a common term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signification', 'n.', 'The act of signifying; a making known by signs or\n   other means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signification', 'n.', 'That which is signified or made known; that meaning\n   which a sign, character, or token is intended to convey; as, the\n   signification of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significative', 'a.', 'Betokening or representing by an external sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significative', 'a.', 'Having signification or meaning; expressive of a\n   meaning or purpose; significant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significator', 'n.', 'One who, or that which, signifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significatory', 'a.', 'Significant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significatory', 'n.', 'That which is significatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Significavit', 'n.', 'Formerly, a writ issuing out of chancery, upon\n   certificate given by the ordinary, of a mans standing excommunicate by\n   the space of forty days, for the laying him up in prison till he submit\n   himself to the authority of the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signified', 'imp. & p. p.', 'of Signify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signifying', 'p. pr. & vb. n.', 'of Signify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signify', 'n.', 'To show by a sign; to communicate by any conventional\n   token, as words, gestures, signals, or the like; to announce; to make\n   known; to declare; to express; as, a signified his desire to be\n   present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signify', 'n.', 'To mean; to import; to denote; to betoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signior', 'n.', 'Sir; Mr. The English form and pronunciation for the\n   Italian Signor and the Spanish Seor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signiorize', 'v. t.', 'To exercise dominion over; to lord it over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signiorize', 'v. i.', 'To exercise dominion; to seigniorize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signiorship', 'n.', 'State or position of a signior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signiory', 'n.', 'Same as Seigniory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signor', 'n.', 'Alt. of Signore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signore', 'n.', 'Sir; Mr.; -- a title of address or respect among the\n   Italians. Before a noun the form is Signor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signora', 'n.', 'Madam; Mrs; -- a title of address or respect among the\n   Italians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signorina', 'n.', 'Miss; -- a title of address among the Italians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Signpost', 'n.', 'A post on which a sign hangs, or on which papers are\n   placed to give public notice of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sik', 'a.', 'Alt. of Sike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sike', 'a.', 'Such. See Such.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sike', 'n.', 'A gutter; a stream, such as is usually dry in summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sike', 'n.', 'A sick person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sike', 'v. i.', 'To sigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sike', 'n.', 'A sigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siker', 'n.', 'Alt. of Sikerness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sikerly', 'n.', 'Alt. of Sikerness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sikerness', 'n.', 'See 2d Sicker, Sickerly, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sikhs', 'n. pl.', 'A religious sect noted for warlike traits, founded in\n   the Punjab at the end of the 15th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silage', 'n. & v.', 'Short for Ensilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sile', 'v. t.', 'To strain, as fresh milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sile', 'v. i.', 'To drop; to flow; to fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sile', 'n.', 'A sieve with fine meshes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sile', 'n.', 'Filth; sediment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sile', 'n.', 'A young or small herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'n.', 'The state of being silent; entire absence of sound or\n   noise; absolute stillness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'n.', 'Forbearance from, or absence of, speech; taciturnity;\n   muteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'n.', 'Secrecy; as, these things were transacted in silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'n.', 'The cessation of rage, agitation, or tumilt; calmness;\n   quiest; as, the elements were reduced to silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'n.', 'Absence of mention; oblivion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'interj.', 'Be silent; -- used elliptically for let there be\n   silence, or keep silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silenced', 'imp. & p. p.', 'of Silence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silencing', 'p. pr. & vb. n.', 'of Silence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'v. t.', 'To compel to silence; to cause to be still; to still;\n   to hush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'v. t.', 'To put to rest; to quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'v. t.', 'To restrain from the exercise of any function,\n   privilege of instruction, or the like, especially from the act of\n   preaching; as, to silence a minister of the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silence', 'v. t.', 'To cause to cease firing, as by a vigorous cannonade;\n   as, to silence the batteries of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silene', 'n.', 'A genus of caryophyllaceous plants, usually covered with a\n   viscid secretion by which insects are caught; catchfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silent', 'a.', 'Free from sound or noise; absolutely still; perfectly\n   quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silent', 'a.', 'Not speaking; indisposed to talk; speechless; mute;\n   taciturn; not loquacious; not talkative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silent', 'a.', 'Keeping at rest; inactive; calm; undisturbed; as, the wind\n   is silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silent', 'a.', 'Not pronounced; having no sound; quiescent; as, e is silent\n   in \"fable.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silent', 'a.', 'Having no effect; not operating; inefficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silent', 'n.', 'That which is silent; a time of silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silentiary', 'n.', 'One appointed to keep silence and order in court; also,\n   one sworn not to divulge secrets of state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silentious', 'a.', 'Habitually silent; taciturn; reticent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silently', 'adv.', 'In a silent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silentness', 'n.', 'State of being silent; silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silenus', 'n.', 'See Wanderoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silesia', 'n.', 'A kind of linen cloth, originally made in Silesia, a\n   province of Prussia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silesia', 'n.', 'A twilled cotton fabric, used for dress linings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silesian', 'a.', 'Of or pertaining to Silesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silesian', 'n.', 'A native or inhabitant of Silesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silex', 'n.', 'Silica, SiO2 as found in nature, constituting quarz, and\n   most sands and sandstones. See Silica, and Silicic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silhouette', 'n.', 'A representation of the outlines of an object filled in\n   with a black color; a profile portrait in black, such as a shadow\n   appears to be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silhouette', 'v. t.', 'To represent by a silhouette; to project upon a\n   background, so as to be like a silhouette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silica', 'n.', 'Silicon dioxide, SiO/. It constitutes ordinary quartz (also\n   opal and tridymite), and is artifically prepared as a very fine, white,\n   tasteless, inodorous powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicate', 'n.', 'A salt of silicic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicated', 'a.', 'Combined or impregnated with silicon or silica; as,\n   silicated hydrogen; silicated rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicatization', 'n.', 'Silicification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicea', 'n. pl.', 'Same as Silicoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliceous', 'a.', 'Of or pertaining to silica; containing silica, or\n   partaking of its nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicic', 'a.', 'Pertaining to, derived from, or resembling, silica;\n   specifically, designating compounds of silicon; as, silicic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicicalcareous', 'a.', 'Consisting of silica and calcareous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicide', 'n.', 'A binary compound of silicon, or one regarded as binary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliciferous', 'a.', 'Producing silica; united with silica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicification', 'n.', 'Thae act or process of combining or impregnating\n   with silicon or silica; the state of being so combined or impregnated;\n   as, the silicification of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicified', 'a.', 'Combined or impregnated with silicon or silica,\n   especially the latter; as, silicified wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicified', 'imp. & p. p.', 'of Silicify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicifying', 'p. pr. & vb. n.', 'of Silicify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicify', 'v. t.', 'To convert into, or to impregnate with, silica, or\n   with the compounds of silicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicify', 'v. i.', 'To become converted into silica, or to be impregnated\n   with silica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicioidea', 'n. pl.', 'Same as Silicoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicious', 'a.', 'See Siliceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicispongiae', 'n. pl.', 'Same as Silicoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicited', 'a.', 'Silicified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicium', 'n.', 'See Silicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliciureted', 'a.', 'Combined or impregnated with silicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicle', 'n.', 'A seed vessel resembling a silique, but about as broad as\n   it is long. See Silique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silico-', '', 'A combining form (also used adjectively) denoting the\n   presence of silicon or its compounds; as, silicobenzoic,\n   silicofluoride, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicofluoric', 'a.', 'Containing, or composed of, silicon and fluorine;\n   especially, denoting the compounds called silicofluorides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicofluoride', 'n.', 'A fluosilicate; a salt of silicofluoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicoidea', 'n. pl.', 'An extensive order of Porifera, which includes\n   those that have the skeleton composed mainly of siliceous fibers or\n   spicules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicon', 'n.', 'A nonmetalic element analogous to carbon. It always occurs\n   combined in nature, and is artificially obtained in the free state,\n   usually as a dark brown amorphous powder, or as a dark crystalline\n   substance with a meetallic luster. Its oxide is silica, or common\n   quartz, and in this form, or as silicates, it is, next to oxygen, the\n   most abundant element of the earths crust. Silicon is\n   characteristically the element of the mineral kingdom, as carbon is of\n   the organic world. Symbol Si. Atomic weight 28. Called also silicium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicotungstic', 'a.', 'Of, pertaining to, or designating, any one of a\n   series of double acids of silicon and tungsten, known in the free\n   state, and also in their salts (called silicotungstates).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicula', 'n.', 'A silicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silicule', 'n.', 'A silicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliculose', 'a.', 'Bearing silicles; pertaining to, or resembling,\n   silicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliculose', 'a.', 'Full of, or consisting of, husks; husky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliginose', 'a.', 'Made of fine wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siling', '', 'a. & n. from Sile to strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliquae', 'pl. ', 'of Siliqua');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliqua', 'n.', 'Same as Silique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliqua', 'n.', 'A weight of four grains; a carat; -- a term used by\n   jewelers, and refiners of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silique', 'n.', 'An oblong or elongated seed vessel, consisting of two\n   valves with a dissepiment between, and opening by sutures at either\n   margin. The seeds are attached to both edges of the dissepiment,\n   alternately upon each side of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliqyiform', 'a.', 'Having the form of a silique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliquosa', 'n. pl.', 'A Linnaean order of plants including those which\n   bear siliques.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliquose', 'a.', 'Alt. of Siliquous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siliquous', 'a.', 'Bearing siliques; as, siliquose plants; pertaining to,\n   or resembling, siliques; as, siliquose capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silk', 'n.', 'The fine, soft thread produced by various species of\n   caterpillars in forming the cocoons within which the worm is inclosed\n   during the pupa state, especially that produced by the larvae of Bombyx\n   mori.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silk', 'n.', 'Hence, thread spun, or cloth woven, from the above-named\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silk', 'n.', 'That which resembles silk, as the filiform styles of the\n   female flower of maize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silken', 'a.', 'Of or pertaining to silk; made of, or resembling, silk; as,\n   silken cloth; a silken veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silken', 'a.', 'Fig.: Soft; delicate; tender; smooth; as, silken language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silken', 'a.', 'Dressed in silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silken', 'v. t.', 'To render silken or silklike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silkiness', 'n.', 'The quality or state of being silky or silken; softness\n   and smoothness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silkiness', 'n.', 'Fig.: Effeminacy; weakness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silkmen', 'pl. ', 'of Silkman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silkman', 'n.', 'A dealer in silks; a silk mercer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silkness', 'n.', 'Silkiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silkweed', 'n.', 'Any plant of the genera Asclepias and Acerates whose seed\n   vessels contain a long, silky down; milkweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silkworm', 'n.', 'The larva of any one of numerous species of bombycid\n   moths, which spins a large amount of strong silk in constructing its\n   cocoon before changing to a pupa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silky', 'superl.', 'Of or pertaining to silk; made of, or resembling, silk;\n   silken; silklike; as, a silky luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silky', 'superl.', 'Hence, soft and smooth; as, silky wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silky', 'superl.', 'Covered with soft hairs pressed close to the surface,\n   as a leaf; sericeous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sill', 'n.', 'The basis or foundation of a thing; especially, a horizontal\n   piece, as a timber, which forms the lower member of a frame, or\n   supports a structure; as, the sills of a house, of a bridge, of a loom,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sill', 'n.', 'The timber or stone at the foot of a door; the threshold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sill', 'n.', 'The timber or stone on which a window frame stands; or, the\n   lowest piece in a window frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sill', 'n.', 'The floor of a gallery or passage in a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sill', 'n.', 'A piece of timber across the bottom of a canal lock for the\n   gates to shut against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sill', 'n.', 'The shaft or thill of a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sill', 'n.', 'A young herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sillabub', 'n.', 'A dish made by mixing wine or cider with milk, and thus\n   forming a soft curd; also, sweetened cream, flavored with wine and\n   beaten to a stiff froth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siller', 'n.', 'Silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sillily', 'adv.', 'In a silly manner; foolishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sillimanite', 'n.', 'Same as Fibrolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silliness', 'n.', 'The quality or state of being silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sillock', 'n.', 'The pollock, or coalfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sillon', 'n.', 'A work raised in the middle of a wide ditch, to defend it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silly', 'n.', 'Happy; fortunate; blessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silly', 'n.', 'Harmless; innocent; inoffensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silly', 'n.', 'Weak; helpless; frail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silly', 'n.', 'Rustic; plain; simple; humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silly', 'n.', 'Weak in intellect; destitute of ordinary strength of mind;\n   foolish; witless; simple; as, a silly woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silly', 'n.', 'Proceeding from want of understanding or common judgment;\n   characterized by weakness or folly; unwise; absurd; stupid; as, silly\n   conduct; a silly question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sillyhow', 'a.', 'A caul. See Caul, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silo', 'n.', 'A pit or vat for packing away green fodder for winter use so\n   as to exclude air and outside moisture. See Ensilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silt', 'n.', 'Mud or fine earth deposited from running or standing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silted', 'imp. & p. p.', 'of Silt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silting', 'p. pr. & vb. n.', 'of Silt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silt', 'v. t.', 'To choke, fill, or obstruct with silt or mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silt', 'v. i.', 'To flow through crevices; to percolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silty', 'a.', 'Full of silt; resembling silt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silure', 'n.', 'A fish of the genus Silurus, as the sheatfish; a siluroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silurian', 'a.', 'Of or pertaining to the country of the ancient Silures;\n   -- a term applied to the earliest of the Paleozoic eras, and also to\n   the strata of the era, because most plainly developed in that country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silurian', 'n.', 'The Silurian age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siluridan', 'n.', 'Any fish of the family Siluridae or of the order\n   Siluroidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siluroid', 'n.', 'Belonging to the Siluroidei, or Nematognathi, an order of\n   fishes including numerous species, among which are the American\n   catfishes and numerous allied fresh-water species of the Old World, as\n   the sheatfish (Silurus glanis) of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siluroid', 'n.', 'A siluroid fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siluroidei', 'n. pl.', 'An order of fishes, the Nematognathi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silurus', 'n.', 'A genus of large malacopterygious fishes of the order\n   Siluroidei. They inhabit the inland waters of Europe and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvas', 'pl. ', 'of Silva');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvae', 'pl. ', 'of Silva');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silva', 'n.', 'The forest trees of a region or country, considered\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silva', 'n.', 'A description or history of the forest trees of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvan', 'a.', 'Of or pertaining to woods; composed of woods or groves;\n   woody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvan', 'n.', 'See Sylvanium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvanite', 'n.', 'See Sylvanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvas', 'n. pl.', 'Alt. of Selvas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Selvas', 'n. pl.', 'Vast woodland plains of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvate', 'n.', 'Same as Sylvate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'n.', 'A soft white metallic element, sonorous, ductile, very\n   malleable, and capable of a high degree of polish. It is found native,\n   and also combined with sulphur, arsenic, antimony, chlorine, etc., in\n   the minerals argentite, proustite, pyrargyrite, ceragyrite, etc. Silver\n   is one of the \"noble\" metals, so-called, not being easily oxidized, and\n   is used for coin, jewelry, plate, and a great variety of articles.\n   Symbol Ag (Argentum). Atomic weight 107.7. Specific gravity 10.5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'n.', 'Coin made of silver; silver money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'n.', 'Anything having the luster or appearance of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'n.', 'The color of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'a.', 'Of or pertaining to silver; made of silver; as, silver\n   leaf; a silver cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'a.', 'Resembling silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'a.', 'Bright; resplendent; white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'a.', 'Precious; costly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'a.', 'Giving a clear, ringing sound soft and clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'a.', 'Sweet; gentle; peaceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvered', 'imp. & p. p.', 'of Silver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvering', 'p. pr. & vb. n.', 'of Silver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'v. t.', 'To cover with silver; to give a silvery appearance to by\n   applying a metal of a silvery color; as, to silver a pin; to silver a\n   glass mirror plate with an amalgam of tin and mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'v. t.', 'To polish like silver; to impart a brightness to, like\n   that of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'v. t.', 'To make hoary, or white, like silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver', 'v. i.', 'To acquire a silvery color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverback', 'n.', 'The knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverberry', 'n.', 'A tree or shrub (Elaeagnus argentea) with silvery\n   foliage and fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverbill', 'n.', 'An Old World finch of the genus Minia, as the M.\n   Malabarica of India, and M. cantans of Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverboom', 'n.', 'See Leucadendron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverfin', 'n.', 'A small North American fresh-water cyprinoid fish\n   (Notropis Whipplei).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverfish', 'n.', 'The tarpum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverfish', 'n.', 'A white variety of the goldfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silver-gray', 'a.', 'Having a gray color with a silvery luster; as,\n   silver-gray hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silveriness', 'n.', 'The state of being silvery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvering', 'n.', 'The art or process of covering metals, wood, paper,\n   glass, etc., with a thin film of metallic silver, or a substance\n   resembling silver; also, the firm do laid on; as, the silvering of a\n   glass speculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverized', 'imp. & p. p.', 'of Silverize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverizing', 'p. pr. & vb. n.', 'of Silverize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverize', 'v. t.', 'To cover with silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverless', 'a.', 'Having no silcver; hence, without money; impecunious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverling', 'n.', 'A small silver coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverly', 'adv.', 'Like silver in appearance or in sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvern', 'a.', 'Made of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silversides', 'n.', 'Any one of several species of small fishes of the\n   family Atherinidae, having a silvery stripe along each side of the\n   body. The common species of the American coast (Menidia notata) is very\n   abundant. Called also silverside, sand smelt, friar, tailor, and\n   tinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silversmith', 'n.', 'One whose occupation is to manufacture utensils,\n   ornaments, etc., of silver; a worker in silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverspot', 'n.', 'Any one of numerous species of butterflies of the genus\n   Argynnis and allied genera, having silvery spots on the under side of\n   the wings. See Illust. under Aphrodite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverware', 'n.', 'Dishes, vases, ornaments, and utensils of various\n   sorts, made of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silverweed', 'n.', 'A perennial rosaceous herb (Potentilla Anserina) having\n   the leaves silvery white beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvery', 'a.', 'Resembling, or having the luster of, silver; grayish white\n   and lustrous; of a mild luster; bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvery', 'a.', 'Besprinkled or covered with silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silvery', 'a.', 'Having the clear, musical tone of silver; soft and clear\n   in sound; as, silvery voices; a silvery laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Silviculture', 'n.', 'See Sylviculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sima', 'n.', 'A cyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simagre', 'n.', 'A grimace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simar', 'n.', 'A womans long dress or robe; also light covering; a scarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simarre', '', 'See Simar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simblot', 'n.', 'The harness of a drawloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simia', 'n.', 'A Linnaean genus of Quadrumana which included the types of\n   numerous modern genera. By modern writers it is usually restricted to\n   the genus which includes the orang-outang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simial', 'a.', 'Simian; apelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simian', 'a.', 'Of or pertaining to the family Simiadae, which, in its\n   widest sense, includes all the Old World apes and monkeys; also,\n   apelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simian', 'n.', 'Any Old World monkey or ape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similar', 'a.', 'Exactly corresponding; resembling in all respects;\n   precisely like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similar', 'a.', 'Nearly corresponding; resembling in many respects;\n   somewhat like; having a general likeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similar', 'a.', 'Homogenous; uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similar', 'n.', 'That which is similar to, or resembles, something else, as\n   in quality, form, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ties', 'pl. ', 'of Similarity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similarity', 'n.', 'The quality or state of being similar; likeness;\n   resemblance; as, a similarity of features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similarly', 'adv.', 'In a similar manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similary', 'a.', 'Similar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similative', 'a.', 'Implying or indicating likeness or resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similes', 'pl. ', 'of Simile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simile', 'n.', 'A word or phrase by which anything is likened, in one or\n   more of its aspects, to something else; a similitude; a poetical or\n   imaginative comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similiter', 'n.', 'The technical name of the form by which either party, in\n   pleading, accepts the issue tendered by his opponent; -- called\n   sometimes a joinder in issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similitude', 'n.', 'The quality or state of being similar or like;\n   resemblance; likeness; similarity; as, similitude of substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similitude', 'n.', 'The act of likening, or that which likens, one thing to\n   another; fanciful or imaginative comparison; a simile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similitude', 'n.', 'That which is like or similar; a representation,\n   semblance, or copy; a facsimile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similitudinary', 'a.', 'Involving or expressing similitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similize', 'v. t.', 'To liken; to compare; as, to similize a person, thing,\n   or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Similor', 'n.', 'An alloy of copper and zinc, resembling brass, but of a\n   golden color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Semious', 'a.', 'Of or pertaining to the Sim/; monkeylike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simitar', 'n.', 'See Scimiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simmered', 'imp. & p. p.', 'of Simmer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simmering', 'p. pr. & vb. n.', 'of Simmer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simmer', 'v. i.', 'To boil gently, or with a gentle hissing; to begin to\n   boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simmer', 'v. t.', 'To cause to boil gently; to cook in liquid heated almost\n   or just to the boiling point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simnel', 'n.', 'A kind of cake made of fine flour; a cracknel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simnel', 'n.', 'A kind of rich plum cake, eaten especially on Mid-Lent\n   Sunday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simoniac', 'n.', 'One who practices simony, or who buys or sells preferment\n   in the church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simoniacal', 'a.', 'Of or pertaining to simony; guilty of simony;\n   consisting of simony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simonial', 'a.', 'Simoniacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simonian', 'n.', 'One of the followers of Simon Magus; also, an adherent of\n   certain heretical sects in the early Christian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simonious', 'a.', 'Simoniacal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simonist', 'n.', 'One who practices simony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simony', 'n.', 'The crime of buying or selling ecclesiastical preferment;\n   the corrupt presentation of any one to an ecclesiastical benefice for\n   money or reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simoom', 'n.', 'Alt. of Simoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simoon', 'n.', 'A hot, dry, suffocating, dust-laden wind, that blows\n   occasionally in Arabia, Syria, and neighboring countries, generated by\n   the extreme heat of the parched deserts or sandy plains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simous', 'a.', 'Having a very flat or snub nose, with the end turned up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpai', 'n.', 'A long-tailed monkey (Semnopitchecus melalophus) native of\n   Sumatra. It has a crest of black hair. The forehead and cheeks are fawn\n   color, the upper parts tawny and red, the under parts white. Called\n   also black-crested monkey, and sinpae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpered', 'imp. & p. p.', 'of Simper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpering', 'p. pr. & vb. n.', 'of Simper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simper', 'v. i.', 'To smile in a silly, affected, or conceited manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simper', 'v. i.', 'To glimmer; to twinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simper', 'n.', 'A constrained, self-conscious smile; an affected, silly\n   smile; a smirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simperer', 'n.', 'One who simpers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpering', '', 'a. &. n. from Simper, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simperingly', 'adv.', 'In a simpering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Single; not complex; not infolded or entangled; uncombined;\n   not compounded; not blended with something else; not complicated; as, a\n   simple substance; a simple idea; a simple sound; a simple machine; a\n   simple problem; simple tasks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Plain; unadorned; as, simple dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Mere; not other than; being only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Not given to artifice, stratagem, or duplicity;\n   undesigning; sincere; true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Artless in manner; unaffected; unconstrained; natural;\n   inartificial;; straightforward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Direct; clear; intelligible; not abstruse or enigmatical;\n   as, a simple statement; simple language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Weak in intellect; not wise or sagacious; of but moderate\n   understanding or attainments; hence, foolish; silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Not luxurious; without much variety; plain; as, a simple\n   diet; a simple way of living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Humble; lowly; undistinguished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Without subdivisions; entire; as, a simple stem; a simple\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Not capable of being decomposed into anything more simple\n   or ultimate by any means at present known; elementary; thus, atoms are\n   regarded as simple bodies. Cf. Ultimate, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Homogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Consisting of a single individual or zooid; as, a simple\n   ascidian; -- opposed to compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'Something not mixed or compounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'A medicinal plant; -- so called because each vegetable was\n   supposed to possess its particular virtue, and therefore to constitute\n   a simple remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'A drawloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'A part of the apparatus for raising the heddles of a\n   drawloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'a.', 'A feast which is not a double or a semidouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple', 'v. i.', 'To gather simples, or medicinal plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple-hearted', 'a.', 'Sincere; inguenuous; guileless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simple-minded', 'a.', 'Artless; guileless; simple-hearted; undesigning;\n   unsuspecting; devoid of duplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpleness', 'n.', 'The quality or state of being simple; simplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpler', 'n.', 'One who collects simples, or medicinal plants; a\n   herbalist; a simplist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpless', 'n.', 'Simplicity; silliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simpleton', 'n.', 'A person of weak intellect; a silly person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplician', 'n.', 'One who is simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplicity', 'n.', 'The quality or state of being simple, unmixed, or\n   uncompounded; as, the simplicity of metals or of earths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplicity', 'n.', 'The quality or state of being not complex, or of\n   consisting of few parts; as, the simplicity of a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplicity', 'n.', 'Artlessness of mind; freedom from cunning or duplicity;\n   lack of acuteness and sagacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplicity', 'n.', 'Freedom from artificial ornament, pretentious style, or\n   luxury; plainness; as, simplicity of dress, of style, or of language;\n   simplicity of diet; simplicity of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplicity', 'n.', 'Freedom from subtlety or abstruseness; clearness; as,\n   the simplicity of a doctrine; the simplicity of an explanation or a\n   demonstration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplicity', 'n.', 'Weakness of intellect; silliness; folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplification', 'n.', 'The act of simplifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplified', 'imp. & p. p.', 'of Simplify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplifying', 'p. pr. & vb. n.', 'of Simplify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplify', 'v. t.', 'To make simple; to make less complex; to make clear by\n   giving the explanation for; to show an easier or shorter process for\n   doing or making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplist', 'n.', 'One skilled in simples, or medicinal plants; a simpler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplistic', 'a.', 'Of or pertaining to simples, or a simplist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simplity', 'n.', 'Simplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simploce', 'n.', 'See Symploce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simply', 'adv.', 'In a simple manner or state; considered in or by itself;\n   without addition; along; merely; solely; barely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simply', 'adv.', 'Plainly; without art or subtlety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simply', 'adv.', 'Weakly; foolishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulacher', 'n.', 'Alt. of Simulachre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulachre', 'n.', 'See Simulacrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulacra', 'pl. ', 'of Simulacrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulacrum', 'n.', 'A likeness; a semblance; a mock appearance; a sham; --\n   now usually in a derogatory sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simular', 'n.', 'One who pretends to be what he is not; one who, or that\n   which, simulates or counterfeits something; a pretender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simular', 'a.', 'False; specious; counterfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulate', 'a.', 'Feigned; pretended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulated', 'imp. & p. p.', 'of Simulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulating', 'p. pr. & vb. n.', 'of Simulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulate', 'v. t.', 'To assume the mere appearance of, without the reality;\n   to assume the signs or indications of, falsely; to counterfeit; to\n   feign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulation', 'n.', 'The act of simulating, or assuming an appearance which\n   is feigned, or not true; -- distinguished from dissimulation, which\n   disguises or conceals what is true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulator', 'n.', 'One who simulates, or feigns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulatory', 'a.', 'Simulated, or capable of being simulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simultaneity', 'n.', 'The quality or state of being simultaneous;\n   simultaneousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simultaneous', 'a.', 'Existing, happening, or done, at the same time; as,\n   simultaneous events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Simulty', 'n.', 'Private grudge or quarrel; as, domestic simulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sin', 'adv., prep., & conj.', 'Old form of Since.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sin', 'n.', 'Transgression of the law of God; disobedience of the divine\n   command; any violation of Gods will, either in purpose or conduct;\n   moral deficiency in the character; iniquity; as, sins of omission and\n   sins of commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sin', 'n.', 'An offense, in general; a violation of propriety; a\n   misdemeanor; as, a sin against good manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sin', 'n.', 'A sin offering; a sacrifice for sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sin', 'n.', 'An embodiment of sin; a very wicked person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinned', 'imp. & p. p.', 'of Sin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinning', 'p. pr. & vb. n.', 'of Sin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sin', 'n.', 'To depart voluntarily from the path of duty prescribed by God\n   to man; to violate the divine law in any particular, by actual\n   transgression or by the neglect or nonobservance of its injunctions; to\n   violate any known rule of duty; -- often followed by against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sin', 'n.', 'To violate human rights, law, or propriety; to commit an\n   offense; to trespass; to transgress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinaic', 'a.', 'Alt. of Sinaitic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinaitic', 'a.', 'Of or pertaining to Mount Sinai; given or made at Mount\n   Sinai; as, the Sinaitic law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinalbin', 'n.', 'A glucoside found in the seeds of white mustard (Brassica\n   alba, formerly Sinapis alba), and extracted as a white crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinamine', 'n.', 'A bitter white crystalline nitrogenous substance,\n   obtained indirectly from oil of mustard and ammonia; -- called also\n   allyl melamine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapate', 'n.', 'A salt of sinapic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapic', 'a.', 'Of or pertaining to sinapine; specifically, designating an\n   acid (C11H12O5) related to gallic acid, and obtained by the\n   decomposition of sinapine, as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapine', 'n.', 'An alkaloid occuring in the seeds of mustard. It is\n   extracted, in combination with sulphocyanic acid, as a white\n   crystalline substance, having a hot, bitter taste. When sinapine is\n   isolated it is unstable and undergoes decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapis', 'n.', 'A disused generic name for mustard; -- now called\n   Brassica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapisin', 'n.', 'A substance extracted from mustard seed and probably\n   identical with sinalbin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapism', 'n.', 'A plaster or poultice composed principally of powdered\n   mustard seed, or containing the volatile oil of mustard seed. It is a\n   powerful irritant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapoleic', 'a.', 'Of or pertaining to mustard oil; specifically,\n   designating an acid of the oleic acid series said to occur in mistard\n   oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinapoline', 'n.', 'A nitrogenous base, CO.(NH.C3H5)2, related to urea,\n   extracted from mustard oil, and also produced artifically, as a white\n   crystalline substance; -- called also diallyl urea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincaline', 'n.', 'Choline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Since', 'adv.', 'From a definite past time until now; as, he went a month\n   ago, and I have not seen him since.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Since', 'adv.', 'In the time past, counting backward from the present;\n   before this or now; ago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Since', 'adv.', 'When or that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Since', 'prep.', 'From the time of; in or during the time subsequent to;\n   subsequently to; after; -- usually with a past event or time for the\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Since', 'conj.', 'Seeing that; because; considering; -- formerly followed\n   by that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincere', 'superl.', 'Pure; unmixed; unadulterated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincere', 'superl.', 'Whole; perfect; unhurt; uninjured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincere', 'superl.', 'Being in reality what it appears to be; having a\n   character which corresponds with the appearance; not falsely assumed;\n   genuine; true; real; as, a sincere desire for knowledge; a sincere\n   contempt for meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincere', 'superl.', 'Honest; free from hypocrisy or dissimulation; as, a\n   sincere friend; a sincere person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincerely', 'adv.', 'In a sincere manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincerely', 'adv.', 'Purely; without alloy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincerely', 'adv.', 'Honestly; unfeignedly; without dissimulation; as, to\n   speak ones mind sincerely; to love virtue sincerely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincereness', 'n.', 'Same as Sincerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincerity', 'n.', 'The quality or state of being sincere; honesty of mind\n   or intention; freedom from simulation, hypocrisy, disguise, or false\n   pretense; sincereness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinch', 'n.', 'A saddle girth made of leather, canvas, woven horsehair, or\n   woven grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinch', 'v. t.', 'To gird with a sinch; to tighten the sinch or girth of (a\n   saddle); as, to sinch up a sadle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sincipital', 'a.', 'Of or pertaining to the sinciput; being in the region\n   of the sinciput.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinciput', 'n.', 'The fore part of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinciput', 'n.', 'The part of the head of a bird between the base of the\n   bill and the vertex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sindon', 'n.', 'A wrapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sindon', 'n.', 'A small rag or pledget introduced into the hole in the\n   cranium made by a trephine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sine', 'n.', 'The length of a perpendicular drawn from one extremity of an\n   arc of a circle to the diameter drawn through the other extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sine', 'n.', 'The perpendicular itself. See Sine of angle, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sine', 'prep.', 'Without.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinecural', 'a.', 'Of or pertaining to a sinecure; being in the nature of a\n   sinecure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinecure', 'n.', 'An ecclesiastical benefice without the care of souls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinecure', 'n.', 'Any office or position which requires or involves little\n   or no responsibility, labor, or active service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinecure', 'v. t.', 'To put or place in a sinecure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinecurism', 'n.', 'The state of having a sinecure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinecurist', 'n.', 'One who has a sinecure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinew', 'n.', 'A tendon or tendonous tissue. See Tendon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinew', 'n.', 'Muscle; nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinew', 'n.', 'Fig.: That which supplies strength or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewed', 'imp. & p. p.', 'of Sinew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewing', 'p. pr. & vb. n.', 'of Sinew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinew', 'v. t.', 'To knit together, or make strong with, or as with,\n   sinews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewed', 'a.', 'Furnished with sinews; as, a strong-sinewed youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewed', 'a.', 'Fig.: Equipped; strengthened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewiness', 'n.', 'Quality of being sinewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewish', 'a.', 'Sinewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewless', 'a.', 'Having no sinews; hence, having no strength or vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewous', 'a.', 'Sinewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinew-shrunk', 'a.', 'Having the sinews under the belly shrunk by excessive\n   fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewy', 'a.', 'Pertaining to, consisting of, or resembling, a sinew or\n   sinews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinewy', 'a.', 'Well braced with, or as if with, sinews; nervous; vigorous;\n   strong; firm; tough; as, the sinewy Ajax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinful', 'a.', 'Tainted with, or full of, sin; wicked; iniquitous;\n   criminal; unholy; as, sinful men; sinful thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sung', 'imp.', 'of Sing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sang', '', 'of Sing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sung', 'p. p.', 'of Sing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singing', 'p. pr. & vb. n.', 'of Sing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. i.', 'To utter sounds with musical inflections or melodious\n   modulations of voice, as fancy may dictate, or according to the notes\n   of a song or tune, or of a given part (as alto, tenor, etc.) in a\n   chorus or concerted piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. i.', 'To utter sweet melodious sounds, as birds do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. i.', 'To make a small, shrill sound; as, the air sings in\n   passing through a crevice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. i.', 'To tell or relate something in numbers or verse; to\n   celebrate something in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. i.', 'Ti cry out; to complain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. t.', 'To utter with musical infections or modulations of voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. t.', 'To celebrate is song; to give praises to in verse; to\n   relate or rehearse in numbers, verse, or poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. t.', 'To influence by singing; to lull by singing; as, to sing a\n   child to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing', 'v. t.', 'To accompany, or attend on, with singing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singed', 'imp. & p. p.', 'of Singe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singeing', 'p. pr. & vb. n.', 'of Singe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singe', 'v. t.', 'To burn slightly or superficially; to burn the surface\n   of; to burn the ends or outside of; as, to singe the hair or the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singe', 'v. t.', 'To remove the nap of (cloth), by passing it rapidly over\n   a red-hot bar, or over a flame, preliminary to dyeing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singe', 'v. t.', 'To remove the hair or down from (a plucked chicken or the\n   like) by passing it over a flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singe', 'n.', 'A burning of the surface; a slight burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singer', 'n.', 'One who, or that which, singes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singer', 'n.', 'One employed to singe cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singer', 'n.', 'A machine for singeing cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singer', 'n.', 'One who sings; especially, one whose profession is to sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singeress', 'n.', 'A songstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singhalese', 'n. & a.', 'Same as Cingalese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singing', '', 'a. & n. from Sing, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singingly', 'adv.', 'With sounds like singing; with a kind of tune; in a\n   singing tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'One only, as distinguished from more than one; consisting\n   of one alone; individual; separate; as, a single star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'Alone; having no companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'Hence, unmarried; as, a single man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'Not doubled, twisted together, or combined with others; as,\n   a single thread; a single strand of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'Performed by one person, or one on each side; as, a single\n   combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'Uncompounded; pure; unmixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'Not deceitful or artful; honest; sincere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'a.', 'Simple; not wise; weak; silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singled', 'imp. & p. p.', 'of Single');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singling', 'p. pr. & vb. n.', 'of Single');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'v. t.', 'To select, as an individual person or thing, from among\n   a number; to choose out from others; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'v. t.', 'To sequester; to withdraw; to retire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'v. t.', 'To take alone, or one by one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'v. i.', 'To take the irrregular gait called single-foot;- said of\n   a horse. See Single-foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'n.', 'A unit; one; as, to score a single.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'n.', 'The reeled filaments of silk, twisted without doubling to\n   give them firmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'n.', 'A handful of gleaned grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'n.', 'A game with but one player on each side; -- usually in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single', 'n.', 'A hit by a batter which enables him to reach first base\n   only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single-acting', 'a.', 'Having simplicity of action; especially (Mach.),\n   acting or exerting force during strokes in one direction only; -- said\n   of a reciprocating engine, pump, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single-breasted', 'a.', 'Lapping over the breast only far enough to permit\n   of buttoning, and having buttons on one edge only; as, a\n   single-breasted coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single-foot', 'n.', 'An irregular gait of a horse; -- called also\n   single-footed pace. See Single, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single-handed', 'a.', 'Having but one hand, or one workman; also, alone;\n   unassisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single-hearted', 'a.', 'Having an honest heart; free from duplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Single-minded', 'a.', 'Having a single purpose; hence, artless; guileless;\n   single-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singleness', 'n.', 'The quality or state of being single, or separate from\n   all others; the opposite of doubleness, complication, or multiplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singleness', 'n.', 'Freedom from duplicity, or secondary and selfish ends;\n   purity of mind or purpose; simplicity; sincerity; as, singleness of\n   purpose; singleness of heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singles', 'n. pl.', 'See Single, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singlestick', 'n.', 'In England and Scotland, a cudgel used in fencing or\n   fighting; a backsword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singlestick', 'n.', 'The game played with singlesticks, in which he who\n   first brings blood from his adversarys head is pronounced victor;\n   backsword; cudgeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singlet', 'n.', 'An unlined or undyed waistcoat; a single garment; --\n   opposed to doublet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singleton', 'n.', 'In certain games at cards, as whist, a single card of\n   any suit held at the deal by a player; as, to lead a singleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singletree', 'n.', 'The pivoted or swinging bar to which the traces of a\n   harnessed horse are fixed; a whiffletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singly', 'adv.', 'Individually; particularly; severally; as, to make men\n   singly and personally good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singly', 'adv.', 'Only; by ones self; alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singly', 'adv.', 'Without partners, companions, or associates;\n   single-handed; as, to attack another singly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singly', 'adv.', 'Honestly; sincerely; simply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singly', 'adv.', 'Singularly; peculiarly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sing-sing', 'n.', 'The kob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singsong', 'n.', 'Bad singing or poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singsong', 'n.', 'A drawling or monotonous tone, as of a badly executed\n   song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singsong', 'a.', 'Drawling; monotonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singsong', 'v. i.', 'To write poor poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singster', 'n.', 'A songstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Separate or apart from others; single; distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Engaged in by only one on a side; single.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Existing by itself; single; individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Each; individual; as, to convey several parcels of land,\n   all and singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Denoting one person or thing; as, the singular number; --\n   opposed to dual and plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Standing by itself; out of the ordinary course; unusual;\n   uncommon; strange; as, a singular phenomenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Distinguished as existing in a very high degree; rarely\n   equaled; eminent; extraordinary; exceptional; as, a man of singular\n   gravity or attainments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Departing from general usage or expectations; odd;\n   whimsical; -- often implying disapproval or consure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'a.', 'Being alone; belonging to, or being, that of which there\n   is but one; unique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'n.', 'An individual instance; a particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singular', 'n.', 'The singular number, or the number denoting one person or\n   thing; a word in the singular number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularist', 'n.', 'One who affects singularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularities', 'pl. ', 'of Singularity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularity', 'n.', 'The quality or state of being singular; some character\n   or quality of a thing by which it is distinguished from all, or from\n   most, others; peculiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularity', 'n.', 'Anything singular, rare, or curious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularity', 'n.', 'Possession of a particular or exclusive privilege,\n   prerogative, or distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularity', 'n.', 'Celibacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularize', 'v. t.', 'To make singular or single; to distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularly', 'adv.', 'In a singular manner; in a manner, or to a degree,\n   not common to others; extraordinarily; as, to be singularly exact in\n   ones statements; singularly considerate of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularly', 'adv.', 'Strangely; oddly; as, to behave singularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singularly', 'adv.', 'So as to express one, or the singular number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singult', 'n.', 'A sigh or sobbing; also, a hiccough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singultous', 'a.', 'Relating to, or affected with, hiccough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Singultus', 'n.', 'Hiccough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinical', 'a.', 'Of or pertaining to a sine; employing, or founded upon,\n   sines; as, a sinical quadrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinigrin', 'n.', 'A glucoside found in the seeds of black mustard (Brassica\n   nigra, formerly Sinapis nigra) It resembles sinalbin, and consists of a\n   potassium salt of myronic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinister', 'a.', 'On the left hand, or the side of the left hand; left; --\n   opposed to dexter, or right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinister', 'a.', 'Unlucky; inauspicious; disastrous; injurious; evil; --\n   the left being usually regarded as the unlucky side; as, sinister\n   influences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinister', 'a.', 'Wrong, as springing from indirection or obliquity;\n   perverse; dishonest; corrupt; as, sinister aims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinister', 'a.', 'Indicative of lurking evil or harm; boding covert danger;\n   as, a sinister countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinister-handed', 'a.', 'Left-handed; hence, unlucky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinisterly', 'adv.', 'In a sinister manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrad', 'adv.', 'Toward the left side; sinistrally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistral', 'a.', 'Of or pertaining to the left, inclining to the left;\n   sinistrous; -- opposed to dextral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistral', 'a.', 'Having the whorls of the spire revolving or rising to\n   the left; reversed; -- said of certain spiral shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrality', 'n.', 'The quality or state of being sinistral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrally', 'adv.', 'Toward the left; in a sinistral manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrin', 'n.', 'A mucilaginous carbohydrate, resembling achroodextrin,\n   extracted from squill as a colorless amorphous substance; -- so called\n   because it is levorotatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrorsal', 'a.', 'Rising spirally from right to left (of the\n   spectator); sinistrorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrorse', 'a.', 'Turning to the left (of the spectator) in the\n   ascending line; -- the opposite of dextrorse. See Dextrorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrous', 'a.', 'Being on the left side; inclined to the left;\n   sinistral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrous', 'a.', 'Wrong; absurd; perverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrously', 'adv.', 'In a sinistrous manner; perversely; wrongly;\n   unluckily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinistrously', 'adv.', 'With a tendency to use the left hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunk', 'imp.', 'of Sink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sank', '', 'of Sink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunk', 'p. p.', 'of Sink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunken', '', 'of Sink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinking', 'p. pr. & vb. n.', 'of Sink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. i.', 'To fall by, or as by, the force of gravity; to descend\n   lower and lower; to decline gradually; to subside; as, a stone sinks in\n   water; waves rise and sink; the sun sinks in the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. i.', 'To enter deeply; to fall or retire beneath or below the\n   surface; to penetrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. i.', 'Hence, to enter so as to make an abiding impression; to\n   enter completely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. i.', 'To be overwhelmed or depressed; to fall slowly, as so the\n   ground, from weakness or from an overburden; to fail in strength; to\n   decline; to decay; to decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. i.', 'To decrease in volume, as a river; to subside; to become\n   diminished in volume or in apparent height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. t.', 'To cause to sink; to put under water; to immerse or\n   submerge in a fluid; as, to sink a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. t.', 'Figuratively: To cause to decline; to depress; to degrade;\n   hence, to ruin irretrievably; to destroy, as by drowping; as, to sink\n   ones reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. t.', 'To make (a depression) by digging, delving, or cutting,\n   etc.; as, to sink a pit or a well; to sink a die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. t.', 'To bring low; to reduce in quantity; to waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. t.', 'To conseal and appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. t.', 'To keep out of sight; to suppress; to ignore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'v. t.', 'To reduce or extinguish by payment; as, to sink the\n   national debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'n.', 'A drain to carry off filthy water; a jakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'n.', 'A shallow box or vessel of wood, stone, iron, or other\n   material, connected with a drain, and used for receiving filthy water,\n   etc., as in a kitchen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sink', 'n.', 'A hole or low place in land or rock, where waters sink and\n   are lost; -- called also sink hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinker', 'n.', 'One who, or that which, sinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinker', 'n.', 'A weight on something, as on a fish line, to sink it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinker', 'n.', 'In knitting machines, one of the thin plates, blades, or\n   other devices, that depress the loops upon or between the needles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinking', '', 'a. & n. from Sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinless', 'a.', 'Free from sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinner', 'n.', 'One who has sinned; especially, one who has sinned without\n   repenting; hence, a persistent and incorrigible transgressor; one\n   condemned by the law of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinner', 'v. i.', 'To act as a sinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinneress', 'n.', 'A woman who sins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinnet', 'n.', 'See Sennit .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinological', 'a.', 'Relating to the Chinese language or literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinologist', 'n.', 'A sinologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinologue', 'n.', 'A student of Chinese; one versed in the Chinese\n   language, literature, and history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinology', 'n.', 'That branch of systemized knowledge which treats of the\n   Chinese, their language, literature, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinoper', 'n.', 'Sinople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinopia', 'n.', 'Alt. of Sinopis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinopis', 'n.', 'A red pigment made from sinopite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinopite', 'n.', 'A brickred ferruginous clay used by the ancients for red\n   paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinople', 'n.', 'Ferruginous quartz, of a blood-red or brownish red color,\n   sometimes with a tinge of yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinople', 'n.', 'The tincture vert; green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinque', 'n.', 'See Cinque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinsring', 'n.', 'Same as Banxring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinter', 'n.', 'Dross, as of iron; the scale which files from iron when\n   hammered; -- applied as a name to various minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinto', '', 'Alt. of Sintoist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sintu', '', 'Alt. of Sintoist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sintoism', '', 'Alt. of Sintoist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sintoist', '', 'See Shinto, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sintoc', 'n.', 'A kind of spice used in the East Indies, consisting of the\n   bark of a species of Cinnamomum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siniate', 'a.', 'Having the margin alternately curved inward and outward;\n   having rounded lobes separated by rounded sinuses; sinuous; wavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuated', 'imp. & p. p.', 'of Sinuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuating', 'p. pr. & vb. n.', 'of Sinuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuate', 'v. i.', 'To bend or curve in and out; to wind; to turn; to be\n   sinusous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuated', 'a.', 'Same as Sinuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuation', 'n.', 'A winding or bending in and out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuose', 'a.', 'Sinuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuosities', 'pl. ', 'of Sinuosity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuosity', 'n.', 'Quality or state of being sinuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuosity', 'n.', 'A bend, or a series of bends and turns; a winding, or a\n   series of windings; a wave line; a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuous', 'a.', 'Bending in and out; of a serpentine or undulating form;\n   winding; crooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinupalliate', 'a.', 'Having a pallial sinus. See under Sinus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'pl. ', 'of Sinus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinuses', 'pl. ', 'of Sinus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'n.', 'An opening; a hollow; a bending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'n.', 'A bay of the sea; a recess in the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'n.', 'A cavity; a depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'n.', 'A cavity in a bone or other part, either closed or with a\n   narrow opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'n.', 'A dilated vessel or canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'n.', 'A narrow, elongated cavity, in which pus is collected; an\n   elongated abscess with only a small orifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinus', 'n.', 'A depression between adjoining lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinusoid', 'n.', 'The curve whose ordinates are proportional to the sines\n   of the abscissas, the equation of the curve being y = a sin x. It is\n   also called the curve of sines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sinusoidal', 'a.', 'Of or pertaining to a sinusoid; like a sinusoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siogoon', 'n.', 'See Shogun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siogoonate', 'n.', 'See Shogunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sioux', 'n. sing. & pl.', 'See Dakotas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipped', 'imp. & p. p.', 'of Sip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipping', 'p. pr. & vb. n.', 'of Sip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sip', 'v. t.', 'To drink or imbibe in small quantities; especially, to take\n   in with the lips in small quantities, as a liquid; as, to sip tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sip', 'v. t.', 'To draw into the mouth; to suck up; as, a bee sips nectar\n   from the flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sip', 'v. t.', 'To taste the liquor of; to drink out of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sip', 'v. i.', 'To drink a small quantity; to take a fluid with the lips;\n   to take a sip or sips of something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sip', 'n.', 'The act of sipping; the taking of a liquid with the lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sip', 'n.', 'A small draught taken with the lips; a slight taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipage', 'n.', 'See Seepage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sip', 'v. i.', 'See Seep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphilis', 'n.', 'Syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphoid', 'n.', 'A siphon bottle. See under Siphon, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'A device, consisting of a pipe or tube bent so as to form\n   two branches or legs of unequal length, by which a liquid can be\n   transferred to a lower level, as from one vessel to another, over an\n   intermediate elevation, by the action of the pressure of the atmosphere\n   in forcing the liquid up the shorter branch of the pipe immersed in it,\n   while the continued excess of weight of the liquid in the longer branch\n   (when once filled) causes a continuous flow. The flow takes place only\n   when the discharging extremity of the pipe ia lower than the higher\n   liquid surface, and when no part of the pipe is higher above the\n   surface than the same liquid will rise by atmospheric pressure; that\n   is, about 33 feet for water, and 30 inches for mercury, near the sea\n   level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'One of the tubes or folds of the mantle border of a bivalve\n   or gastropod mollusk by which water is conducted into the gill cavity.\n   See Illust. under Mya, and Lamellibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'The anterior prolongation of the margin of any gastropod\n   shell for the protection of the soft siphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'The tubular organ through which water is ejected from the\n   gill cavity of a cephaloid. It serves as a locomotive organ, by guiding\n   and confining the jet of water. Called also siphuncle. See Illust.\n   under Loligo, and Dibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'The siphuncle of a cephalopod shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'The sucking proboscis of certain parasitic insects and\n   crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'A sproutlike prolongation in front of the mouth of many\n   gephyreans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'A tubular organ connected both with the esophagus and the\n   intestine of certain sea urchins and annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'n.', 'A siphon bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphon', 'v. t.', 'To convey, or draw off, by means of a siphon, as a\n   liquid from one vessel to another at a lower level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonage', 'n.', 'The action of a siphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonal', 'a.', 'Of or pertaining to a siphon; resembling a siphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonarid', 'n.', 'Any one of numerous species of limpet-shaped pulmonate\n   gastropods of the genus Siphonaria. They cling to rocks between high\n   and low water marks and have both lunglike organs and gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonata', 'n. pl.', 'A tribe of bivalve mollusks in which the posterior\n   mantle border is prolonged into two tubes or siphons. Called also\n   Siphoniata. See Siphon, 2 (a), and Quahaug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonate', 'a.', 'Having a siphon or siphons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonate', 'a.', 'Belonging to the Siphonata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonet', 'n.', 'One of the two dorsal tubular organs on the hinder part\n   of the abdomen of aphids. They give exit to the honeydew. See Illust.\n   under Aphis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonia', 'n.', 'A former name for a euphorbiaceous genus (Hevea) of South\n   American trees, the principal source of caoutchouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphoniata', 'n. pl.', 'Same as Siphonata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonic', 'a.', 'Of or pertaining to a siphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonifer', 'n.', 'Any cephalopod having a siphonate shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphoniferous', 'a.', 'Siphon-bearing, as the shell of the nautilus and\n   other cephalopods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonia', 'pl. ', 'of Siphonium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonium', 'n.', 'A bony tube which, in some birds, connects the tympanium\n   with the air chambers of the articular piece of the mandible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonobranchiata', 'n. pl.', 'A tribe of gastropods having the mantle\n   border, on one or both sides, prolonged in the form of a spout through\n   which water enters the gill cavity. The shell itself is not always\n   siphonostomatous in this group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonobranchiate', 'a.', 'Having a siphon, or siphons, to convey water to\n   the gills; belonging or pertaining to the Siphonobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonobranchiate', 'n.', 'One of the Siphonobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonoglyphe', 'n.', 'A gonidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonophora', 'n. pl.', 'An order of pelagic Hydrozoa including species\n   which form complex free-swimming communities composed of numerous\n   zooids of various kinds, some of which act as floats or as swimming\n   organs, others as feeding or nutritive zooids, and others as\n   reproductive zooids. See Illust. under Physallia, and Porpita.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonophoran', 'a.', 'Belonging to the Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonophoran', 'n.', 'One of the Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonophore', 'n.', 'One of the Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonopoda', 'n. pl.', 'A division of Scaphopoda including those in which\n   the foot terminates in a circular disk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonostomata', 'n. pl.', 'A tribe of parasitic copepod Crustacea\n   including a large number of species that are parasites of fishes, as\n   the lerneans. They have a mouth adapted to suck blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonostomata', 'n. pl.', 'An artificial division of gastropods including\n   those that have siphonostomatous shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonostomatous', 'a.', 'Having the front edge of the aperture of the\n   shell prolonged in the shape of a channel for the protection of the\n   siphon; -- said of certain gastropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonostomatous', 'a.', 'Pertaining to the Siphonostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonostome', 'n.', 'Any parasitic entomostracan of the tribe\n   Siphonostomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphonostome', 'n.', 'A siphonostomatous shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphorhinal', 'a.', 'Having tubular nostrils, as the petrels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphorhinian', 'n.', 'A siphorhinal bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphuncle', 'n.', 'The tube which runs through the partitions of chambered\n   cephalopod shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphuncled', 'a.', 'Having a siphuncle; siphunculated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphuncular', 'a.', 'Of or pertaining to the siphuncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siphunculated', 'a.', 'Having a siphuncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipid', 'a.', 'Having a taste or flavorl savory; sapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipper', 'n.', 'One whi sips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sippet', 'n.', 'A small sop; a small, thin piece of toasted bread soaked in\n   milk, broth, or the like; a small piece of toasted or fried bread cut\n   into some special shape and used for garnishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipple', 'v. i.', 'To sip often.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sippling', 'a.', 'Sipping often.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipunculacea', 'n. pl.', 'A suborder of Gephyrea, including those which\n   have the body unarmed and the intestine opening anteriorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipunculoid', 'a.', 'Pertaining to the Sipunculoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipunculoid', 'n.', 'One of the Sipunculoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipunculoidea', 'n. pl.', 'Same as Gephyrea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sipunculoidea', 'n. pl.', 'In a restricted sense, same as Sipunculacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Si quis', '', 'A notification by a candidate for orders of his intention to\n   inquire whether any impediment may be alleged against him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sir', 'n.', 'A man of social authority and dignity; a lord; a master; a\n   gentleman; -- in this sense usually spelled sire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sir', 'n.', 'A title prefixed to the Christian name of a knight or a\n   baronet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sir', 'n.', 'An English rendering of the LAtin Dominus, the academical\n   title of a bachelor of arts; -- formerly colloquially, and sometimes\n   contemptuously, applied to the clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sir', 'n.', 'A respectful title, used in addressing a man, without being\n   prefixed to his name; -- used especially in speaking to elders or\n   superiors; sometimes, also, used in the way of emphatic formality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siraskier', 'n.', 'See Seraskier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siraskierate', 'n.', 'See Seraskierate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirbonian', 'a.', 'See Serbonian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sircar', 'n.', 'A Hindoo clerk or accountant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sircar', 'n.', 'A district or province; a circar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sircar', 'n.', 'The government; the supreme authority of the state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirdar', 'n.', 'A native chief in Hindostan; a headman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sire', 'n.', 'A lord, master, or other person in authority. See Sir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sire', 'n.', 'A tittle of respect formerly used in speaking to elders and\n   superiors, but now only in addressing a sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sire', 'n.', 'A father; the head of a family; the husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sire', 'n.', 'A creator; a maker; an author; an originator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sire', 'n.', 'The male parent of a beast; -- applied especially to horses;\n   as, the horse had a good sire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sired', 'imp. & p. p.', 'of Sire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siring', 'p. pr. & vb. n.', 'of Sire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sire', 'v. t.', 'To beget; to procreate; -- used of beasts, and especially\n   of stallions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siredon', 'n.', 'The larval form of any salamander while it still has\n   external gills; especially, one of those which, like the axolotl\n   (Amblystoma Mexicanum), sometimes lay eggs while in this larval state,\n   but which under more favorable conditions lose their gills and become\n   normal salamanders. See also Axolotl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siren', 'n.', 'One of three sea nymphs, -- or, according to some writers,\n   of two, -- said to frequent an island near the coast of Italy, and to\n   sing with such sweetness that they lured mariners to destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siren', 'n.', 'An enticing, dangerous woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siren', 'n.', 'Something which is insidious or deceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siren', 'n.', 'A mermaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siren', 'n.', 'Any long, slender amphibian of the genus Siren or family\n   Sirenidae, destitute of hind legs and pelvis, and having permanent\n   external gills as well as lungs. They inhabit the swamps, lagoons, and\n   ditches of the Southern United States. The more common species (Siren\n   lacertina) is dull lead-gray in color, and becames two feet long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siren', 'n.', 'An instrument for producing musical tones and for\n   ascertaining the number of sound waves or vibrations per second which\n   produce a note of a given pitch. The sounds are produced by a\n   perforated rotating disk or disks. A form with two disks operated by\n   steam or highly compressed air is used sounding an alarm to vessels in\n   fog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siren', 'a.', 'Of or pertaining to a siren; bewitching, like a siren;\n   fascinating; alluring; as, a siren song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirene', 'n.', 'See Siren, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirenia', 'n. pl.', 'An order of large aquatic herbivorous mammals,\n   including the manatee, dugong, rytina, and several fossil genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirenian', 'n.', 'Any species of Sirenia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirenical', 'a.', 'Like, or appropriate to, a siren; fascinating;\n   deceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirenize', 'v. i.', 'To use the enticements of a siren; to act as a siren;\n   to fascinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siriasis', 'n.', 'A sunstroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siriasis', 'n.', 'The act of exposing to a sun bath. [Obs.] Cf. Insolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirius', 'n.', 'The Dog Star. See Dog Star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirkeer', 'n.', 'Any one of several species of Asiatic cuckoos of the genus\n   Taccocua, as the Bengal sirkeer (T. sirkee).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirloin', 'n.', 'A loin of beef, or a part of a loin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirname', 'n.', 'See Surname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siroc', 'n.', 'See Sirocco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siroccos', 'pl. ', 'of Sirocco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirocco', 'n.', 'An oppressive, relaxing wind from the Libyan deserts,\n   chiefly experienced in Italy, Malta, and Sicily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirrah', 'n.', 'A term of address implying inferiority and used in anger,\n   contempt, reproach, or disrespectful familiarity, addressed to a man or\n   boy, but sometimes to a woman. In sililoquies often preceded by ah. Not\n   used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirt', 'n.', 'A quicksand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirup', 'n.', 'Alt. of Syrup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrup', 'n.', 'A thick and viscid liquid made from the juice of fruits,\n   herbs, etc., boiled with sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrup', 'n.', 'A thick and viscid saccharine solution of superior quality\n   (as sugarhouse sirup or molasses, maple sirup); specifically, in\n   pharmacy and often in cookery, a saturated solution of sugar and water\n   (simple sirup), or such a solution flavored or medicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siruped', 'a.', 'Alt. of Syruped');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syruped', 'a.', 'Moistened, covered, or sweetened with sirup, or sweet\n   juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirupy', 'a.', 'Alt. of Syrupy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrupy', 'a.', 'Like sirup, or partaking of its qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sirvente', 'n.', 'A peculiar species of poetry, for the most part devoted\n   to moral and religious topics, and commonly satirical, -- often used by\n   the troubadours of the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sis', 'n.', 'A colloquial abbreviation of Sister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sis', 'n.', 'Six. See Sise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisal grass', '', 'Alt. of Sisal hemp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisal hemp', '', 'The prepared fiber of the Agave Americana, or American\n   aloe, used for cordage; -- so called from Sisal, a port in Yucatan. See\n   Sisal hemp, under Hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siscowet', 'n.', 'A large, fat variety of the namaycush found in Lake\n   Superior; -- called also siskawet, siskiwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sise', 'n.', 'An assize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sise', 'n.', 'Six; the highest number on a die; the cast of six in throwing\n   dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisel', 'n.', 'The suslik.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siser', 'n.', 'Cider. See Sicer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siserara', 'n.', 'Alt. of Siserary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siserary', 'n.', 'A hard blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siskin', 'n.', 'A small green and yellow European finch (Spinus spinus, or\n   Carduelis spinus); -- called also aberdevine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siskin', 'n.', 'The American pinefinch (S. pinus); -- called also pine\n   siskin. See Pinefinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siskiwit', 'n.', 'The siscowet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sismograph', 'n.', 'See Seismograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sismometer', 'n.', 'See Seismometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siss', 'v. i.', 'To make a hissing sound; as, a flatiron hot enough to siss\n   when touched with a wet finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siss', 'n.', 'A hissing noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sissoo', 'n.', 'A leguminous tree (Dalbergia Sissoo) of the northern parts\n   of India; also, the dark brown compact and durable timber obtained from\n   it. It is used in shipbuilding and for gun carriages, railway ties,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sist', 'v. t.', 'To stay, as judicial proceedings; to delay or suspend; to\n   stop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sist', 'v. t.', 'To cause to take a place, as at the bar of a court; hence,\n   to cite; to summon; to bring into court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sist', 'n.', 'A stay or suspension of proceedings; an order for a stay of\n   proceedings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sister', 'n.', 'A female who has the same parents with another person, or\n   who has one of them only. In the latter case, she is more definitely\n   called a half sister. The correlative of brother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sister', 'n.', 'A woman who is closely allied to, or assocciated with,\n   another person, as in the sdame faith, society, order, or community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sister', 'n.', 'One of the same kind, or of the same condition; --\n   generally used adjectively; as, sister fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sister', 'v. t.', 'To be sister to; to resemble closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisterhood', 'n.', 'The state or relation of being a sister; the office or\n   duty of a sister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisterhood', 'n.', 'A society of sisters; a society of women united in one\n   faith or order; sisters, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sistering', 'a.', 'Contiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisters-in-law', 'pl. ', 'of Sister-in-law');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sister-in-law', 'n.', 'The sister of ones husband or wife; also, the wife\n   of ones brother; sometimes, the wife of ones husbands or wifes\n   brother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisterly', 'a.', 'Like a sister; becoming a sister, affectionate; as,\n   sisterly kindness; sisterly remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sistine', 'a.', 'Of or pertaining to Pope Sixtus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sistren', 'n. pl.', 'Sisters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sistrum', '', 'An instrument consisting of a thin metal frame, through\n   which passed a number of metal rods, and furnished with a handle by\n   which it was shaken and made to rattle. It was peculiarly Egyptian, and\n   used especially in the worship of Isis. It is still used in Nubia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisyphean', 'a.', 'Relating to Sisyphus; incessantly recurring; as,\n   Sisyphean labors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sisyphus', 'n.', 'A king of Corinth, son of Aeolus, famed for his cunning.\n   He was killed by Theseus, and in the lower world was condemned by Pluto\n   to roll to the top of a hill a huge stone, which constantly rolled back\n   again, making his task incessant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', '', 'obs. 3d pers. sing. pres. of Sit, for sitteth.');
