  INSERT INTO words (word, word_type, description) VALUES  (    'Sitten', '', 'of Sit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitting', 'p. pr. & vb. n.', 'of Sit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To rest upon the haunches, or the lower extremity of the\n   trunk of the body; -- said of human beings, and sometimes of other\n   animals; as, to sit on a sofa, on a chair, or on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To perch; to rest with the feet drawn up, as birds do on a\n   branch, pole, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To remain in a state of repose; to rest; to abide; to rest\n   in any position or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To lie, rest, or bear; to press or weigh; -- with on; as, a\n   weight or burden sits lightly upon him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To be adjusted; to fit; as, a coat sts well or ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To suit one well or ill, as an act; to become; to befit; --\n   used impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To cover and warm eggs for hatching, as a fowl; to brood;\n   to incubate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To have position, as at the point blown from; to hold a\n   relative position; to have direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To occupy a place or seat as a member of an official body;\n   as, to sit in Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To hold a session; to be in session for official business;\n   -- said of legislative assemblies, courts, etc.; as, the court sits in\n   January; the aldermen sit to-night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To take a position for the purpose of having some artistic\n   representation of ones self made, as a picture or a bust; as, to sit\n   to a painter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To sit upon; to keep ones seat upon; as, he sits a horse\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To cause to be seated or in a sitting posture; to furnish a\n   seat to; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sit', 'v. t.', 'To suit (well / ill); to become.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Site', 'n.', 'The place where anything is fixed; situation; local position;\n   as, the site of a city or of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Site', 'n.', 'A place fitted or chosen for any certain permanent use or\n   occupation; as, a site for a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Site', 'n.', 'The posture or position of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sited', 'a.', 'Having a site; situated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitfast', 'a.', 'Fixed; stationary; immovable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitfast', 'n.', 'A callosity with inflamed edges, on the back of a horse,\n   under the saddle.');
