  INSERT INTO words (word, word_type, description) VALUES  (    'Siththen', 'adv. & conj.', 'See Sithen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitology', 'n.', 'A treatise on the regulation of the diet; dietetics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitophobia', 'n.', 'A version to food; refusal to take nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitten', '', 'p. p. of Sit, for sat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitter', 'n.', 'One who sits; esp., one who sits for a portrait or a bust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitter', 'n.', 'A bird that sits or incubates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sittine', 'a.', 'Of or pertaining to the family Sittidae, or nuthatches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitting', 'a.', 'Being in the state, or the position, of one who, or that\n   which, sits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitting', 'n.', 'The state or act of one who sits; the posture of one who\n   occupies a seat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitting', 'n.', 'A seat, or the space occupied by or allotted for a person,\n   in a church, theater, etc.; as, the hall has 800 sittings.');
