  INSERT INTO words (word, word_type, description) VALUES  (    'Sitting', 'n.', 'The actual presence or meeting of any body of men in their\n   seats, clothed with authority to transact business; a session; as, a\n   sitting of the judges of the Kings Bench, or of a commission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitting', 'n.', 'The time during which one sits while doing something, as\n   reading a book, playing a game, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitting', 'n.', 'A brooding over eggs for hatching, as by fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situate', 'a.', 'Alt. of Situated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situated', 'a.', 'Having a site, situation, or location; being in a\n   relative position; permanently fixed; placed; located; as, a town\n   situated, or situate, on a hill or on the seashore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situated', 'a.', 'Placed; residing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situate', 'v. t.', 'To place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situation', 'n.', 'Manner in which an object is placed; location, esp. as\n   related to something else; position; locality site; as, a house in a\n   pleasant situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situation', 'n.', 'Position, as regards the conditions and circumstances of\n   the case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situation', 'n.', 'Relative position; circumstances; temporary state or\n   relation at a moment of action which excites interest, as of persons in\n   a dramatic scene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situation', 'n.', 'Permanent position or employment; place; office; as, a\n   situation in a store; a situation under government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Situs', 'n.', 'The method in which the parts of a plant are arranged; also,\n   the position of the parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sitz bath', '', 'A tub in which one bathes in a sitting posture; also, a\n   bath so taken; a hip bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siva', 'n.', 'One of the triad of Hindoo gods. He is the avenger or\n   destroyer, and in modern worship symbolizes the reproductive power of\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sivan', 'n.', 'The third month of the Jewish ecclesiastical year; --\n   supposed to correspond nearly with our month of June.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sivatherium', 'n.', 'A genus of very large extinct ruminants found in the\n   Tertiary formation of India. The snout was prolonged in the form of a\n   proboscis. The male had four horns, the posterior pair being large and\n   branched. It was allied to the antelopes, but very much larger than any\n   exsisting species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siver', 'v. i.', 'To simmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sivvens', 'n.', 'See Sibbens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Siwin', 'n.', 'Same as Sewen.');
