  INSERT INTO words (word, word_type, description) VALUES  (    'Sixpenny', 'a.', 'Of the value of, or costing, sixpence; as, a sixpenny\n   loaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sixscore', 'a. & n.', 'Six times twenty; one hundred and twenty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Six-shooter', 'n.', 'A pistol or other firearm which can be fired six times\n   without reloading especially, a six-chambered revolver.');
