  INSERT INTO words (word, word_type, description) VALUES  (    'Skate', 'v. i.', 'To move on skates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skate', 'n.', 'Any one of numerous species of large, flat elasmobranch\n   fishes of the genus Raia, having a long, slender tail, terminated by a\n   small caudal fin. The pectoral fins, which are large and broad and\n   united to the sides of the body and head, give a somewhat rhombic form\n   to these fishes. The skin is more or less spinose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skater', 'n.', 'One who skates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skater', 'n.', 'Any one of numerous species of hemipterous insects\n   belonging to Gerris, Pyrrhocoris, Prostemma, and allied genera. They\n   have long legs, and run rapidly over the surface of the water, as if\n   skating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skatol', 'n.', 'A constituent of human faeces formed in the small\n   intestines as a product of the putrefaction of albuminous matter. It is\n   also found in reduced indigo. Chemically it is methyl indol, C9H9N.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skayles', 'n.', '[159.] Skittles.');
