  INSERT INTO words (word, word_type, description) VALUES  (    'Skedaddle', 'v. i.', 'To betake ones self to flight, as if in a panic; to\n   flee; to run away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skee', 'n.', 'A long strip of wood, curved upwards in front, used on the\n   foot for sliding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeed', 'n.', 'See Skid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeel', 'n.', 'A shallow wooden vessel for holding milk or cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeelduck', 'n.', 'Alt. of Skeelgoose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeelgoose', 'n.', 'The common European sheldrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeet', 'n.', 'A scoop with a long handle, used to wash the sides of a\n   vessel, and formerly to wet the sails or deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeg', 'n.', 'A sort of wild plum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeg', 'n.', 'A kind of oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeg', 'n.', 'The after part of the keel of a vessel, to which the rudder\n   is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skegger', 'n.', 'The parr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skein', 'n.', 'A quantity of yarn, thread, or the like, put up together,\n   after it is taken from the reel, -- usually tied in a sort of knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skein', 'n.', 'A metallic strengthening band or thimble on the wooden arm\n   of an axle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skein', 'n.', 'A flight of wild fowl (wild geese or the like).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeine', 'n.', 'See Skean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelder', 'v. t. & i.', 'To deceive; to cheat; to trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelder', 'n.', 'A vagrant; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeldrake', 'n.', 'Alt. of Skieldrake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skieldrake', 'n.', 'The common European sheldrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skieldrake', 'n.', 'The oyster catcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelet', 'n.', 'A skeleton. See Scelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeletal', 'a.', 'Pertaining to the skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeletogenous', 'a.', 'Forming or producing parts of the skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeletology', 'n.', 'That part of anatomy which treats of the skeleton;\n   also, a treatise on the skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeleton', 'n.', 'The bony and cartilaginous framework which supports the\n   soft parts of a vertebrate animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeleton', 'n.', 'The more or less firm or hardened framework of an\n   invertebrate animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeleton', 'n.', 'A very thin or lean person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeleton', 'n.', 'The framework of anything; the principal parts that\n   support the rest, but without the appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeleton', 'n.', 'The heads and outline of a literary production,\n   especially of a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeleton', 'a.', 'Consisting of, or resembling, a skeleton; consisting\n   merely of the framework or outlines; having only certain leading\n   features of anything; as, a skeleton sermon; a skeleton crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeletonized', 'imp. & p. p.', 'of Skeletonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeletonizing', 'p. pr. & vb. n.', 'of Skeletonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeletonize', 'v. t.', 'To prepare a skeleton of; also, to reduce, as a\n   leaf, to its skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeletonizer', 'n.', 'Any small moth whose larva eats the parenchyma of\n   leaves, leaving the skeleton; as, the apple-leaf skeletonizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skellum', 'n.', 'A scoundrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelly', 'v. i.', 'To squint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelly', 'n.', 'A squint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelp', 'n.', 'A blow; a smart stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelp', 'n.', 'A squall; also, a heavy fall of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelp', 'v. t.', 'To strike; to slap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelp', 'n.', 'A wrought-iron plate from which a gun barrel or pipe is made\n   by bending and welding the edges together, and drawing the thick tube\n   thus formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skelter', 'v. i.', 'To run off helter-skelter; to hurry; to scurry; -- with\n   away or off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sken', 'v. i.', 'To squint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skene', 'n.', 'See Skean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skep', 'n.', 'A coarse round farm basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skep', 'n.', 'A beehive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeptic', 'n.', 'One who is yet undecided as to what is true; one who is\n   looking or inquiring for what is true; an inquirer after facts or\n   reasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeptic', 'n.', 'A doubter as to whether any fact or truth can be certainly\n   known; a universal doubter; a Pyrrhonist; hence, in modern usage,\n   occasionally, a person who questions whether any truth or fact can be\n   established on philosophical grounds; sometimes, a critical inquirer,\n   in opposition to a dogmatist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeptic', 'n.', 'A person who doubts the existence and perfections of God,\n   or the truth of revelation; one who disbelieves the divine origin of\n   the Christian religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeptic', 'a.', 'Alt. of Skeptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeptical', 'a.', 'Of or pertaining to a sceptic or skepticism;\n   characterized by skepticism; hesitating to admit the certainly of\n   doctrines or principles; doubting of everything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skeptical', 'a.', 'Doubting or denying the truth of revelation, or the\n   sacred Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skepticism', 'n.', 'An undecided, inquiring state of mind; doubt;\n   uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skepticism', 'n.', 'The doctrine that no fact or principle can be certainly\n   known; the tenet that all knowledge is uncertain; Pyrrohonism;\n   universal doubt; the position that no fact or truth, however worthy of\n   confidence, can be established on philosophical grounds; critical\n   investigation or inquiry, as opposed to the positive assumption or\n   assertion of certain principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skepticism', 'n.', 'A doubting of the truth of revelation, or a denial of\n   the divine origin of the Christian religion, or of the being,\n   perfections, or truth of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skepticize', 'v. i.', 'To doubt; to pretend to doubt of everything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skerries', 'pl. ', 'of Skerry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skerry', 'n.', 'A rocky isle; an insulated rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketch', 'n.', 'An outline or general delineation of anything; a first\n   rough or incomplete draught or plan of any design; especially, in the\n   fine arts, such a representation of an object or scene as serves the\n   artists purpose by recording its chief features; also, a preliminary\n   study for an original work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketched', 'imp. & p. p.', 'of Sketch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketching', 'p. pr. & vb. n.', 'of Sketch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketch', 'n.', 'To draw the outline or chief features of; to make a rought\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketch', 'n.', 'To plan or describe by giving the principal points or ideas\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketch', 'v. i.', 'To make sketches, as of landscapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketchbook', 'n.', 'A book of sketches or for sketches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketcher', 'n.', 'One who sketches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketchily', 'adv.', 'In a sketchy or incomplete manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketchiness', 'n.', 'The quality or state of being sketchy; lack of finish;\n   incompleteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sketchy', 'a.', 'Containing only an outline or rough form; being in the\n   manner of a sketch; incomplete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'adv.', 'Awry; obliquely; askew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'a.', 'Turned or twisted to one side; situated obliquely; skewed; --\n   chiefly used in technical phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'n.', 'A stone at the foot of the slope of a gable, the offset of a\n   buttress, or the like, cut with a sloping surface and with a check to\n   receive the coping stones and retain them in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skewed', 'imp. & p. p.', 'of Skew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skewing', 'p. pr. & vb. n.', 'of Skew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'v. i.', 'To walk obliquely; to go sidling; to lie or move\n   obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'v. i.', 'To start aside; to shy, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'v. i.', 'To look obliquely; to squint; hence, to look slightingly\n   or suspiciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'adv.', 'To shape or form in an oblique way; to cause to take an\n   oblique position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skew', 'adv.', 'To throw or hurl obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skewbald', 'a.', 'Marked with spots and patches of white and some color\n   other than black; -- usually distinguished from piebald, in which the\n   colors are properly white and black. Said of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skewer', 'n.', 'A pin of wood or metal for fastening meat to a spit, or for\n   keeping it in form while roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skewered', 'imp. & p. p.', 'of Skewer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skewering', 'p. pr. & vb. n.', 'of Skewer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skewer', 'v. t.', 'To fasten with skewers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skid', 'n.', 'A shoe or clog, as of iron, attached to a chain, and placed\n   under the wheel of a wagon to prevent its turning when descending a\n   steep hill; a drag; a skidpan; also, by extension, a hook attached to a\n   chain, and used for the same purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skid', 'n.', 'A piece of timber used as a support, or to receive pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skid', 'n.', 'Large fenders hung over a vessels side to protect it in\n   handling a cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skid', 'n.', 'One of a pair of timbers or bars, usually arranged so as to\n   form an inclined plane, as form a wagon to a door, along which anything\n   is moved by sliding or rolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skid', 'n.', 'One of a pair of horizontal rails or timbers for supporting\n   anything, as a boat, a barrel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skidded', 'imp. & p. p.', 'of Skid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skidding', 'p. pr. & vb. n.', 'of Skid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skid', 'v. t.', 'To protect or support with a skid or skids; also, to cause\n   to move on skids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skid', 'v. t.', 'To check with a skid, as wagon wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiddaw', 'n.', 'The black guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skidpan', 'n.', 'See Skid, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skied', '', 'imp. & p. p. of Sky, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiey', 'a.', 'See Skyey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiff', 'n.', 'A small, light boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiffed', 'imp. & p. p.', 'of Skiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiffing', 'p. pr. & vb. n.', 'of Skiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiff', 'v. t.', 'To navigate in a skiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiffling', 'n.', 'Rough dressing by knocking off knobs or projections;\n   knobbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilder', 'v. i.', 'To beg; to pilfer; to skelder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilful', 'a.', 'See Skilful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'n.', 'Discrimination; judgment; propriety; reason; cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'n.', 'Knowledge; understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'n.', 'The familiar knowledge of any art or science, united with\n   readiness and dexterity in execution or performance, or in the\n   application of the art or science to practical purposes; power to\n   discern and execute; ability to perceive and perform; expertness;\n   aptitude; as, the skill of a mathematician, physician, surgeon,\n   mechanic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'n.', 'Display of art; exercise of ability; contrivance; address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'n.', 'Any particular art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'v. t.', 'To know; to understand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'v. i.', 'To be knowing; to have understanding; to be dexterous in\n   performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill', 'v. i.', 'To make a difference; to signify; to matter; -- used\n   impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilled', 'a.', 'Having familiar knowledge united with readiness and\n   dexterity in its application; familiarly acquainted with; expert;\n   skillful; -- often followed by in; as, a person skilled in drawing or\n   geometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skillet', 'n.', 'A small vessel of iron, copper, or other metal, with a\n   handle, used for culinary purpose, as for stewing meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skillful', 'a.', 'Discerning; reasonable; judicious; cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skillful', 'a.', 'Possessed of, or displaying, skill; knowing and ready;\n   expert; well-versed; able in management; as, a skillful mechanic; --\n   often followed by at, in, or of; as, skillful at the organ; skillful in\n   drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilligalee', 'n.', 'A kind of thin, weak broth or oatmeal porridge, served\n   out to prisoners and paupers in England; also, a drink made of oatmeal,\n   sugar, and water, sometimes used in the English navy or army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilling', 'n.', 'A bay of a barn; also, a slight addition to a cottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilling', 'n.', 'A money od account in Sweden, Norwey, Denmark, and North\n   Germany, and also a coin. It had various values, from three fourths of\n   a cent in Norway to more than two cents in Lubeck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skill-less', 'a.', 'Wanting skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilts', 'n. pl.', 'A kind of large, coarse, short trousers formerly worn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skilty', 'n.', 'The water rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimmed', 'imp. & p. p.', 'of Skim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimming', 'p. pr. & vb. n.', 'of Skim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'v. t.', 'To clear (a liquid) from scum or substance floating or\n   lying thereon, by means of a utensil that passes just beneath the\n   surface; as, to skim milk; to skim broth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'v. t.', 'To take off by skimming; as, to skim cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'v. t.', 'To pass near the surface of; to brush the surface of; to\n   glide swiftly along the surface of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'v. t.', 'Fig.: To read or examine superficially and rapidly, in\n   order to cull the principal facts or thoughts; as, to skim a book or a\n   newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'v. i.', 'To pass lightly; to glide along in an even, smooth course;\n   to glide along near the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'v. i.', 'To hasten along with superficial attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'v. i.', 'To put on the finishing coat of plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skim', 'a.', 'Contraction of Skimming and Skimmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skrim', 'n.', 'Scum; refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimback', 'n.', 'The quillback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimble-scamble', 'a.', 'Rambling; disorderly; unconnected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimitry', 'n.', 'See Skimmington.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimmer', 'n.', 'One who, or that which, skims; esp., a utensil with which\n   liquids are skimmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimmer', 'n.', 'Any species of longwinged marine birds of the genus\n   Rhynchops, allied to the terns, but having the lower mandible\n   compressed and much longer than the upper one. These birds fly rapidly\n   along the surface of the water, with the lower mandible immersed, thus\n   skimming out small fishes. The American species (R. nigra) is common on\n   the southern coasts of the United States. Called also scissorbill, and\n   shearbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimmer', 'n.', 'Any one of several large bivalve shells, sometimes used\n   for skimming milk, as the sea clams, and large scallops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimmerton', 'n.', 'See Skimmington.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimming', 'n.', 'The act of one who skims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimming', 'n.', 'That which is skimmed from the surface of a liquid; --\n   chiefly used in the plural; as, the skimmings of broth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimmingly', 'adv.', 'In a skimming manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimmington', 'n.', 'A word employed in the phrase, To ride Skimmington;\n   that is to ride on a horse with a woman, but behind her, facing\n   backward, carrying a distaff, and accompanied by a procession of\n   jeering neighbors making mock music; a cavalcade in ridicule of a\n   henpecked man. The custom was in vogue in parts of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimped', 'imp. & p. p.', 'of Skimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimping', 'p. pr. & vb. n.', 'of Skimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimp', 'v. t.', 'To slight; to do carelessly; to scamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimp', 'v. t.', 'To make insufficient allowance for; to scant; to scrimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimp', 'v. i.', 'To save; to be parsimonious or niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skimp', 'a.', 'Scanty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'n.', 'The external membranous integument of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'n.', 'The hide of an animal, separated from the body, whether\n   green, dry, or tanned; especially, that of a small animal, as a calf,\n   sheep, or goat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'n.', 'A vessel made of skin, used for holding liquids. See Bottle,\n   1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'n.', 'The bark or husk of a plant or fruit; the exterior coat of\n   fruits and plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'n.', 'That part of a sail, when furled, which remains on the\n   outside and covers the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'n.', 'The covering, as of planking or iron plates, outside the\n   framing, forming the sides and bottom of a vessel; the shell; also, a\n   lining inside the framing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinned', 'imp. & p. p.', 'of Skin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinning', 'p. pr. & vb. n.', 'of Skin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'v. t.', 'To strip off the skin or hide of; to flay; to peel; as, to\n   skin an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'v. t.', 'To cover with skin, or as with skin; hence, to cover\n   superficially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'v. t.', 'To strip of money or property; to cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'v. i.', 'To become covered with skin; as, a wound skins over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin', 'v. i.', 'To produce, in recitation, examination, etc., the work of\n   another for ones own, or to use in such exercise cribs, memeoranda,\n   etc., which are prohibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinbound', 'a.', 'Having the skin adhering closely and rigidly to the\n   flesh; hidebound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinched', 'imp. & p. p.', 'of Skinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinching', 'p. pr. & vb. n.', 'of Skinch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinch', 'v. t. & i.', 'To give scant measure; to squeeze or pinch in order\n   to effect a saving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skin-deep', 'a.', 'Not deeper than the skin; hence, superficial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinflint', 'n.', 'A penurious person; a miser; a niggard.');
