  INSERT INTO words (word, word_type, description) VALUES  (    'Skink', 'n.', 'Drink; also, pottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinker', 'n.', 'One who serves liquor; a tapster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinless', 'a.', 'Having no skin, or a very thin skin; as, skinless fruit.');
