  INSERT INTO words (word, word_type, description) VALUES  (    'Skinniness', 'n.', 'Quality of being skinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skinny', 'a.', 'Consisting, or chiefly consisting, of skin; wanting flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'A basket. See Skep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'A basket on wheels, used in cotton factories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'An iron bucket, which slides between guides, for hoisting\n   mineral and rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'A charge of sirup in the pans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'A beehive; a skep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipped', 'imp. & p. p.', 'of Skip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipping', 'p. pr. & vb. n.', 'of Skip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'v. i.', 'To leap lightly; to move in leaps and hounds; -- commonly\n   implying a sportive spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'v. i.', 'Fig.: To leave matters unnoticed, as in reading, speaking,\n   or writing; to pass by, or overlook, portions of a thing; -- often\n   followed by over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'v. t.', 'To leap lightly over; as, to skip the rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'v. t.', 'To pass over or by without notice; to omit; to miss; as,\n   to skip a line in reading; to skip a lesson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'v. t.', 'To cause to skip; as, to skip a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'A light leap or bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'The act of passing over an interval from one thing to\n   another; an omission of a part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skip', 'n.', 'A passage from one sound to another by more than a degree at\n   once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipjack', 'n.', 'An upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipjack', 'n.', 'An elater; a snap bug, or snapping beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipjack', 'n.', 'A name given to several kinds of a fish, as the common\n   bluefish, the alewife, the bonito, the butterfish, the cutlass fish,\n   the jurel, the leather jacket, the runner, the saurel, the saury, the\n   threadfish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipjack', 'n.', 'A shallow sailboat with a rectilinear or V-shaped cross\n   section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipper', 'n.', 'One who, or that which, skips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipper', 'n.', 'A young, thoughtless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipper', 'n.', 'The saury (Scomberesox saurus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipper', 'n.', 'The cheese maggot. See Cheese fly, under Cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipper', 'n.', 'Any one of numerous species of small butterflies of the\n   family Hesperiadae; -- so called from their peculiar short, jerking\n   flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipper', 'n.', 'The master of a fishing or small trading vessel; hence,\n   the master, or captain, of any vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skipper', 'n.', 'A ship boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skippet', 'n.', 'A small boat; a skiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skippet', 'n.', 'A small round box for keeping records.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skippingly', 'adv.', 'In a skipping manner; by skips, or light leaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirl', 'v. t.& i.', 'To utter in a shrill tone; to scream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirl', 'n.', 'A shrill cry or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirlcock', 'n.', 'The missel thrush; -- so called from its harsh alarm\n   note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirlcrake', 'n.', 'The turnstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirling', 'n.', 'A shrill cry or sound; a crying shrilly; a skirl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirling', 'n.', 'A small trout or salmon; -- a name used loosely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirmished', 'imp. & p. p.', 'of Skirmish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirmishing', 'p. pr. & vb. n.', 'of Skirmish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirmish', 'v. i.', 'To fight slightly or in small parties; to engage in a\n   skirmish or skirmishes; to act as skirmishers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirmish', 'v. i.', 'A slight fight in war; a light or desultory combat\n   between detachments from armies, or between detached and small bodies\n   of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirmish', 'v. i.', 'A slight contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirmisher', 'n.', 'One who skirmishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirmisher', 'n.', 'Soldiers deployed in loose order, to cover the front or\n   flanks of an advancing army or a marching column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirr', 'v. t.', 'To ramble over in order to clear; to scour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirr', 'v. i.', 'To scour; to scud; to run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirr', 'n.', 'A tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirret', 'n.', 'An umbelliferous plant (Sium, / Pimpinella, Sisarum). It\n   is a native of Asia, but has been long cultivated in Europe for its\n   edible clustered tuberous roots, which are very sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirrhus', 'n.', 'See Scirrhus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirt', 'n.', 'The lower and loose part of a coat, dress, or other like\n   garment; the part below the waist; as, the skirt of a coat, a dress, or\n   a mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirt', 'n.', 'A loose edging to any part of a dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirt', 'n.', 'Border; edge; margin; extreme part of anything');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirt', 'n.', 'A petticoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirt', 'n.', 'The diaphragm, or midriff, in animals.');
