  INSERT INTO words (word, word_type, description) VALUES  (    'Skirt', 'v. t.', 'To be on the border; to live near the border, or\n   extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirting', 'n.', 'A skirting board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skirting', 'n.', 'Skirts, taken collectivelly; material for skirts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skit', 'v. t.', 'To cast reflections on; to asperse.');
