  INSERT INTO words (word, word_type, description) VALUES  (    'Skit', 'n.', 'A wanton girl; a light wench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skittish', 'v. t.', 'Easily frightened; timorous; shy; untrustworthy; as, a\n   skittish colt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skittish', 'v. t.', 'Wanton; restive; freakish; volatile; changeable;\n   fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skittle', 'a.', 'Pertaining to the game of skittles.');
