  INSERT INTO words (word, word_type, description) VALUES  (    'Skittles', 'v. t.', 'An English game resembling ninepins, but played by\n   throwing wooden disks, instead of rolling balls, at the pins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skitty', 'n.', 'A rail; as, the water rail (called also skitty cock, and\n   skitty coot); the spotted crake (Porzana maruetta), and the moor hen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skive', 'n.', 'The iron lap used by diamond polishers in finishing the\n   facets of the gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skive', 'v. t.', 'To pare or shave off the rough or thick parts of (hides\n   or leather).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiver', 'n.', 'An inferior quality of leather, made of split sheepskin,\n   tanned by immersion in sumac, and dyed. It is used for hat linings,\n   pocketbooks, bookbinding, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiver', 'n.', 'The cutting tool or machine used in splitting leather or\n   skins, as sheepskins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiving', 'n.', 'The act of paring or splitting leather or skins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skiving', 'n.', 'A piece made in paring or splitting leather; specifically,\n   the part from the inner, or flesh, side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sklayre', 'n.', 'A vell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sklere', 'v. t.', 'To shelter; to cover.');
