  INSERT INTO words (word, word_type, description) VALUES  (    'Skull', 'n.', 'A school, company, or shoal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skull', 'n.', 'The skeleton of the head of a vertebrate animal, including\n   the brain case, or cranium, and the bones and cartilages of the face\n   and mouth. See Illusts. of Carnivora, of Facial angles under Facial,\n   and of Skeleton, in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skull', 'n.', 'The head or brain; the seat of intelligence; mind.');
