  INSERT INTO words (word, word_type, description) VALUES  (    'Skull', 'n.', 'A sort of oar. See Scull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skullcap', 'n.', 'A cap which fits the head closely; also, formerly, a\n   headpiece of iron sewed inside of a cap for protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skullcap', 'n.', 'Any plant of the labiate genus Scutellaria, the calyx of\n   whose flower appears, when inverted, like a helmet with the visor\n   raised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skullcap', 'n.', 'The Lophiomys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skullfish', 'n.', 'A whalers name for a whale more than two years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skulpin', 'n.', 'See Sculpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skun', 'n. & v.', 'See Scum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunk', 'n.', 'Any one of several species of American musteline carnivores\n   of the genus Mephitis and allied genera. They have two glands near the\n   anus, secreting an extremely fetid liquid, which the animal ejects at\n   pleasure as a means of defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunk', 'v. t.', 'In games of chance and skill: To defeat (an opponent) (as\n   in cards) so that he fails to gain a point, or (in checkers) to get a\n   king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunkball', 'n.', 'The surf duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunkhead', 'n.', 'The surf duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunkhead', 'n.', 'A duck (Camptolaimus Labradorus) which formerly\n   inhabited the Atlantic coast of New England. It is now supposed to be\n   extinct. Called also Labrador duck, and pied duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunkish', 'a.', 'Like the skunk, especially in odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunktop', 'n.', 'The surf duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skunkweed', 'n.', 'Skunk cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skurry', 'n. & v.', 'See Scurry.');
