  INSERT INTO words (word, word_type, description) VALUES  (    'Skies', 'pl. ', 'of Sky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sky', 'n.', 'A cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sky', 'n.', 'Hence, a shadow.');
