  INSERT INTO words (word, word_type, description) VALUES  (    'Sky-high', 'adv. & a.', 'Very high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skyish', 'a.', 'Like the sky, or approaching the sky; lofty; ethereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skylark', 'n.', 'A lark that mounts and sings as it files, especially the\n   common species (Alauda arvensis) found in Europe and in some parts of\n   Asia, and celebrated for its melodious song; -- called also sky\n   laverock. See under Lark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skylarking', 'n.', 'The act of running about the rigging of a vessel in\n   sport; hence, frolicking; scuffing; sporting; carousing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skylight', 'n.', 'A window placed in the roof of a building, in the ceiling\n   of a room, or in the deck of a ship, for the admission of light from\n   above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skyrocket', 'n.', 'A rocket that ascends high and burns as it flies; a\n   species of fireworks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skysail', 'n.', 'The sail set next above the royal. See Illust. under Sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Skyward', 'a. & adv.', 'Toward the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slab', 'n.', 'A thin piece of anything, especially of marble or other\n   stone, having plane surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slab', 'n.', 'An outside piece taken from a log or timber in sawing it into\n   boards, planks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slab', 'n.', 'The wryneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slab', 'n.', 'The slack part of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slab', 'a.', 'Thick; viscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slab', 'n.', 'That which is slimy or viscous; moist earth; mud; also, a\n   puddle.');
