  INSERT INTO words (word, word_type, description) VALUES  (    'Slack', 'superl.', 'Lax; not tense; not hard drawn; not firmly extended;\n   as, a slack rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slack', 'superl.', 'Weak; not holding fast; as, a slack hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slack', 'superl.', 'Remiss; backward; not using due diligence or care; not\n   earnest or eager; as, slack in duty or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slack', 'superl.', 'Not violent, rapid, or pressing; slow; moderate; easy;\n   as, business is slack.');
