  INSERT INTO words (word, word_type, description) VALUES  (    'Slaveholder', 'n.', 'One who holds slaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaveholding', 'a.', 'Holding persons in slavery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaveocracy', 'n.', 'See Slavocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaver', 'n.', 'A vessel engaged in the slave trade; a slave ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaver', 'n.', 'A person engaged in the purchase and sale of slaves; a\n   slave merchant, or slave trader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavered', 'imp. & p. p.', 'of Slaver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavering', 'p. pr. & vb. n.', 'of Slaver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaver', 'v. i.', 'To suffer spittle, etc., to run from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaver', 'v. i.', 'To be besmeared with saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaver', 'v. t.', 'To smear with saliva issuing from the mouth; to defile\n   with drivel; to slabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaver', 'n.', 'Saliva driveling from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaverer', 'n.', 'A driveler; an idiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavering', 'a.', 'Drooling; defiling with saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaveries', 'pl. ', 'of Slavery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavery', 'n.', 'The condition of a slave; the state of entire subjection\n   of one person to the will of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavery', 'n.', 'A condition of subjection or submission characterized by\n   lack of freedom of action or of will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavery', 'n.', 'The holding of slaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavey', 'n.', 'A maidservant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavic', 'a.', 'Slavonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavic', 'n.', 'The group of allied languages spoken by the Slavs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavish', 'a.', 'Of or pertaining to slaves; such as becomes or befits a\n   slave; servile; excessively laborious; as, a slavish life; a slavish\n   dependance on the great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavism', 'n.', 'The common feeling and interest of the Slavonic race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavocracy', 'n.', 'The persons or interest formerly representing slavery\n   politically, or wielding political power for the preservation or\n   advancement of slavery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavonian', 'a.', 'Alt. of Slavonic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavonic', 'a.', 'Of or pertaining to Slavonia, or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavonic', 'a.', 'Of or pertaining to the Slavs, or their language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavonian', 'n.', 'A native or inhabitant of Slavonia; ethnologically, a\n   Slav.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavophil', 'n.', 'Alt. of Slavophile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slavophile', 'n.', 'One, not being a Slav, who is interested in the\n   development and prosperity of that race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaw', 'n.', 'Sliced cabbage served as a salad, cooked or uncooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaw', '', 'Alt. of Slawen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slawen', '', 'p. p. of Slee, to slay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slew', 'imp.', 'of Slay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slain', 'p. p.', 'of Slay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slaying', 'p. pr. & vb. n.', 'of Slay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slay', 'v. t.', 'To put to death with a weapon, or by violence; hence, to\n   kill; to put an end to; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slayer', 'n.', 'One who slays; a killer; a murderer; a destrroyer of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slazy', 'a.', 'See Sleazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sle', 'v. t.', 'To slay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleave', 'n.', 'The knotted or entangled part of silk or thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleave', 'n.', 'Silk not yet twisted; floss; -- called also sleave silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleaved', 'imp. & p. p.', 'of Sleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleaving', 'p. pr. & vb. n.', 'of Sleave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleave', 'v. t.', 'To separate, as threads; to divide, as a collection of\n   threads; to sley; -- a weavers term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleaved', 'a.', 'Raw; not spun or wrought; as, sleaved thread or silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleaziness', 'n.', 'Quality of being sleazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleazy', 'a.', 'Wanting firmness of texture or substance; thin; flimsy; as,\n   sleazy silk or muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sled', 'n.', 'A vehicle on runners, used for conveying loads over the snow\n   or ice; -- in England called sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sled', 'n.', 'A small, light vehicle with runners, used, mostly by young\n   persons, for sliding on snow or ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledded', 'imp. & p. p.', 'of Sled');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledding', 'p. pr. & vb. n.', 'of Sled');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sled', 'v. t.', 'To convey or transport on a sled; as, to sled wood or\n   timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledding', 'n.', 'The act of transporting or riding on a sled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledding', 'n.', 'The state of the snow which admits of the running of\n   sleds; as, the sledding is good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledge', 'n.', 'A strong vehicle with low runners or low wheels; or one\n   without wheels or runners, made of plank slightly turned up at one end,\n   used for transporting loads upon the snow, ice, or bare ground; a sled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledge', 'n.', 'A hurdle on which, formerly, traitors were drawn to the\n   place of execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledge', 'n.', 'A sleigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledge', 'n.', 'A game at cards; -- called also old sledge, and all fours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledged', 'imp. & p. p.', 'of Sledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledging', 'p. pr. & vb. n.', 'of Sledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledge', 'v. i. & t.', 'To travel or convey in a sledge or sledges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sledge', 'v. t.', 'A large, heavy hammer, usually wielded with both hands;\n   -- called also sledge hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slee', 'v. t.', 'To slay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleek', 'superl.', 'Having an even, smooth surface; smooth; hence, glossy;\n   as, sleek hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleek', 'superl.', 'Not rough or harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleek', 'adv.', 'With ease and dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleek', 'n.', 'That which makes smooth; varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeked', 'imp. & p. p.', 'of Sleek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeking', 'p. pr. & vb. n.', 'of Sleek');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleek', 'v. t.', 'To make even and smooth; to render smooth, soft, and\n   glossy; to smooth over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleekly', 'adv.', 'In a sleek manner; smoothly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleekness', 'n.', 'The quality or state of being sleek; smoothness and\n   glossiness of surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeky', 'a.', 'Of a sleek, or smooth, and glossy appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeky', 'a.', 'Fawning and deceitful; sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', '', 'imp. of Sleep. Slept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slept', 'imp. & p. p.', 'of Sleep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeping', 'p. pr. & vb. n.', 'of Sleep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', 'v. i.', 'To take rest by a suspension of the voluntary exercise of\n   the powers of the body and mind, and an apathy of the organs of sense;\n   to slumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', 'v. i.', 'To be careless, inattentive, or uncouncerned; not to be\n   vigilant; to live thoughtlessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', 'v. i.', 'To be dead; to lie in the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', 'v. i.', 'To be, or appear to be, in repose; to be quiet; to be\n   unemployed, unused, or unagitated; to rest; to lie dormant; as, a\n   question sleeps for the present; the law sleeps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', 'v. t.', 'To be slumbering in; -- followed by a cognate object; as,\n   to sleep a dreamless sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', 'v. t.', 'To give sleep to; to furnish with accomodations for\n   sleeping; to lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep', 'v. i.', 'A natural and healthy, but temporary and periodical,\n   suspension of the functions of the organs of sense, as well as of those\n   of the voluntary and rational soul; that state of the animal in which\n   there is a lessened acuteness of sensory perception, a confusion of\n   ideas, and a loss of mental control, followed by a more or less\n   unconscious state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep-at-noon', 'n.', 'A plant (Tragopogon pratensis) which closes its\n   flowers at midday; a kind of goats beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleep-charged', 'a.', 'Heavy with sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'One who sleeps; a slumberer; hence, a drone, or lazy\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'That which lies dormant, as a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'A sleeping car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'An animal that hibernates, as the bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'A large fresh-water gobioid fish (Eleotris dormatrix).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'A nurse shark. See under Nurse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'Something lying in a reclining posture or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'One of the pieces of timber, stone, or iron, on or near\n   the level of the ground, for the support of some superstructure, to\n   steady framework, to keep in place the rails of a railway, etc.; a\n   stringpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'One of the joists, or roughly shaped timbers, laid\n   directly upon the ground, to receive the flooring of the ground story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'One of the knees which connect the transoms to the after\n   timbers on the ships quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeper', 'n.', 'The lowest, or bottom, tier of casks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepful', 'a.', 'Strongly inclined to sleep; very sleepy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepily', 'adv.', 'In a sleepy manner; drowsily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepiness', 'n.', 'The quality or state of being sleepy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeping', '', 'a. & n. from Sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepish', 'a.', 'Disposed to sleep; sleepy; drowsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepless', 'a.', 'Having no sleep; wakeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepless', 'a.', 'Having no rest; perpetually agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepmarken', 'n.', 'See 1st Hag, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepwaker', 'n.', 'On in a state of magnetic or mesmeric sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepwaking', 'n.', 'The state of one mesmerized, or in a partial and\n   morbid sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepwalker', 'n.', 'One who walks in his sleep; a somnambulist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepwalking', 'n.', 'Walking in ones sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepy', 'n.', 'Drowsy; inclined to, or overcome by, sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepy', 'n.', 'Tending to induce sleep; soporiferous; somniferous; as, a\n   sleepy drink or potion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepy', 'n.', 'Dull; lazy; heavy; sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepy', 'n.', 'Characterized by an absence of watchfulness; as, sleepy\n   security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepyhead', 'n.', 'A sleepy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleepyhead', 'n.', 'The ruddy duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleer', 'n.', 'A slayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleet', 'n.', 'The part of a mortar extending from the chamber to the\n   trunnions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleet', 'n.', 'Hail or snow, mingled with rain, usually falling, or driven\n   by the wind, in fine particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeted', 'imp. & p. p.', 'of Sleet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeting', 'p. pr. & vb. n.', 'of Sleet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleet', 'v. i.', 'To snow or hail with a mixture of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleetch', 'n.', 'Mud or slime, such as that at the bottom of rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleetiness', 'n.', 'The state of being sleety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleety', 'a.', 'Of or pertaining to sleet; characterized by sleet; as, a\n   sleety storm; sleety weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeve', 'n.', 'See Sleave, untwisted thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeve', 'n.', 'The part of a garment which covers the arm; as, the sleeve\n   of a coat or a gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeve', 'n.', 'A narrow channel of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeve', 'n.', 'A tubular part made to cover, sustain, or steady another\n   part, or to form a connection between two parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeve', 'n.', 'A long bushing or thimble, as in the nave of a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeve', 'n.', 'A short piece of pipe used for covering a joint, or forming\n   a joint between the ends of two other pipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeved', 'imp. & p. p.', 'of Sleeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeving', 'p. pr. & vb. n.', 'of Sleeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeve', 'v. t.', 'To furnish with sleeves; to put sleeves into; as, to\n   sleeve a coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeved', 'a.', 'Having sleeves; furnished with sleeves; -- often in\n   composition; as, long-sleeved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleevefish', 'n.', 'A squid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleevehand', 'n.', 'The part of a sleeve nearest the hand; a cuff or\n   wristband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeveless', 'a.', 'Having no sleeves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleeveless', 'a.', 'Wanting a cover, pretext, or palliation; unreasonable;\n   profitless; bootless; useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleided', 'imp. & p. p.', 'of Sleid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleiding', 'p. pr. & vb. n.', 'of Sleid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleid', 'v. t.', 'To sley, or prepare for use in the weavers sley, or\n   slaie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleigh', 'a.', 'Sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleigh', 'n.', 'A vehicle moved on runners, and used for transporting\n   persons or goods on snow or ice; -- in England commonly called a\n   sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleighing', 'n.', 'The act of riding in a sleigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleighing', 'n.', 'The state of the snow or ice which admits of running\n   sleighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleight', 'n.', 'Cunning; craft; artful practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleight', 'n.', 'An artful trick; sly artifice; a feat so dexterous that\n   the manner of performance escapes observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleight', 'n.', 'Dexterous practice; dexterity; skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleightful', 'a.', 'Cunning; dexterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleightly', 'adv.', 'Cinningly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleighty', 'a.', 'Cinning; sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slender', 'superl.', 'Small or narrow in proportion to the length or the\n   height; not thick; slim; as, a slender stem or stalk of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slender', 'superl.', 'Weak; feeble; not strong; slight; as, slender hope; a\n   slender constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slender', 'superl.', 'Moderate; trivial; inconsiderable; slight; as, a man\n   of slender intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slender', 'superl.', 'Small; inadequate; meager; pitiful; as, slender means\n   of support; a slender pittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slender', 'superl.', 'Spare; abstemious; frugal; as, a slender diet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slender', 'superl.', 'Uttered with a thin tone; -- the opposite of broad;\n   as, the slender vowels long e and i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slent', 'n. & v.', 'See Slant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slep', '', 'imp. of Sleep. Slept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slepez', 'n.', 'A burrowing rodent (Spalax typhlus), native of Russia and\n   Asia Minor. It has the general appearance of a mole, and is destitute\n   of eyes. Called also mole rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slept', '', 'imp. & p. p. of Sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleuth', 'n.', 'The track of man or beast as followed by the scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sleuthhound', 'n.', 'A hound that tracks animals by the scent;\n   specifically, a bloodhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slew', '', 'imp. of Slay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slew', 'v. t.', 'See Slue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slewed', 'a.', 'Somewhat drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slewth', 'n.', 'Sloth; idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sley', 'v. t.', 'A weavers reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sley', 'v. t.', 'A guideway in a knitting machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sley', 'v. t.', 'To separate or part the threads of, and arrange them in a\n   reed; -- a term used by weavers. See Sleave, and Sleid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slibber', 'a.', 'Slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'A thin, broad piece cut off; as, a slice of bacon; a\n   slice of cheese; a slice of bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'That which is thin and broad, like a slice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'A broad, thin piece of plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'A salver, platter, or tray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'A knife with a thin, broad blade for taking up or serving\n   fish; also, a spatula for spreading anything, as paint or ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'A plate of iron with a handle, forming a kind of chisel,\n   or a spadelike implement, variously proportioned, and used for various\n   purposes, as for stripping the planking from a vessels side, for\n   cutting blubber from a whale, or for stirring a fire of coals; a slice\n   bar; a peel; a fire shovel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'One of the wedges by which the cradle and the ship are\n   lifted clear of the building blocks to prepare for launching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'A removable sliding bottom to galley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliced', 'imp. & p. p.', 'of Slice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slicing', 'p. pr. & vb. n.', 'of Slice');
