  INSERT INTO words (word, word_type, description) VALUES  (    'Slice', 'v. t.', 'To clear by means of a slice bar, as a fire or the grate\n   bars of a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slicer', 'n.', 'One who, or that which, slices; specifically, the circular\n   saw of the lapidary.');
