  INSERT INTO words (word, word_type, description) VALUES  (    'Slick', 'v. t.', 'To make sleek or smoth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slick', 'n.', 'A wide paring chisel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slicken', 'a.', 'Sleek; smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slickens', 'n.', 'The pulverized matter from a quartz mill, or the lighter\n   soil of hydraulic mines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slickensides', 'n.', 'The smooth, striated, or partially polished surfaces\n   of a fissure or seam, supposed to have been produced by the sliding of\n   one surface on another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slickensides', 'n.', 'A variety of galena found in Derbyshire, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slicker', 'n.', 'That which makes smooth or sleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slicker', 'n.', 'A kind of burnisher for leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slicker', 'n.', 'A curved tool for smoothing the surfaces of a mold after\n   the withdrawal of the pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slicker', 'n.', 'A waterproof coat.');
