  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To move along the surface of any body by slipping, or\n   without walking or rolling; to slip; to glide; as, snow slides down the\n   mountains side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'Especially, to move over snow or ice with a smooth,\n   uninterrupted motion, as on a sled moving by the force of gravity, or\n   on the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To pass inadvertently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To pass along smoothly or unobservedly; to move gently\n   onward without friction or hindrance; as, a ship or boat slides through\n   the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To slip when walking or standing; to fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To pass from one note to another with no perceptible\n   cassation of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To pass out of ones thought as not being of any\n   consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To cause to slide; to thrust along; as, to slide one\n   piece of timber along another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'v. t.', 'To pass or put imperceptibly; to slip; as, to slide in a\n   word to vary the sense of a question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'The act of sliding; as, a slide on the ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'Smooth, even passage or progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'That on which anything moves by sliding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'An inclined plane on which heavy bodies slide by the force\n   of gravity, esp. one constructed on a mountain side for conveying logs\n   by sliding them down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A surface of ice or snow on which children slide for\n   amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'That which operates by sliding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A cover which opens or closes an aperture by sliding over\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A moving piece which is guided by a part or parts along\n   which it slides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A clasp or brooch for a belt, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A plate or slip of glass on which is a picture or\n   delineation to be exhibited by means of a magic lantern, stereopticon,\n   or the like; a plate on which is an object to be examined with a\n   microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'The descent of a mass of earth, rock, or snow down a hill or\n   mountain side; as, a land slide, or a snow slide; also, the track of\n   bare rock left by a land slide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A small dislocation in beds of rock along a line of fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A grace consisting of two or more small notes moving by\n   conjoint degrees, and leading to a principal note either above or\n   below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'An apparatus in the trumpet and trombone by which the\n   sounding tube is lengthened and shortened so as to produce the tones\n   between the fundamental and its harmonics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A sound which, by a gradual change in the position of the\n   vocal organs, passes imperceptibly into another sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'Same as Guide bar, under Guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slide', 'n.', 'A slide valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slidegroat', 'n.', 'The game of shovelboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slider', 'a.', 'See Slidder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slider', 'n.', 'One who, or that which, slides; especially, a sliding part\n   of an instrument or machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slider', 'n.', 'The red-bellied terrapin (Pseudemys rugosa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliding', 'a.', 'That slides or slips; gliding; moving smoothly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliding', 'a.', 'Slippery; elusory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slidometer', 'n.', 'An instrument for indicating and recording shocks to\n   railway cars occasioned by sudden stopping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'n.', 'Sleight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'v. t.', 'To overthrow; to demolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'v. t.', 'To make even or level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'v. t.', 'To throw heedlessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'superl.', 'Not decidedly marked; not forcible; inconsiderable;\n   unimportant; insignificant; not severe; weak; gentle; -- applied in a\n   great variety of circumstances; as, a slight (i. e., feeble) effort; a\n   slight (i. e., perishable) structure; a slight (i. e., not deep)\n   impression; a slight (i. e., not convincing) argument; a slight (i. e.,\n   not thorough) examination; slight (i. e., not severe) pain, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'superl.', 'Not stout or heavy; slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'superl.', 'Foolish; silly; weak in intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slighted', 'imp. & p. p.', 'of Slight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slighting', 'p. pr. & vb. n.', 'of Slight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'v. t.', 'To disregard, as of little value and unworthy of notice;\n   to make light of; as, to slight the divine commands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'n.', 'The act of slighting; the manifestation of a moderate\n   degree of contempt, as by neglect or oversight; neglect; indignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slight', 'adv.', 'Slightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slighten', 'v. t.', 'To slight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slighter', 'n.', 'One who slights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slightful', 'a.', 'See Sleightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slighting', 'a.', 'Characterized by neglect or disregard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slightingly', 'adv.', 'In a slighting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slightly', 'adv.', 'In a slight manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slightly', 'adv.', 'Slightingly; negligently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slightness', 'n.', 'The quality or state of being slight; slenderness;\n   feebleness; superficiality; also, formerly, negligence; indifference;\n   disregard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slighty', 'a.', 'Slight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slik', 'a.', 'Such.');
