  INSERT INTO words (word, word_type, description) VALUES  (    'Slily', 'adv.', 'See Slyly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slim', 'superl.', 'Worthless; bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slim', 'superl.', 'Weak; slight; unsubstantial; poor; as, a slim argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slim', 'superl.', 'Of small diameter or thickness in proportion to the\n   height or length; slender; as, a slim person; a slim tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slime', 'n.', 'Soft, moist earth or clay, having an adhesive quality;\n   viscous mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slime', 'n.', 'Any mucilaginous substance; any substance of a dirty nature,\n   that is moist, soft, and adhesive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slime', 'n.', 'Bitumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slime', 'n.', 'Mud containing metallic ore, obtained in the preparatory\n   dressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slime', 'n.', 'A mucuslike substance which exudes from the bodies of\n   certain animals.');
