  INSERT INTO words (word, word_type, description) VALUES  (    'Sliming', 'p. pr. & vb. n.', 'of Slime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slime', 'v. t.', 'To smear with slime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slimily', 'adv.', 'In a slimy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliminess', 'n.', 'The quality or state of being slimy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slimly', 'adv.', 'In a state of slimness; in a slim manner; slenderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slimness', 'n.', 'The quality or state of being slim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slimsy', 'a.', 'Flimsy; frail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slimy', 'superl.', 'Of or pertaining to slime; resembling slime; of the\n   nature of slime; viscous; glutinous; also, covered or daubed with\n   slime; yielding, or abounding in, slime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliness', 'n.', 'See Slyness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'An instrument for throwing stones or other missiles,\n   consisting of a short strap with two strings fastened to its ends, or\n   with a string fastened to one end and a light stick to the other. The\n   missile being lodged in a hole in the strap, the ends of the string are\n   taken in the hand, and the whole whirled rapidly round until, by\n   loosing one end, the missile is let fly with centrifugal force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'The act or motion of hurling as with a sling; a throw;\n   figuratively, a stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'A contrivance for sustaining anything by suspension');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'A kind of hanging bandage put around the neck, in which a\n   wounded arm or hand is supported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'A loop of rope, or a rope or chain with hooks, for\n   suspending a barrel, bale, or other heavy object, in hoisting or\n   lowering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'A strap attached to a firearm, for suspending it from the\n   shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'A band of rope or iron for securing a yard to a mast; --\n   chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slung', 'imp.', 'of Sling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slang', '', 'of Sling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slung', 'p. p.', 'of Sling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slinging', 'p. pr. & vb. n.', 'of Sling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'To throw with a sling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'To throw; to hurl; to cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'To hang so as to swing; as, to sling a pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'v. t.', 'To pass a rope round, as a cask, gun, etc., preparatory\n   to attaching a hoisting or lowering tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sling', 'n.', 'A drink composed of spirit (usually gin) and water\n   sweetened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slinger', 'n.', 'One who slings, or uses a sling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slunk', 'imp.', 'of Slink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slank', '', 'of Slink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slunk', 'p. p.', 'of Slink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slinking', 'p. pr. & vb. n.', 'of Slink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slink', 'a.', 'To creep away meanly; to steal away; to sneak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slink', 'a.', 'To miscarry; -- said of female beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slink', 'v. t.', 'To cast prematurely; -- said of female beasts; as, a cow\n   that slinks her calf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slink', 'a.', 'Produced prematurely; as, a slink calf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slink', 'a.', 'Thin; lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slink', 'n.', 'The young of a beast brought forth prematurely, esp. a calf\n   brought forth before its time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slink', 'n.', 'A thievish fellow; a sneak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slinky', 'a.', 'Thin; lank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipped', 'imp. & p. p.', 'of Slip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipping', 'p. pr. & vb. n.', 'of Slip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'To move along the surface of a thing without bounding,\n   rolling, or stepping; to slide; to glide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'To slide; to lose ones footing or ones hold; not to tread\n   firmly; as, it is necessary to walk carefully lest the foot should\n   slip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'To move or fly (out of place); to shoot; -- often with out,\n   off, etc.; as, a bone may slip out of its place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'To depart, withdraw, enter, appear, intrude, or escape as if\n   by sliding; to go or come in a quiet, furtive manner; as, some errors\n   slipped into the work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'To err; to fall into error or fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'v. t.', 'To cause to move smoothly and quickly; to slide; to convey\n   gently or secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'v. t.', 'To omit; to loose by negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'v. t.', 'To cut slips from; to cut; to take off; to make a slip or\n   slips of; as, to slip a piece of cloth or paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'v. t.', 'To let loose in pursuit of game, as a greyhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'v. t.', 'To cause to slip or slide off, or out of place; as, a\n   horse slips his bridle; a dog slips his collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'v. t.', 'To bring forth (young) prematurely; to slink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'The act of slipping; as, a slip on the ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'An unintentional error or fault; a false step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A twig separated from the main stock; a cutting; a scion;\n   hence, a descendant; as, a slip from a vine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A slender piece; a strip; as, a slip of paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A leash or string by which a dog is held; -- so called from\n   its being made in such a manner as to slip, or become loose, by\n   relaxation of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'An escape; a secret or unexpected desertion; as, to give one\n   the slip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A portion of the columns of a newspaper or other work struck\n   off by itself; a proof from a column of type when set up and in the\n   galley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'Any covering easily slipped on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A loose garment worn by a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A childs pinafore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'An outside covering or case; as, a pillow slip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'The slip or sheath of a sword, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A counterfeit piece of money, being brass covered with\n   silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'Matter found in troughs of grindstones after the grinding of\n   edge tools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'Potters clay in a very liquid state, used for the decoration\n   of ceramic ware, and also as a cement for handles and other applied\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A particular quantity of yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'An inclined plane on which a vessel is built, or upon which\n   it is hauled for repair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'An opening or space for vessels to lie in, between wharves or\n   in a dock; as, Peck slip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A narrow passage between buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A long seat or narrow pew in churches, often without a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A dislocation of a lead, destroying continuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'The motion of the center of resistance of the float of a\n   paddle wheel, or the blade of an oar, through the water horozontally,\n   or the difference between a vessels actual speed and the speed which\n   she would have if the propelling instrument acted upon a solid; also,\n   the velocity, relatively to still water, of the backward current of\n   water produced by the propeller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A fish, the sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip', 'n.', 'A fielder stationed on the off side and to the rear of the\n   batsman. There are usually two of them, called respectively short slip,\n   and long slip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipboard', 'n.', 'A board sliding in grooves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipcoat cheese', '', 'A rich variety of new cheese, resembling butter, but\n   white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipes', 'v.', 'Sledge runners on which a skip is dragged in a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipknot', 'n.', 'knot which slips along the rope or line around which it\n   is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slip-on', 'n.', 'A kind of overcoat worn upon the shoulders in the manner\n   of a cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippage', 'n.', 'The act of slipping; also, the amount of slipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipper', 'n.', 'One who, or that which, slips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipper', 'n.', 'A kind of light shoe, which may be slipped on with ease,\n   and worn in undress; a slipshoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipper', 'n.', 'A kind of apron or pinafore for children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipper', 'n.', 'A kind of brake or shoe for a wagon wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipper', 'n.', 'A piece, usually a plate, applied to a sliding piece, to\n   receive wear and afford a means of adjustment; -- also called shoe, and\n   gib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipper', 'a.', 'Slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippered', 'a.', 'Wearing slippers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipperily', 'adv.', 'In a slippery manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipperiness', 'n.', 'The quality of being slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipperness', 'n.', 'Slipperiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipperwort', 'n.', 'See Calceolaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippery', 'a.', 'Having the quality opposite to adhesiveness; allowing or\n   causing anything to slip or move smoothly, rapidly, and easily upon the\n   surface; smooth; glib; as, oily substances render things slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippery', 'a.', 'Not affording firm ground for confidence; as, a slippery\n   promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippery', 'a.', 'Not easily held; liable or apt to slip away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippery', 'a.', 'Liable to slip; not standing firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippery', 'a.', 'Unstable; changeable; mutable; uncertain; inconstant;\n   fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippery', 'a.', 'Uncertain in effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippery', 'a.', 'Wanton; unchaste; loose in morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippiness', 'n.', 'Slipperiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slippy', 'a.', 'Slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipshod', 'a.', 'Wearing shoes or slippers down at the heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipshod', 'a.', 'Figuratively: Careless in dress, manners, style, etc.;\n   slovenly; shuffling; as, slipshod manners; a slipshod or loose style of\n   writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipshoe', 'n.', 'A slipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipskin', 'a.', 'Evasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipslop', 'n.', 'Weak, poor, or flat liquor; weak, profitless discourse or\n   writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipstring', 'n.', 'One who has shaken off restraint; a prodigal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slipthrift', 'n.', 'A spendthrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slish', 'n.', 'A cut; as, slish and slash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slit', '', '3d. pers. sing. pres. of Slide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slit', 'imp. & p. p.', 'of Slit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slitted', '', 'of Slit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slitting', 'p. pr. & vb. n.', 'of Slit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slit', 'n.', 'To cut lengthwise; to cut into long pieces or strips; as, to\n   slit iron bars into nail rods; to slit leather into straps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slit', 'n.', 'To cut or make a long fissure in or upon; as, to slit the ear\n   or the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slit', 'n.', 'To cut; to sever; to divide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slit', 'n.', 'A long cut; a narrow opening; as, a slit in the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slither', 'v. i.', 'To slide; to glide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slit-shell', 'n.', 'Any species of Pleurotomaria, a genus of beautiful,\n   pearly, spiral gastropod shells having a deep slit in the outer lip.\n   Many fossil species are known, and a few living ones are found in deep\n   water in tropical seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slitter', 'n.', 'One who, or that which, slits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slitting', '', 'a. & n. from Slit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slive', 'v. i.', 'To sneak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slive', 'v. t.', 'To cut; to split; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slivered', 'imp. & p. p.', 'of Sliver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slivering', 'p. pr. & vb. n.', 'of Sliver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliver', 'v. t.', 'To cut or divide into long, thin pieces, or into very\n   small pieces; to cut or rend lengthwise; to slit; as, to sliver wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliver', 'n.', 'A long piece cut ot rent off; a sharp, slender fragment; a\n   splinter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliver', 'n.', 'A strand, or slender roll, of cotton or other fiber in a\n   loose, untwisted state, produced by a carding machine and ready for the\n   roving or slubbing which preceeds spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sliver', 'n.', 'Bait made of pieces of small fish. Cf. Kibblings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloakan', 'n.', 'A species of seaweed. [Spelled also slowcawn.] See 3d\n   Laver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloam', 'n.', 'A layer of earth between coal seams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloat', 'n.', 'A narrow piece of timber which holds together large pieces;\n   a slat; as, the sloats of a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slobber', 'v. t. & i.', 'See Slabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slobber', 'n.', 'See Slabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slobber', 'n.', 'A jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slobber', 'n.', 'Salivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slobberer', 'n.', 'One who slobbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slobberer', 'n.', 'A slovenly farmer; a jobbing tailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slobbery', 'a.', 'Wet; sloppy, as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slock', 'v. t.', 'Alt. of Slocken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slocken', 'v. t.', 'To quench; to allay; to slake. See Slake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slocking', '', 'a. & n. from Slock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloe', 'n.', 'A small, bitter, wild European plum, the fruit of the\n   blackthorn (Prunus spinosa); also, the tree itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slogan', 'n.', 'The war cry, or gathering word, of a Highland clan in\n   Scotland; hence, any rallying cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloggy', 'a.', 'Sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloke', 'n.', 'See Sloakan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloo', 'n.', 'Alt. of Slue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slue', 'n.', 'A slough; a run or wet place. See 2d Slough, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloom', 'n.', 'Slumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloomy', 'a.', 'Sluggish; slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloop', 'n.', 'A vessel having one mast and fore-and-aft rig, consisting of\n   a boom-and-gaff mainsail, jibs, staysail, and gaff topsail. The typical\n   sloop has a fixed bowsprit, topmast, and standing rigging, while those\n   of a cutter are capable of being readily shifted. The sloop usually\n   carries a centerboard, and depends for stability upon breadth of beam\n   rather than depth of keel. The two types have rapidly approximated\n   since 1880. One radical distinction is that a slop may carry a\n   centerboard. See Cutter, and Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'n.', 'Water or other liquid carelessly spilled or thrown aboyt, as\n   upon a table or a floor; a puddle; a soiled spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'n.', 'Mean and weak drink or liquid food; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'n.', 'Dirty water; water in which anything has been washed or\n   rinsed; water from wash-bowls, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopped', 'imp. & p. p.', 'of Slop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopping', 'p. pr. & vb. n.', 'of Slop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'v. t.', 'To cause to overflow, as a liquid, by the motion of the\n   vessel containing it; to spill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'v. t.', 'To spill liquid upon; to soil with a liquid spilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'v. i.', 'To overflow or be spilled as a liquid, by the motion of\n   the vessel containing it; -- often with over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'v. i.', 'Any kind of outer garment made of linen or cotton, as a\n   night dress, or a smock frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'v. i.', 'A loose lower garment; loose breeches; chiefly used in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slop', 'v. i.', 'Ready-made clothes; also, among seamen, clothing, bedding,\n   and other furnishings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slope', 'v. i.', 'An oblique direction; a line or direction including from\n   a horizontal line or direction; also, sometimes, an inclination, as of\n   one line or surface to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slope', 'v. i.', 'Any ground whose surface forms an angle with the plane of\n   the horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slope', 'a.', 'Sloping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slope', 'adv.', 'In a sloping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloped', 'imp. & p. p.', 'of Slope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloping', 'p. pr. & vb. n.', 'of Slope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slope', 'v. t.', 'To form with a slope; to give an oblique or slanting\n   direction to; to direct obliquely; to incline; to slant; as, to slope\n   the ground in a garden; to slope a piece of cloth in cutting a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slope', 'v. i.', 'To take an oblique direction; to be at an angle with the\n   plane of the horizon; to incline; as, the ground slopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slope', 'v. i.', 'To depart; to disappear suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopeness', 'n.', 'State of being slope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopewise', 'adv.', 'Obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloping', 'a.', 'Inclining or inclined from the plane of the horizon, or\n   from a horizontal or other right line; oblique; declivous; slanting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloppiness', 'n.', 'The quality or state of being sloppy; muddiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloppy', 'superl.', 'Wet, so as to spatter easily; wet, as with something\n   slopped over; muddy; plashy; as, a sloppy place, walk, road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopseller', 'n.', 'One who sells slops, or ready-made clothes. See 4th\n   Slop, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopshop', 'n.', 'A shop where slops. or ready-made clothes, are sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopwork', 'n.', 'The manufacture of slops, or cheap ready-made clothing;\n   also, such clothing; hence, hasty, slovenly work of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slopy', 'a.', 'Sloping; inclined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slosh', '', 'Alt. of Sloshy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloshy', '', 'See Slush, Slushy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slot', 'n.', 'A broad, flat, wooden bar; a slat or sloat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slot', 'n.', 'A bolt or bar for fastening a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slot', 'n.', 'A narrow depression, perforation, or aperture; esp., one for\n   the reception of a piece fitting or sliding in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slot', 'v. t.', 'To shut with violence; to slam; as, to slot a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slot', 'n.', 'The track of a deer; hence, a track of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloth', 'n.', 'Slowness; tardiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloth', 'n.', 'Disinclination to action or labor; sluggishness; laziness;\n   idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloth', 'n.', 'Any one of several species of arboreal edentates\n   constituting the family Bradypodidae, and the suborder Tardigrada. They\n   have long exserted limbs and long prehensile claws. Both jaws are\n   furnished with teeth (see Illust. of Edentata), and the ears and tail\n   are rudimentary. They inhabit South and Central America and Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloth', 'v. i.', 'To be idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slothful', 'a.', 'Addicted to sloth; inactive; sluggish; lazy; indolent;\n   idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slothhound', 'n.', 'See Sleuthhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slotted', 'a.', 'Having a slot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slotting', 'n.', 'The act or process of making slots, or mortises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouch', 'n.', 'A hanging down of the head; a drooping attitude; a limp\n   appearance; an ungainly, clownish gait; a sidewise depression or\n   hanging down, as of a hat brim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouch', 'n.', 'An awkward, heavy, clownish fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouched', 'imp. & p. p.', 'of Slouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouching', 'p. pr. & vb. n.', 'of Slouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouch', 'v. i.', 'To droop, as the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouch', 'v. i.', 'To walk in a clumsy, lazy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouch', 'v. t.', 'To cause to hang down; to depress at the side; as, to\n   slouth the hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouching', 'a.', 'Hanging down at the side; limp; drooping; without\n   firmness or shapeliness; moving in an ungainly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slouchy', 'a.', 'Slouching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', 'a.', 'Slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', 'n.', 'A place of deep mud or mire; a hole full of mire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', 'n.', 'A wet place; a swale; a side channel or inlet from a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', '', 'imp. of Slee, to slay. Slew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', 'n.', 'The skin, commonly the cast-off skin, of a serpent or of\n   some similar animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', 'n.', 'The dead mass separating from a foul sore; the dead part\n   which separates from the living tissue in mortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloughed', 'imp. & p. p.', 'of Slough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloughing', 'p. pr. & vb. n.', 'of Slough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', 'v. i.', 'To form a slough; to separate in the form of dead matter\n   from the living tissues; -- often used with off, or away; as, a\n   sloughing ulcer; the dead tissues slough off slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slough', 'v. t.', 'To cast off; to discard as refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloughing', 'n.', 'The act of casting off the skin or shell, as do insects\n   and crustaceans; ecdysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloughy', 'a.', 'Full of sloughs, miry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloughy', 'a.', 'Resembling, or of the nature of, a slough, or the dead\n   matter which separates from living flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sloven', 'n.', 'A man or boy habitually negligent of neathess and order; --\n   the correlative term to slattern, or slut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slovenliness', 'n.', 'The quality or state of being slovenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowenly', 'a.', 'Having the habits of a sloven; negligent of neatness and\n   order, especially in dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowenly', 'a.', 'Characteristic of a solven; lacking neatness and order;\n   evincing negligence; as, slovenly dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slovenly', 'adv.', 'a slovenly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slovenness', 'n.', 'Slovenliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slovenry', 'n.', 'Slovenliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', '', 'imp. of Slee, to slay. Slew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'superl.', 'Moving a short space in a relatively long time; not\n   swift; not quick in motion; not rapid; moderate; deliberate; as, a slow\n   stream; a slow motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'superl.', 'Not happening in a short time; gradual; late.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'superl.', 'Not ready; not prompt or quick; dilatory; sluggish; as,\n   slow of speech, and slow of tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'superl.', 'Not hasty; not precipitate; acting with deliberation;\n   tardy; inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'superl.', 'Behind in time; indicating a time earlier than the true\n   time; as, the clock or watch is slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'superl.', 'Not advancing or improving rapidly; as, the slow growth\n   of arts and sciences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'superl.', 'Heavy in wit; not alert, prompt, or spirited; wearisome;\n   dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'adv.', 'Slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowed', 'imp. & p. p.', 'of Slow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowing', 'p. pr. & vb. n.', 'of Slow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'v. t.', 'To render slow; to slacken the speed of; to retard; to\n   delay; as, to slow a steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'v. i.', 'To go slower; -- often with up; as, the train slowed up\n   before crossing the bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow', 'n.', 'A moth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowback', 'n.', 'A lubber; an idle fellow; a loiterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowh', '', 'imp. of Slee,to slay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowhound', 'n.', 'A sleuthhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowly', 'adv.', 'In a slow manner; moderately; not rapidly; not early; not\n   rashly; not readly; tardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowness', 'n.', 'The quality or state of being slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slows', 'n.', 'Milk sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slow-witted', 'a.', 'Dull of apprehension; not possessing quick\n   intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slowworm', 'v. t.', 'A lecertilian reptile; the blindworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slub', 'n.', 'A roll of wool slightly twisted; a rove; -- called also\n   slubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubbed', 'imp. & p. p.', 'of Slub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubbing', 'p. pr. & vb. n.', 'of Slub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slub', 'v. t.', 'To draw out and twist slightly; -- said of slivers of\n   wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubbered', 'imp. & p. p.', 'of Slubber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubbering', 'p. pr. & vb. n.', 'of Slubber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubber', 'v. t.', 'To do lazily, imperfectly, or coarsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubber', 'v. t.', 'To daub; to stain; to cover carelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubber', 'n.', 'A slubbing machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubberdegullion', 'n.', 'A mean, dirty wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubberingly', 'adv.', 'In a slovenly, or hurried and imperfect, manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slubbing', '', 'a. & n. from Slub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sludge', 'n.', 'Mud; mire; soft mud; slush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sludge', 'n.', 'Small floating pieces of ice, or masses of saturated snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sludge', 'n.', 'See Slime, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sludger', 'n.', 'A bucket for removing mud from a bored hole; a sand pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sludy', 'a.', 'Miry; slushy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slued', 'imp. & p. p.', 'of Slue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluing', 'p. pr. & vb. n.', 'of Slue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slue', 'v. t.', 'To turn about a fixed point, usually the center or axis,\n   as a spar or piece of timber; to turn; -- used also of any heavy body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slue', 'v. t.', 'In general, to turn about; to twist; -- often used\n   reflexively and followed by round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slue', 'v. i.', 'To turn about; to turn from the course; to slip or slide\n   and turn from an expected or desired course; -- often followed by\n   round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slue', 'n.', 'See Sloough, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'n.', 'A drone; a slow, lazy fellow; a sluggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'n.', 'A hindrance; an obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'n.', 'Any one of numerous species of terrestrial pulmonate mollusks\n   belonging to Limax and several related genera, in which the shell is\n   either small and concealed in the mantle, or altogether wanting. They\n   are closely allied to the land snails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'n.', 'Any smooth, soft larva of a sawfly or moth which creeps like\n   a mollusk; as, the pear slug; rose slug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'n.', 'A ship that sails slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'n.', 'An irregularly shaped piece of metal, used as a missile for a\n   gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'n.', 'A thick strip of metal less than type high, and as long as\n   the width of a column or a page, -- used in spacing out pages and to\n   separate display lines, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'v. i.', 'To move slowly; to lie idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'v. t.', 'To make sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slugged', 'imp. & p. p.', 'of Slug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slugging', 'p. pr. & vb. n.', 'of Slug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'v. t.', 'To load with a slug or slugs; as, to slug a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'v. t.', 'To strike heavily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug', 'v. i.', 'To become reduced in diameter, or changed in shape, by\n   passing from a larger to a smaller part of the bore of the barrel; --\n   said of a bullet when fired from a gun, pistol, or other firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slugabed', 'n.', 'One who indulges in lying abed; a sluggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggard', 'n.', 'A person habitually lazy, idle, and inactive; a drone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggard', 'a.', 'Sluggish; lazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggardize', 'v. t.', 'To make lazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggardy', 'n.', 'The state of being a sluggard; sluggishness; sloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slugger', 'n.', 'One who strikes heavy blows; hence, a boxer; a prize\n   fighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggish', 'a.', 'Habitually idle and lazy; slothful; dull; inactive; as, a\n   sluggish man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggish', 'a.', 'Slow; having little motion; as, a sluggish stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggish', 'a.', 'Having no power to move ones self or itself; inert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggish', 'a.', 'Characteristic of a sluggard; dull; stupid; tame; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluggy', 'a.', 'Sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slug-horn', 'a.', 'An erroneous form of the Scotch word slughorne, or\n   sloggorne, meaning slogan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slugs', 'n. pl.', 'Half-roasted ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slugworm', 'n.', 'Any caterpillar which has the general appearance of a\n   slug, as do those of certain moths belonging to Limacodes and allied\n   genera, and those of certain sawflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluice', 'n.', 'An artifical passage for water, fitted with a valve or\n   gate, as in a mill stream, for stopping or regulating the flow; also, a\n   water gate or flood gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluice', 'n.', 'Hence, an opening or channel through which anything flows;\n   a source of supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluice', 'n.', 'The stream flowing through a flood gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluice', 'n.', 'A long box or trough through which water flows, -- used for\n   washing auriferous earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluiced', 'imp. & p. p.', 'of Sluice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluicing', 'p. pr. & vb. n.', 'of Sluice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluice', 'v. t.', 'To emit by, or as by, flood gates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluice', 'v. t.', 'To wet copiously, as by opening a sluice; as, to sluice\n   meadows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluice', 'v. t.', 'To wash with, or in, a stream of water running through a\n   sluice; as, to sluice eart or gold dust in mining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluiceway', 'n.', 'An artificial channel into which water is let by a\n   sluice; specifically, a trough constructed over the bed of a stream, so\n   that logs, lumber, or rubbish can be floated down to some convenient\n   place of delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluicy', 'a.', 'Falling copiously or in streams, as from a sluice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slum', 'n.', 'A foul back street of a city, especially one filled with a\n   poor, dirty, degraded, and often vicious population; any low\n   neighborhood or dark retreat; -- usually in the plural; as, Westminster\n   slums are haunts for theives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slum', 'n.', 'Same as Slimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumbered', 'imp. & p. p.', 'of Slumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumbering', 'p. pr. & vb. n.', 'of Slumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumber', 'v. i.', 'To sleep; especially, to sleep lightly; to doze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumber', 'v. i.', 'To be in a state of negligence, sloth, supineness, or\n   inactivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumber', 'v. t.', 'To lay to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumber', 'v. t.', 'To stun; to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumber', 'n.', 'Sleep; especially, light sleep; sleep that is not deep or\n   sound; repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumberer', 'n.', 'One who slumbers; a sleeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumberingly', 'adv.', 'In a slumbering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumberless', 'a.', 'Without slumber; sleepless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumberous', 'a.', 'Inviting slumber; soporiferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumberous', 'a.', 'Being in the repose of slumber; sleepy; drowsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumbery', 'a.', 'Sleepy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumbrous', 'a.', 'Slumberous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumming', 'vb. n.', 'Visiting slums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slump', 'n.', 'The gross amount; the mass; the lump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slump', 'v. t.', 'To lump; to throw into a mess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumped', 'imp. & p. p.', 'of Slump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumping', 'p. pr. & vb. n.', 'of Slump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slump', 'v. i.', 'To fall or sink suddenly through or in, when walking on a\n   surface, as on thawing snow or ice, partly frozen ground, a bog, etc.,\n   not strong enough to bear the person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slump', 'n.', 'A boggy place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slump', 'n.', 'The noise made by anything falling into a hole, or into a\n   soft, miry place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slumpy', 'a.', 'Easily broken through; boggy; marshy; swampy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slung', '', 'imp. & p. p. of Sling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slunk', '', 'imp. & p. p. of Slink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slurred', 'imp. & p. p.', 'of Slur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slurring', 'p. pr. & vb. n.', 'of Slur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'v. t.', 'To soil; to sully; to contaminate; to disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'v. t.', 'To disparage; to traduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'v. t.', 'To cover over; to disguise; to conceal; to pass over\n   lightly or with little notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'v. t.', 'To cheat, as by sliding a die; to trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'v. t.', 'To pronounce indistinctly; as, to slur syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'v. t.', 'To sing or perform in a smooth, gliding style; to connect\n   smoothly in performing, as several notes or tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'v. t.', 'To blur or double, as an impression from type; to mackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'n.', 'A mark or stain; hence, a slight reproach or disgrace; a\n   stigma; a reproachful intimation; an innuendo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'n.', 'A trick played upon a person; an imposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'n.', 'A mark, thus [/ or /], connecting notes that are to be sung\n   to the same syllable, or made in one continued breath of a wind\n   instrument, or with one stroke of a bow; a tie; a sign of legato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slur', 'n.', 'In knitting machines, a contrivance for depressing the\n   sinkers successively by passing over them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slurred', 'a.', 'Marked with a slur; performed in a smooth, gliding style,\n   like notes marked with a slur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slush', 'n.', 'Soft mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slush', 'n.', 'A mixture of snow and water; half-melted snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slush', 'n.', 'A soft mixture of grease and other materials, used for\n   lubrication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slush', 'n.', 'The refuse grease and fat collected in cooking, especially\n   on shipboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slush', 'n.', 'A mixture of white lead and lime, with which the bright\n   parts of machines, such as the connecting rods of steamboats, are\n   painted to be preserved from oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slushed', 'imp. & p. p.', 'of Slush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slushing', 'p. pr. & vb. n.', 'of Slush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slush', 'v. t.', 'To smear with slush or grease; as, to slush a mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slush', 'v. t.', 'To paint with a mixture of white lead and lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slushy', 'a.', 'Abounding in slush; characterized by soft mud or\n   half-melted snow; as, the streets are slushy; the snow is slushy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slut', 'n.', 'An untidy woman; a slattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slut', 'n.', 'A servant girl; a drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slut', 'n.', 'A female dog; a bitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slutch', 'n.', 'Slush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slutchy', 'a.', 'Slushy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluthhound', 'n.', 'Sleuthhound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluttery', 'n.', 'The qualities and practices of a slut; sluttishness;\n   slatternlines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sluttish', 'a.', 'Like a slut; untidy; indecently negligent of cleanliness;\n   disorderly; as, a sluttish woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sly', 'v. t.', 'Dexterous in performing an action, so as to escape notice;\n   nimble; skillful; cautious; shrewd; knowing; -- in a good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sly', 'v. t.', 'Artfully cunning; secretly mischievous; wily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sly', 'v. t.', 'Done with, and marked by, artful and dexterous secrecy;\n   subtle; as, a sly trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sly', 'v. t.', 'Light or delicate; slight; thin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sly', 'adv.', 'Slyly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slyboots', 'n.', 'A humerous appellation for a sly, cunning, or waggish\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slyly', 'adv.', 'In a sly manner; shrewdly; craftily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slyness', 'n.', 'The quality or state of being sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Slype', 'n.', 'A narrow passage between two buildings, as between the\n   transept and chapter house of a monastery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'n.', 'A small sailing vessel, commonly rigged as a sloop, used\n   chiefly in the coasting and fishing trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. i.', 'Taste or flavor, esp. a slight taste or flavor; savor;\n   tincture; as, a smack of bitter in the medicine. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. i.', 'A small quantity; a taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. i.', 'A loud kiss; a buss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. i.', 'A quick, sharp noise, as of the lips when suddenly\n   separated, or of a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. i.', 'A quick, smart blow; a slap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'adv.', 'As if with a smack or slap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smacked', 'imp. & p. p.', 'of Smack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smacking', 'p. pr. & vb. n.', 'of Smack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'n.', 'To have a smack; to be tinctured with any particular taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'n.', 'To have or exhibit indications of the presence of any\n   character or quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'n.', 'To kiss with a close compression of the lips, so as to make\n   a sound when they separate; to kiss with a sharp noise; to buss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'n.', 'To make a noise by the separation of the lips after tasting\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. t.', 'To kiss with a sharp noise; to buss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. t.', 'To open, as the lips, with an inarticulate sound made by\n   a quick compression and separation of the parts of the mouth; to make a\n   noise with, as the lips, by separating them in the act of kissing or\n   after tasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smack', 'v. t.', 'To make a sharp noise by striking; to crack; as, to smack\n   a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smacking', 'n.', 'A sharp, quick noise; a smack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smacking', 'a.', 'Making a sharp, brisk sound; hence, brisk; as, a smacking\n   breeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'superl.', 'Having little size, compared with other things of the\n   same kind; little in quantity or degree; diminutive; not large or\n   extended in dimension; not great; not much; inconsiderable; as, a small\n   man; a small river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'superl.', 'Being of slight consequence; feeble in influence or\n   importance; unimportant; trivial; insignificant; as, a small fault; a\n   small business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'superl.', 'Envincing little worth or ability; not large-minded; --\n   sometimes, in reproach, paltry; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'superl.', 'Not prolonged in duration; not extended in time; short;\n   as, after a small space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'superl.', 'Weak; slender; fine; gentle; soft; not loud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'adv.', 'In or to small extent, quantity, or degree; little;\n   slightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'adv.', 'Not loudly; faintly; timidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'n.', 'The small or slender part of a thing; as, the small of the\n   leg or of the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'n.', 'Smallclothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'n.', 'Same as Little go. See under Little, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Small', 'v. t.', 'To make little or less.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smallage', 'n.', 'A biennial umbelliferous plant (Apium graveolens) native\n   of the seacoats of Europe and Asia. When deprived of its acrid and even\n   poisonous properties by cultivation, it becomes celery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smallclothes', 'n. pl.', 'A mans garment for the hips and thighs;\n   breeches. See Breeches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smallish', 'a.', 'Somewhat small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smallness', 'n.', 'The quality or state of being small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smallpox', 'n.', 'A contagious, constitutional, febrile disease\n   characterized by a peculiar eruption; variola. The cutaneous eruption\n   is at first a collection of papules which become vesicles (first flat,\n   subsequently umbilicated) and then pustules, and finally thick crusts\n   which slough after a certain time, often leaving a pit, or scar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smalls', 'n. pl.', 'See Small, n., 2, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smallsword', 'n.', 'A light sword used for thrusting only; especially, the\n   sword worn by civilians of rank in the eighteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smally', 'adv.', 'In a small quantity or degree; with minuteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smalt', 'v. t.', 'A deep blue pigment or coloring material used in various\n   arts. It is a vitreous substance made of cobalt, potash, and calcined\n   quartz fused, and reduced to a powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smalt-blue', 'a.', 'Deep blue, like smalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smaltine', 'n.', 'Alt. of Smaltite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smaltite', 'n.', 'A tin-white or gray mineral of metallic luster. It is an\n   arsenide of cobalt, nickel, and iron. Called also speiskobalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smaragd', 'n.', 'The emerald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smaragdine', 'a.', 'Of or pertaining to emerald; resembling emerald; of an\n   emerald green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smaragdite', 'n.', 'A green foliated kind of amphibole, observed in\n   eclogite and some varietis of gabbro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smarted', 'imp. & p. p.', 'of Smart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smarting', 'p. pr. & vb. n.', 'of Smart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'To feel a lively, pungent local pain; -- said of some\n   part of the body as the seat of irritation; as, my finger smarts; these\n   wounds smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'To feel a pungent pain of mind; to feel sharp pain or\n   grief; to suffer; to feel the sting of evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. t.', 'To cause a smart in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Quick, pungent, lively pain; a pricking local pain, as\n   the pain from puncture by nettles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Severe, pungent pain of mind; pungent grief; as, the\n   smart of affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'A fellow who affects smartness, briskness, and vivacity;\n   a dandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Smart money (see below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Causing a smart; pungent; pricking; as, a smart stroke or\n   taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Keen; severe; poignant; as, smart pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Vigorous; sharp; severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Accomplishing, or able to accomplish, results quickly;\n   active; sharp; clever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Efficient; vigorous; brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Marked by acuteness or shrewdness; quick in suggestion or\n   reply; vivacious; witty; as, a smart reply; a smart saying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Pretentious; showy; spruce; as, a smart gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smart', 'v. i.', 'Brisk; fresh; as, a smart breeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smarten', 'v. t.', 'To make smart or spruce; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smartle', 'v. i.', 'To waste away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smartly', 'adv.', 'In a smart manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smartness', 'n.', 'The quality or state of being smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smartweed', 'n.', 'An acrid plant of the genus Polygonum (P. Hydropiper),\n   which produces smarting if applied where the skin is tender.');
