  INSERT INTO words (word, word_type, description) VALUES  (    'Smash', 'v. i.', 'To break up, or to pieces suddenly, as the result of\n   collision or pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smash', 'n.', 'A breaking or dashing to pieces; utter destruction; wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smash', 'n.', 'Hence, bankruptcy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smasher', 'n.', 'One who, or that which, smashes or breaks things to\n   pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smasher', 'n.', 'Anything very large or extraordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smasher', 'n.', 'One who passes counterfeit coin.');
