  INSERT INTO words (word, word_type, description) VALUES  (    'Smatter', 'v. i.', 'To have a slight taste, or a slight, superficial\n   knowledge, of anything; to smack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smatter', 'v. t.', 'To talk superficially about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smatter', 'v. t.', 'To gain a slight taste of; to acquire a slight,\n   superficial knowledge of; to smack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smatter', 'n.', 'Superficial knowledge; a smattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smatterer', 'n.', 'One who has only a slight, superficial knowledge; a\n   sciolist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smattering', 'n.', 'A slight, superficial knowledge of something; sciolism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeared', 'imp. & p. p.', 'of Smear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smearing', 'p. pr. & vb. n.', 'of Smear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smear', 'n.', 'To overspread with anything unctuous, viscous, or adhesive;\n   to daub; as, to smear anything with oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smear', 'n.', 'To soil in any way; to contaminate; to pollute; to stain\n   morally; as, to be smeared with infamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smear', 'n.', 'A fat, oily substance; oinment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smear', 'n.', 'Hence, a spot made by, or as by, an unctuous or adhesive\n   substance; a blot or blotch; a daub; a stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smear dab', '', 'The sand fluke (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeared', 'a.', 'Having the color mark ings ill defined, as if rubbed; as,\n   the smeared dagger moth (Apatela oblinita).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeary', 'a.', 'Tending to smear or soil; adhesive; viscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeath', 'n.', 'The smew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smectite', 'n.', 'A hydrous silicate of alumina, of a greenish color,\n   which, in certain states of humidity, appears transparent and almost\n   gelatinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smee', 'n.', 'The pintail duck.');
