  INSERT INTO words (word, word_type, description) VALUES  (    'Smegma', 'n.', 'The matter secreted by any of the sebaceous glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smegma', 'n.', 'The soapy substance covering the skin of newborn infants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smegma', 'n.', 'The cheesy, sebaceous matter which collects between the\n   glans penis and the foreskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smegmatic', 'a.', 'Being of the nature of soap; soapy; cleansing;\n   detersive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeir', 'n.', 'A salt glaze on pottery, made by adding common salt to an\n   earthenware glaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelled', 'imp. & p. p.', 'of Smell');
