  INSERT INTO words (word, word_type, description) VALUES  (    'Smelling', 'p. pr. & vb. n.', 'of Smell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'n.', 'To perceive by the olfactory nerves, or organs of smell; to\n   have a sensation of, excited through the nasal organs when affected by\n   the appropriate materials or qualities; to obtain the scent of; as, to\n   smell a rose; to smell perfumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'n.', 'To detect or perceive, as if by the sense of smell; to scent\n   out; -- often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'n.', 'To give heed to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'v. i.', 'To affect the olfactory nerves; to have an odor or scent;\n   -- often followed by of; as, to smell of smoke, or of musk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'v. i.', 'To have a particular tincture or smack of any quality; to\n   savor; as, a report smells of calumny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'v. i.', 'To exercise the sense of smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'v. i.', 'To exercise sagacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'v. t.', 'The sense or faculty by which certain qualities of bodies\n   are perceived through the instrumentally of the olfactory nerves. See\n   Sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell', 'v. t.', 'The quality of any thing or substance, or emanation\n   therefrom, which affects the olfactory organs; odor; scent; fragrance;\n   perfume; as, the smell of mint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeller', 'n.', 'One who smells, or perceives by the sense of smell; one\n   who gives out smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeller', 'n.', 'The nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell-feast', 'n.', 'One who is apt to find and frequent good tables; a\n   parasite; a sponger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell-feast', 'n.', 'A feast at which the guests are supposed to feed upon\n   the odors only of the viands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelling', 'n.', 'The act of one who smells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelling', 'n.', 'The sense by which odors are perceived; the sense of\n   smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smell-less', 'a.', 'Destitute of smell; having no odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelt', '', 'imp. & p. p. of Smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelt', 'n.', 'Any one of numerous species of small silvery salmonoid\n   fishes of the genus Osmerus and allied genera, which ascend rivers to\n   spawn, and sometimes become landlocked in lakes. They are esteemed as\n   food, and have a peculiar odor and taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelt', 'n.', 'A gull; a simpleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelted', 'imp. & p. p.', 'of Smelt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelting', 'p. pr. & vb. n.', 'of Smelt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelt', 'v. i.', 'To melt or fuse, as, ore, for the purpose of separating\n   and refining the metal; hence, to reduce; to refine; to flux or\n   scorify; as, to smelt tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelter', 'n.', 'One who, or that which, smelts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeltery', 'n.', 'A house or place for smelting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smeltie', 'n.', 'A fish, the bib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smelting', '', 'a. & n. from Smelt.');
