  INSERT INTO words (word, word_type, description) VALUES  (    'Smerlin', 'n.', 'A small loach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smew', 'n.', 'small European merganser (Mergus albellus) which has a white\n   crest; -- called also smee, smee duck, white merganser, and white nun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smew', 'n.', 'The hooded merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smicker', 'a.', 'To look amorously or wantonly; to smirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smicker', 'v.', 'Amorous; wanton; gay; spruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smickering', 'n.', 'Amorous glance or inclination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smicket', 'n.', 'A womans under-garment; a smock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smickly', 'adv.', 'Smugly; finically.');
