  INSERT INTO words (word, word_type, description) VALUES  (    'Smight', 'v. t.', 'To smite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilacin', 'n.', 'See Parrilin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilax', 'n.', 'A genus of perennial climbing plants, usually with a\n   prickly woody stem; green brier, or cat brier. The rootstocks of\n   certain species are the source of the medicine called sarsaparilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilax', 'n.', 'A delicate trailing plant (Myrsiphyllum asparagoides) much\n   used for decoration. It is a native of the Cape of Good Hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smiled', 'imp. & p. p.', 'of Smile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smiling', 'p. pr. & vb. n.', 'of Smile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'To express amusement, pleasure, moderate joy, or love and\n   kindness, by the features of the face; to laugh silently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'To express slight contempt by a look implying sarcasm or\n   pity; to sneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'To look gay and joyous; to have an appearance suited to\n   excite joy; as, smiling spring; smiling plenty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'To be propitious or favorable; to favor; to countenance;\n   -- often with on; as, to smile on ones labors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. t.', 'To express by a smile; as, to smile consent; to smile a\n   welcome to visitors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. t.', 'To affect in a certain way with a smile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'The act of smiling; a peculiar change or brightening of\n   the face, which expresses pleasure, moderate joy, mirth, approbation,\n   or kindness; -- opposed to frown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'A somewhat similar expression of countenance, indicative\n   of satisfaction combined with malevolent feelings, as contempt, scorn,\n   etc; as, a scornful smile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'Favor; countenance; propitiousness; as, the smiles of\n   Providence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smile', 'v. i.', 'Gay or joyous appearance; as, the smiles of spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smileless', 'a.', 'Not having a smile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smiler', 'n.', 'One who smiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilet', 'n.', 'A little smile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilingly', 'adv.', 'In a smiling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilingness', 'n.', 'Quality or state of being smiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilodon', 'n.', 'An extinct genus of saber-toothed tigers. See Mach/rodus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smilt', 'v. i.', 'To melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sminthurid', 'n.', 'Any one of numerous small species of springtails, of\n   the family Sminthuridae, -- usually found on flowers. See Illust. under\n   Collembola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smirch', 'v. t.', 'To smear with something which stains, or makes dirty; to\n   smutch; to begrime; to soil; to sully.');
