  INSERT INTO words (word, word_type, description) VALUES  (    'Smirk', 'n.', 'A forced or affected smile; a simper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smirk', 'a.', 'Nice,; smart; spruce; affected; simpering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smirkingly', 'adv.', 'With smirking; with a smirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smirky', 'a.', 'Smirk; smirking.');
