  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. t.', 'To strike; to inflict a blow upon with the hand, or with\n   any instrument held in the hand, or with a missile thrown by the hand;\n   as, to smite with the fist, with a rod, sword, spear, or stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. t.', 'To cause to strike; to use as an instrument in striking\n   or hurling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. t.', 'To destroy the life of by beating, or by weapons of any\n   kind; to slay by a blow; to kill; as, to smite one with the sword, or\n   with an arrow or other instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. t.', 'To put to rout in battle; to overthrow by war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. t.', 'To blast; to destroy the life or vigor of, as by a stroke\n   or by some visitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. t.', 'To afflict; to chasten; to punish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. t.', 'To strike or affect with passion, as love or fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'v. i.', 'To strike; to collide; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smite', 'n.', 'The act of smiting; a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smiter', 'n.', 'One who smites.');
