  INSERT INTO words (word, word_type, description) VALUES  (    'Smith', 'n.', 'To beat into shape; to forge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smithcraft', 'n.', 'The art or occupation of a smith; smithing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smither', 'n.', 'Light, fine rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smither', 'n.', 'Fragments; atoms; finders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smithereens', 'n. pl.', 'Fragments; atoms; smithers.');
