  INSERT INTO words (word, word_type, description) VALUES  (    'Smithing', 'n.', 'The act or art of working or forging metals, as iron,\n   into any desired shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smithsonian', 'a.', 'Of or pertaining to the Englishman J. L. M. Smithson,\n   or to the national institution of learning which he endowed at\n   Washington, D. C.; as, the Smithsonian Institution; Smithsonian\n   Reports.');
