  INSERT INTO words (word, word_type, description) VALUES  (    'Smittlish', 'a.', 'Infectious; catching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smock', 'n.', 'A womans under-garment; a shift; a chemise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smock', 'n.', 'A blouse; a smoock frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smock', 'a.', 'Of or pertaining to a smock; resembling a smock; hence, of\n   or pertaining to a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smock', 'v. t.', 'To provide with, or clothe in, a smock or a smock frock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smock-faced', 'a.', 'Having a feminine countenance or complexion;\n   smooth-faced; girlish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smock frock', '', 'A coarse frock, or shirt, worn over the other dress, as\n   by farm laborers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smockless', 'a.', 'Wanting a smock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smokable', 'a.', 'Capable of being smoked; suitable or ready to be smoked;\n   as, smokable tobacco.');
